subroutine test01 ( kprint )

!*****************************************************************************80
!
!! TEST01 tests AAAAAA.
!
  implicit none

  integer i1mach
  integer ipass
  integer kprint
  integer lin
  integer lun
  integer nfail

  lun = i1mach(2)
  lin = i1mach(1)

  nfail = 0

  call xermax ( 1000 )
  call xsetun ( lun )

  if ( kprint  <=  1 ) then
    call xsetf ( 0 )
  else
    call xsetf ( 1 )
  end if

  call QC6A ( lun, kprint, ipass )

  if ( ipass == 0 ) then
    nfail = nfail + 1
  end if

  if ( nfail == 0 ) then
    write ( LUN, '(a)' ) 'TEST01 passed all tests.'
  else
    write (LUN, 9010) nfail
  end if

  return
 9010 FORMAT (/' ************* WARNING -- ', I5, &
          ' TEST(S) FAILED IN PROGRAM TEST01  *************')
end
