                     CHER2, CHKXER, CHPMV, CHPR, CHPR2, CTBMV, &
!***FIRST EXECUTABLE STATEMENT  CCHKE2
                     CTBSV, CTPMV, CTPSV, CTRMV, CTRSV
  call XGETF (KONTRL)
  if ( kprint  <=  2 ) then
    call xsetf ( 0 )
  else
    call xsetf ( 1 )
  end if
