  W( 9) = CATAN2(-.5E0-.866025403784438E0*CI,C1)
  W(10) = CATAN2(.5E0-.866025403784438E0*CI,C1)
  W(11) = CATAN2(.3125E-1+.541265877365273E-1*CI,C1)
  W(12) = CATAN2(-.3125E-1+.541265877365273E-1*CI,C1)
  W(13) = CCOT(.5E0+0.E0*CI)
