  W(16) = CTAN(.5E0+0.E0*CI)
  W(17) = CLOG10(1.E0-CI)
  W(18) = CLOG10(SQRT3+CI)
  W(19) = CEXPRL(PI*CI)
  W(20) = CEXPRL(1.E0+CI)
  W(21) = CLNREL(-.25E0*CI)
