  DATA DTRUE3/.0D0,.3D0,.7D0,1.1D0,1.D0/
  DATA DTRUE5/.10D0,2.D0,2.D0,2.D0,2.D0,2.D0,2.D0,2.D0, &
              .09D0,3.D0,3.D0,3.D0,3.D0,3.D0,3.D0,3.D0, &
