!! CHPQC
!***PURPOSE  Quick check for CHPFA, CHPCO, CHPSL and CHPDI.
!***LIBRARY   SLATEC
!***KEYWORDS  QUICK CHECK
!***AUTHOR  Voorhees, E. A., (LANL)
!***DESCRIPTION
!
!    LET  A*X=B  BE A COMPLEX LINEAR SYSTEM WHERE THE MATRIX  A  IS
!    OF THE PROPER TYPE FOR THE LINPACK SUBROUTINES BEING TESTED.
!    THE VALUES OF  A  AND  B  AND THE PRE-COMPUTED VALUES OF  C
!    (THE SOLUTION VECTOR),  AINV  (INVERSE OF MATRIX  A ),  DC
!    (DETERMINANT OF  A ), AND  RCND  ( RCOND ) ARE ENTERED
!    WITH DATA STATEMENTS.
!
!    THE COMPUTED TEST RESULTS FOR  X, RCOND, THE DETERMINANT, AND
!    THE INVERSE ARE COMPARED TO THE STORED PRE-COMPUTED VALUES.
!    FAILURE OF THE TEST OCCURS WHEN AGREEMENT TO 3 SIGNIFICANT
!    DIGITS IS NOT ACHIEVED AND AN ERROR MESSAGE INDICATING WHICH
!    LINPACK SUBROUTINE FAILED AND WHICH QUANTITY WAS INVOLVED IS
!    PRINTED.  A SUMMARY LINE IS ALWAYS PRINTED.
!
!    NO INPUT ARGUMENTS ARE REQUIRED.
!    ON RETURN,  NERR  (INTEGER TYPE) CONTAINS THE TOTAL COUNT OF
!    ALL FAILURES DETECTED BY CHPQC.
!
!***ROUTINES CALLED  CHPCO, CHPDI, CHPFA, CHPSL
!***REVISION HISTORY  (YYMMDD)
!   801022  DATE WRITTEN
!   890618  REVISION DATE from Version 3.2
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   901010  Restructured using IF-THEN-else-end if and cleaned up
!           FORMATs.  (RWC)
!***END PROLOGUE  CHPQC
subroutine CHPQC (LUN, KPRINT, NERR)
  COMPLEX AP(10),AT(10),B(4),BT(4),C(4),AINV(10), &
   Z(4),XA,XB
  REAL R,RCOND,RCND,DELX,DET(2),DC(2)
  CHARACTER KPROG*19, KFAIL*47
  integer N,IPVT(4),INFO,I,J,INDX,NERR
  integer INERT(3),IRT(3)
  DATA AP/(2.E0,0.E0),(0.E0,-1.E0),(2.E0,0.E0),(0.E0,0.E0), &
   (0.E0,0.E0),(3.E0,0.E0),(0.E0,0.E0),(0.E0,0.E0), &
   (0.E0,-1.E0),(4.E0,0.E0)/
  DATA B/(3.E0,2.E0),(-1.E0,3.E0),(0.E0,-4.E0),(5.E0,0.E0)/
  DATA C/(1.E0,1.E0),(0.E0,1.E0),(0.E0,-1.E0),(1.E0,0.E0)/
  DATA AINV/(.66667E0,0.E0),(0.E0,.33333E0),(.66667E0,0.E0), &
   (0.E0,0.E0), &
   (0.E0,0.E0),(.36364E0,0.E0),(0.E0,0.E0),(0.E0,0.E0), &
   (0.E0,.09091E0),(.27273E0,0.E0)/
  DATA DC/3.3E0,1.0E0/
  DATA KPROG/'HPFA HPCO HPSL HPDI'/
  DATA KFAIL/'INFO RCOND SOLUTION DETERMINANT INVERSE INERTIA'/
  DATA RCND/.24099E0/
!
  DATA IRT/4,0,0/
!***FIRST EXECUTABLE STATEMENT  CHPQC
  DELX(XA,XB)=ABS(REAL(XA-XB))+ABS(AIMAG(XA-XB))
  N = 4
!
!     FORM AT FOR CHPFA AND BT FOR CHPSL, TEST CHPFA
!
  NERR = 0
  BT(1:n) = B(1:n)

  DO 20 I=1,10
     AT(I) = AP(I)
!
   20 continue
  call CHPFA(AT,N,IPVT,INFO)
  if ( INFO /= 0 ) then
     write (LUN,201) KPROG(1:4),KFAIL(1:4)
     NERR = NERR + 1
!
!     TEST CHPSL
!
  end if
  call CHPSL(AT,N,IPVT,BT)
  INDX = 0
  DO 40 I=1,N
     if ( DELX(C(I),BT(I))  >  .0001) INDX=INDX+1
!
   40 continue
  if ( INDX /= 0 ) then
     write (LUN,201) KPROG(11:14),KFAIL(12:19)
     NERR = NERR + 1
!
!     FORM AT FOR CHPCO, TEST CHPCO
!
  end if
  DO 70 I=1,10
     AT(I) = AP(I)
!
   70 continue
  call CHPCO(AT,N,IPVT,RCOND,Z)
  R = ABS(RCND-RCOND)
  if ( R >= .0001 ) then
     write (LUN,201) KPROG(6:9),KFAIL(6:10)
     NERR = NERR + 1
!
!     TEST CHPDI FOR JOB=111
!
  end if
  call CHPDI(AT,N,IPVT,DET,INERT,Z,111)
  INDX = 0
  DO 110 I=1,2
     if ( ABS(DC(I)-DET(I))  >  .0001) INDX=INDX+1
!
  110 continue
  if ( INDX /= 0 ) then
     write (LUN,201) KPROG(16:19),KFAIL(21:31)
     NERR = NERR + 1
!
  end if
  INDX = 0
  DO 140 I=1,10
     if ( DELX(AINV(I),AT(I))  >  .0001) INDX=INDX+1
!
  140 continue
  if ( INDX /= 0 ) then
     write (LUN,201) KPROG(16:19),KFAIL(33:39)
     NERR = NERR + 1
!
  end if
  INDX = 0
  DO 160 I=1,3
     if ( (INERT(I)-IRT(I)) /= 0) INDX=INDX+1
!
  160 continue
  if ( INDX /= 0 ) then
     write (LUN,201) KPROG(16:19),KFAIL(41:47)
     NERR = NERR + 1
!
  end if
  if ( KPRINT >= 2 .OR. NERR /= 0) write (LUN,200) NERR
!
  return
  200 FORMAT(/' * CHPQC - TEST FOR CHPFA, CHPCO, CHPSL AND CHPDI FOUND ' &
     , I1, ' ERRORS.'/)
  201 FORMAT (/' *** C', A, ' FAILURE - ERROR IN ', A)
end
subroutine CMAKE2 (TYPE, UPLO, DIAG, M, N, A, NMAX, AA, LDA, KL, &
!! CMAKE2
!***SUBSIDIARY
!***PURPOSE  Generate values for an M by N matrix A.
!***LIBRARY   SLATEC (BLAS)
!***AUTHOR  Du Croz, J. J., (NAG)
!           Hanson, R. J., (SNLA)
!***DESCRIPTION
!
!  Generates values for an M by N matrix A within the bandwidth
!  defined by KL and KU.
!  Stores the values in the array AA in the data structure required
!  by the routine, with unwanted elements set to rogue value.
!
!  TYPE is 'GE', 'GB', 'SY', 'SB', 'SP', 'TR', 'TB' OR 'TP'.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  CBEG
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910620  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  CMAKE2
!     .. Parameters ..
     KU, RESET, TRANSL)
  COMPLEX            ZERO, ONE
  PARAMETER          ( ZERO = ( 0.0, 0.0 ), ONE = ( 1.0, 0.0 ) )
  COMPLEX            ROGUE
  PARAMETER          ( ROGUE = ( -1.0E10, 1.0E10 ) )
  REAL               RZERO
  PARAMETER          ( RZERO = 0.0 )
  REAL               RROGUE
!     .. Scalar Arguments ..
  PARAMETER          ( RROGUE = -1.0E10 )
  COMPLEX            TRANSL
  integer            KL, KU, LDA, M, N, NMAX
  LOGICAL            RESET
  CHARACTER*1        DIAG, UPLO
!     .. Array Arguments ..
  CHARACTER*2        TYPE
!     .. Local Scalars ..
  COMPLEX            A( NMAX, * ), AA( * )
  integer            I, I1, I2, I3, IBEG, IEND, IOFF, J, JJ, KK
!     .. External Functions ..
  LOGICAL            GEN, LOWER, SYM, TRI, UNIT, UPPER
  COMPLEX            CBEG
!     .. Intrinsic Functions ..
  EXTERNAL           CBEG
!***FIRST EXECUTABLE STATEMENT  CMAKE2
  INTRINSIC          CMPLX, CONJG, MAX, MIN, REAL
  GEN = TYPE( 1: 1 ) == 'G'
  SYM = TYPE( 1: 1 ) == 'H'
  TRI = TYPE( 1: 1 ) == 'T'
  UPPER = ( SYM.OR.TRI ) .and. UPLO == 'U'
  LOWER = ( SYM.OR.TRI ) .and. UPLO == 'L'
!
!  Generate data in array A.
!
  UNIT = TRI .and. DIAG == 'U'
  DO 20 J = 1, N

     DO I = 1, M
        if (  GEN.OR.( UPPER .and. I <= J ).OR.( LOWER.AND.I >= J ) ) &
            THEN
           if (  ( I <= J .and. J - I <= KU ).OR. &
               ( I >= J .and. I - J <= KL )  ) then
              A( I, J ) = CBEG( RESET ) + TRANSL
           else
              A( I, J ) = ZERO
           end if
           if (  I /= J  ) then
              if (  SYM  ) then
                 A( J, I ) = CONJG( A( I, J ) )
              else if (  TRI  ) then
                 A( J, I ) = ZERO
              end if
           end if
        end if
     end do

     if (  SYM ) &
        A( J, J ) = CMPLX( REAL( A( J, J ) ), RZERO )
     if (  TRI ) &
        A( J, J ) = A( J, J ) + ONE
     if (  UNIT ) &
        A( J, J ) = ONE
!
!     Store elements in array AS in data structure required by routine.
!
   20 continue
  if (  TYPE == 'GE'  ) then
     DO 50 J = 1, N
        DO 30 I = 1, M
           AA( I + ( J - 1 )*LDA ) = A( I, J )
   30       continue
        DO 40 I = M + 1, LDA
           AA( I + ( J - 1 )*LDA ) = ROGUE
   40       continue
   50    continue
  else if (  TYPE == 'GB'  ) then
     DO 90 J = 1, N
        DO 60 I1 = 1, KU + 1 - J
           AA( I1 + ( J - 1 )*LDA ) = ROGUE
   60       continue
        DO 70 I2 = I1, min (  KL + KU + 1, KU + 1 + M - J )
           AA( I2 + ( J - 1 )*LDA ) = A( I2 + J - KU - 1, J )
   70       continue
        DO 80 I3 = I2, LDA
           AA( I3 + ( J - 1 )*LDA ) = ROGUE
   80       continue
   90    continue
  else if (  TYPE == 'HE'.OR.TYPE == 'TR'  ) then
     DO 130 J = 1, N
        if (  UPPER  ) then
           IBEG = 1
           if (  UNIT  ) then
              IEND = J - 1
           else
              IEND = J
           end if
        else
           if (  UNIT  ) then
              IBEG = J + 1
           else
              IBEG = J
           end if
           IEND = N
        end if

        DO I = 1, IBEG - 1
           AA( I + ( J - 1 )*LDA ) = ROGUE
        end do

        DO 110 I = IBEG, IEND
           AA( I + ( J - 1 )*LDA ) = A( I, J )
  110       continue
        DO 120 I = IEND + 1, LDA
           AA( I + ( J - 1 )*LDA ) = ROGUE
  120       continue
        if (  SYM  ) then
           JJ = J + ( J - 1 )*LDA
           AA( JJ ) = CMPLX( REAL( AA( JJ ) ), RROGUE )
        end if
  130    continue
  else if (  TYPE == 'HB'.OR.TYPE == 'TB'  ) then
     DO 170 J = 1, N
        if (  UPPER  ) then
           KK = KL + 1
           IBEG = max (  1, KL + 2 - J )
           if (  UNIT  ) then
              IEND = KL
           else
              IEND = KL + 1
           end if
        else
           KK = 1
           if (  UNIT  ) then
              IBEG = 2
           else
              IBEG = 1
           end if
           IEND = min (  KL + 1, 1 + M - J )
        end if
        DO 140 I = 1, IBEG - 1
           AA( I + ( J - 1 )*LDA ) = ROGUE
  140       continue
        DO 150 I = IBEG, IEND
           AA( I + ( J - 1 )*LDA ) = A( I + J - KK, J )
  150       continue
        DO 160 I = IEND + 1, LDA
           AA( I + ( J - 1 )*LDA ) = ROGUE
  160       continue
        if (  SYM  ) then
           JJ = KK + ( J - 1 )*LDA
           AA( JJ ) = CMPLX( REAL( AA( JJ ) ), RROGUE )
        end if
  170    continue
  else if (  TYPE == 'HP'.OR.TYPE == 'TP'  ) then
     IOFF = 0
     DO 190 J = 1, N
        if (  UPPER  ) then
           IBEG = 1
           IEND = J
        else
           IBEG = J
           IEND = N
        end if
        DO 180 I = IBEG, IEND
           IOFF = IOFF + 1
           AA( IOFF ) = A( I, J )
           if (  I == J  ) then
              if (  UNIT ) &
                 AA( IOFF ) = ROGUE
              if (  SYM ) &
                 AA( IOFF ) = CMPLX( REAL( AA( IOFF ) ), RROGUE )
           end if
  180       continue
  190    continue
  end if
!
!     End of CMAKE2.
!
  return
end
subroutine CMAKE3 (TYPE, UPLO, DIAG, M, N, A, NMAX, AA, LDA, &
!! CMAKE3
!***SUBSIDIARY
!***PURPOSE  Generate values for an M by N matrix A.
!***LIBRARY   SLATEC (BLAS)
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Generates values for an M by N matrix A within the bandwidth
!  defined by KL and KU.
!  Stores the values in the array AA in the data structure required
!  by the routine, with unwanted elements set to rogue value.
!
!  TYPE is 'GE', 'HE', 'SY', OR 'TR'.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  CBEG
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910620  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  CMAKE3
!     .. Parameters ..
     RESET, TRANSL)
  COMPLEX            ZERO, ONE
  PARAMETER          ( ZERO = ( 0.0, 0.0 ), ONE = ( 1.0, 0.0 ) )
  COMPLEX            ROGUE
  PARAMETER          ( ROGUE = ( -1.0E10, 1.0E10 ) )
  REAL               RZERO
  PARAMETER          ( RZERO = 0.0 )
  REAL               RROGUE
!     .. Scalar Arguments ..
  PARAMETER          ( RROGUE = -1.0E10 )
  COMPLEX            TRANSL
  integer            LDA, M, N, NMAX
  LOGICAL            RESET
  CHARACTER*1        DIAG, UPLO
!     .. Array Arguments ..
  CHARACTER*2        TYPE
!     .. Local Scalars ..
  COMPLEX            A( NMAX, * ), AA( * )
  integer            I, IBEG, IEND, J, JJ
!     .. External Functions ..
  LOGICAL            GEN, LOWER, SYM, TRI, UNIT, UPPER, HER
  COMPLEX            CBEG
!     .. Intrinsic Functions ..
  EXTERNAL           CBEG
!***FIRST EXECUTABLE STATEMENT  CMAKE3
  INTRINSIC          CMPLX, CONJG, REAL
  GEN = TYPE == 'GE'
  HER = TYPE == 'HE'
  SYM = TYPE == 'SY'
  TRI = TYPE == 'TR'
  UPPER = ( HER.OR.SYM.OR.TRI ) .and. UPLO == 'U'
  LOWER = ( HER.OR.SYM.OR.TRI ) .and. UPLO == 'L'
!
!     Generate data in array A.
!
  UNIT = TRI .and. DIAG == 'U'
  DO 20 J = 1, N

     DO I = 1, M
        if (  GEN.OR.( UPPER .and. I <= J ).OR.( LOWER.AND.I >= J ) ) &
            THEN
           A( I, J ) = CBEG( RESET ) + TRANSL
!                 Set some elements to zero
           if (  I /= J  ) then
              if (  N > 3 .and. J == N/2 ) &
                 A( I, J ) = ZERO
              if (  HER  ) then
                 A( J, I ) = CONJG( A( I, J ) )
              else if (  SYM  ) then
                 A( J, I ) = A( I, J )
              else if (  TRI  ) then
                 A( J, I ) = ZERO
              end if
           end if
        end if
     end do

     if (  HER ) &
        A( J, J ) = CMPLX( REAL( A( J, J ) ), RZERO )
     if (  TRI ) &
        A( J, J ) = A( J, J ) + ONE
     if (  UNIT ) &
        A( J, J ) = ONE
!
!     Store elements in array AS in data structure required by routine.
!
   20 continue
  if (  TYPE == 'GE'  ) then
     DO 50 J = 1, N
        DO 30 I = 1, M
           AA( I + ( J - 1 )*LDA ) = A( I, J )
   30       continue
        DO 40 I = M + 1, LDA
           AA( I + ( J - 1 )*LDA ) = ROGUE
   40       continue
   50    continue
  else if (  TYPE == 'HE'.OR.TYPE == 'SY'.OR.TYPE == 'TR'  ) then
     DO 90 J = 1, N
        if (  UPPER  ) then
           IBEG = 1
           if (  UNIT  ) then
              IEND = J - 1
           else
              IEND = J
           end if
        else
           if (  UNIT  ) then
              IBEG = J + 1
           else
              IBEG = J
           end if
           IEND = N
        end if
        DO 60 I = 1, IBEG - 1
           AA( I + ( J - 1 )*LDA ) = ROGUE
   60       continue
        DO 70 I = IBEG, IEND
           AA( I + ( J - 1 )*LDA ) = A( I, J )
   70       continue
        DO 80 I = IEND + 1, LDA
           AA( I + ( J - 1 )*LDA ) = ROGUE
   80       continue
        if (  HER  ) then
           JJ = J + ( J - 1 )*LDA
           AA( JJ ) = CMPLX( REAL( AA( JJ ) ), RROGUE )
        end if
   90    continue
  end if
!
!     End of CMAKE3.
!
  return
end
subroutine CMMCH (TRANSA, TRANSB, M, N, KK, ALPHA, A, LDA, B, LDB, &
!! CMMCH
!***SUBSIDIARY
!***PURPOSE  Check the results of the computational tests.
!***LIBRARY   SLATEC (BLAS)
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Checks the results of the computational tests.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  (NONE)
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910620  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  CMMCH
!     .. Parameters ..
     BETA, C, LDC, CT, G, CC, LDCC, EPS, ERR, FTL, NOUT, MV, KPRINT)
  COMPLEX            ZERO
  PARAMETER          ( ZERO = ( 0.0, 0.0 ) )
  REAL               RZERO, RONE
!     .. Scalar Arguments ..
  PARAMETER          ( RZERO = 0.0, RONE = 1.0 )
  LOGICAL            FTL
  COMPLEX            ALPHA, BETA
  REAL               EPS, ERR
  integer            KK, KPRINT, LDA, LDB, LDC, LDCC, M, N, NOUT
  LOGICAL            MV
!     .. Array Arguments ..
  CHARACTER*1        TRANSA, TRANSB
  COMPLEX            A( LDA, * ), B( LDB, * ), C( LDC, * ), &
                     CC( LDCC, * ), CT( * )
!     .. Local Scalars ..
  REAL               G( * )
  COMPLEX            CL
  REAL               ERRI
  integer            I, J, K
!     .. Intrinsic Functions ..
  LOGICAL            CTRANA, CTRANB, TRANA, TRANB
!     .. Statement Functions ..
  INTRINSIC          ABS, AIMAG, CONJG, MAX, REAL, SQRT
!     .. Statement Function definitions ..
  REAL               ABS1
!***FIRST EXECUTABLE STATEMENT  CMMCH
  ABS1( CL ) = ABS( REAL( CL ) ) + ABS( AIMAG( CL ) )
  TRANA = TRANSA == 'T'.OR.TRANSA == 'C'
  TRANB = TRANSB == 'T'.OR.TRANSB == 'C'
  CTRANA = TRANSA == 'C'
!
!     Compute expected result, one column at a time, in CT using data
!     in A, B and C.
!     Compute gauges in G.
!
  CTRANB = TRANSB == 'C'
!
  DO 220 J = 1, N

     CT(1:m) = ZERO
     G(1:m) = RZERO

     if (  .NOT.TRANA .and. .NOT.TRANB  ) then
        DO 30 K = 1, KK
           DO 20 I = 1, M
              CT( I ) = CT( I ) + A( I, K )*B( K, J )
              G( I ) = G( I ) + ABS1( A( I, K ) )*ABS1( B( K, J ) )
   20          continue
   30       continue
     else if (  TRANA .and. .NOT.TRANB  ) then
        if (  CTRANA  ) then
           DO 50 K = 1, KK
              DO 40 I = 1, M
                 CT( I ) = CT( I ) + CONJG( A( K, I ) )*B( K, J )
                 G( I ) = G( I ) + ABS1( A( K, I ) )* &
                          ABS1( B( K, J ) )
   40             continue
   50          continue
        else
           DO 70 K = 1, KK
              DO 60 I = 1, M
                 CT( I ) = CT( I ) + A( K, I )*B( K, J )
                 G( I ) = G( I ) + ABS1( A( K, I ) )* &
                          ABS1( B( K, J ) )
   60             continue
   70          continue
        end if
     else if (  .NOT.TRANA .and. TRANB  ) then
        if (  CTRANB  ) then
           DO 90 K = 1, KK
              DO 80 I = 1, M
                 CT( I ) = CT( I ) + A( I, K )*CONJG( B( J, K ) )
                 G( I ) = G( I ) + ABS1( A( I, K ) )* &
                          ABS1( B( J, K ) )
   80             continue
   90          continue
        else

           DO K = 1, KK
              DO I = 1, M
                 CT( I ) = CT( I ) + A( I, K )*B( J, K )
                 G( I ) = G( I ) + ABS1( A( I, K ) )* &
                          ABS1( B( J, K ) )
              end do
           end do

        end if
     else if (  TRANA .and. TRANB  ) then
        if (  CTRANA  ) then
           if (  CTRANB  ) then
              DO 130 K = 1, KK
                 DO 120 I = 1, M
                    CT( I ) = CT( I ) + CONJG( A( K, I ) )* &
                              CONJG( B( J, K ) )
                    G( I ) = G( I ) + ABS1( A( K, I ) )* &
                             ABS1( B( J, K ) )
  120                continue
  130             continue
           else
              DO 150 K = 1, KK
                 DO 140 I = 1, M
                    CT( I ) = CT( I ) + CONJG( A( K, I ) )*B( J, K )
                    G( I ) = G( I ) + ABS1( A( K, I ) )* &
                             ABS1( B( J, K ) )
  140                continue
  150             continue
           end if
        else
           if (  CTRANB  ) then
              DO 170 K = 1, KK
                 DO 160 I = 1, M
                    CT( I ) = CT( I ) + A( K, I )*CONJG( B( J, K ) )
                    G( I ) = G( I ) + ABS1( A( K, I ) )* &
                             ABS1( B( J, K ) )
  160                continue
  170             continue
           else
              DO 190 K = 1, KK
                 DO 180 I = 1, M
                    CT( I ) = CT( I ) + A( K, I )*B( J, K )
                    G( I ) = G( I ) + ABS1( A( K, I ) )* &
                             ABS1( B( J, K ) )
  180                continue
  190             continue
           end if
        end if
     end if
     DO 200 I = 1, M
        CT( I ) = ALPHA*CT( I ) + BETA*C( I, J )
        G( I ) = ABS1( ALPHA )*G( I ) + &
                 ABS1( BETA )*ABS1( C( I, J ) )
!
!        Compute the error ratio for this result.
!
  200    continue
     ERR = ZERO
     DO 210 I = 1, M
        ERRI = ABS1( CT( I ) - CC( I, J ) )/EPS
        if (  G( I ) /= RZERO ) &
           ERRI = ERRI/G( I )
        ERR = max (  ERR, ERRI )
        if (  ERR*SQRT( EPS ) >= RONE  ) then
           FTL = .TRUE.
           if ( kprint >= 2 ) then
             write ( NOUT, FMT = 9999 )
             DO 240 K = 1, M
             if (  MV  ) then
               write ( NOUT, FMT = 9998 )K, CT( K ), CC( K, J )
             else
               write ( NOUT, FMT = 9998 )K, CC( K, J ), CT( K )
             end if
  240            continue
           end if
      end if
  210   continue
  220   continue
!
  return
 9999 FORMAT( ' ******* FATAL ERROR - COMPUTED RESULT IS LESS THAN HAL', &
        'F ACCURATE *******', /'                       EXPECTED RE', &
        'SULT                    COMPUTED RESULT' )
!
!     End of CMMCH.
!
 9998 FORMAT( 1X, I7, 2( '  (', G15.6, ',', G15.6, ')' ) )
end
!! CMPARE
!***PURPOSE  Compare values in COMMON block CHECK for quick check
!            routine PFITQX.
!***LIBRARY   SLATEC
!***TYPE      SINGLE PRECISION (CMPARE-S, DCMPAR-D)
!***AUTHOR  (UNKNOWN)
!***ROUTINES CALLED  (NONE)
!***COMMON BLOCKS    CHECK
!***REVISION HISTORY  (YYMMDD)
!   ??????  DATE WRITTEN
!   890921  Realigned order of variables in the COMMON block.
!           (WRB)
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   920214  Minor improvements to code for readability.  (WRB)
!***END PROLOGUE  CMPARE
!     .. Scalar Arguments ..
subroutine CMPARE (ICNT, ITEST)
!     .. Array Arguments ..
  integer ICNT
!     .. Scalars in Common ..
  integer ITEST(9)
  REAL EPS, RP, SVEPS, TOL
!     .. Arrays in Common ..
  integer IERP, IERR, NORD, NORDP
!     .. Local Scalars ..
  REAL R(11)
  REAL RPP, SS
!     .. Local Arrays ..
  integer IERPP, NRDP
!     .. Intrinsic Functions ..
  integer ITEMP(4)
!     .. Common blocks ..
  INTRINSIC ABS
!***FIRST EXECUTABLE STATEMENT  CMPARE
  COMMON /CHECK/ EPS, R, RP, SVEPS, TOL, NORDP, NORD, IERP, IERR
  ICNT = ICNT + 1
  ITEMP(1) = 0
  ITEMP(2) = 0
  ITEMP(3) = 0
  ITEMP(4) = 0
  SS = SVEPS - EPS
  NRDP = NORDP - NORD
  RPP = RP - R(11)
  IERPP = IERP - IERR
  if ( ABS(SS) <= TOL .OR. ICNT <= 2 .OR. ICNT >= 6) ITEMP(1) = 1
  if ( ABS(NRDP) == 0) ITEMP(2) = 1
  if ( ABS(RPP)  <=  TOL) ITEMP(3) = 1
!
!     Check to see if all four tests were good.
!     If so, set the test number equal to 1.
!
  if ( ABS(IERPP) == 0) ITEMP(4) = 1
  ITEST(ICNT) = ITEMP(1)*ITEMP(2)*ITEMP(3)*ITEMP(4)
  return
end
subroutine CMVCH (TRANS, M, N, ALPHA, A, NMAX, X, INCX, BETA, Y, &
!! CMVCH
!***SUBSIDIARY
!***PURPOSE  Check the results of the computational tests.
!***LIBRARY   SLATEC (BLAS)
!***AUTHOR  Du Croz, J. J., (NAG)
!           Hanson, R. J., (SNLA)
!***DESCRIPTION
!
!  Checks the results of the computational tests.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  (NONE)
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910620  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  CMVCH
!     .. Parameters ..
     INCY, YT, G, YY, EPS, ERR, FTL, NOUT, MV, KPRINT)
  COMPLEX            ZERO
  PARAMETER          ( ZERO = ( 0.0, 0.0 ) )
  REAL               RZERO, RONE
!     .. Scalar Arguments ..
  PARAMETER          ( RZERO = 0.0, RONE = 1.0 )
  COMPLEX            ALPHA, BETA
  REAL               EPS, ERR
  integer            INCX, INCY, KPRINT, M, N, NMAX, NOUT
  LOGICAL            MV, FTL
!     .. Array Arguments ..
  CHARACTER*1        TRANS
  COMPLEX            A( NMAX, * ), X( * ), Y( * ), YT( * ), YY( * )
!     .. Local Scalars ..
  REAL               G( * )
  COMPLEX            C
  REAL               ERRI
  integer            I, INCXL, INCYL, IY, J, JX, K, KX, KY, ML, NL
!     .. Intrinsic Functions ..
  LOGICAL            CTRAN, TRAN
!     .. Statement Functions ..
  INTRINSIC          ABS, AIMAG, CONJG, MAX, REAL, SQRT
!     .. Statement Function definitions ..
  REAL               ABS1
!***FIRST EXECUTABLE STATEMENT  CMVCH
  ABS1( C ) = ABS( REAL( C ) ) + ABS( AIMAG( C ) )
  TRAN = TRANS == 'T'
  CTRAN = TRANS == 'C'
  if (  TRAN.OR.CTRAN  ) then
     ML = N
     NL = M
  else
     ML = M
     NL = N
  end if
  if (  INCX < 0  ) then
     KX = NL
     INCXL = -1
  else
     KX = 1
     INCXL = 1
  end if
  if (  INCY < 0  ) then
     KY = ML
     INCYL = -1
  else
     KY = 1
     INCYL = 1
!
!     Compute expected result in YT using data in A, X and Y.
!     Compute gauges in G.
!
  end if
  IY = KY
  DO 40 I = 1, ML
     YT( IY ) = ZERO
     G( IY ) = RZERO
     JX = KX

     if (  TRAN  ) then

        DO J = 1, NL
           YT( IY ) = YT( IY ) + A( J, I )*X( JX )
           G( IY ) = G( IY ) + ABS1( A( J, I ) )*ABS1( X( JX ) )
           JX = JX + INCXL
        end do

     else if (  CTRAN  ) then
        DO 20 J = 1, NL
           YT( IY ) = YT( IY ) + CONJG( A( J, I ) )*X( JX )
           G( IY ) = G( IY ) + ABS1( A( J, I ) )*ABS1( X( JX ) )
           JX = JX + INCXL
   20       continue
     else
        DO 30 J = 1, NL
           YT( IY ) = YT( IY ) + A( I, J )*X( JX )
           G( IY ) = G( IY ) + ABS1( A( I, J ) )*ABS1( X( JX ) )
           JX = JX + INCXL
   30       continue
     end if
     YT( IY ) = ALPHA*YT( IY ) + BETA*Y( IY )
     G( IY ) = ABS1( ALPHA )*G( IY ) + ABS1( BETA )*ABS1( Y( IY ) )
     IY = IY + INCYL
!
!     Compute the error ratio for this result.
!
   40 continue
  ERR = ZERO
  DO 50 I = 1, ML
     ERRI = ABS( YT( I ) - YY( 1 + ( I - 1 )*ABS( INCY ) ) )/EPS
     if (  G( I ) /= RZERO ) &
        ERRI = ERRI/G( I )
     ERR = max (  ERR, ERRI )
     if (  ERR*SQRT( EPS ) >= RONE  ) then
       FTL = .TRUE.
       if ( kprint >= 2 ) then
          write ( NOUT, FMT = 9999 )
          DO 70 K = 1, ML
             if (  MV  ) then
                write ( NOUT, FMT = 9998 )K, YT( K ), &
                   YY( 1 + ( K - 1 )*ABS( INCY ) )
             else
                write ( NOUT, FMT = 9998 )I, &
                   YY( 1 + ( K - 1 )*ABS( INCY ) ), YT( K )
             end if
   70         continue
        end if
!
     end if
   50 continue
!
  return
 9999 FORMAT( ' ******* FATAL ERROR - COMPUTED RESULT IS LESS THAN HAL', &
        'F ACCURATE *******', /'                       EXPECTED RE', &
        'SULT                    COMPUTED RESULT' )
!
!     End of CMVCH.
!
 9998 FORMAT( 1X, I7, 2( '  (', G15.6, ',', G15.6, ')' ) )
end
