!
!     See if test passed
!
  call DPOLFT (M, X, Y, W, MAXORD, NORD, EPS, R, IERR, A)
!
!     Check for suppression of printing.
!
  call DCMPAR (ICNT, ITEST)
  if ( kprint == 0) GO TO 190
