!     Check for suppression of printing.
!
  M = -2
  call XGETF (KONTRL)
  if ( kprint  <=  2 ) then
     call XSETF (0)
  else
     call XSETF (1)
  end if
!
  call xerclr
  if ( kprint >= 3) write (LUN,9090)
!
!     See if test passed
!
  call DPOLFT (M, X, Y, W, MAXORD, NORD, EPS, R, IERR, A)
  ICNT = ICNT + 1
  if ( IERR == 2 ) then
    ITEST(ICNT) = 1
    if ( kprint >= 3 ) then
      write (LUN, 9100) 'PASSED', IERR
    end if
  else
