subroutine DUTERR (METHOD, IERR, IOUT, nfail, ISTDO, ITER, ERR)
  double precision ERR
  integer IERR, IOUT, ISTDO, ITER, nfail
!***FIRST EXECUTABLE STATEMENT  DUTERR
  CHARACTER METHOD*6
