  REAL ERR
  integer IERR, IOUT, ISTDO, ITER, nfail
!***FIRST EXECUTABLE STATEMENT  OUTERR
  CHARACTER METHOD*6
  if (  IERR /= 0 ) nfail = nfail + 1
  if (  IOUT == 1  .and.  IERR /= 0  ) then
