  NCOMP = 2
  NXPTS = 15
  A(1,1) = 1.0D+00
  A(1,2) = 0.0D+00
  NROWA = 2
  ALPHA(1) = 5.0D+00
  NIC = 1
  B(1,1) = 1.0D+00
  B(1,2) = 0.0D+00
  NROWB = 2
  BETA(1) = 0.0D+00
  NFC = 1
  IGOFX = 1
  RE = 1.0D-05
  AE = 1.0D-05
  NDW = 1000
  NDIW = 100
  NEQIVP = 0
  ipass = 1

  DO 20 I = 1, 15
     IWORK(I) = 0
   20 continue

  call DBVSUP(Y,NROWY,NCOMP,XPTS,NXPTS,A,NROWA,ALPHA,NIC,B,NROWB, &
!
!  IF IFLAG = 0, WE HAVE A SUCCESSFUL SOLUTION; OTHERWISE, SKIP
!     THE ARGUMENT CHECKING AND GO TO THE END.
!
       BETA,NFC,IGOFX,RE,AE,IFLAG,WORK,NDW,IWORK,NDIW,NEQIVP)
  if ( IFLAG /= 0 ) then
     ipass = 0
     if ( kprint  >  1) write (LUN,820) IFLAG
     GO TO 170
!
!  CHECK THE ACCURACY OF THE SOLUTION.
!
  end if
  NUMORT = IWORK(1)
  DO 50 J = 1, NXPTS
     DO 40 L = 1, 2
        ABSER = ABS(YANS(L,J)-Y(L,J))
        RELER = ABSER/ABS(YANS(L,J))
        if ( RELER > TOL  .and.  ABSER > TOL) ipass = 0
   40    continue
!
!  CHECK FOR SUPPRESSION OF PRINTING.
!
   50 continue
  if ( kprint == 0 .OR. (KPRINT == 1  .and.  ipass == 1)) GO TO 190

  if ( kprint /= 1 .OR. ipass /= 0 ) then
     if ( KPRINT >= 3 .OR. ipass == 0 ) then
        write (LUN,830)
        write (LUN,840) NUMORT
        write (LUN,850) (WORK(J),J = 1, NUMORT)
        write (LUN,860)
        DO 60 J = 1, NXPTS
           MSG = 'PASS'
           ABSER = ABS(YANS(1,J)-Y(1,J))
           RELER = ABSER/ABS(YANS(1,J))
           if ( RELER > TOL  .and.  ABSER > TOL) MSG = 'FAIL'
           ABSER = ABS(YANS(2,J)-Y(2,J))
           RELER = ABSER/ABS(YANS(2,J))
           if ( RELER > TOL  .and.  ABSER > TOL) MSG = 'FAIL'
           write (LUN,870) XPTS(J),Y(1,J),Y(2,J),YANS(1,J), &
              YANS(2,J),MSG
   60       continue
     end if
!
!  SEND MESSAGE INDICATING PASSAGE OR FAILURE OF TESTS.
!
  end if
!
!  ERROR MESSAGE TESTS.
!
  call PASS (LUN, 1, IPASS)
  if ( kprint == 1) GO TO 190
  KONT = 1
!
!  NROWY LESS THAN NCOMP
!
  write (LUN,880)
  KOUNT = 1
  NROWY = 1
  150 DO 160 I = 1, 15
     IWORK(I) = 0
  160 continue
  call DBVSUP(Y,NROWY,NCOMP,XPTS,NXPTS,A,NROWA,ALPHA,NIC,B,NROWB, &
       BETA,NFC,IGOFX,RE,AE,IFLAG,WORK,NDW,IWORK,NDIW,NEQIVP)
!
  GO TO (80,90,100,110,120,130,140), KOUNT
   80 write (LUN,900) IFLAG
  if ( IFLAG == -2) ITMP(KONT) = 1
!
!  IGOFX NOT EQUAL TO 0 OR 1
!
  KONT = KONT + 1
  KOUNT = 2
  NROWY = 2
  IGOFX = 3
!
  GO TO 150
   90 write (LUN,900) IFLAG
  if ( IFLAG == -2) ITMP(KONT) = 1
!
!  RE OR AE NEGATIVE
!
  KONT = KONT + 1
  KOUNT = 3
  IGOFX = 1
  RE = -1.0D+00
  AE = -2.0D+00
!
  GO TO 150
  100 write (LUN,900) IFLAG
  if ( IFLAG == -2) ITMP(KONT) = 1
!
!  NROWA LESS THAN NIC
!
  KONT = KONT + 1
  KOUNT = 4
  RE = 1.0D-05
  AE = 1.0D-05
!
  NROWA = 0
  110 write (LUN,900) IFLAG
  if ( IFLAG == -2) ITMP(KONT) = 1
!  NROWB LESS THAN NFC
  KONT = KONT + 1
  KOUNT = 5
  NROWA = 2
!
  NROWB = 0
  120 write (LUN,900) IFLAG
  if ( IFLAG == -2) ITMP(KONT) = 1
!  STORAGE ALLOCATION IS INSUFFICIENT
  KONT = KONT + 1
  KOUNT = 6
  NROWB = 2
  NDIW = 17
!
  GO TO 150
  130 write (LUN,910) IFLAG
  if ( IFLAG == -1) ITMP(KONT) = 1
!  INCORRECT ORDERING OF XPTS
  KONT = KONT + 1
  KOUNT = 7
  NDIW = 100
  SVE = XPTS(1)
  XPTS(1) = XPTS(4)
  XPTS(4) = SVE
!
  GO TO 150
  140 write (LUN,900) IFLAG
!
!  SEE IF IFLAG TESTS PASSED
!
  if ( IFLAG == -2) ITMP(KONT) = 1
  170 IPSS = 1
  DO 180 I = 1, KONT
     IPSS = IPSS*ITMP(I)
!
  180 continue
!
!  SEE IF ALL TESTS PASSED.
!
  call PASS (LUN, 2, IPSS)
!
  ipass = IPASS*IPSS
  190 if ( ipass == 1  .and.  kprint  >  1) write (LUN,980)
  if ( ipass == 0  .and.  kprint /= 0) write (LUN,990)
!
  return
  800 FORMAT ('1')
  810 FORMAT (/' DBVSUP QUICK CHECK')
  820 FORMAT (10X,'IFLAG =',I2)
  830 FORMAT (/' ACCURACY TEST')
  840 FORMAT (/' NUMBER OF ORTHONORMALIZATIONS =',I3)
  850 FORMAT (/' ORTHONORMALIZATION POINTS ARE'/(1X,4F10.2))
  860 FORMAT (//20X,'CALCULATION',30X,'TRUE SOLUTION'/ &
     2X,'X',14X,'Y',17X,'Y-PRIME',15X,'Y',17X,'Y-PRIME'/)
  870 FORMAT (F5.1,4E20.7,5X,A)
  880 FORMAT (/' (7) TESTS OF IFLAG VALUES')
  900 FORMAT (/' IFLAG SHOULD BE -2, IFLAG =',I3)
  910 FORMAT (/' IFLAG SHOULD BE -1, IFLAG =',I3)
  980 FORMAT (/' ***************DBVSUP PASSED ALL TESTS***************')
  990 FORMAT (/' ***************DBVSUP FAILED SOME TESTS**************')
end
!! QXDRKF
!***PURPOSE  Test the DEPAC routine DDERKF.
!***LIBRARY   SLATEC
!***TYPE      DOUBLE PRECISION (QXRKF-S, QXDRKF-D)
!***KEYWORDS  QUICK CHECK
!***AUTHOR  Chow, Jeff, (LANL)
!***DESCRIPTION
!
! *Usage:
!
!        INTEGER  LUN, KPRINT, IPASS
!
!        call QXDRKF (LUN, KPRINT, IPASS)
!
! *Arguments:
!
!     LUN   :IN  is the unit number to which output is to be written.
!
!     KPRINT:IN  controls the amount of output, as specified in the
!                SLATEC Guidelines.
!
!     IPASS:OUT  will contain a pass/fail flag.  IPASS=1 is good.
!                IPASS=0 indicates one or more tests failed.
!
! *Description:
!
!   DDERKF is tested by solving the equations of motion of a body
!   moving in a plane about a spherical earth, namely
!           (D/DT)(D/DT)X = -G*X/R**3
!           (D/DT)(D/DT)Y = -G*Y/R**3
!   where G = 1, R = SQRT(X**2 + Y**2) and
!           X(0) = 1
!           (D/DT)X(0) = 0
!           Y(0) = 0
!           (D/DT)Y(0) = 1.
!
!***ROUTINES CALLED  d1mach, DDERKF, DFDEQC
!***REVISION HISTORY  (YYMMDD)
!   810801  DATE WRITTEN
!   890618  REVISION DATE from Version 3.2
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   900415  Code extensively revised.  (WRB)
!***END PROLOGUE  QXDRKF
!
!     Declare arguments.
!
subroutine QXDRKF (LUN, KPRINT, IPASS)
!
!     Declare local variables.
!
  integer  LUN, KPRINT, IPASS
  integer  IDID, INFO(15), IPAR, IWORK(34), N, LIW, LRW, NSTEP
  double precision ABSERR, d1mach, R, RELERR, RELTOL, RPAR, &
                   RWORK(61), T, TOUT, U(4)
!***FIRST EXECUTABLE STATEMENT  QXDRKF
  EXTERNAL DFDEQC
!
!     Initialize problem.
!
  if ( kprint >= 2)  write (LUN, 9000)
  N = 4
  LRW = 61
  LIW = 34
  T = 0.0D0
  TOUT = 8.0D0*ATAN(1.0D0)
  U(1) = 1.0D0
  U(2) = 0.0D0
  U(3) = 0.0D0
  U(4) = 1.0D0
  ipass = 1
  NSTEP = 0
  RELTOL = max ( SQRT(d1mach(4)),1.D-10)
  RELERR = max ( .1D0*RELTOL,1.D-12)
  ABSERR = RELERR**1.5D0
  INFO(1) = 0
  INFO(2) = 0
  INFO(3) = 1
  INFO(4) = 0
!
  if ( kprint  >  2) write (LUN, 9010) RELERR, ABSERR, T, (1.0D0)
  100 call DDERKF (DFDEQC, N, T, U, TOUT, INFO, RELERR, ABSERR, &
              IDID, RWORK, LRW, IWORK, LIW, RPAR, IPAR)
  R = SQRT(U(1)*U(1)+U(2)*U(2))
  if ( ABS(R-1.0D0)  >  RELTOL) ipass = 0
  if ( kprint  >  2) write (LUN, 9020) T, R
  INFO(1) = 1
!
!     For the double precision version, we allow the integrator to take
!     up to 2000 steps before we declare failure.
!
  if ( IDID == 1) GO TO 100
  if ( IDID == -1 ) then
     NSTEP = NSTEP + 500
     if ( NSTEP  <  2000) GOTO 100
!
!     Finish up.
!
  end if
  if ( IDID  <  1) ipass = 0
  if ( kprint > 1  .and.  IDID < 1)  write (LUN, 9030) IDID
  if ( kprint > 1  .and.  ipass == 1) write (LUN, 9040)
  if ( KPRINT >= 1  .and.  ipass == 0) write (LUN, 9050)
!
!     FORMATs.
!
  return
 9000 FORMAT ('1'/' ------------  DDERKF QUICK CHECK OUTPUT', &
          ' ------------')
 9010 FORMAT (/ ' RELERR = ', D16.8, '   ABSERR =', D16.8 / &
          12X, 'T', 19X, 'R' / 2D20.8)
 9020 FORMAT (2D20.8)
 9030 FORMAT (1X, 'ERROR RETURN FROM DDERKF.  IDID = ', I3)
 9040 FORMAT (/ ' ------------  DDERKF PASSED TESTS  ------------')
 9050 FORMAT (/ ' ************  DDERKF FAILED TESTS  ************')
end
!! QXGBUN
!***PURPOSE
!***LIBRARY   SLATEC
!***KEYWORDS  QUICK CHECK
!***AUTHOR  (UNKNOWN)
!***DESCRIPTION
!
!     * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
!     *                                                               *
!     *                        F I S H P A K                          *
!     *                                                               *
!     *                                                               *
!     *     A PACKAGE OF FORTRAN SUBPROGRAMS FOR THE SOLUTION OF      *
!     *                                                               *
!     *      SEPARABLE ELLIPTIC PARTIAL DIFFERENTIAL EQUATIONS        *
!     *                                                               *
!     *                  (VERSION  3 , JUNE 1979)                     *
!     *                                                               *
!     *                             BY                                *
!     *                                                               *
!     *        JOHN ADAMS, PAUL SWARZTRAUBER AND ROLAND SWEET         *
!     *                                                               *
!     *                             OF                                *
!     *                                                               *
!     *         THE NATIONAL CENTER FOR ATMOSPHERIC RESEARCH          *
!     *                                                               *
!     *                BOULDER, COLORADO  (80307)  U.S.A.             *
!     *                                                               *
!     *                   WHICH IS SPONSORED BY                       *
!     *                                                               *
!     *              THE NATIONAL SCIENCE FOUNDATION                  *
!     *                                                               *
!     * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
!
!     PROGRAM TO ILLUSTRATE THE USE OF SUBROUTINE GENBUN
!
!***ROUTINES CALLED  GENBUN, PIMACH
!***REVISION HISTORY  (YYMMDD)
!   750701  DATE WRITTEN
!   890718  Changed computation of PI to use PIMACH.  (WRB)
!   890911  Removed unnecessary intrinsics.  (WRB)
!   891009  Removed unreferenced variable.  (WRB)
!   891009  REVISION DATE from Version 3.2
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   901010  Added PASS/FAIL message and cleaned up FORMATs.  (RWC)
!***END PROLOGUE  QXGBUN
subroutine QXGBUN (LUN, KPRINT, IPASS)
!***FIRST EXECUTABLE STATEMENT  QXGBUN
!
!     FROM DIMENSION STATEMENT WE GET VALUE OF IDIMY.  ALSO NOTE THAT
!     W(.) IS DIMENSIONED 6*N + 5*M.
!
  dimension F(25,130), A(20), B(20), C(20), W(1200), X(20), Y(120)
  ERMAX=1.E-2
  IDIMY = 25
  MPEROD = 1
  M = 20
  DELTAX = 1.0E0/M
  NPEROD = 0
  N = 120
  PI = PIMACH(DUM)
!
!     GENERATE AND STORE GRID POINTS FOR THE PURPOSE OF COMPUTING
!     COEFFICIENTS AND RIGHT SIDE OF EQUATION.
!
  DELTAY = 2.0E0*PI/N
  DO 100 I=1,M
     X(I) = (I-1)*DELTAX
  100    continue
  DO 105 J=1,N
     Y(J) = -PI + (J-1)*DELTAY
!
!     GENERATE COEFFICIENTS.
!
  105    continue
  S = (DELTAY/DELTAX)**2
  T = S*DELTAX
  A(1) = 0.
  B(1) = -2.0E0*S
  C(1) = 2.0E0*S
  DO 110 I=2,M
     A(I) = (1.+X(I))**2*S + (1.+X(I))*T
     C(I) = (1.+X(I))**2*S - (1.+X(I))*T
     B(I) = -2.0E0*(1.0E0+X(I))**2*S
  110    continue
!
!     GENERATE RIGHT SIDE OF EQUATION FOR I = 1 SHOWING INTRODUCTION OF
!     BOUNDARY DATA.
!
  C(M) = 0.
  DYSQ = DELTAY**2
  DO 115 J=1,N
     F(1,J) = DYSQ*(11. + 8./DELTAX)*SIN(Y(J))
!
!     GENERATE RIGHT SIDE.
!
  115    continue
  MM1 = M-1
  DO 125 I=2,MM1
     DO 120 J=1,N
        F(I,J) = DYSQ*3.*(1.+X(I))**4*SIN(Y(J))
  120       continue
!
!     GENERATE RIGHT SIDE FOR I = M SHOWING INTRODUCTION OF
!     BOUNDARY DATA.
!
  125    continue
  DO 130 J=1,N
     F(M,J) = DYSQ*(3.*(1.+X(M))**4 - 16.*((1.+X(M))/DELTAX)**2 &
                  + 16.*(1.+X(M))/DELTAX)*SIN(Y(J))
  130    continue
!
!     COMPUTE DISCRETIZATION ERROR.  THE EXACT SOLUTION IS
!                   U(X,Y) = (1+X)**4*SIN(Y)
!
  call GENBUN(NPEROD,N,MPEROD,M,A,B,C,IDIMY,F,IERROR,W)
  ERR = 0.
  DO 140 I=1,M
     DO 135 J=1,N
        Z = ABS(F(I,J)-(1.+X(I))**4*SIN(Y(J)))
        if ( Z  >  ERR) ERR = Z
  135       continue
!
  140    continue
  ipass = 1
  if ( ERR > ERMAX) ipass = 0
  if ( kprint == 0) RETURN
  if ( KPRINT >= 2 .OR. ipass == 0 ) then
     write (LUN,1001) IERROR, ERR, INT(W(1))
     if ( ipass == 1 ) then
        write (LUN, 1002)
     else
        write (LUN, 1003)
     end if
  end if
!
  return
 1001 FORMAT ('1',20X,'SUBROUTINE GENBUN EXAMPLE'/// &
          10X,'THE OUTPUT FROM THE NCAR CONTROL DATA 7600 WAS'// &
          32X,'IERROR = 0'/ &
          18X,'DISCRETIZATION ERROR = 7.94113E-03'/ &
          12X,'REQUIRED LENGTH OF W ARRAY = 740'// &
          10X,'THE OUTPUT FROM YOUR COMPUTER IS'// &
          32X,'IERROR =',I2/ &
          18X,'DISCRETIZATION ERROR =',1PE12.5/ &
          12X,'REQUIRED LENGTH OF W ARRAY =',I4)
 1002 FORMAT (60X,'PASS'/)
 1003 FORMAT (60X,'FAIL'/)
end
!! QXPLR
!***PURPOSE
!***LIBRARY   SLATEC
!***KEYWORDS  QUICK CHECK
!***AUTHOR  (UNKNOWN)
!***DESCRIPTION
!
!     * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
!     *                                                               *
!     *                        F I S H P A K                          *
!     *                                                               *
!     *                                                               *
!     *     A PACKAGE OF FORTRAN SUBPROGRAMS FOR THE SOLUTION OF      *
!     *                                                               *
!     *      SEPARABLE ELLIPTIC PARTIAL DIFFERENTIAL EQUATIONS        *
!     *                                                               *
!     *                  (VERSION  3 , JUNE 1979)                     *
!     *                                                               *
!     *                             BY                                *
!     *                                                               *
!     *        JOHN ADAMS, PAUL SWARZTRAUBER AND ROLAND SWEET         *
!     *                                                               *
!     *                             OF                                *
!     *                                                               *
!     *         THE NATIONAL CENTER FOR ATMOSPHERIC RESEARCH          *
!     *                                                               *
!     *                BOULDER, COLORADO  (80307)  U.S.A.             *
!     *                                                               *
!     *                   WHICH IS SPONSORED BY                       *
!     *                                                               *
!     *              THE NATIONAL SCIENCE FOUNDATION                  *
!     *                                                               *
!     * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
!
!
!          PROGRAM TO ILLUSTRATE THE USE OF SUBROUTINE HWSPLR TO SOLVE
!     THE EQUATION
!
!     (1/R)(D/DR)(R*(DU/DR)) + (1/R**2)(D/DTHETA)(DU/DTHETA) = 16*R**2
!
!     ON THE QUARTER-DISK 0  <  R  <  1, 0  <  THETA  <  PI/2 WITH
!     WITH THE BOUNDARY CONDITIONS
!
!     U(1,THETA) = 1 - COS(4*THETA), 0  <=  THETA  <=  1
!
!     AND
!
!     (DU/DTHETA)(R,0) = (DU/DTHETA)(R,PI/2) = 0,  0  <=  R  <=  1.
!
!     (NOTE THAT THE SOLUTION U IS UNSPECIFIED AT R = 0.)
!          THE R-INTERVAL WILL BE DIVIDED INTO 50 PANELS AND THE
!     THETA-INTERVAL WILL BE DIVIDED INTO 48 PANELS.
!
!***ROUTINES CALLED  HWSPLR, PIMACH
!***REVISION HISTORY  (YYMMDD)
!   800103  DATE WRITTEN
!   890718  Changed computation of PI to use PIMACH.  (WRB)
!   890911  Removed unnecessary intrinsics.  (WRB)
!   890911  REVISION DATE from Version 3.2
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   901010  Added PASS/FAIL message and cleaned up FORMATs.  (RWC)
!***END PROLOGUE  QXPLR
subroutine QXPLR (LUN, KPRINT, IPASS)
!***FIRST EXECUTABLE STATEMENT  QXPLR
!
!     FROM DIMENSION STATEMENT WE GET VALUE OF IDIMF.  ALSO NOTE THAT W
!     IS DIMENSIONED 6*(N+1) + 8*(M+1).
!
  dimension F(100,50), BDC(51), BDD(51), W(1200), R(51), THETA(49)
  IDIMF = 100
  ERMAX=1.E-3
  A = 0.
  B = 1.
  M = 50
  MBDCND = 5
  C = 0.
  PI = PIMACH(DUM)
  D = PI/2.
  N = 48
  NBDCND = 3
!
!     AUXILIARY QUANTITIES.
!
  ELMBDA = 0.
  MP1 = M+1
!
!     GENERATE AND STORE GRID POINTS FOR THE PURPOSE OF COMPUTING
!     BOUNDARY DATA AND THE RIGHT SIDE OF THE POISSON EQUATION.
!
  NP1 = N+1
  DO 101 I=1,MP1
     R(I) = (I-1)/50.0E0
  101 continue
  DO 102 J=1,NP1
     THETA(J) = (J-1)*PI/96.0E0
!
!     GENERATE BOUNDARY DATA.
!
  102 continue
  DO 103 I=1,MP1
     BDC(I) = 0.
     BDD(I) = 0.
!
!     BDA AND BDB ARE DUMMY VARIABLES.
!
  103 continue
  DO 104 J=1,NP1
     F(MP1,J) = 1.-COS(4.*THETA(J))
!
!     GENERATE RIGHT SIDE OF EQUATION.
!
  104 continue
  DO 106 I=1,M
     DO 105 J=1,NP1
        F(I,J) = 16.*R(I)**2
  105    continue
  106 continue
  call HWSPLR(A,B,M,MBDCND,BDA,BDB,C,D,N,NBDCND,BDC,BDD,ELMBDA,F, &
!
!     COMPUTE DISCRETIZATION ERROR.  THE EXACT SOLUTION IS
!                U(R,THETA) = R**4*(1 - COS(4*THETA))
!
               IDIMF,PERTRB,IERROR,W)
  ERR = 0.
  DO 108 I=1,MP1
     DO 107 J=1,NP1
        Z = ABS(F(I,J)-R(I)**4*(1.-COS(4.*THETA(J))))
        if ( Z  >  ERR) ERR = Z
  107    continue
!
  108 continue
  ipass = 1
  if ( ERR > ERMAX) ipass = 0
  if ( kprint == 0) RETURN
  if ( KPRINT >= 2 .OR. ipass == 0 ) then
     write (LUN,1001) IERROR,ERR,INT(W(1))
     if ( ipass == 1 ) then
        write (LUN, 1002)
     else
        write (LUN, 1003)
     end if
  end if
!
  return
 1001 FORMAT ('1',20X,'SUBROUTINE HWSPLR EXAMPLE'/// &
          10X,'THE OUTPUT FROM THE NCAR CONTROL DATA 7600 WAS'// &
          32X,'IERROR = 0'/ &
          18X,'DISCRETIZATION ERROR = 6.19134E-04'/ &
          12X,'REQUIRED LENGTH OF W ARRAY = 882'// &
          10X,'THE OUTPUT FROM YOUR COMPUTER IS'// &
          32X,'IERROR =',I2/ &
          18X,'DISCRETIZATION ERROR =',1PE12.5/ &
          12X,'REQUIRED LENGTH OF W ARRAY =',I4)
 1002 FORMAT (60X,'PASS'/)
 1003 FORMAT (60X,'FAIL'/)
end
!! QXRKF
!***PURPOSE  Test the DEPAC routine DERKF.
!***LIBRARY   SLATEC
!***TYPE      SINGLE PRECISION (QXRKF-S, QXDRKF-D)
!***KEYWORDS  QUICK CHECK
!***AUTHOR  Chow, Jeff, (LANL)
!***DESCRIPTION
!
! *Usage:
!
!        INTEGER  LUN, KPRINT, IPASS
!
!        call QXRKF (LUN, KPRINT, IPASS)
!
! *Arguments:
!
!     LUN   :IN  is the unit number to which output is to be written.
!
!     KPRINT:IN  controls the amount of output, as specified in the
!                SLATEC Guidelines.
!
!     IPASS:OUT  will contain a pass/fail flag.  IPASS=1 is good.
!                IPASS=0 indicates one or more tests failed.
!
! *Description:
!
!   DERKF is tested by solving the equations of motion of a body
!   moving in a plane about a spherical earth, namely
!           (D/DT)(D/DT)X = -G*X/R**3
!           (D/DT)(D/DT)Y = -G*Y/R**3
!   where G = 1, R = SQRT(X**2 + Y**2) and
!           X(0) = 1
!           (D/DT)X(0) = 0
!           Y(0) = 0
!           (D/DT)Y(0) = 1.
!
!***ROUTINES CALLED  DERKF, FDEQC, R1MACH
!***REVISION HISTORY  (YYMMDD)
!   810801  DATE WRITTEN
!   890618  REVISION DATE from Version 3.2
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   900415  Code extensively revised.  (WRB)
!***END PROLOGUE  QXRKF
!
!     Declare arguments.
!
subroutine QXRKF (LUN, KPRINT, IPASS)
!
!     Declare local variables.
!
  integer  LUN, KPRINT, IPASS
  integer  IDID, INFO(15), IPAR, IWORK(34), N, LIW, LRW
  REAL ABSERR, R, R1MACH, RELERR, RELTOL, RPAR, RWORK(61), T, TOUT, &
       U(4)
!***FIRST EXECUTABLE STATEMENT  QXRKF
  EXTERNAL FDEQC
!
!     Initialize problem.
!
  if ( kprint >= 2)  write (LUN, 9000)
  N = 4
  LRW = 61
  LIW = 34
  T = 0.0E0
  TOUT = 8.0E0*ATAN(1.0E0)
  U(1) = 1.0E0
  U(2) = 0.0E0
  U(3) = 0.0E0
  U(4) = 1.0E0
  ipass = 1
  RELTOL = SQRT(R1MACH(4))
  RELERR = 0.1E0*RELTOL
  ABSERR = RELERR**1.5E0
  INFO(1) = 0
  INFO(2) = 0
  INFO(3) = 1
  INFO(4) = 0
!
  if ( kprint  >  2) write (LUN, 9010) RELERR, ABSERR, T, (1.0E0)
  100 call DERKF (FDEQC, N, T, U, TOUT, INFO, RELERR, ABSERR, &
              IDID, RWORK, LRW, IWORK, LIW, RPAR, IPAR)
  R = SQRT(U(1)*U(1)+U(2)*U(2))
  if ( ABS(R-1.0E0)  >  RELTOL) ipass = 0
  if ( kprint  >  2) write (LUN, 9020) T, R
  INFO(1) = 1
!
!     Finish up.
!
  if ( IDID == 1) GO TO 100
  if ( IDID  <  1) ipass = 0
  if ( kprint > 1  .and.  IDID < 1)  write (LUN, 9030) IDID
  if ( kprint > 1  .and.  ipass == 1) write (LUN, 9040)
  if ( KPRINT >= 1  .and.  ipass == 0) write (LUN, 9050)
!
!     FORMATs.
!
  return
 9000 FORMAT ('1'/' ------------  DERKF QUICK CHECK OUTPUT', &
          ' ------------')
 9010 FORMAT (/ ' RELERR = ', E16.8, '   ABSERR =', E16.8 / &
          12X, 'T', 19X, 'R' / 2E20.8)
 9020 FORMAT (2E20.8)
 9030 FORMAT (1X, 'ERROR RETURN FROM DERKF.  IDID = ', I3)
 9040 FORMAT (/ ' ------------  DERKF PASSED TESTS  ------------')
 9050 FORMAT (/ ' ************  DERKF FAILED TESTS  ************')
end
!! QXSSP
!***PURPOSE
!***LIBRARY   SLATEC
!***KEYWORDS  QUICK CHECK
!***AUTHOR  (UNKNOWN)
!***DESCRIPTION
!
!     * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
!     *                                                               *
!     *                        F I S H P A K                          *
!     *                                                               *
!     *                                                               *
!     *     A PACKAGE OF FORTRAN SUBPROGRAMS FOR THE SOLUTION OF      *
!     *                                                               *
!     *      SEPARABLE ELLIPTIC PARTIAL DIFFERENTIAL EQUATIONS        *
!     *                                                               *
!     *                  (VERSION  3 , JUNE 1979)                     *
!     *                                                               *
!     *                             BY                                *
!     *                                                               *
!     *        JOHN ADAMS, PAUL SWARZTRAUBER AND ROLAND SWEET         *
!     *                                                               *
!     *                             OF                                *
!     *                                                               *
!     *         THE NATIONAL CENTER FOR ATMOSPHERIC RESEARCH          *
!     *                                                               *
!     *                BOULDER, COLORADO  (80307)  U.S.A.             *
!     *                                                               *
!     *                   WHICH IS SPONSORED BY                       *
!     *                                                               *
!     *              THE NATIONAL SCIENCE FOUNDATION                  *
!     *                                                               *
!     * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
!
!
!
!     PROGRAM TO ILLUSTRATE THE USE OF HWSSSP
!
!***ROUTINES CALLED  HWSSSP, PIMACH
!***REVISION HISTORY  (YYMMDD)
!   800103  DATE WRITTEN
!   890718  Changed computation of PI to use PIMACH.  (WRB)
!   890911  Removed unnecessary intrinsics.  (WRB)
!   890911  REVISION DATE from Version 3.2
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   901010  Added PASS/FAIL message and cleaned up FORMATs.  (RWC)
!***END PROLOGUE  QXSSP
subroutine QXSSP (LUN, KPRINT, IPASS)
!***FIRST EXECUTABLE STATEMENT  QXSSP
!
!     THE VALUE OF IDIMF IS THE FIRST DIMENSION OF F.  W IS
!     DIMENSIONED 11*(M+1)+6*(N+1)=647 SINCE M=18 AND N=72.
!
  dimension F(19,73), BDTF(73), SINT(19), SINP(73), W(1200)
  PI = PIMACH(DUM)
  ERMAX=5.E-3
  TS = 0.0
  TF = PI/2.
  M = 18
  MBDCND = 6
  PS = 0.0
  PF = PI+PI
  N = 72
  NBDCND = 0
  ELMBDA = 0.
!
!     GENERATE SINES FOR USE IN SUBSEQUENT COMPUTATIONS
!
  IDIMF = 19
  DTHETA = TF/M
  MP1 = M+1
  DO 101 I=1,MP1
     SINT(I) = SIN((I-1)*DTHETA)
  101 continue
  DPHI = (PI+PI)/N
  NP1 = N+1
  DO 102 J=1,NP1
     SINP(J) = SIN((J-1)*DPHI)
!
!     COMPUTE RIGHT SIDE OF EQUATION AND STORE IN F
!
  102 continue
  DO 104 J=1,NP1
     DO 103 I=1,MP1
        F(I,J) = 2.-6.*(SINT(I)*SINP(J))**2
  103    continue
!
!     STORE DERIVATIVE DATA AT THE EQUATOR
!
  104 continue
  DO 105 J=1,NP1
     BDTF(J) = 0.
!
  105 continue
  call HWSSSP(TS,TF,M,MBDCND,BDTS,BDTF,PS,PF,N,NBDCND,BDPS,BDPF, &
!
!     COMPUTE DISCRETIZATION ERROR. SINCE PROBLEM IS SINGULAR, THE
!     SOLUTION MUST BE NORMALIZED.
!
               ELMBDA,F,IDIMF,PERTRB,IERROR,W)
  ERR = 0.0
  DO 107 J=1,NP1
     DO 106 I=1,MP1
        Z = ABS(F(I,J)-(SINT(I)*SINP(J))**2-F(1,1))
        if ( Z  >  ERR) ERR = Z
  106    continue
!
  107 continue
  ipass = 1
  if ( ERR > ERMAX) ipass = 0
  if ( kprint == 0) RETURN
  if ( KPRINT >= 2 .OR. ipass == 0 ) then
     write (LUN,1001) IERROR,ERR,INT(W(1))
     if ( ipass == 1 ) then
        write (LUN, 1002)
     else
        write (LUN, 1003)
     end if
  end if
!
  return
 1001 FORMAT ('1',20X,'SUBROUTINE HWSSSP EXAMPLE'/// &
          10X,'THE OUTPUT FROM THE NCAR CONTROL DATA 7600 WAS'// &
          32X,'IERROR = 0'/ &
          18X,'DISCRETIZATION ERROR = 3.38107E-03'/ &
          12X,'REQUIRED LENGTH OF W ARRAY = 600'// &
          10X,'THE OUTPUT FROM YOUR COMPUTER IS'// &
          32X,'IERROR =',I2/ &
          18X,'DISCRETIZATION ERROR =',1PE12.5 / &
          12X,'REQUIRED LENGTH OF W ARRAY =',I4)
 1002 FORMAT (60X,'PASS'/)
 1003 FORMAT (60X,'FAIL'/)
end
!! RQRTST
!***PURPOSE  Quick check for RPQR79.
!***LIBRARY   SLATEC
!***TYPE      SINGLE PRECISION (RQRTST-S, CQRTST-C)
!***AUTHOR  (UNKNOWN)
!***ROUTINES CALLED  NUMXER, PASS, R1MACH, RPQR79, XERCLR, XGETF, XSETF
!***REVISION HISTORY  (YYMMDD)
!   ??????  DATE WRITTEN
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   901010  Restructured using IF-THEN-else-end if, cleaned up FORMATs
!           and changed TOL from sqrt R1MACH(3) to sqrt R1MACH(4) for
!           the IBM 370 mainframes.  (RWC)
!   911010  Code reworked and simplified.  (RWC and WRB)
!***END PROLOGUE  RQRTST
subroutine RQRTST (LUN, KPRINT, IPASS)
  integer ITMP(7)
  COMPLEX ROOT(7), CHK(7)
  dimension WORK(63)
  REAL COEF(8)
!
  LOGICAL FATAL
  DATA CHK / ( 1.4142135623731,  1.4142135623731), &
             ( 1.4142135623731, -1.4142135623731), &
             (0.0, 2.0), (0.0, -2.0), (-2.0, 0.0), &
             (-1.4142135623731,  1.4142135623731), &
!***FIRST EXECUTABLE STATEMENT  RQRTST
             (-1.4142135623731, -1.4142135623731) /
  if ( kprint >= 2) write (LUN, 90000)
  TOL = SQRT(R1MACH(4))
!
!     Initialize variables for testing.
!
  ipass = 1
  BETA = 0.0078125
  DO 20 J=1,8
     COEF(J) = BETA
     BETA = 2.0*BETA
!
   20 continue
!
!     Check to see if test passed.
!
  call RPQR79 (7, COEF, ROOT, IERR, WORK)
  DO 10 I=1,7
     ITMP(I) = 0
!
!     Check for roots in any order.
!
   10 continue
  DO 40 I=1,7
     DO 30 J=1,7
        if ( ABS(ROOT(I)-CHK(J))  <=  TOL ) then
           ITMP(J) = 1
           GO TO 40
        end if
   30    continue
!
!     Check that we found all 7 roots.
!
   40 continue
  ipass = 1
  DO 50 I=1,7
     ipass = IPASS*ITMP(I)
!
!     Print test results.
!
   50 continue
  if ( KPRINT >= 3 .OR. (KPRINT>=2 .and. ipass == 0) ) then
     write (LUN, 90010)
     write (LUN, 90020) (J,COEF(J), J=1,8)
     write (LUN, 90030)
     write (LUN, 90040) (J,ROOT(J), J=1,7)
  end if
  if ( kprint >= 2 ) then
     call PASS (LUN, 1, IPASS)
!
!     Trigger 2 error conditions
!
  end if
  call XGETF (KONTRL)
  if ( kprint  <=  2 ) then
     call XSETF (0)
  else
     call XSETF (1)
  end if
  FATAL = .FALSE.
  call xerclr
!
!     call RPQR79 with 0 degree polynomial.
!
  if ( kprint >= 3) write (LUN, 90060)
  call RPQR79 (0, COEF, ROOT, IERR, WORK)
  if ( NUMXER(NERR) /= 3 ) then
     FATAL = .TRUE.
  end if
!
!     call RPQR79 with zero leading coefficient.
!
  call xerclr
  COEF(1) = 0.0
  call RPQR79 (2, COEF, ROOT, IERR, WORK)
  if ( NUMXER(NERR) /= 2 ) then
     FATAL = .TRUE.
  end if
!
  call xerclr
  call XSETF (KONTRL)
  if ( FATAL ) then
     ipass = 0
     if ( kprint >= 2 ) then
        write (LUN, 90070)
     end if
  else
     if ( kprint >= 3 ) then
        write (LUN, 90080)
     end if
!
  end if
  if ( ipass == 1  .and.  kprint > 1) write (LUN,90100)
  if ( ipass == 0  .and.  kprint /= 0) write (LUN,90110)
!
  return
90000 FORMAT ('1', /,' RPQR79 QUICK CHECK')
90010 FORMAT (/, ' CHECK REAL AND IMAGINARY PARTS OF ROOT' / &
            ' COEFFICIENTS')
90020 FORMAT (/ (I6, 3X, 1P, E22.14))
90030 FORMAT (// 25X, 'TABLE of ROOTS' // &
          '   ROOT         REAL  PART', 12X, 'IMAG  PART' / &
          '  NUMBER', 8X, 2(' of  ZERO ', 12X))
90040 FORMAT (I6, 3X, 1P, 2E22.14)
90060 FORMAT (// ' TRIGGER 2 ERROR CONDITIONS' //)
90070 FORMAT (/ ' AT LEAST ONE INCORRECT ARGUMENT TEST FAILED')
90080 FORMAT (/ ' ALL INCORRECT ARGUMENT TESTS PASSED')
90100 FORMAT (/' **************RPQR79 PASSED ALL TESTS**************')
90110 FORMAT (/' **************RPQR79 FAILED SOME TESTS*************')
end
!! SBEG
!***SUBSIDIARY
!***PURPOSE  Generate random numbers.
!***LIBRARY   SLATEC (BLAS)
!***AUTHOR  Du Croz, J. (NAG)
!           Hanson, R. J. (SNLA)
!***DESCRIPTION
!
!  Generates random numbers uniformly distributed between -0.5 and 0.5.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  (NONE)
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SBEG
!     .. Scalar Arguments ..
  REAL FUNCTION SBEG (RESET)
!     .. Local Scalars ..
  LOGICAL            RESET
!     .. Save statement ..
  integer            I, IC, MI
!     .. Intrinsic Functions ..
  SAVE               I, IC, MI
! ***FIRST EXECUTABLE STATEMENT  SBEG
  INTRINSIC          REAL
!        Initialize local variables.
  if (  RESET  ) then
     MI = 891
     I = 7
     IC = 0
     RESET = .FALSE.
!
!     The sequence of values of I is bounded between 1 and 999.
!     If initial I = 1,2,3,6,7 or 9, the period will be 50.
!     If initial I = 4 or 8, the period will be 25.
!     If initial I = 5, the period will be 10.
!     IC is used to break up the period by skipping 1 value of I in 6.
!
  end if
  IC = IC + 1
   10 I = I*MI
  I = I - 1000*( I/1000 )
  if (  IC >= 5  ) then
     IC = 0
     GO TO 10
  end if
  SBEG = REAL( I - 500 )/1001.0
!
!     End of SBEG.
!
  return
end
!! SBLAT2
!***PURPOSE  Driver for testing Level 2 BLAS single precision
!            subroutines.
!***LIBRARY   SLATEC (BLAS)
!***CATEGORY  A3A
!***TYPE      SINGLE PRECISION (SBLAT2-S, DBLAT2-D, CBLAT2-C)
!***KEYWORDS  BLAS, QUICK CHECK DRIVER
!***AUTHOR  Du Croz, J. J., (NAG)
!           Hanson, R. J., (SNLA)
!***DESCRIPTION
!
!  Test program for the REAL             Level 2 Blas.
!
!***REFERENCES  Dongarra, J. J., Du Croz, J. J., Hammarling, S. and
!                 Hanson, R. J.  An  extended  set of Fortran Basic
!                 Linear Algebra Subprograms. ACM TOMS, Vol. 14, No. 1,
!                 pp. 1-17, March 1988.
!***ROUTINES CALLED  LSE, R1MACH, SCHK12, SCHK22, SCHK32, SCHK42,
!                    SCHK52, SCHK62, SCHKE2, SMVCH, XERCLR
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!   930315  Removed unused variables.  (WRB)
!   930618  Code modified to improve PASS/FAIL reporting.  (BKS, WRB)
!***END PROLOGUE  SBLAT2
!     .. Parameters ..
subroutine SBLAT2 (NOUT, KPRINT, IPASS)
  integer            NSUBS
  PARAMETER          ( NSUBS = 16)
  REAL               ZERO, ONE
  PARAMETER          ( ZERO = 0.0, ONE = 1.0 )
  integer            NMAX, INCMAX
!     .. Scalar Arguments ..
  PARAMETER          ( NMAX = 65, INCMAX = 2 )
!     .. Local Scalars ..
  integer            IPASS, KPRINT
  LOGICAL            FTL, FTL1, FTL2
  REAL               EPS, ERR, THRESH
  integer            I, ISNUM, J, N, NALF, NBET, NIDIM, NINC, NKB, &
                     NOUT
  PARAMETER          (NIDIM=6, NKB=4,NINC=4, NALF=3, NBET=3)
  LOGICAL            SAME, TSTERR
!     .. Local Arrays ..
  CHARACTER*1        TRANS
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), &
                     ALF( NALF ), AS( NMAX*NMAX ), BET( NBET ), &
                     G( NMAX ), X( NMAX ), XS( NMAX*INCMAX ), &
                     XX( NMAX*INCMAX ), Y( NMAX ), &
                     YS( NMAX*INCMAX ), YT( NMAX ), &
                     YY( NMAX*INCMAX ), Z( 2*NMAX )
  integer            IDIM( NIDIM ), INC( NINC ), KB( NKB )
  LOGICAL            LTEST( NSUBS )
!     .. External Functions ..
  CHARACTER*6        SNAMES( NSUBS )
  REAL               R1MACH
  LOGICAL            LSE
!     .. External Subroutines ..
  EXTERNAL           LSE, R1MACH
  EXTERNAL           SCHK12, SCHK22, SCHK32, SCHK42, SCHK52, SCHK62, &
!     .. Intrinsic Functions ..
                     SCHKE2, SMVCH
!     .. Data statements ..
  INTRINSIC          ABS, MAX, MIN
  DATA               SNAMES/'SGEMV ', 'SGBMV ', 'SSYMV ', 'SSBMV ', &
                     'SSPMV ', 'STRMV ', 'STBMV ', 'STPMV ', &
                     'STRSV ', 'STBSV ', 'STPSV ', 'SGER  ', &
                     'SSYR  ', 'SSPR  ', 'SSYR2 ', 'SSPR2 '/
  DATA               IDIM/0,1,2,3,5,9/
  DATA               KB/0,1,2,4/
  DATA               INC/1,2,-1,-2/
  DATA               ALF/0.0,1.0,0.7/
!***FIRST EXECUTABLE STATEMENT  SBLAT2
!
!     Set the flag that indicates whether error exits are to be tested.
!
  DATA               BET/0.0,1.0,0.9/
!
!     Set the threshold value of the test ratio
!
  TSTERR=.TRUE.
!
!     Initialize ipass to 1 assuming everything will pass.
!
  THRESH=16.0
!
!     Report values of parameters.
!
  ipass = 1
  if ( kprint >= 3 ) then
    write ( NOUT, FMT = 9993 )
    write ( NOUT, FMT = 9992 )( IDIM( I ), I = 1, NIDIM )
    write ( NOUT, FMT = 9991 )( KB( I ), I = 1, NKB )
    write ( NOUT, FMT = 9990 )( INC( I ), I = 1, NINC )
    write ( NOUT, FMT = 9989 )( ALF( I ), I = 1, NALF )
    write ( NOUT, FMT = 9988 )( BET( I ), I = 1, NBET )
    if (  .NOT.TSTERR  ) then
       write ( NOUT, FMT = 9980 )
    end if
    write ( NOUT, FMT = 9999 )THRESH
!
!     Set names of subroutines and flags which indicate
!     whether they are to be tested.
!
  end if
  DO 40 I = 1, NSUBS
     LTEST( I ) = .TRUE.
!
!     Set EPS (the machine precision).
!
   40 continue
!
!     Check the reliability of SMVCH using exact data.
!
  EPS = R1MACH (4)
  N = min (  32, NMAX )
  DO 120 J = 1, N
     DO 110 I = 1, N
        A( I, J ) = max (  I - J + 1, 0 )
  110    continue
     X( J ) = J
     Y( J ) = ZERO
  120 continue
  DO 130 J = 1, N
     YY( J ) = J*( ( J + 1 )*J )/2 - ( ( J + 1 )*J*( J - 1 ) )/3
!     YY holds the exact result. On exit from SMVCH YT holds
!     the result computed by SMVCH.
  130 continue
  TRANS = 'N'
  FTL = .FALSE.
  call SMVCH( TRANS, N, N, ONE, A, NMAX, X, 1, ZERO, Y, 1, YT, G, &
              YY, EPS, ERR, FTL, NOUT, .TRUE., kprint )
  SAME = LSE( YY, YT, N )
  if (  .NOT.SAME.OR.ERR /= ZERO  ) then
    ipass = 0
    if ( kprint >= 2 ) then
       write ( NOUT, FMT = 9985 )TRANS, SAME, ERR
    end if
  end if
  TRANS = 'T'
  FTL = .FALSE.
  call SMVCH( TRANS, N, N, ONE, A, NMAX, X, -1, ZERO, Y, -1, YT, G, &
              YY, EPS, ERR, FTL, NOUT, .TRUE., kprint )
  SAME = LSE( YY, YT, N )
  if (  .NOT.SAME.OR.ERR /= ZERO  ) then
    ipass = 0
    if ( kprint >= 2 ) then
       write ( NOUT, FMT = 9985 )TRANS, SAME, ERR
    end if
!
!     Test each subroutine in turn.
!
  end if
  DO 210 ISNUM = 1, NSUBS
!           Subprogram is not to be tested.
     if (  .NOT.LTEST( ISNUM )  ) then
        write ( NOUT, FMT = 9983 )SNAMES( ISNUM )
!           Test error exits.
     else
        FTL1 = .FALSE.
        if (  TSTERR  ) then
          call SCHKE2( ISNUM, SNAMES( ISNUM ), NOUT, KPRINT, FTL1)
!           Test computations.
        end if
        FTL2 = .FALSE.
        call xerclr
        GO TO ( 140, 140, 150, 150, 150, 160, 160, &
                160, 160, 160, 160, 170, 180, 180, &
!           Test SGEMV, 01, and SGBMV, 02.
                190, 190 )ISNUM
  140       call SCHK12( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NKB, KB, NALF, ALF, &
                    NBET, BET, NINC, INC, NMAX, INCMAX, A, AA, AS, &
                    X, XX, XS, Y, YY, YS, YT, G )
!           Test SSYMV, 03, SSBMV, 04, and SSPMV, 05.
        GO TO 200
  150       call SCHK22( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NKB, KB, NALF, ALF, &
                    NBET, BET, NINC, INC, NMAX, INCMAX, A, AA, AS, &
                    X, XX, XS, Y, YY, YS, YT, G )
!           Test STRMV, 06, STBMV, 07, STPMV, 08,
!           STRSV, 09, STBSV, 10, and STPSV, 11.
        GO TO 200
  160       call SCHK32( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NKB, KB, NINC, INC, &
                    NMAX, INCMAX, A, AA, AS, Y, YY, YS, YT, G, Z )
!           Test SGER, 12.
        GO TO 200
  170       call SCHK42( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NINC, INC, &
                    NMAX, INCMAX, A, AA, AS, X, XX, XS, Y, YY, YS, &
                    YT, G, Z )
!           Test SSYR, 13, and SSPR, 14.
        GO TO 200
  180       call SCHK52( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NINC, INC, &
                    NMAX, INCMAX, A, AA, AS, X, XX, XS, Y, YY, YS, &
                    YT, G, Z )
!           Test SSYR2, 15, and SSPR2, 16.
        GO TO 200
  190       call SCHK62( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NINC, INC, &
                    NMAX, INCMAX, A, AA, AS, X, XX, XS, Y, YY, YS, &
!
                    YT, G, Z )
  200       if ( FTL1 .OR. FTL2  ) then
          ipass = 0
        end if
     end if
  210 continue
!
  return
 9999 FORMAT( ' ROUTINES PASS COMPUTATIONAL TESTS IF TEST RATIO IS LES', &
        'S THAN', F8.2 )
 9993 FORMAT( ' TESTS OF THE REAL             LEVEL 2 BLAS', //' THE F', &
        'OLLOWING PARAMETER VALUES WILL BE USED:' )
 9992 FORMAT( '   FOR N              ', 9I6 )
 9991 FORMAT( '   FOR K              ', 7I6 )
 9990 FORMAT( '   FOR INCX AND INCY  ', 7I6 )
 9989 FORMAT( '   FOR ALPHA          ', 7F6.1 )
 9988 FORMAT( '   FOR BETA           ', 7F6.1 )
 9985 FORMAT( ' ERROR IN SMVCH -  IN-LINE DOT PRODUCTS ARE BEING EVALU', &
        'ATED WRONGLY.', /' SMVCH WAS CALLED WITH TRANS = ', A1, &
        ' AND RETURNED SAME = ', L1, ' AND ERR = ', F12.3, '.', / &
        ' THIS MAY BE DUE TO FAULTS IN THE ARITHMETIC OR THE', &
        ' COMPILER.')
 9983 FORMAT( 1X, A6, ' WAS NOT TESTED' )
!
!     End of SBLAT2.
!
 9980 FORMAT( ' ERROR-EXITS WILL NOT BE TESTED' )
end
!! SBLAT3
!***PURPOSE  Driver for testing Level 3 BLAS single precision
!            subroutines.
!***LIBRARY   SLATEC (BLAS)
!***CATEGORY  A3A
!***TYPE      SINGLE PRECISION (SBLAT3-S, DBLAT3-D, CBLAT3-C)
!***KEYWORDS  BLAS, QUICK CHECK DRIVER
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Test program for the REAL             Level 3 Blas.
!
!***REFERENCES  Dongarra, J., Du Croz, J., Duff, I., and Hammarling, S.
!                 A set of level 3 basic linear algebra subprograms.
!                 ACM TOMS, Vol. 16, No. 1, pp. 1-17, March 1990.
!***ROUTINES CALLED  LSE, R1MACH, SCHK13, SCHK23, SCHK33, SCHK43,
!                    SCHK53, SCHKE3, SMMCH, XERCLR
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!   930315  Removed unused variables.  (WRB)
!   930618  Code modified to improve PASS/FAIL reporting.  (BKS, WRB)
!   930701  Call to SCHKE5 changed to call to SCHKE3.  (BKS)
!***END PROLOGUE  SBLAT3
!     .. Parameters ..
subroutine SBLAT3 (NOUT, KPRINT, IPASS)
  integer            NSUBS
  PARAMETER          ( NSUBS = 6)
  REAL               ZERO, ONE
  PARAMETER          ( ZERO = 0.0, ONE = 1.0 )
  integer            NMAX, INCMAX
!     .. Scalar Arguments ..
  PARAMETER          ( NMAX = 65, INCMAX = 2 )
!     .. Local Scalars ..
  integer            IPASS, KPRINT
  REAL               EPS, ERR, THRESH
  integer            I, ISNUM, J, N, NALF, NBET, NIDIM, NOUT
  PARAMETER          (NIDIM=6, NALF=3, NBET=3)
  LOGICAL            SAME, TSTERR, FTL, FTL1, FTL2
!     .. Local Arrays ..
  CHARACTER*1        TRANSA, TRANSB
  REAL               AB( NMAX, 2*NMAX ), AA( NMAX*NMAX ), &
                     ALF( NALF ), AS( NMAX*NMAX ), BET( NBET ), &
                     G( NMAX ),  BB( NMAX*NMAX ), &
                     BS( NMAX*NMAX ), C( NMAX,NMAX), &
                     CC( NMAX*NMAX ), CS( NMAX*NMAX), &
                     CT( NMAX), W( 2*NMAX )
  integer            IDIM( NIDIM )
  LOGICAL            LTEST( NSUBS )
!     .. External Functions ..
  CHARACTER*6        SNAMES( NSUBS )
  REAL               R1MACH
  LOGICAL            LSE
!     .. External Subroutines ..
  EXTERNAL           LSE, R1MACH
  EXTERNAL           SCHK13, SCHK23, SCHK33, SCHK43, SCHK53, &
!     .. Intrinsic Functions ..
                     SCHKE3, SMMCH
!     .. Data statements ..
  INTRINSIC          MAX, MIN
  DATA               SNAMES/'SGEMM ', 'SSYMM ', 'STRMM ', 'STRSM ', &
                     'SSYRK ', 'SSYR2K'/
  DATA               IDIM/0,1,2,3,5,9/
  DATA               ALF/0.0,1.0,0.7/
!***FIRST EXECUTABLE STATEMENT  SBLAT3
!
!     Set the flag that indicates whether error exits are to be tested.
!
  DATA               BET/0.0,1.0,1.3/
!
!     Set the threshold value of the test ratio
!
  TSTERR=.TRUE.
!
!     Initialize ipass to 1 assuming everything will pass.
!
  THRESH=16.0
!
!     Report values of parameters.
!
  ipass = 1
  if ( kprint >= 3 ) then
    write ( NOUT, FMT = 9995 )
    write ( NOUT, FMT = 9994 )( IDIM( I ), I = 1, NIDIM )
    write ( NOUT, FMT = 9993 )( ALF( I ), I = 1, NALF )
    write ( NOUT, FMT = 9992 )( BET( I ), I = 1, NBET )
    if (  .NOT.TSTERR  ) then
       write ( NOUT, FMT = 9984 )
    end if
    write ( NOUT, FMT = 9999 )THRESH
!
!     Set names of subroutines and flags which indicate
!     whether they are to be tested.
!
  end if
  DO 40 I = 1, NSUBS
     LTEST( I ) = .TRUE.
!
!     Set EPS (the machine precision).
!
   40 continue
!
!     Check the reliability of SMMCH using exact data.
!
  EPS = R1MACH (4)
  N = min (  32, NMAX )
  DO 120 J = 1, N
     DO 110 I = 1, N
        AB( I, J ) = max (  I - J + 1, 0 )
  110    continue
     AB( J, NMAX + 1 ) = J
     AB( 1, NMAX + J ) = J
     C( J, 1 ) = ZERO
  120 continue
  DO 130 J = 1, N
     CC( J ) = J*( ( J + 1 )*J )/2 - ( ( J + 1 )*J*( J - 1 ) )/3
!     CC holds the exact result. On exit from SMMCH CT holds
!     the result computed by SMMCH.
  130 continue
  TRANSA = 'N'
  TRANSB = 'N'
  FTL = .FALSE.
  call SMMCH( TRANSA, TRANSB, N, 1, N, ONE, AB, NMAX, &
              AB( 1, NMAX + 1 ), NMAX, ZERO, C, NMAX, CT, G, CC, &
              NMAX, EPS, ERR, FTL, NOUT, .TRUE., kprint )
  SAME = LSE( CC, CT, N )
  if (  .NOT.SAME.OR.ERR /= ZERO  ) then
    ipass = 0
    if ( kprint >= 2 ) then
       write ( NOUT, FMT = 9989 )TRANSA, TRANSB, SAME, ERR
    end if
  end if
  TRANSB = 'T'
  FTL = .FALSE.
  call SMMCH( TRANSA, TRANSB, N, 1, N, ONE, AB, NMAX, &
              AB( 1, NMAX + 1 ), NMAX, ZERO, C, NMAX, CT, G, CC, &
              NMAX, EPS, ERR, FTL, NOUT, .TRUE., kprint )
  SAME = LSE( CC, CT, N )
  if (  .NOT.SAME.OR.ERR /= ZERO  ) then
    ipass = 0
    if (  kprint >= 2 ) then
       write ( NOUT, FMT = 9989 )TRANSA, TRANSB, SAME, ERR
    end if
  end if
  DO 125 J = 1, N
     AB( J, NMAX + 1 ) = N - J + 1
     AB( 1, NMAX + J ) = N - J + 1
  125 continue
  DO 135 J = 1, N
     CC( N - J + 1 ) = J*( ( J + 1 )*J )/2 - &
                       ( ( J + 1 )*J*( J - 1 ) )/3
  135 continue
  TRANSA = 'T'
  TRANSB = 'N'
  FTL = .FALSE.
  call SMMCH( TRANSA, TRANSB, N, 1, N, ONE, AB, NMAX, &
              AB( 1, NMAX + 1 ), NMAX, ZERO, C, NMAX, CT, G, CC, &
              NMAX, EPS, ERR, FTL, NOUT, .TRUE., kprint )
  SAME = LSE( CC, CT, N )
  if (  .NOT.SAME.OR.ERR /= ZERO  ) then
    ipass = 0
    if (  kprint >= 2 ) then
       write ( NOUT, FMT = 9989 )TRANSA, TRANSB, SAME, ERR
    end if
  end if
  TRANSB = 'T'
  FTL = .FALSE.
  call SMMCH( TRANSA, TRANSB, N, 1, N, ONE, AB, NMAX, &
              AB( 1, NMAX + 1 ), NMAX, ZERO, C, NMAX, CT, G, CC, &
              NMAX, EPS, ERR, FTL, NOUT, .TRUE., kprint )
  SAME = LSE( CC, CT, N )
  if (  .NOT.SAME.OR.ERR /= ZERO  ) then
    ipass = 0
    if (  kprint >= 2 ) then
       write ( NOUT, FMT = 9989 )TRANSA, TRANSB, SAME, ERR
    end if
!
!     Test each subroutine in turn.
!
  end if
  DO 210 ISNUM = 1, NSUBS
!           Subprogram is not to be tested.
     if (  .NOT.LTEST( ISNUM )  ) then
        write ( NOUT, FMT = 9987 )SNAMES( ISNUM )
!           Test error exits.
     else
        FTL1 = .FALSE.
        if (  TSTERR  ) then
          call SCHKE3(ISNUM, SNAMES( ISNUM ), NOUT, KPRINT, FTL1)
!           Test computations.
        end if
        FTL2 = .FALSE.
        call xerclr
!           Test SGEMM, 01.
        GO TO ( 140, 150, 160, 160, 170, 180) ISNUM
  140       call SCHK13( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NBET, BET, &
                    NMAX, AB, AA, AS, AB(1, NMAX + 1), &
                    BB, BS, C, CC, CS, CT, G )
!           Test SSYMM, 02.
        GO TO 200
  150       call SCHK23( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NBET, BET, &
                    NMAX, AB, AA, AS, AB(1, NMAX + 1), &
                    BB, BS, C, CC, CS, CT, G )
!           Test STRMM, 03, STRSM, 04.
        GO TO 200
  160       call SCHK33( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NMAX, AB, &
                    AA, AS ,AB(1, NMAX + 1), BB, BS, CT, G, C)
!           Test SSYRK, 05.
        GO TO 200
  170       call SCHK43( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NBET, BET, &
                    NMAX, AB, AA, AS, AB(1, NMAX + 1), BB, BS, C, &
                    CC, CS, CT, G )
!           Test SSYR2K, 06.
        GO TO 200
  180       call SCHK53( SNAMES( ISNUM ), EPS, THRESH, NOUT, KPRINT, &
                    FTL2, NIDIM, IDIM, NALF, ALF, NBET, BET, &
                    NMAX, AB, AA, AS, BB, BS, C, CC, CS, CT, G, W)
        GO TO 200
  200       if ( FTL1 .OR. FTL2 ) then
          ipass = 0
        end if
     end if
  210 continue
!
  return
 9999 FORMAT( ' ROUTINES PASS COMPUTATIONAL TESTS IF TEST RATIO IS LES', &
        'S THAN', F8.2 )
 9995 FORMAT( ' TESTS OF THE REAL             LEVEL 3 BLAS', //' THE F', &
        'OLLOWING PARAMETER VALUES WILL BE USED:' )
 9994 FORMAT( '   FOR N              ', 9I6 )
 9993 FORMAT( '   FOR ALPHA          ', 7F6.1 )
 9992 FORMAT( '   FOR BETA           ', 7F6.1 )
 9989 FORMAT( ' ERROR IN SMMCH -  IN-LINE DOT PRODUCTS ARE BEING EVALU', &
        'ATED WRONGLY.', /' SMMCH WAS CALLED WITH TRANSA = ', A1, &
        ' AND TRANSB = ', A1, &
        ' AND RETURNED SAME = ', L1, ' AND ERR = ', F12.3, '.', / &
        ' THIS MAY BE DUE TO FAULTS IN THE ARITHMETIC OR THE', &
        ' COMPILER.')
 9987 FORMAT( 1X, A6, ' WAS NOT TESTED' )
!
!     End of SBLAT3.
!
 9984 FORMAT( ' ERROR-EXITS WILL NOT BE TESTED' )
end
!! SBOCQX
!***PURPOSE  Quick check for SBOCLS.
!***LIBRARY   SLATEC
!***TYPE      SINGLE PRECISION (SBOCQX-S, DBOCQX-D)
!***KEYWORDS  QUICK CHECK
!***AUTHOR  (UNKNOWN)
!***DESCRIPTION
!
!     MINIMAL TEST DRIVER FOR SBOCLS, BOUNDED CONSTRAINED LEAST
!     SQUARES SOLVER.  DELIVERS THE VALUE IPASS=1 IF 8 TESTS WERE
!     PASSED.  DELIVER THE VALUE IPASS=0 IF ANY ONE OF THEM FAILED.
!
!     RUN FOUR BOUNDED LEAST SQUARES PROBLEMS THAT COME FROM THE
!     DIPLOME WORK OF P. ZIMMERMANN.
!
!***ROUTINES CALLED  R1MACH, SBOCLS, SBOLS, SCOPY, SNRM2
!***REVISION HISTORY  (YYMMDD)
!   850310  DATE WRITTEN
!   890618  REVISION DATE from Version 3.2
!   891214  Prologue converted to Version 4.0 format.  (BAB)
!   901013  Added PASS/FAIL message and cleaned up FORMATs.  (RWC)
!***END PROLOGUE  SBOCQX
subroutine SBOCQX (LUN, KPRINT, IPASS)
  REAL D(6,5),W(11,11),BL(5,2),BU(5,2),X(30),RW(55),XTRUE(9)
  REAL C(5,5)
  REAL BL1(10),BU1(10)
  integer IND(10),IW(20),IOPT(40)
  REAL RHS(6,2)
!
  CHARACTER*4 MSG
  DATA ((C(I,J),I=1,5),J=1,5) /1.,10.,4.,8.,1.,1.,10.,2.,-1.,1.,1., &
    -3.,-3.,2.,1.,1.,5.,5.,5.,1.,1.,4.,-1.,-3.,1./
  DATA ((D(I,J),I=1,6),J=1,5) /-74.,14.,66.,-12.,3.,4.,80.,-69., &
    -72.,66.,8.,-12.,18.,21.,-5.,-30.,-7.,4.,-11.,28.,7.,-23.,-4., &
    4.,-4.,0.,1.,3.,1.,0./
  DATA ((BL(I,J),I=1,5),J=1,2) /1.,0.,-1.,1.,-4.,-1.,0.,-3.,1.,-6./
  DATA ((BU(I,J),I=1,5),J=1,2) /3.,2.,1.,3.,-2.,3.,4.,1.,5.,-2./
  DATA ((RHS(I,J),I=1,6),J=1,2) /51.,-61.,-56.,69.,10.,-12.,-5.,-9., &
    708.,4165.,-13266.,8409./
!***FIRST EXECUTABLE STATEMENT  SBOCQX
  DATA (XTRUE(J),J=1,9) /1.,2.,-1.,3.,-4.,1.,32.,30.,31./
  MDW = 11
  MROWS = 6
  NCOLS = 5
  MCON = 4
  IOPT(1) = 99
  ipass = 1
!
  ITEST = 0
!
  if ( KPRINT >= 2) write (LUN, 99998)
  DO 50 IB = 1,2
!
!           TRANSFER DATA TO WORKING ARRAY W(*,*).
!
      DO 40 IRHS = 1,2
          DO 10 J = 1,NCOLS
              call SCOPY(MROWS,D(1,J),1,W(1,J),1)
!
   10         continue
!
!             SET BOUND INDICATOR FLAGS.
!
          call SCOPY(MROWS,RHS(1,IRHS),1,W(1,NCOLS+1),1)
          DO 20 J = 1,NCOLS
              IND(J) = 3
!
   20         continue
          call SBOLS(W,MDW,MROWS,NCOLS,BL(1,IB),BU(1,IB),IND,IOPT,X, &
                     RNORM,MODE,RW,IW)
          DO 30 J = 1,NCOLS
              X(J) = X(J) - XTRUE(J)
!
   30         continue
          SR = SNRM2(NCOLS,X,1)
          MPASS = 1
          if ( SR > 10.E3*SQRT(R1MACH(4))) MPASS = 0
          ipass = IPASS*MPASS
          if ( KPRINT >= 2 ) then
             MSG = 'PASS'
             if ( MPASS == 0) MSG = 'FAIL'
             ITEST = ITEST + 1
             write (LUN, 99999) ITEST, IB, IRHS, SR, MSG
          end if
   40     continue
!
!     RUN STOER'S PROBLEM FROM 1971 SIAM J. N. ANAL. PAPER.
!
   50 continue
  DO 90 IB = 1,2
     DO 80 IRHS = 1,2
        call SCOPY(11*10,0.E0,0,W,1)
        call SCOPY(NCOLS,BL(1,IB),1,BL1,1)
        call SCOPY(NCOLS,BU(1,IB),1,BU1,1)
        IND(NCOLS+1) = 2
        IND(NCOLS+2) = 1
        IND(NCOLS+3) = 2
        IND(NCOLS+4) = 3
        BU1(NCOLS+1) = 5.
        BL1(NCOLS+2) = 20.
        BU1(NCOLS+3) = 30.
        BL1(NCOLS+4) = 11.
        BU1(NCOLS+4) = 40.
        DO 60 J = 1,NCOLS
           call SCOPY(MCON,C(1,J),1,W(1,J),1)
           call SCOPY(MROWS,D(1,J),1,W(MCON+1,J),1)
!
   60       continue
!
!           CHECK LENGTHS OF REQD. ARRAYS.
!
        call SCOPY(MROWS,RHS(1,IRHS),1,W(MCON+1,NCOLS+1),1)
        IOPT(01) = 2
        IOPT(02) = 11
        IOPT(03) = 11
        IOPT(04) = 10
        IOPT(05) = 30
        IOPT(06) = 55
        IOPT(07) = 20
        IOPT(08) = 40
        IOPT(09) = 99
        call SBOCLS(W,MDW,MCON,MROWS,NCOLS,BL1,BU1,IND,IOPT,X, &
                    RNORMC,RNORM,MODE,RW,IW)
        DO 70 J = 1,NCOLS + MCON
           X(J) = X(J) - XTRUE(J)
!
   70       continue
        SR = SNRM2(NCOLS+MCON,X,1)
        MPASS = 1
        if ( SR > 10.E3*SQRT(R1MACH(4))) MPASS = 0
        ipass = IPASS*MPASS
        if ( KPRINT >= 2 ) then
           MSG = 'PASS'
           if ( MPASS == 0) MSG = 'FAIL'
           ITEST = ITEST + 1
           write (LUN, 99999) ITEST, IB, IRHS, SR, MSG
        end if
   80    continue
!
!     HERE THE VALUE OF IPASS=1 SAYS THAT SBOCLS() HAS PASSED ITS TESTS.
!          THE VALUE OF IPASS=0 SAYS THAT SBOCLS() HAS NOT PASSED.
!
   90 continue
  if ( KPRINT >= 3) &
  write (LUN,'('' ipass VALUE. (A 1 IS GOOD, 0 IS BAD.)'',I4)')IPASS
  if ( KPRINT >= 2 .and. ipass == 0) WRITE(LUN,10789)
!
  return
10789 FORMAT (' ERROR IN SBOCLS OR SBOLS')
99998 FORMAT (' TEST   IB IRHS             SR')
99999 FORMAT (3I5, 1P, E20.6, ' TEST ', A, 'ED.')
end
subroutine SCHK12 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NKB, KB, NALF, ALF, NBET, BET, NINC, INC, NMAX, INCMAX, &
!! SCHK12
!***SUBSIDIARY
!***PURPOSE  Quick check for SGEMV and SGBMV.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Du Croz, J. (NAG)
!           Hanson, R. J. (SNLA)
!***DESCRIPTION
!
!  Quick check for SGEMV and SGBMV.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SGBMV, SGEMV, SMAKE2, SMVCH
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK12
!     .. Parameters ..
     A, AA, AS, X, XX, XS, Y, YY, YS, YT, G)
  REAL               ZERO, HALF
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, HALF = 0.5 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            INCMAX, KPRINT, NALF, NBET, NIDIM, NINC, NKB, &
                     NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), BET( NBET ), G( NMAX ), &
                     X( NMAX ), XS( NMAX*INCMAX ), &
                     XX( NMAX*INCMAX ), Y( NMAX ), &
                     YS( NMAX*INCMAX ), YT( NMAX ), &
                     YY( NMAX*INCMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM ), INC( NINC ), KB( NKB )
  REAL               ALPHA, ALS, BETA, BLS, ERR, ERRMAX, TRANSL
  integer            I, IA, IB, IC, IKU, IM, IN, INCX, INCXS, INCY, &
                     INCYS, IX, IY, KL, KLS, KU, KUS, LAA, LDA, &
                     LDAS, LX, LY, M, ML, MS, N, NARGS, NC, ND, NK, &
                     NL, NS, NERR
  LOGICAL            BANDED, FTL, FULL, NULL, RESET, TRAN
  CHARACTER*1        TRANS, TRANSS
!     .. Local Arrays ..
  CHARACTER*3        ICH
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SGBMV, SGEMV, SMAKE2, SMVCH
!     .. Data statements ..
  INTRINSIC          ABS, MAX, MIN
!***FIRST EXECUTABLE STATEMENT  SCHK12
  DATA               ICH/'NTC'/
  FULL = SNAME( 3: 3 ) == 'E'
!     Define the number of arguments.
  BANDED = SNAME( 3: 3 ) == 'B'
  if (  FULL  ) then
     NARGS = 11
  else if (  BANDED  ) then
     NARGS = 13
!
  end if
  NC = 0
  RESET = .TRUE.
!
  ERRMAX = ZERO
  DO 120 IN = 1, NIDIM
     N = IDIM( IN )
!
     ND = N/2 + 1
     DO 110 IM = 1, 2
        if (  IM == 1 ) &
           M = max (  N - ND, 0 )
        if (  IM == 2 ) &
!
           M = min (  N + ND, NMAX )
        if (  BANDED  ) then
           NK = NKB
        else
           NK = 1
        end if
        DO 100 IKU = 1, NK
           if (  BANDED  ) then
              KU = KB( IKU )
              KL = max (  KU - 1, 0 )
           else
              KU = N - 1
              KL = M - 1
!              Set LDA to 1 more than minimum value if room.
           end if
           if (  BANDED  ) then
              LDA = KL + KU + 1
           else
              LDA = M
           end if
           if (  LDA < NMAX ) &
!              Skip tests if not enough room.
              LDA = LDA + 1
           if (  LDA > NMAX ) &
              GO TO 100
           LAA = LDA*N
!
!              Generate the matrix A.
!
           NULL = N <= 0.OR.M <= 0
           TRANSL = ZERO
           call SMAKE2( SNAME( 2: 3 ), ' ', ' ', M, N, A, NMAX, AA, &
!
                       LDA, KL, KU, RESET, TRANSL )
           DO 90 IC = 1, 3
              TRANS = ICH( IC: IC )
!
              TRAN = TRANS == 'T'.OR.TRANS == 'C'
              if (  TRAN  ) then
                 ML = N
                 NL = M
              else
                 ML = M
                 NL = N
!
              end if
              DO 80 IX = 1, NINC
                 INCX = INC( IX )
!
!                    Generate the vector X.
!
                 LX = ABS( INCX )*NL
                 TRANSL = HALF
                 call SMAKE2( 'GE', ' ', ' ', 1, NL, X, 1, XX, &
                             ABS( INCX ), 0, NL - 1, RESET, TRANSL )
                 if (  NL > 1  ) then
                    X( NL/2 ) = ZERO
                    XX( 1 + ABS( INCX )*( NL/2 - 1 ) ) = ZERO
!
                 end if
                 DO 70 IY = 1, NINC
                    INCY = INC( IY )
!
                    LY = ABS( INCY )*ML
                    DO 60 IA = 1, NALF
!
                       ALPHA = ALF( IA )
                       DO 50 IB = 1, NBET
!
!                             Generate the vector Y.
!
                          BETA = BET( IB )
                          TRANSL = ZERO
                          call SMAKE2( 'GE', ' ', ' ', 1, ML, Y, 1, &
                                      YY, ABS( INCY ), 0, ML - 1, &
!
                                      RESET, TRANSL )
!
!                             Save every datum before calling the
!                             subroutine.
!
                          NC = NC + 1
                          TRANSS = TRANS
                          MS = M
                          NS = N
                          KLS = KL
                          KUS = KU
                          ALS = ALPHA
                          DO 10 I = 1, LAA
                             AS( I ) = AA( I )
   10                         continue
                          LDAS = LDA
                          DO 20 I = 1, LX
                             XS( I ) = XX( I )
   20                         continue
                          INCXS = INCX
                          BLS = BETA
                          DO 30 I = 1, LY
                             YS( I ) = YY( I )
   30                         continue
!
!                             Call the subroutine.
!
                          INCYS = INCY
                          if (  FULL  ) then
                             call SGEMV( TRANS, M, N, ALPHA, AA, &
                                         LDA, XX, INCX, BETA, YY, &
                                         INCY )
                          else if (  BANDED  ) then
                             call SGBMV( TRANS, M, N, KL, KU, ALPHA, &
                                         AA, LDA, XX, INCX, BETA, &
                                         YY, INCY )
!
!                             Check if error-exit was taken incorrectly.
!
                          end if
                          if ( NUMXER(NERR) /= 0 ) then
                             if ( kprint >= 2 ) then
                               write ( NOUT, FMT = 9993 )
                             end if
                             FATAL = .TRUE.
!
!                             See what data changed inside subroutines.
!
                          end if
                          ISAME( 1 ) = TRANS == TRANSS
                          ISAME( 2 ) = MS == M
                          ISAME( 3 ) = NS == N
                          if (  FULL  ) then
                             ISAME( 4 ) = ALS == ALPHA
                             ISAME( 5 ) = LSE( AS, AA, LAA )
                             ISAME( 6 ) = LDAS == LDA
                             ISAME( 7 ) = LSE( XS, XX, LX )
                             ISAME( 8 ) = INCXS == INCX
                             ISAME( 9 ) = BLS == BETA
                             if (  NULL  ) then
                                ISAME( 10 ) = LSE( YS, YY, LY )
                             else
                                ISAME( 10 ) = LSERES( 'GE', ' ', 1, &
                                              ML, YS, YY, &
                                              ABS( INCY ) )
                             end if
                             ISAME( 11 ) = INCYS == INCY
                          else if (  BANDED  ) then
                             ISAME( 4 ) = KLS == KL
                             ISAME( 5 ) = KUS == KU
                             ISAME( 6 ) = ALS == ALPHA
                             ISAME( 7 ) = LSE( AS, AA, LAA )
                             ISAME( 8 ) = LDAS == LDA
                             ISAME( 9 ) = LSE( XS, XX, LX )
                             ISAME( 10 ) = INCXS == INCX
                             ISAME( 11 ) = BLS == BETA
                             if (  NULL  ) then
                                ISAME( 12 ) = LSE( YS, YY, LY )
                             else
                                ISAME( 12 ) = LSERES( 'GE', ' ', 1, &
                                              ML, YS, YY, &
                                              ABS( INCY ) )
                             end if
                             ISAME( 13 ) = INCYS == INCY
!
!                             If data was incorrectly changed, report
!                             and return.
!
                          end if
                          DO 40 I = 1, NARGS
                            if ( .NOT. ISAME( I ) ) then
                              FATAL = .TRUE.
                              if ( kprint >= 2 ) then
                                write ( NOUT, FMT = 9998 )I
                              end if
                            end if
!
  40                          continue
                          FTL = .FALSE.
!
!                                Check the result.
!
                          if (  .NOT.NULL  ) then
                             call SMVCH( TRANS, M, N, ALPHA, A, &
                                         NMAX, X, INCX, BETA, Y, &
                                         INCY, YT, G, YY, EPS, ERR, &
                                         FTL, NOUT, .TRUE., &
                                         kprint )
                             ERRMAX = max (  ERRMAX, ERR )
                          end if
                          if ( FTL ) then
                            FATAL = .TRUE.
                            if ( kprint >= 3 ) then
                              write ( NOUT, FMT = 9996 )SNAME
                              if (  FULL  ) then
                                 write ( NOUT, FMT = 9994 )NC, SNAME, &
                                  TRANS, M, N, ALPHA, &
                                  LDA, INCX, BETA, INCY
                              else if (  BANDED  ) then
                                 write ( NOUT, FMT = 9995 )NC, SNAME, &
                                  TRANS, M, N, KL, KU, &
                                  ALPHA, LDA, INCX, BETA, INCY
                              end if
                            end if
!
                          end if
!
   50                      continue
!
   60                   continue
!
   70                continue
!
   80             continue
!
   90          continue
!
  100       continue
!
  110    continue
!
!     Report result.
!
  120 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', 4( I3, ',' ), F4.1, &
        ', A,', I3, ', X,', I2, ',', F4.1, ', Y,', I2, ') .' )
 9994 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', 2( I3, ',' ), F4.1, &
        ', A,', I3, ', X,', I2, ',', F4.1, ', Y,', I2, &
        ')         .' )
 9993 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK12.
!
        '******' )
end
subroutine SCHK13 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NALF, ALF, NBET, BET, NMAX, A, AA, AS, B, BB, BS, C, CC, &
!! SCHK13
!***SUBSIDIARY
!***PURPOSE  Quick check for SGEMM.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Quick check for SGEMM.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SGEMM, SMAKE3, SMMCH
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK13
!     .. Parameters ..
     CS, CT, G)
  REAL               ZERO
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            KPRINT, NALF, NBET, NIDIM, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), BET( NBET ), G( NMAX ), &
                     B( NMAX, NMAX ), BB( NMAX*NMAX ), &
                     BS( NMAX*NMAX ), C( NMAX, NMAX ), &
                     CC( NMAX*NMAX ), CS( NMAX*NMAX ), &
                     CT( NMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM )
  REAL               ALPHA, ALS, BETA, BLS, ERR, ERRMAX
  integer            I, IA, IB, ICA, ICB, IK, IM, IN, K, KS, LAA, &
                     LBB, LCC, LDA, LDAS, LDB, LDBS, LDC, LDCS, M, &
                     MA, MB, MS, N, NA, NARGS, NB, NC, NERR, NS
  LOGICAL            FTL, NULL, RESET, TRANA, TRANB
  CHARACTER*1        TRANAS, TRANBS, TRANSA, TRANSB
!     .. Local Arrays ..
  CHARACTER*3        ICH
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SGEMM, SMAKE3, SMMCH
!     .. Data statements ..
  INTRINSIC          MAX
!***FIRST EXECUTABLE STATEMENT  SCHK13
  DATA               ICH/'NTC'/
!
  NARGS = 13
  NC = 0
  RESET = .TRUE.
!
!
  ERRMAX = ZERO
  DO 110 IM = 1, NIDIM
!
     M = IDIM( IM )
     DO 100 IN = 1, NIDIM
!           Set LDC to 1 more than minimum value if room.
        N = IDIM( IN )
        LDC = M
        if (  LDC < NMAX ) &
!           Skip tests if not enough room.
           LDC = LDC + 1
        if (  LDC > NMAX ) &
           GO TO 100
        LCC = LDC*N
!
        NULL = N <= 0.OR.M <= 0
        DO 90 IK = 1, NIDIM
!
           K = IDIM( IK )
           DO 80 ICA = 1, 3
              TRANSA = ICH( ICA: ICA )
!
              TRANA = TRANSA == 'T'.OR.TRANSA == 'C'
              if (  TRANA  ) then
                 MA = K
                 NA = M
              else
                 MA = M
                 NA = K
!                 Set LDA to 1 more than minimum value if room.
              end if
              LDA = MA
              if (  LDA < NMAX ) &
!                 Skip tests if not enough room.
                 LDA = LDA + 1
              if (  LDA > NMAX ) &
                 GO TO 80
!
!                 Generate the matrix A.
!
              LAA = LDA*NA
              call SMAKE3( 'GE', ' ', ' ', MA, NA, A, NMAX, AA, LDA, &
!
                          RESET, ZERO )
              DO 70 ICB = 1, 3
                 TRANSB = ICH( ICB: ICB )
!
                 TRANB = TRANSB == 'T'.OR.TRANSB == 'C'
                 if (  TRANB  ) then
                    MB = N
                    NB = K
                 else
                    MB = K
                    NB = N
!                    Set LDB to 1 more than minimum value if room.
                 end if
                 LDB = MB
                 if (  LDB < NMAX ) &
!                    Skip tests if not enough room.
                    LDB = LDB + 1
                 if (  LDB > NMAX ) &
                    GO TO 70
!
!                    Generate the matrix B.
!
                 LBB = LDB*NB
                 call SMAKE3( 'GE', ' ', ' ', MB, NB, B, NMAX, BB, &
!
                             LDB, RESET, ZERO )
                 DO 60 IA = 1, NALF
!
                    ALPHA = ALF( IA )
                    DO 50 IB = 1, NBET
!
!                          Generate the matrix C.
!
                       BETA = BET( IB )
                       call SMAKE3( 'GE', ' ', ' ', M, N, C, NMAX, &
!
                                   CC, LDC, RESET, ZERO )
!
!                          Save every datum before calling the
!                          subroutine.
!
                       NC = NC + 1
                       TRANAS = TRANSA
                       TRANBS = TRANSB
                       MS = M
                       NS = N
                       KS = K
                       ALS = ALPHA
                       DO 10 I = 1, LAA
                          AS( I ) = AA( I )
   10                      continue
                       LDAS = LDA
                       DO 20 I = 1, LBB
                          BS( I ) = BB( I )
   20                      continue
                       LDBS = LDB
                       BLS = BETA
                       DO 30 I = 1, LCC
                          CS( I ) = CC( I )
   30                      continue
!
!                          Call the subroutine.
!
                       LDCS = LDC
                       call SGEMM( TRANSA, TRANSB, M, N, K, ALPHA, &
!
!                          Check if error-exit was taken incorrectly.
!
                                   AA, LDA, BB, LDB, BETA, CC, LDC )
                       if (  NUMXER(NERR) /= 0 ) then
                        if ( kprint >= 2 ) then
                          write ( NOUT, FMT = 9994 )
                        end if
                        FATAL = .TRUE.
!
!                          See what data changed inside subroutines.
!
                       end if
                       ISAME( 1 ) = TRANSA == TRANAS
                       ISAME( 2 ) = TRANSB == TRANBS
                       ISAME( 3 ) = MS == M
                       ISAME( 4 ) = NS == N
                       ISAME( 5 ) = KS == K
                       ISAME( 6 ) = ALS == ALPHA
                       ISAME( 7 ) = LSE( AS, AA, LAA )
                       ISAME( 8 ) = LDAS == LDA
                       ISAME( 9 ) = LSE( BS, BB, LBB )
                       ISAME( 10 ) = LDBS == LDB
                       ISAME( 11 ) = BLS == BETA
                       if (  NULL  ) then
                          ISAME( 12 ) = LSE( CS, CC, LCC )
                       else
                          ISAME( 12 ) = LSERES( 'GE', ' ', M, N, CS, &
                                        CC, LDC )
                       end if
!
!                          If data was incorrectly changed, report
!                          and return.
!
                       ISAME( 13 ) = LDCS == LDC
                          DO 40 I = 1, NARGS
                            if ( .NOT. ISAME( I ) ) then
                              FATAL = .TRUE.
                              if ( kprint >= 2 ) then
                                write ( NOUT, FMT = 9998 )I
                              end if
                            end if
!
  40                          continue
                       FTL = .FALSE.
!
!                             Check the result.
!
                       if (  .NOT.NULL  ) then
                          call SMMCH( TRANSA, TRANSB, M, N, K, &
                                      ALPHA, A, NMAX, B, NMAX, BETA, &
                                      C, NMAX, CT, G, CC, LDC, EPS, &
                                      ERR, FTL, NOUT, .TRUE., &
                                      kprint )
                          ERRMAX = max (  ERRMAX, ERR )
                       end if
                       if ( FTL ) then
                         FATAL = .TRUE.
                         if ( kprint >= 3 ) then
                           write (NOUT, FMT = 9996) SNAME
                           write ( NOUT, FMT = 9995 )NC, SNAME, &
                             TRANSA, TRANSB, M, N, K, &
                             ALPHA, LDA, LDB, BETA, LDC
                         end if
!
                       end if
!
   50                   continue
!
   60                continue
!
   70             continue
!
   80          continue
!
   90       continue
!
  100    continue
!
!     Report result.
!
  110 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',''', A1, ''',', &
        3( I3, ',' ), F4.1, ', A,', I3, ', B,', I3, ',', F4.1, ', ', &
        'C,', I3, ').' )
 9994 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK13.
!
        '******' )
end
subroutine SCHK22 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NKB, KB, NALF, ALF, NBET, BET, NINC, INC, NMAX, INCMAX, &
!! SCHK22
!***SUBSIDIARY
!***PURPOSE  Quick check for SSYMV, SSBMV and SSPMV.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Du Croz, J. (NAG)
!           Hanson, R. J. (SNLA)
!***DESCRIPTION
!
!  Quick check for SSYMV, SSBMV and SSPMV.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE2, SMVCH, SSBMV, SSPMV,
!                    SSYMV
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK22
!     .. Parameters ..
     A, AA, AS, X, XX, XS, Y, YY, YS, YT, G)
  REAL               ZERO, HALF
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, HALF = 0.5 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            INCMAX, KPRINT, NALF, NBET, NIDIM, NINC, NKB, &
                     NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), BET( NBET ), G( NMAX ), &
                     X( NMAX ), XS( NMAX*INCMAX ), &
                     XX( NMAX*INCMAX ), Y( NMAX ), &
                     YS( NMAX*INCMAX ), YT( NMAX ), &
                     YY( NMAX*INCMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM ), INC( NINC ), KB( NKB )
  REAL               ALPHA, ALS, BETA, BLS, ERR, ERRMAX, TRANSL
  integer            I, IA, IB, IC, IK, IN, INCX, INCXS, INCY, &
                     INCYS, IX, IY, K, KS, LAA, LDA, LDAS, LX, LY, &
                     N, NARGS, NC, NK, NS, NERR
  LOGICAL            BANDED, FTL, FULL, NULL, PACKED, RESET
  CHARACTER*1        UPLO, UPLOS
!     .. Local Arrays ..
  CHARACTER*2        ICH
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SMAKE2, SMVCH, SSBMV, SSPMV, SSYMV
!     .. Data statements ..
  INTRINSIC          ABS, MAX
!***FIRST EXECUTABLE STATEMENT  SCHK22
  DATA               ICH/'UL'/
  FULL = SNAME( 3: 3 ) == 'Y'
  BANDED = SNAME( 3: 3 ) == 'B'
!     Define the number of arguments.
  PACKED = SNAME( 3: 3 ) == 'P'
  if (  FULL  ) then
     NARGS = 10
  else if (  BANDED  ) then
     NARGS = 11
  else if (  PACKED  ) then
     NARGS = 9
!
  end if
  NC = 0
  RESET = .TRUE.
!
  ERRMAX = ZERO
  DO 110 IN = 1, NIDIM
!
     N = IDIM( IN )
     if (  BANDED  ) then
        NK = NKB
     else
        NK = 1
     end if
     DO 100 IK = 1, NK
        if (  BANDED  ) then
           K = KB( IK )
        else
           K = N - 1
!           Set LDA to 1 more than minimum value if room.
        end if
        if (  BANDED  ) then
           LDA = K + 1
        else
           LDA = N
        end if
        if (  LDA < NMAX ) &
!           Skip tests if not enough room.
           LDA = LDA + 1
        if (  LDA > NMAX ) &
           GO TO 100
        if (  PACKED  ) then
           LAA = ( N*( N + 1 ) )/2
        else
           LAA = LDA*N
        end if
!
        NULL = N <= 0
        DO 90 IC = 1, 2
!
!              Generate the matrix A.
!
           UPLO = ICH( IC: IC )
           TRANSL = ZERO
           call SMAKE2( SNAME( 2: 3 ), UPLO, ' ', N, N, A, NMAX, AA, &
!
                       LDA, K, K, RESET, TRANSL )
           DO 80 IX = 1, NINC
              INCX = INC( IX )
!
!                 Generate the vector X.
!
              LX = ABS( INCX )*N
              TRANSL = HALF
              call SMAKE2( 'GE', ' ', ' ', 1, N, X, 1, XX, &
                          ABS( INCX ), 0, N - 1, RESET, TRANSL )
              if (  N > 1  ) then
                 X( N/2 ) = ZERO
                 XX( 1 + ABS( INCX )*( N/2 - 1 ) ) = ZERO
!
              end if
              DO 70 IY = 1, NINC
                 INCY = INC( IY )
!
                 LY = ABS( INCY )*N
                 DO 60 IA = 1, NALF
!
                    ALPHA = ALF( IA )
                    DO 50 IB = 1, NBET
!
!                          Generate the vector Y.
!
                       BETA = BET( IB )
                       TRANSL = ZERO
                       call SMAKE2( 'GE', ' ', ' ', 1, N, Y, 1, YY, &
                                   ABS( INCY ), 0, N - 1, RESET, &
!
                                   TRANSL )
!
!                          Save every datum before calling the
!                          subroutine.
!
                       NC = NC + 1
                       UPLOS = UPLO
                       NS = N
                       KS = K
                       ALS = ALPHA
                       DO 10 I = 1, LAA
                          AS( I ) = AA( I )
   10                      continue
                       LDAS = LDA
                       DO 20 I = 1, LX
                          XS( I ) = XX( I )
   20                      continue
                       INCXS = INCX
                       BLS = BETA
                       DO 30 I = 1, LY
                          YS( I ) = YY( I )
   30                      continue
!
!                          Call the subroutine.
!
                       INCYS = INCY
                       if (  FULL  ) then
                          call SSYMV( UPLO, N, ALPHA, AA, LDA, XX, &
                                      INCX, BETA, YY, INCY )
                       else if (  BANDED  ) then
                          call SSBMV( UPLO, N, K, ALPHA, AA, LDA, &
                                      XX, INCX, BETA, YY, INCY )
                       else if (  PACKED  ) then
                          call SSPMV( UPLO, N, ALPHA, AA, XX, INCX, &
                                      BETA, YY, INCY )
!
!                          Check if error-exit was taken incorrectly.
!
                       end if
                       if ( NUMXER(NERR) /= 0 ) then
                          if ( kprint >= 2 ) then
                            write ( NOUT, FMT = 9992 )
                          end if
                          FATAL = .TRUE.
!
!                          See what data changed inside subroutines.
!
                       end if
                       ISAME( 1 ) = UPLO == UPLOS
                       ISAME( 2 ) = NS == N
                       if (  FULL  ) then
                          ISAME( 3 ) = ALS == ALPHA
                          ISAME( 4 ) = LSE( AS, AA, LAA )
                          ISAME( 5 ) = LDAS == LDA
                          ISAME( 6 ) = LSE( XS, XX, LX )
                          ISAME( 7 ) = INCXS == INCX
                          ISAME( 8 ) = BLS == BETA
                          if (  NULL  ) then
                             ISAME( 9 ) = LSE( YS, YY, LY )
                          else
                             ISAME( 9 ) = LSERES( 'GE', ' ', 1, N, &
                                          YS, YY, ABS( INCY ) )
                          end if
                          ISAME( 10 ) = INCYS == INCY
                       else if (  BANDED  ) then
                          ISAME( 3 ) = KS == K
                          ISAME( 4 ) = ALS == ALPHA
                          ISAME( 5 ) = LSE( AS, AA, LAA )
                          ISAME( 6 ) = LDAS == LDA
                          ISAME( 7 ) = LSE( XS, XX, LX )
                          ISAME( 8 ) = INCXS == INCX
                          ISAME( 9 ) = BLS == BETA
                          if (  NULL  ) then
                             ISAME( 10 ) = LSE( YS, YY, LY )
                          else
                             ISAME( 10 ) = LSERES( 'GE', ' ', 1, N, &
                                           YS, YY, ABS( INCY ) )
                          end if
                          ISAME( 11 ) = INCYS == INCY
                       else if (  PACKED  ) then
                          ISAME( 3 ) = ALS == ALPHA
                          ISAME( 4 ) = LSE( AS, AA, LAA )
                          ISAME( 5 ) = LSE( XS, XX, LX )
                          ISAME( 6 ) = INCXS == INCX
                          ISAME( 7 ) = BLS == BETA
                          if (  NULL  ) then
                             ISAME( 8 ) = LSE( YS, YY, LY )
                          else
                             ISAME( 8 ) = LSERES( 'GE', ' ', 1, N, &
                                          YS, YY, ABS( INCY ) )
                          end if
                          ISAME( 9 ) = INCYS == INCY
!
!                          If data was incorrectly changed, report and
!                          return.
!
                       end if
                          DO 40 I = 1, NARGS
                            if ( .NOT. ISAME( I ) ) then
                              FATAL = .TRUE.
                              if ( kprint >= 2 ) then
                                write ( NOUT, FMT = 9998 )I
                              end if
                            end if
!
  40                          continue
                       FTL = .FALSE.
!
!                             Check the result.
!
                       if (  .NOT.NULL  ) then
                          call SMVCH( 'N', N, N, ALPHA, A, NMAX, X, &
                                      INCX, BETA, Y, INCY, YT, G, &
                                      YY, EPS, ERR, FTL, NOUT, &
                                      .TRUE., kprint )
                          ERRMAX = max (  ERRMAX, ERR )
                       end if
                       if ( FTL ) then
                         FATAL = .TRUE.
                         if ( kprint >= 3 ) then
                           write ( NOUT, FMT = 9996 )SNAME
                           if (  FULL  ) then
                              write ( NOUT, FMT = 9993 )NC, SNAME, &
                               UPLO, N, ALPHA, LDA, &
                               INCX, BETA, INCY
                           else if (  BANDED  ) then
                              write ( NOUT, FMT = 9994 )NC, SNAME, &
                               UPLO, N, K, ALPHA, &
                               LDA, INCX, BETA, INCY
                           else if (  PACKED  ) then
                              write ( NOUT, FMT = 9995 )NC, SNAME, &
                               UPLO, N, ALPHA, INCX, &
                               BETA, INCY
                           end if
                         end if
!
                       end if
!
   50                   continue
!
   60                continue
!
   70             continue
!
   80          continue
!
   90       continue
!
  100    continue
!
!     Report result.
!
  110 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', I3, ',', F4.1, ', AP', &
        ', X,', I2, ',', F4.1, ', Y,', I2, ')                .' )
 9994 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', 2( I3, ',' ), F4.1, &
        ', A,', I3, ', X,', I2, ',', F4.1, ', Y,', I2, &
        ')         .' )
 9993 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', I3, ',', F4.1, ', A,', &
        I3, ', X,', I2, ',', F4.1, ', Y,', I2, ')             .' )
 9992 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK22.
!
        '******' )
end
subroutine SCHK23 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NALF, ALF, NBET, BET, NMAX, A, AA, AS, B, BB, BS, C, CC, &
!! SCHK23
!***SUBSIDIARY
!***PURPOSE  Quick check for SSYMM.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Quick check for SSYMM.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE3, SMMCH, SSYMM
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK23
!     .. Parameters ..
     CS, CT, G)
  REAL               ZERO
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            KPRINT, NALF, NBET, NIDIM, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), BET( NBET ), G( NMAX ), &
                     B( NMAX, NMAX ), BB( NMAX*NMAX ), &
                     BS( NMAX*NMAX ), C( NMAX, NMAX ), &
                     CC( NMAX*NMAX ), CS( NMAX*NMAX ), &
                     CT( NMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM )
  REAL               ALPHA, ALS, BETA, BLS, ERR, ERRMAX
  integer            I, IA, IB, ICS, ICU, IM, IN, LAA, &
                     LBB, LCC, LDA, LDAS, LDB, LDBS, LDC, LDCS, M, &
                     MS, N, NA, NARGS, NC, NERR, NS
  LOGICAL            FTL, NULL, RESET, LEFT
  CHARACTER*1        SIDE, SIDES, UPLO, UPLOS
!     .. Local Arrays ..
  CHARACTER*2        ICHS, ICHU
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SSYMM, SMAKE3, SMMCH
!     .. Data statements ..
  INTRINSIC          MAX
!***FIRST EXECUTABLE STATEMENT  SCHK23
  DATA               ICHS/'LR'/, ICHU/'UL'/
!
  NARGS = 12
  NC = 0
  RESET = .TRUE.
!
  ERRMAX = ZERO
  DO 100 IM = 1, NIDIM
!
     M = IDIM( IM )
     DO 90 IN = 1, NIDIM
!           Set LDC to 1 more than minimum value if room.
        N = IDIM( IN )
        LDC = M
        if (  LDC < NMAX ) &
!           Skip tests if not enough room.
           LDC = LDC + 1
        if (  LDC > NMAX ) &
           GO TO 90
        LCC = LDC*N
!
!           Set LDB to 1 more than minimum value if room.
        NULL = N <= 0.OR.M <= 0
        LDB = M
        if (  LDB < NMAX ) &
!           Skip tests if not enough room.
           LDB = LDB + 1
        if (  LDB > NMAX ) &
           GO TO 90
!
!           Generate the matrix B.
!
        LBB = LDB*N
        call SMAKE3( 'GE', ' ', ' ', M, N, B, NMAX, BB, LDB, RESET, &
!
                    ZERO )
        DO 80 ICS = 1, 2
           SIDE = ICHS( ICS: ICS )
!
           LEFT = SIDE == 'L'
           if (  LEFT  ) then
              NA = M
           else
              NA = N
!              Set LDA to 1 more than minimum value if room.
           end if
           LDA = NA
           if (  LDA < NMAX ) &
!              Skip tests if not enough room.
              LDA = LDA + 1
           if (  LDA > NMAX ) &
              GO TO 80
!
           LAA = LDA*NA
           DO 70 ICU = 1, 2
!
!                 Generate the symmetric matrix A.
!
              UPLO = ICHU( ICU: ICU )
              call SMAKE3('SY', UPLO, ' ', NA, NA, A, NMAX, AA, LDA, &
!
                          RESET, ZERO )
              DO 60 IA = 1, NALF
!
                 ALPHA = ALF( IA )
                 DO 50 IB = 1, NBET
!
!                       Generate the matrix C.
!
                    BETA = BET( IB )
                    call SMAKE3( 'GE', ' ', ' ', M, N, C, NMAX, CC, &
!
                                LDC, RESET, ZERO )
!
!                       Save every datum before calling the
!                       subroutine.
!
                    NC = NC + 1
                    SIDES = SIDE
                    UPLOS = UPLO
                    MS = M
                    NS = N
                    ALS = ALPHA
                    DO 10 I = 1, LAA
                       AS( I ) = AA( I )
   10                   continue
                    LDAS = LDA
                    DO 20 I = 1, LBB
                       BS( I ) = BB( I )
   20                   continue
                    LDBS = LDB
                    BLS = BETA
                    DO 30 I = 1, LCC
                       CS( I ) = CC( I )
   30                   continue
!
!                       Call the subroutine.
!
                    LDCS = LDC
                    call SSYMM( SIDE, UPLO, M, N, ALPHA, AA, LDA, &
!
!                       Check if error-exit was taken incorrectly.
!
                                BB, LDB, BETA, CC, LDC )
                    if ( NUMXER(NERR) /= 0 ) then
                      if ( kprint >= 2 ) then
                       write ( NOUT, FMT = 9994 )
                      end if
                      FATAL = .TRUE.
!
!                       See what data changed inside subroutines.
!
                    end if
                    ISAME( 1 ) = SIDES == SIDE
                    ISAME( 2 ) = UPLOS == UPLO
                    ISAME( 3 ) = MS == M
                    ISAME( 4 ) = NS == N
                    ISAME( 5 ) = ALS == ALPHA
                    ISAME( 6 ) = LSE( AS, AA, LAA )
                    ISAME( 7 ) = LDAS == LDA
                    ISAME( 8 ) = LSE( BS, BB, LBB )
                    ISAME( 9 ) = LDBS == LDB
                    ISAME( 10 ) = BLS == BETA
                    if (  NULL  ) then
                       ISAME( 11 ) = LSE( CS, CC, LCC )
                    else
                       ISAME( 11 ) = LSERES( 'GE', ' ', M, N, CS, &
                                     CC, LDC )
                    end if
!
!                       If data was incorrectly changed, report and
!                       return.
!
                    ISAME( 12 ) = LDCS == LDC
                    DO 40 I = 1, NARGS
                      if ( .NOT. ISAME( I ) ) then
                        FATAL = .TRUE.
                        if ( kprint >= 2 ) then
                          write ( NOUT, FMT = 9998 )I
                        end if
                      end if
!
  40                    continue
                    FTL = .FALSE.
!
!                          Check the result.
!
                    if (  .NOT.NULL  ) then
                       if (  LEFT  ) then
                          call SMMCH( 'N', 'N', M, N, M, ALPHA, A, &
                                      NMAX, B, NMAX, BETA, C, NMAX, &
                                      CT, G, CC, LDC, EPS, ERR, &
                                      FTL, NOUT, .TRUE., kprint )
                       else
                          call SMMCH( 'N', 'N', M, N, N, ALPHA, B, &
                                      NMAX, A, NMAX, BETA, C, NMAX, &
                                      CT, G, CC, LDC, EPS, ERR, &
                                      FTL, NOUT, .TRUE., kprint )
                       end if
                       ERRMAX = max (  ERRMAX, ERR )
                    end if
                    if ( FTL ) then
                      FATAL = .TRUE.
                      if ( kprint >= 3 ) then
                        write (NOUT, FMT = 9996) SNAME
                        write ( NOUT, FMT = 9995 )NC, SNAME, &
                          SIDE, UPLO, M, N, &
                          ALPHA, LDA, LDB, BETA, LDC
                      end if
!
                    end if
!
   50                continue
!
   60             continue
!
   70          continue
!
   80       continue
!
   90    continue
!
!     Report result.
!
  100 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
     end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( 1X, I6, ': ', A6, '(', 2( '''', A1, ''',' ), 2( I3, ',' ), &
        F4.1, ', A,', I3, ', B,', I3, ',', F4.1, ', C,', I3, ')   ', &
        ' .' )
 9994 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK23.
!
        '******' )
end
subroutine SCHK32 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NKB, KB, NINC, INC, NMAX, INCMAX, A, AA, AS, X, XX, XS, &
!! SCHK32
!***SUBSIDIARY
!***PURPOSE  Quick check for STRMV, STBMV, STPMV, STRSV, STBSV and
!            STPSV.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Du Croz, J. (NAG)
!           Hanson, R. J. (SNLA)
!***DESCRIPTION
!
!  Quick check for STRMV, STBMV, STPMV, STRSV, STBSV and STPSV.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE2, SMVCH, STBMV, STBSV,
!                    STPMV, STPSV, STRMV, STRSV
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK32
!     .. Parameters ..
     XT, G, Z)
  REAL               ZERO, HALF, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, HALF = 0.5, ONE = 1.0 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            INCMAX, KPRINT, NIDIM, NINC, NKB, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), &
                     AS( NMAX*NMAX ), G( NMAX ), X( NMAX ), &
                     XS( NMAX*INCMAX ), XT( NMAX ), &
                     XX( NMAX*INCMAX ), Z( NMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM ), INC( NINC ), KB( NKB )
  REAL               ERR, ERRMAX, TRANSL
  integer            I, ICD, ICT, ICU, IK, IN, INCX, INCXS, IX, K, &
                     KS, LAA, LDA, LDAS, LX, N, NARGS, NC, NK, NS, &
                     NERR
  LOGICAL            BANDED, FTL, FULL, NULL, PACKED, RESET
  CHARACTER*1        DIAG, DIAGS, TRANS, TRANSS, UPLO, UPLOS
  CHARACTER*2        ICHD, ICHU
!     .. Local Arrays ..
  CHARACTER*3        ICHT
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
  EXTERNAL           SMAKE2, SMVCH, STBMV, STBSV, STPMV, STPSV, &
!     .. Intrinsic Functions ..
                     STRMV, STRSV
!     .. Data statements ..
  INTRINSIC          ABS, MAX
!***FIRST EXECUTABLE STATEMENT  SCHK32
  DATA               ICHU/'UL'/, ICHT/'NTC'/, ICHD/'UN'/
  FULL = SNAME( 3: 3 ) == 'R'
  BANDED = SNAME( 3: 3 ) == 'B'
!     Define the number of arguments.
  PACKED = SNAME( 3: 3 ) == 'P'
  if (  FULL  ) then
     NARGS = 8
  else if (  BANDED  ) then
     NARGS = 9
  else if (  PACKED  ) then
     NARGS = 7
!
  end if
  NC = 0
  RESET = .TRUE.
!     Set up zero vector for SMVCH.
  ERRMAX = ZERO
  DO 10 I = 1, NMAX
     Z( I ) = ZERO
!
   10 continue
  DO 110 IN = 1, NIDIM
!
     N = IDIM( IN )
     if (  BANDED  ) then
        NK = NKB
     else
        NK = 1
     end if
     DO 100 IK = 1, NK
        if (  BANDED  ) then
           K = KB( IK )
        else
           K = N - 1
!           Set LDA to 1 more than minimum value if room.
        end if
        if (  BANDED  ) then
           LDA = K + 1
        else
           LDA = N
        end if
        if (  LDA < NMAX ) &
!           Skip tests if not enough room.
           LDA = LDA + 1
        if (  LDA > NMAX ) &
           GO TO 100
        if (  PACKED  ) then
           LAA = ( N*( N + 1 ) )/2
        else
           LAA = LDA*N
        end if
!
        NULL = N <= 0
        DO 90 ICU = 1, 2
!
           UPLO = ICHU( ICU: ICU )
           DO 80 ICT = 1, 3
!
              TRANS = ICHT( ICT: ICT )
              DO 70 ICD = 1, 2
!
!                    Generate the matrix A.
!
                 DIAG = ICHD( ICD: ICD )
                 TRANSL = ZERO
                 call SMAKE2( SNAME( 2: 3 ), UPLO, DIAG, N, N, A, &
!
                             NMAX, AA, LDA, K, K, RESET, TRANSL )
                 DO 60 IX = 1, NINC
                    INCX = INC( IX )
!
!                       Generate the vector X.
!
                    LX = ABS( INCX )*N
                    TRANSL = HALF
                    call SMAKE2( 'GE', ' ', ' ', 1, N, X, 1, XX, &
                                ABS( INCX ), 0, N - 1, RESET, &
                                TRANSL )
                    if (  N > 1  ) then
                       X( N/2 ) = ZERO
                       XX( 1 + ABS( INCX )*( N/2 - 1 ) ) = ZERO
!
                    end if
!
!                       Save every datum before calling the subroutine.
!
                    NC = NC + 1
                    UPLOS = UPLO
                    TRANSS = TRANS
                    DIAGS = DIAG
                    NS = N
                    KS = K
                    DO 20 I = 1, LAA
                       AS( I ) = AA( I )
   20                   continue
                    LDAS = LDA
                    DO 30 I = 1, LX
                       XS( I ) = XX( I )
   30                   continue
!
!                       Call the subroutine.
!
                    INCXS = INCX
                    if (  SNAME( 4: 5 ) == 'MV'  ) then
                       if (  FULL  ) then
                          call STRMV( UPLO, TRANS, DIAG, N, AA, LDA, &
                                      XX, INCX )
                       else if (  BANDED  ) then
                          call STBMV( UPLO, TRANS, DIAG, N, K, AA, &
                                      LDA, XX, INCX )
                       else if (  PACKED  ) then
                          call STPMV( UPLO, TRANS, DIAG, N, AA, XX, &
                                      INCX )
                       end if
                    else if (  SNAME( 4: 5 ) == 'SV'  ) then
                       if (  FULL  ) then
                          call STRSV( UPLO, TRANS, DIAG, N, AA, LDA, &
                                      XX, INCX )
                       else if (  BANDED  ) then
                          call STBSV( UPLO, TRANS, DIAG, N, K, AA, &
                                      LDA, XX, INCX )
                       else if (  PACKED  ) then
                          call STPSV( UPLO, TRANS, DIAG, N, AA, XX, &
                                      INCX )
                       end if
!
!                       Check if error-exit was taken incorrectly.
!
                    end if
                    if ( NUMXER(NERR) /= 0 ) then
                       if ( kprint >= 2 ) then
                         write ( NOUT, FMT = 9992 )
                       end if
                       FATAL = .TRUE.
!
!                       See what data changed inside subroutines.
!
                    end if
                    ISAME( 1 ) = UPLO == UPLOS
                    ISAME( 2 ) = TRANS == TRANSS
                    ISAME( 3 ) = DIAG == DIAGS
                    ISAME( 4 ) = NS == N
                    if (  FULL  ) then
                       ISAME( 5 ) = LSE( AS, AA, LAA )
                       ISAME( 6 ) = LDAS == LDA
                       if (  NULL  ) then
                          ISAME( 7 ) = LSE( XS, XX, LX )
                       else
                          ISAME( 7 ) = LSERES( 'GE', ' ', 1, N, XS, &
                                       XX, ABS( INCX ) )
                       end if
                       ISAME( 8 ) = INCXS == INCX
                    else if (  BANDED  ) then
                       ISAME( 5 ) = KS == K
                       ISAME( 6 ) = LSE( AS, AA, LAA )
                       ISAME( 7 ) = LDAS == LDA
                       if (  NULL  ) then
                          ISAME( 8 ) = LSE( XS, XX, LX )
                       else
                          ISAME( 8 ) = LSERES( 'GE', ' ', 1, N, XS, &
                                       XX, ABS( INCX ) )
                       end if
                       ISAME( 9 ) = INCXS == INCX
                    else if (  PACKED  ) then
                       ISAME( 5 ) = LSE( AS, AA, LAA )
                       if (  NULL  ) then
                          ISAME( 6 ) = LSE( XS, XX, LX )
                       else
                          ISAME( 6 ) = LSERES( 'GE', ' ', 1, N, XS, &
                                       XX, ABS( INCX ) )
                       end if
                       ISAME( 7 ) = INCXS == INCX
!
!                       If data was incorrectly changed, report and
!                       return.
!
                    end if
                    DO 40 I = 1, NARGS
                      if ( .NOT. ISAME( I ) ) then
                        FATAL = .TRUE.
                        if ( kprint >= 2 ) then
                          write ( NOUT, FMT = 9998 )I
                        end if
                      end if
!
  40                    continue
                    FTL = .FALSE.
                    if (  .NOT.NULL  ) then
!
!                             Check the result.
!
                       if (  SNAME( 4: 5 ) == 'MV'  ) then
                          call SMVCH( TRANS, N, N, ONE, A, NMAX, X, &
                                      INCX, ZERO, Z, INCX, XT, G, &
                                      XX, EPS, ERR, FTL, NOUT, &
                                      .TRUE., kprint )
!
!                             Compute approximation to original vector.
!
                       else if (  SNAME( 4: 5 ) == 'SV'  ) then
                          DO 50 I = 1, N
                             Z( I ) = XX( 1 + ( I - 1 )* &
                                      ABS( INCX ) )
                             XX( 1 + ( I - 1 )*ABS( INCX ) ) &
                                = X( I )
   50                         continue
                          call SMVCH( TRANS, N, N, ONE, A, NMAX, Z, &
                                      INCX, ZERO, X, INCX, XT, G, &
                                      XX, EPS, ERR, FTL, NOUT, &
                                      .FALSE., kprint )
                       end if
                       ERRMAX = max (  ERRMAX, ERR )
                    end if
                    if ( FTL ) then
                      FATAL = .TRUE.
                      if ( kprint >= 3 ) then
                        write ( NOUT, FMT = 9996 )SNAME
                        if (  FULL  ) then
                           write ( NOUT, FMT = 9993 )NC, SNAME, &
                            UPLO, TRANS, DIAG, N, &
                            LDA, INCX
                        else if (  BANDED  ) then
                           write ( NOUT, FMT = 9994 )NC, SNAME, &
                            UPLO, TRANS, DIAG, N, &
                            K, LDA, INCX
                        else if (  PACKED  ) then
                           write ( NOUT, FMT = 9995 )NC, SNAME, &
                            UPLO, TRANS, DIAG, N, &
                            INCX
                        end if
                      end if
!
                    end if
!
   60                continue
!
   70             continue
!
   80          continue
!
   90       continue
!
  100    continue
!
!     Report result.
!
  110 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( 1X, I6, ': ', A6, '(', 3( '''', A1, ''',' ), I3, ', AP, ', &
        'X,', I2, ')                        .' )
 9994 FORMAT( 1X, I6, ': ', A6, '(', 3( '''', A1, ''',' ), 2( I3, ',' ), &
        ' A,', I3, ', X,', I2, ')                 .' )
 9993 FORMAT( 1X, I6, ': ', A6, '(', 3( '''', A1, ''',' ), I3, ', A,', &
        I3, ', X,', I2, ')                     .' )
 9992 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK32.
!
        '******' )
end
subroutine SCHK33 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
!! SCHK33
!***SUBSIDIARY
!***PURPOSE  Quick check for STRMM and STRSM.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Quick check for STRMM and STRSM.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE3, SMMCH, STRMM, STRSM
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK33
!     .. Parameters ..
     IDIM, NALF, ALF, NMAX, A, AA, AS, B, BB, BS, CT, G, C)
  REAL               ZERO, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, ONE = 1.0)
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            KPRINT, NALF, NIDIM, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), G( NMAX ), &
                     B( NMAX, NMAX ), BB( NMAX*NMAX ), &
                     BS( NMAX*NMAX ), C( NMAX, NMAX ), &
                     CT( NMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM )
  REAL               ALPHA, ALS, ERR, ERRMAX
  integer            I, IA, ICD, ICS, ICT, ICU, IM, IN, J, LAA, &
                     LBB, LDA, LDAS, LDB, LDBS, M, &
                     MS, N, NA, NARGS, NC, NERR, NS
  LOGICAL            FTL, NULL, RESET, LEFT
  CHARACTER*1        SIDE, SIDES, UPLO, UPLOS, TRANAS, TRANSA, DIAG, &
                     DIAGS
  CHARACTER*2        ICHS, ICHU, ICHD
!     .. Local Arrays ..
  CHARACTER*3        ICHT
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           STRMM, STRSM, SMAKE3, SMMCH
!     .. Data statements ..
  INTRINSIC          MAX
!***FIRST EXECUTABLE STATEMENT  SCHK33
  DATA               ICHT/'NTC'/, ICHU/'UL'/, ICHD/'UN'/, ICHS/'LR'/
!
  NARGS = 11
  NC = 0
  RESET = .TRUE.
!
!     Set up zero matrix for SMMCH.
  ERRMAX = ZERO
  DO 20 J = 1, NMAX
     DO 10 I = 1, NMAX
        C( I, J ) = ZERO
   10    continue
!
   20 continue
  DO 140 IM = 1, NIDIM
!
     M = IDIM( IM )
     DO 130 IN = 1, NIDIM
!           Set LDB to 1 more than minimum value if room.
        N = IDIM( IN )
        LDB = M
        if (  LDB < NMAX ) &
!           Skip tests if not enough room.
           LDB = LDB + 1
        if (  LDB > NMAX ) &
           GO TO 130
        LBB = LDB*N
!
        NULL = M <= 0.OR.N <= 0
        DO 120 ICS = 1, 2
           SIDE = ICHS( ICS: ICS )
           LEFT = SIDE == 'L'
           if (  LEFT  ) then
              NA = M
           else
              NA = N
!              Set LDA to 1 more than minimum value if room.
           end if
           LDA = NA
           if (  LDA < NMAX ) &
!              Skip tests if not enough room.
              LDA = LDA + 1
           if (  LDA > NMAX ) &
              GO TO 130
!
           LAA = LDA*NA
           DO 110 ICU = 1, 2
!
              UPLO = ICHU( ICU: ICU )
              DO 100 ICT = 1, 3
!
                 TRANSA = ICHT( ICT: ICT )
                 DO 90 ICD = 1, 2
!
                    DIAG = ICHD( ICD: ICD )
                    DO 80 IA = 1, NALF
!
!                          Generate the matrix A.
!
                       ALPHA = ALF( IA )
                       call SMAKE3( 'TR', UPLO, DIAG, NA, NA, A, &
!
!                          Generate the matrix B.
!
                                   NMAX, AA, LDA, RESET, ZERO )
                       call SMAKE3( 'GE', ' ', ' ', M, N, B, NMAX, &
!
                                   BB, LDB, RESET, ZERO )
!
!                          Save every datum before calling the
!                          subroutine.
!
                       NC = NC + 1
                       SIDES = SIDE
                       UPLOS = UPLO
                       TRANAS = TRANSA
                       DIAGS = DIAG
                       MS = M
                       NS = N
                       ALS = ALPHA
                       DO 30 I = 1, LAA
                          AS( I ) = AA( I )
   30                      continue
                       LDAS = LDA
                       DO 40 I = 1, LBB
                          BS( I ) = BB( I )
   40                      continue
!
!                          Call the subroutine.
!
                       LDBS = LDB
                       if (  SNAME( 4: 5 ) == 'MM'  ) then
                          call STRMM( SIDE, UPLO, TRANSA, DIAG, M, &
                                      N, ALPHA, AA, LDA, BB, LDB )
                       else if (  SNAME( 4: 5 ) == 'SM'  ) then
                          call STRSM( SIDE, UPLO, TRANSA, DIAG, M, &
                                      N, ALPHA, AA, LDA, BB, LDB )
!
!                          Check if error-exit was taken incorrectly.
!
                       end if
                       if ( NUMXER(NERR) /= 0 ) then
                         if ( kprint >= 2 ) then
                          write ( NOUT, FMT = 9994 )
                       end if
                       FATAL = .TRUE.
!
!                          See what data changed inside subroutines.
!
                       end if
                       ISAME( 1 ) = SIDES == SIDE
                       ISAME( 2 ) = UPLOS == UPLO
                       ISAME( 3 ) = TRANAS == TRANSA
                       ISAME( 4 ) = DIAGS == DIAG
                       ISAME( 5 ) = MS == M
                       ISAME( 6 ) = NS == N
                       ISAME( 7 ) = ALS == ALPHA
                       ISAME( 8 ) = LSE( AS, AA, LAA )
                       ISAME( 9 ) = LDAS == LDA
                       if (  NULL  ) then
                          ISAME( 10 ) = LSE( BS, BB, LBB )
                       else
                          ISAME( 10 ) = LSERES( 'GE', ' ', M, N, BS, &
                                        BB, LDB )
                       end if
!
!                          If data was incorrectly changed, report and
!                          return.
!
                       ISAME( 11 ) = LDBS == LDB
                       DO 50 I = 1, NARGS
                         if ( .NOT. ISAME( I ) ) then
                           FATAL = .TRUE.
                           if ( kprint >= 2 ) then
                             write ( NOUT, FMT = 9998 )I
                           end if
                         end if
!
  50                       continue
                       FTL = .FALSE.
                       if (  .NOT.NULL  ) then
!
!                                Check the result.
!
                          if (  SNAME( 4: 5 ) == 'MM'  ) then
                             if (  LEFT  ) then
                                call SMMCH( TRANSA, 'N', M, N, M, &
                                            ALPHA, A, NMAX, B, NMAX, &
                                            ZERO, C, NMAX, CT, G, &
                                            BB, LDB, EPS, ERR, &
                                            FTL, NOUT, .TRUE., &
                                            kprint )
                             else
                                call SMMCH( 'N', TRANSA, M, N, N, &
                                            ALPHA, B, NMAX, A, NMAX, &
                                            ZERO, C, NMAX, CT, G, &
                                            BB, LDB, EPS, ERR, &
                                            FTL, NOUT, .TRUE., &
                                            kprint )
                             end if
!
!                                Compute approximation to original
!                                matrix.
!
                          else if (  SNAME( 4: 5 ) == 'SM'  ) then
                             DO 70 J = 1, N
                                DO 60 I = 1, M
                                   C( I, J ) = BB( I + ( J - 1 )* &
                                               LDB )
                                   BB( I + ( J - 1 )*LDB ) = ALPHA* &
                                      B( I, J )
   60                               continue
!
   70                            continue
                             if (  LEFT  ) then
                                call SMMCH( TRANSA, 'N', M, N, M, &
                                            ONE, A, NMAX, C, NMAX, &
                                            ZERO, B, NMAX, CT, G, &
                                            BB, LDB, EPS, ERR, &
                                            FTL, NOUT, .FALSE., &
                                            kprint )
                             else
                                call SMMCH( 'N', TRANSA, M, N, N, &
                                            ONE, C, NMAX, A, NMAX, &
                                            ZERO, B, NMAX, CT, G, &
                                            BB, LDB, EPS, ERR, &
                                            FTL, NOUT, .FALSE., &
                                            kprint )
                             end if
                          end if
                          ERRMAX = max (  ERRMAX, ERR )
                       end if
                       if ( FTL ) then
                         FATAL = .TRUE.
                         if ( kprint >= 3 ) then
                           write (NOUT, FMT = 9996) SNAME
                           write ( NOUT, FMT = 9995 )NC, SNAME, &
                             SIDE, UPLO, TRANSA, DIAG, M, &
                             N, ALPHA, LDA, LDB
                         end if
!
                       end if
!
   80                   continue
!
   90                continue
!
  100             continue
!
  110          continue
!
  120       continue
!
  130    continue
!
!     Report result.
!
  140 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
!
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( 1X, I6, ': ', A6, '(', 4( '''', A1, ''',' ), 2( I3, ',' ), &
        F4.1, ', A,', I3, ', B,', I3, ')        .' )
 9994 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK33.
!
        '******' )
end
subroutine SCHK42 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NALF, ALF, NINC, INC, NMAX, INCMAX, A, AA, AS, X, XX, XS, &
!! SCHK42
!***SUBSIDIARY
!***PURPOSE  Quick check for SGER.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Du Croz, J. (NAG)
!           Hanson, R. J. (SNLA)
!***DESCRIPTION
!
!  Quick check for SGER.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SGER, SMAKE2, SMVCH
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK42
!     .. Parameters ..
     Y, YY, YS, YT, G, Z)
  REAL               ZERO, HALF, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, HALF = 0.5, ONE = 1.0 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            INCMAX, KPRINT, NALF, NIDIM, NINC, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), G( NMAX ), X( NMAX ), &
                     XS( NMAX*INCMAX ), XX( NMAX*INCMAX ), &
                     Y( NMAX ), YS( NMAX*INCMAX ), YT( NMAX ), &
                     YY( NMAX*INCMAX ), Z( NMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM ), INC( NINC )
  REAL               ALPHA, ALS, ERR, ERRMAX, TRANSL
  integer            I, IA, IM, IN, INCX, INCXS, INCY, INCYS, IX, &
                     IY, J, LAA, LDA, LDAS, LX, LY, M, MS, N, NARGS, &
                     NC, ND, NS, NERR
!     .. Local Arrays ..
  LOGICAL            FTL, NULL, RESET
  REAL               W( 1 )
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SGER, SMAKE2, SMVCH
!***FIRST EXECUTABLE STATEMENT SCHK42
!     Define the number of arguments.
  INTRINSIC          ABS, MAX, MIN
!
  NARGS = 9
  NC = 0
  RESET = .TRUE.
!
  ERRMAX = ZERO
  DO 120 IN = 1, NIDIM
     N = IDIM( IN )
!
     ND = N/2 + 1
     DO 110 IM = 1, 2
        if (  IM == 1 ) &
           M = max (  N - ND, 0 )
        if (  IM == 2 ) &
!
!           Set LDA to 1 more than minimum value if room.
           M = min (  N + ND, NMAX )
        LDA = M
        if (  LDA < NMAX ) &
!           Skip tests if not enough room.
           LDA = LDA + 1
        if (  LDA > NMAX ) &
           GO TO 110
        LAA = LDA*N
!
        NULL = N <= 0.OR.M <= 0
        DO 100 IX = 1, NINC
           INCX = INC( IX )
!
!              Generate the vector X.
!
           LX = ABS( INCX )*M
           TRANSL = HALF
           call SMAKE2( 'GE', ' ', ' ', 1, M, X, 1, XX, ABS( INCX ), &
                       0, M - 1, RESET, TRANSL )
           if (  M > 1  ) then
              X( M/2 ) = ZERO
              XX( 1 + ABS( INCX )*( M/2 - 1 ) ) = ZERO
!
           end if
           DO 90 IY = 1, NINC
              INCY = INC( IY )
!
!                 Generate the vector Y.
!
              LY = ABS( INCY )*N
              TRANSL = ZERO
              call SMAKE2( 'GE', ' ', ' ', 1, N, Y, 1, YY, &
                          ABS( INCY ), 0, N - 1, RESET, TRANSL )
              if (  N > 1  ) then
                 Y( N/2 ) = ZERO
                 YY( 1 + ABS( INCY )*( N/2 - 1 ) ) = ZERO
!
              end if
              DO 80 IA = 1, NALF
!
!                    Generate the matrix A.
!
                 ALPHA = ALF( IA )
                 TRANSL = ZERO
                 call SMAKE2(SNAME( 2: 3 ), ' ', ' ', M, N, A, NMAX, &
!
                             AA, LDA, M - 1, N - 1, RESET, TRANSL )
!
!                    Save every datum before calling the subroutine.
!
                 NC = NC + 1
                 MS = M
                 NS = N
                 ALS = ALPHA
                 DO 10 I = 1, LAA
                    AS( I ) = AA( I )
   10                continue
                 LDAS = LDA
                 DO 20 I = 1, LX
                    XS( I ) = XX( I )
   20                continue
                 INCXS = INCX
                 DO 30 I = 1, LY
                    YS( I ) = YY( I )
   30                continue
!
!                    Call the subroutine.
!
                 INCYS = INCY
                 call SGER( M, N, ALPHA, XX, INCX, YY, INCY, AA, &
!
!                    Check if error-exit was taken incorrectly.
!
                            LDA )
                 if ( NUMXER(NERR) /= 0 ) then
                    if ( kprint >= 2 ) then
                      write ( NOUT, FMT = 9993 )
                    end if
                    FATAL = .TRUE.
!
!                    See what data changed inside subroutine.
!
                 end if
                 ISAME( 1 ) = MS == M
                 ISAME( 2 ) = NS == N
                 ISAME( 3 ) = ALS == ALPHA
                 ISAME( 4 ) = LSE( XS, XX, LX )
                 ISAME( 5 ) = INCXS == INCX
                 ISAME( 6 ) = LSE( YS, YY, LY )
                 ISAME( 7 ) = INCYS == INCY
                 if (  NULL  ) then
                    ISAME( 8 ) = LSE( AS, AA, LAA )
                 else
                    ISAME( 8 ) = LSERES( 'GE', ' ', M, N, AS, AA, &
                                 LDA )
                 end if
!
!                    If data was incorrectly changed, report and return.
!
                 ISAME( 9 ) = LDAS == LDA
                 DO 40 I = 1, NARGS
                   if ( .NOT. ISAME( I ) ) then
                     FATAL = .TRUE.
                     if ( kprint >= 2 ) then
                       write ( NOUT, FMT = 9998 )I
                     end if
                   end if
!
  40                 continue
                 FTL = .FALSE.
!
!                       Check the result column by column.
!
                 if (  .NOT.NULL  ) then
                    if (  INCX > 0  ) then
                       DO 50 I = 1, M
                          Z( I ) = X( I )
   50                      continue
                    else
                       DO 60 I = 1, M
                          Z( I ) = X( M - I + 1 )
   60                      continue
                    end if
                    DO 70 J = 1, N
                       if (  INCY > 0  ) then
                          W( 1 ) = Y( J )
                       else
                          W( 1 ) = Y( N - J + 1 )
                       end if
                       call SMVCH( 'N', M, 1, ALPHA, Z, NMAX, W, 1, &
                                   ONE, A( 1, J ), 1, YT, G, &
                                   AA( 1 + ( J - 1 )*LDA ), EPS, &
                                   ERR, FTL, NOUT, .TRUE., KPRINT)
                       ERRMAX = max (  ERRMAX, ERR )
   70                   continue
                 end if
                 if ( FTL ) then
                   FATAL = .TRUE.
                   if ( kprint >= 3 ) then
                     write ( NOUT, FMT = 9995 )J
                     write ( NOUT, FMT = 9996 )SNAME
                     write ( NOUT, FMT = 9994 )NC, SNAME, M, &
                      N, ALPHA, INCX, INCY, LDA
                   end if
!
                 end if
!
   80             continue
!
   90          continue
!
  100       continue
!
  110    continue
!
!     Report result.
!
  120 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( '      THESE ARE THE RESULTS FOR COLUMN ', I3 )
 9994 FORMAT( 1X, I6, ': ', A6, '(', 2( I3, ',' ), F4.1, ', X,', I2, &
        ', Y,', I2, ', A,', I3, ')                  .' )
 9993 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK42.
!
        '******' )
end
subroutine SCHK43 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NALF, ALF, NBET, BET, NMAX, A, AA, AS, B, BB, BS, C, CC, &
!! SCHK43
!***SUBSIDIARY
!***PURPOSE  Quick check for SSYRK.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Quick check for SSYRK.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE3, SMMCH, SSYRK
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK43
!     .. Parameters ..
     CS, CT, G)
  REAL               ZERO, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, ONE = 1.0)
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            KPRINT, NALF, NBET, NIDIM, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX), AA( NMAX*NMAX), ALF( NALF), &
                     AS( NMAX*NMAX ), BET( NBET ), G( NMAX ), &
                     B( NMAX, NMAX ), BB( NMAX*NMAX ), &
                     BS( NMAX*NMAX ), C( NMAX, NMAX ), &
                     CC( NMAX*NMAX ), CS( NMAX*NMAX ), &
                     CT( NMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM )
  REAL               ALPHA, ALS, BETA, BETS, ERR, ERRMAX
  integer            I, IA, IB, ICT, ICU, IK, IN, J, JC, JJ, K, LAA, &
                     LCC, LDA, LDAS, LDC, LDCS, &
                     N, NA, NARGS, NC, NERR, NS, KS, LJ, MA
  LOGICAL            FTL, NULL, RESET, TRAN, UPPER
  CHARACTER*1        UPLO, UPLOS, TRANS, TRANSS
  CHARACTER*2        ICHU
!     .. Local Arrays ..
  CHARACTER*3        ICHT
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SSYRK, SMAKE3, SMMCH
!     .. Data statements ..
  INTRINSIC          MAX
!***FIRST EXECUTABLE STATEMENT  SCHK43
  DATA               ICHT/'NTC'/, ICHU/'UL'/
!
  NARGS = 10
  NC = 0
  RESET = .TRUE.
!
!
  ERRMAX = ZERO
  DO 100 IN = 1, NIDIM
!        Set LDC to 1 more than minimum value if room.
     N = IDIM( IN )
     LDC = N
     if (  LDC < NMAX ) &
!        Skip tests if not enough room.
        LDC = LDC + 1
     if (  LDC > NMAX ) &
        GO TO 100
     LCC = LDC*N
!
     NULL = N <= 0
     DO 90 IK = 1, NIDIM
!
        K = IDIM( IK )
        DO 80 ICT = 1, 3
           TRANS = ICHT( ICT: ICT )
           TRAN = TRANS == 'T'.OR.TRANS == 'C'
           if (  TRAN  ) then
              MA = K
              NA = N
           else
              MA = N
              NA = K
!              Set LDA to 1 more than minimum value if room.
           end if
           LDA = MA
           if (  LDA < NMAX ) &
!              Skip tests if not enough room.
              LDA = LDA + 1
           if (  LDA > NMAX ) &
              GO TO 80
!
!  Generate the matrix A.
!
           LAA = LDA*NA
           call SMAKE3( 'GE', ' ', ' ', MA, NA, A, NMAX, AA, LDA, &
                       RESET, ZERO )

           DO 70 ICU = 1, 2
              UPLO = ICHU( ICU: ICU )

              UPPER = UPLO == 'U'
              DO 60 IA = 1, NALF

                 ALPHA = ALF( IA )
                 DO 50 IB = 1, NBET
!
!  Generate the matrix C.
!
                    BETA = BET( IB )
                    call SMAKE3( 'SY', UPLO, ' ', N, N, C, NMAX, CC, &
                                LDC, RESET, ZERO )
!
!  Save every datum before calling the subroutine.
!
                    NC = NC + 1
                    UPLOS = UPLO
                    TRANSS = TRANS
                    NS = N
                    KS = K
                    ALS = ALPHA
                    DO 10 I = 1, LAA
                       AS( I ) = AA( I )
   10                   continue
                    LDAS = LDA
                    BETS = BETA
                    DO 20 I = 1, LCC
                       CS( I ) = CC( I )
   20                   continue
!
!  Call the subroutine.
!
                    LDCS = LDC
                    call SSYRK( UPLO, TRANS, N, K, ALPHA, AA, LDA, &
                                BETA, CC, LDC )
!
!  Check if error-exit was taken incorrectly.
!
                    if ( NUMXER(NERR) /= 0 ) then
                       if ( kprint >= 2 ) then
                       write ( NOUT, FMT = 9993 )
                       end if
                       FATAL = .TRUE.
!
!                       See what data changed inside subroutines.
!
                    end if
                    ISAME( 1 ) = UPLOS == UPLO
                    ISAME( 2 ) = TRANSS == TRANS
                    ISAME( 3 ) = NS == N
                    ISAME( 4 ) = KS == K
                    ISAME( 5 ) = ALS == ALPHA
                    ISAME( 6 ) = LSE( AS, AA, LAA )
                    ISAME( 7 ) = LDAS == LDA
                    ISAME( 8 ) = BETS == BETA
                    if (  NULL  ) then
                       ISAME( 9 ) = LSE( CS, CC, LCC )
                    else
                       ISAME( 9 ) = LSERES( 'SY', UPLO, N, N, CS, &
                                    CC, LDC )
                    end if
!
!                       If data was incorrectly changed, report and
!                       return.
!
                    ISAME( 10 ) = LDCS == LDC
                    DO 30 I = 1, NARGS
                      if ( .NOT. ISAME( I ) ) then
                        FATAL = .TRUE.
                        if ( kprint >= 2 ) then
                          write ( NOUT, FMT = 9998 )I
                        end if
                      end if
!
  30                    continue
                    FTL = .FALSE.
!
!                          Check the result column by column.
!
                    if (  .NOT.NULL  ) then
                       JC = 1
                       DO 40 J = 1, N
                          if (  UPPER  ) then
                             JJ = 1
                             LJ = J
                          else
                             JJ = J
                             LJ = N - J + 1
                          end if
                          if (  TRAN  ) then
                             call SMMCH( 'T', 'N', LJ, 1, K, ALPHA, &
                                         A( 1, JJ ), NMAX, &
                                         A( 1, J ), NMAX, BETA, &
                                         C( JJ, J ), NMAX, CT, G, &
                                         CC( JC ), LDC, EPS, ERR, &
                                         FTL, NOUT, .TRUE.,KPRINT)
                          else
                             call SMMCH( 'N', 'T', LJ, 1, K, ALPHA, &
                                         A( JJ, 1 ), NMAX, &
                                         A( J, 1 ), NMAX, BETA, &
                                         C( JJ, J ), NMAX, CT, G, &
                                         CC( JC ), LDC, EPS, ERR, &
                                         FTL, NOUT, .TRUE.,KPRINT)
                          end if
                          if (  UPPER  ) then
                             JC = JC + LDC
                          else
                             JC = JC + LDC + 1
                          end if
                          ERRMAX = max (  ERRMAX, ERR )
   40                      continue
                    end if
                    if ( FTL ) then
                      FATAL = .TRUE.
                      if ( kprint >= 3 ) then
                        write (NOUT, FMT = 9996) SNAME
                        write ( NOUT, FMT = 9994 )NC, SNAME, &
                          UPLO, TRANS, N, K, &
                          ALPHA, LDA, BETA, LDC
                      end if
!
                    end if
!
   50                continue
!
   60             continue
!
   70          continue
!
   80       continue
!
   90    continue
!
!     Report result.
!
  100 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9994 FORMAT( 1X, I6, ': ', A6, '(', 2( '''', A1, ''',' ), 2( I3, ',' ), &
        F4.1, ', A,', I3, ',', F4.1, ', C,', I3, ')           .' )
 9993 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
!
!     End of SCHK43.
!
        '******' )
end
subroutine SCHK52 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NALF, ALF, NINC, INC, NMAX, INCMAX, A, AA, AS, X, XX, XS, &
!! SCHK52
!***SUBSIDIARY
!***PURPOSE  Quick check for SSYR and SSPR.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Du Croz, J. (NAG)
!           Hanson, R. J. (SNLA)
!***DESCRIPTION
!
!  Quick check for SSYR and SSPR.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE2, SMVCH, SSPR, SSYR
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK52
!     .. Parameters ..
     Y, YY, YS, YT, G, Z)
  REAL               ZERO, HALF, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, HALF = 0.5, ONE = 1.0 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            INCMAX, KPRINT, NALF, NIDIM, NINC, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), G( NMAX ), X( NMAX ), &
                     XS( NMAX*INCMAX ), XX( NMAX*INCMAX ), &
                     Y( NMAX ), YS( NMAX*INCMAX ), YT( NMAX ), &
                     YY( NMAX*INCMAX ), Z( NMAX )
!     .. Local Scalars ..
  integer            IDIM( NIDIM ), INC( NINC )
  REAL               ALPHA, ALS, ERR, ERRMAX, TRANSL
  integer            I, IA, IC, IN, INCX, INCXS, IX, J, JA, JJ, LAA, &
                     LDA, LDAS, LJ, LX, N, NARGS, NC, NS, NERR
  LOGICAL            FTL, FULL, NULL, PACKED, RESET, UPPER
  CHARACTER*1        UPLO, UPLOS
!     .. Local Arrays ..
  CHARACTER*2        ICH
  REAL               W( 1 )
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SMAKE2, SMVCH, SSPR, SSYR
!     .. Data statements ..
  INTRINSIC          ABS, MAX
!***FIRST EXECUTABLE STATEMENT  SCHK52
  DATA               ICH/'UL'/
  FULL = SNAME( 3: 3 ) == 'Y'
!     Define the number of arguments.
  PACKED = SNAME( 3: 3 ) == 'P'
  if (  FULL  ) then
     NARGS = 7
  else if (  PACKED  ) then
     NARGS = 6
!
  end if
  NC = 0
  RESET = .TRUE.
!
  ERRMAX = ZERO
  DO 100 IN = 1, NIDIM
!        Set LDA to 1 more than minimum value if room.
     N = IDIM( IN )
     LDA = N
     if (  LDA < NMAX ) &
!
!        Skip tests if not enough room.
!
        LDA = LDA + 1
     if (  LDA > NMAX ) &
        GO TO 100
     if (  PACKED  ) then
        LAA = ( N*( N + 1 ) )/2
     else
        LAA = LDA*N

     end if
     DO 90 IC = 1, 2
        UPLO = ICH( IC: IC )

        UPPER = UPLO == 'U'
        DO 80 IX = 1, NINC
           INCX = INC( IX )
!
!              Generate the vector X.
!
           LX = ABS( INCX )*N
           TRANSL = HALF
           call SMAKE2( 'GE', ' ', ' ', 1, N, X, 1, XX, ABS( INCX ), &
                       0, N - 1, RESET, TRANSL )
           if (  N > 1  ) then
              X( N/2 ) = ZERO
              XX( 1 + ABS( INCX )*( N/2 - 1 ) ) = ZERO
!
           end if
           DO 70 IA = 1, NALF
              ALPHA = ALF( IA )
!
!                 Generate the matrix A.
!
              NULL = N <= 0.OR.ALPHA == ZERO
              TRANSL = ZERO
              call SMAKE2( SNAME( 2: 3 ), UPLO, ' ', N, N, A, NMAX, &
!
                          AA, LDA, N - 1, N - 1, RESET, TRANSL )
!
!                 Save every datum before calling the subroutine.
!
              NC = NC + 1
              UPLOS = UPLO
              NS = N
              ALS = ALPHA
              DO 10 I = 1, LAA
                 AS( I ) = AA( I )
   10             continue
              LDAS = LDA
              DO 20 I = 1, LX
                 XS( I ) = XX( I )
   20             continue
!
!                 Call the subroutine.
!
              INCXS = INCX
              if (  FULL  ) then
                 call SSYR( UPLO, N, ALPHA, XX, INCX, AA, LDA )
              else if (  PACKED  ) then
                 call SSPR( UPLO, N, ALPHA, XX, INCX, AA )
!
!                 Check if error-exit was taken incorrectly.
!
              end if
              if ( NUMXER(NERR) /= 0 ) then
                 if ( kprint >= 2 ) then
                   write ( NOUT, FMT = 9992 )
                 end if
                 FATAL = .TRUE.
!
!                 See what data changed inside subroutines.
!
              end if
              ISAME( 1 ) = UPLO == UPLOS
              ISAME( 2 ) = NS == N
              ISAME( 3 ) = ALS == ALPHA
              ISAME( 4 ) = LSE( XS, XX, LX )
              ISAME( 5 ) = INCXS == INCX
              if (  NULL  ) then
                 ISAME( 6 ) = LSE( AS, AA, LAA )
              else
                 ISAME( 6 ) = LSERES( SNAME( 2: 3 ), UPLO, N, N, AS, &
                              AA, LDA )
              end if
              if (  .NOT.PACKED  ) then
                 ISAME( 7 ) = LDAS == LDA
!
!                 If data was incorrectly changed, report and return.
!
              end if
              DO 30 I = 1, NARGS
                if ( .NOT. ISAME( I ) ) then
                  FATAL = .TRUE.
                  if ( kprint >= 2 ) then
                    write ( NOUT, FMT = 9998 )I
                  end if
                end if
!
  30              continue
!
!                    Check the result column by column.
!
              if (  .NOT.NULL  ) then
                 if (  INCX > 0  ) then
                    DO 40 I = 1, N
                       Z( I ) = X( I )
   40                   continue
                 else
                    DO 50 I = 1, N
                       Z( I ) = X( N - I + 1 )
   50                   continue
                 end if
                 JA = 1
                 DO 60 J = 1, N
                    W( 1 ) = Z( J )
                    if (  UPPER  ) then
                       JJ = 1
                       LJ = J
                    else
                       JJ = J
                       LJ = N - J + 1
                    end if
                    FTL = .FALSE.
                    call SMVCH( 'N', LJ, 1, ALPHA, Z( JJ ), LJ, W, &
                                1, ONE, A( JJ, J ), 1, YT, G, &
                                AA( JA ), EPS, ERR, FTL, NOUT, &
                                .TRUE., kprint )
                    if (  FULL  ) then
                       if (  UPPER  ) then
                          JA = JA + LDA
                       else
                          JA = JA + LDA + 1
                       end if
                    else
                       JA = JA + LJ
                    end if
                    ERRMAX = max (  ERRMAX, ERR )
                    if ( FTL ) then
                      FATAL = .TRUE.
                      if ( kprint >= 3 ) then
                        write ( NOUT, FMT = 9995 )J
                        write ( NOUT, FMT = 9996 )SNAME
                        if (  FULL  ) then
                           write ( NOUT, FMT = 9993 )NC, SNAME, &
                            UPLO, N, ALPHA, INCX, &
                            LDA
                        else if (  PACKED  ) then
                           write ( NOUT, FMT = 9994 )NC, SNAME, &
                            UPLO, N, ALPHA, INCX
                        end if
                      end if
                    end if
   60                continue
!
              end if
!
   70          continue
!
   80       continue
!
   90    continue
!
!     Report result.
!
  100 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( '      THESE ARE THE RESULTS FOR COLUMN ', I3 )
 9994 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', I3, ',', F4.1, ', X,', &
        I2, ', AP)                           .' )
 9993 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', I3, ',', F4.1, ', X,', &
        I2, ', A,', I3, ')                        .' )
 9992 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
        '******' )
end
subroutine SCHK53 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NALF, ALF, NBET, BET, NMAX, AB, AA, AS, BB, BS, C, CC, &
!! SCHK53
!***SUBSIDIARY
!***PURPOSE  Quick check for SSYR2K.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Quick check for SSYR2K.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE3, SMMCH, SSYR2K
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK53
!     .. Parameters ..
     CS, CT, G, W)
  REAL               ZERO
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0)
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            KPRINT, NALF, NBET, NIDIM, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               AA( NMAX*NMAX), ALF( NALF), &
                     AS( NMAX*NMAX ), BET( NBET ), G( NMAX ), &
                     BB( NMAX*NMAX ), &
                     BS( NMAX*NMAX ), C( NMAX, NMAX ), &
                     CC( NMAX*NMAX ), CS( NMAX*NMAX ), &
                     CT( NMAX ), W( 2*NMAX ), AB(2*NMAX*NMAX)
!     .. Local Scalars ..
  integer            IDIM( NIDIM )
  REAL               ALPHA, ALS, BETA, BETS, ERR, ERRMAX
  integer            I, IA, IB, ICT, ICU, IK, IN, J, JC, JJ, K, LAA, &
                     LBB, LCC, LDA, LDAS, LDB, LDBS, LDC, LDCS, &
                     N, NA, NARGS, NC, NERR, NS, KS, LJ, MA, JJAB
  LOGICAL            FTL, NULL, RESET, TRAN, UPPER
  CHARACTER*1        UPLO, UPLOS, TRANS, TRANSS
  CHARACTER*2        ICHU
!     .. Local Arrays ..
  CHARACTER*3        ICHT
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SSYR2K, SMAKE3, SMMCH
!     .. Data statements ..
  INTRINSIC          MAX
!***FIRST EXECUTABLE STATEMENT  SCHK53
  DATA               ICHT/'NTC'/, ICHU/'UL'/
!
  NARGS = 12
  NC = 0
  RESET = .TRUE.
!
!
  ERRMAX = ZERO
  DO 130 IN = 1, NIDIM
!        Set LDC to 1 more than minimum value if room.
     N = IDIM( IN )
     LDC = N
     if (  LDC < NMAX ) &
!        Skip tests if not enough room.
        LDC = LDC + 1
     if (  LDC > NMAX ) &
        GO TO 130
     LCC = LDC*N
!
     NULL = N <= 0
     DO 120 IK = 1, NIDIM
!
        K = IDIM( IK )
        DO 110 ICT = 1, 3
           TRANS = ICHT( ICT: ICT )
           TRAN = TRANS == 'T'.OR.TRANS == 'C'
           if (  TRAN  ) then
              MA = K
              NA = N
           else
              MA = N
              NA = K
!              Set LDA to 1 more than minimum value if room.
           end if
           LDA = MA
           if (  LDA < NMAX ) &
!              Skip tests if not enough room.
              LDA = LDA + 1
           if (  LDA > NMAX ) &
              GO TO 110
!
!              Generate the matrix A.
!
           LAA = LDA*NA
           if (  TRAN  ) then
              call SMAKE3( 'GE', ' ', ' ', MA, NA, AB, 2*NMAX, AA, &
                          LDA, RESET, ZERO )
           else
              call SMAKE3('GE', ' ', ' ', MA, NA, AB, NMAX, AA, LDA, &
                          RESET, ZERO )
!
!              Generate the matrix B.
!
           end if
           LDB = LDA
           LBB = LAA
           if (  TRAN  ) then
              call SMAKE3( 'GE', ' ', ' ', MA, NA, AB( K + 1 ), &
                          2*NMAX, BB, LDB, RESET, ZERO )
           else
              call SMAKE3( 'GE', ' ', ' ', MA, NA, AB( K*NMAX + 1 ), &
                          NMAX, BB, LDB, RESET, ZERO )
!
           end if
           DO 100 ICU = 1, 2
              UPLO = ICHU( ICU: ICU )
!
              UPPER = UPLO == 'U'
              DO 90 IA = 1, NALF
!
                 ALPHA = ALF( IA )
                 DO 80 IB = 1, NBET
!
!                       Generate the matrix C.
!
                    BETA = BET( IB )
                    call SMAKE3( 'SY', UPLO, ' ', N, N, C, NMAX, CC, &
!
                                LDC, RESET, ZERO )
!
!                       Save every datum before calling the subroutine.
!
                    NC = NC + 1
                    UPLOS = UPLO
                    TRANSS = TRANS
                    NS = N
                    KS = K
                    ALS = ALPHA
                    DO I = 1, LAA
                       AS( I ) = AA( I )
                    end do
                    LDAS = LDA
                    DO 20 I = 1, LBB
                       BS( I ) = BB( I )
   20                   continue
                    LDBS = LDB
                    BETS = BETA
                    DO 30 I = 1, LCC
                       CS( I ) = CC( I )
   30                   continue
!
!  Call the subroutine.
!
                    LDCS = LDC
                    call SSYR2K( UPLO, TRANS, N, K, ALPHA, AA, LDA, &
!
!                       Check if error-exit was taken incorrectly.
!
                                 BB, LDB, BETA, CC, LDC )
                    if (  NUMXER(NERR) /= 0 ) then
                      if ( kprint >= 2 ) then
                       write ( NOUT, FMT = 9993 )
                      end if
                      FATAL = .TRUE.
!
!                       See what data changed inside subroutines.
!
                    end if
                    ISAME( 1 ) = UPLOS == UPLO
                    ISAME( 2 ) = TRANSS == TRANS
                    ISAME( 3 ) = NS == N
                    ISAME( 4 ) = KS == K
                    ISAME( 5 ) = ALS == ALPHA
                    ISAME( 6 ) = LSE( AS, AA, LAA )
                    ISAME( 7 ) = LDAS == LDA
                    ISAME( 8 ) = LSE( BS, BB, LBB )
                    ISAME( 9 ) = LDBS == LDB
                    ISAME( 10 ) = BETS == BETA
                    if (  NULL  ) then
                       ISAME( 11 ) = LSE( CS, CC, LCC )
                    else
                       ISAME( 11 ) = LSERES( 'SY', UPLO, N, N, CS, &
                                     CC, LDC )
                    end if
!
!                       If data was incorrectly changed, report and
!                       return.
!
                    ISAME( 12 ) = LDCS == LDC
                    DO 40 I = 1, NARGS
                      if ( .NOT. ISAME( I ) ) then
                        FATAL = .TRUE.
                        if ( kprint >= 2 ) then
                          write ( NOUT, FMT = 9998 )I
                        end if
                      end if
!
  40                    continue
!
!                          Check the result column by column.
!
                    if (  .NOT.NULL  ) then
                       JJAB = 1
                       JC = 1
                       DO 70 J = 1, N
                          if (  UPPER  ) then
                             JJ = 1
                             LJ = J
                          else
                             JJ = J
                             LJ = N - J + 1
                          end if
                          if (  TRAN  ) then
                             DO 50 I = 1, K
                                W( I ) = AB( ( J - 1 )*2*NMAX + K + &
                                         I )
                                W( K + I ) = AB( ( J - 1 )*2*NMAX + &
                                             I )
   50                            continue
                             FTL = .FALSE.
                             call SMMCH( 'T', 'N', LJ, 1, 2*K, &
                                         ALPHA, AB( JJAB ), 2*NMAX, &
                                         W, 2*NMAX, BETA, &
                                         C( JJ, J ), NMAX, CT, G, &
                                         CC( JC ), LDC, EPS, ERR, &
                                         FTL, NOUT, .TRUE.,KPRINT)
                          else
                             DO 60 I = 1, K
                                W( I ) = AB( ( K + I - 1 )*NMAX + &
                                         J )
                                W( K + I ) = AB( ( I - 1 )*NMAX + &
                                             J )
   60                            continue
                             FTL = .FALSE.
                             call SMMCH( 'N', 'N', LJ, 1, 2*K, &
                                         ALPHA, AB( JJ ), NMAX, W, &
                                         2*NMAX, BETA, C( JJ, J ), &
                                         NMAX, CT, G, CC( JC ), LDC, &
                                         EPS, ERR, FTL, NOUT, &
                                         .TRUE., kprint )
                          end if
                          if (  UPPER  ) then
                             JC = JC + LDC
                          else
                             JC = JC + LDC + 1
                             if (  TRAN ) &
                                JJAB = JJAB + 2*NMAX
                          end if
                          ERRMAX = max (  ERRMAX, ERR )
                          if ( FTL ) then
                            FATAL = .TRUE.
                            if ( kprint >= 3 ) then
                              write (NOUT, FMT = 9996) SNAME
                              write ( NOUT, FMT = 9994 )NC, SNAME, &
                                UPLO, TRANS, N, K, &
                                ALPHA, LDA, LDB, BETA, LDC
                            end if
                          end if
   70                      continue
!
                    end if
!
   80                continue
!
   90             continue
!
  100          continue
!
  110       continue
!
  120    continue
!
!     Report result.
!
  130 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if

  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
