subroutine SCHK62 (SNAME, EPS, THRESH, NOUT, KPRINT, FATAL, NIDIM, &
     IDIM, NALF, ALF, NINC, INC, NMAX, INCMAX, A, AA, AS, X, XX, XS, &
!! SCHK62
!***SUBSIDIARY
!***PURPOSE  Quick check for SSYR2 and SSPR2.
!***LIBRARY   SLATEC (BLAS)
!***KEYWORDS  BLAS, QUICK CHECK SERVICE ROUTINE
!***AUTHOR  Du Croz, J. (NAG)
!           Hanson, R. J. (SNLA)
!***DESCRIPTION
!
!  Quick check for SSYR2 and SSPR2.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  LSE, LSERES, NUMXER, SMAKE2, SMVCH, SSPR2, SSYR2
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910619  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SCHK62
!     .. Parameters ..
     Y, YY, YS, YT, G, Z)
  REAL               ZERO, HALF, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, HALF = 0.5, ONE = 1.0 )
  LOGICAL            FATAL
  REAL               EPS, THRESH
  integer            INCMAX, KPRINT, NALF, NIDIM, NINC, NMAX, NOUT
!     .. Array Arguments ..
  CHARACTER*6        SNAME
  REAL               A( NMAX, NMAX ), AA( NMAX*NMAX ), ALF( NALF ), &
                     AS( NMAX*NMAX ), G( NMAX ), X( NMAX ), &
                     XS( NMAX*INCMAX ), XX( NMAX*INCMAX ), &
                     Y( NMAX ), YS( NMAX*INCMAX ), YT( NMAX ), &
                     YY( NMAX*INCMAX ), Z( NMAX, 2 )
!     .. Local Scalars ..
  integer            IDIM( NIDIM ), INC( NINC )
  REAL               ALPHA, ALS, ERR, ERRMAX, TRANSL
  integer            I, IA, IC, IN, INCX, INCXS, INCY, INCYS, IX, &
                     IY, J, JA, JJ, LAA, LDA, LDAS, LJ, LX, LY, N, &
                     NARGS, NC, NS, NERR
  LOGICAL            FTL, FULL, NULL, PACKED, RESET, UPPER
  CHARACTER*1        UPLO, UPLOS
!     .. Local Arrays ..
  CHARACTER*2        ICH
  REAL               W( 2 )
!     .. External Functions ..
  LOGICAL            ISAME( 13 )
  integer            NUMXER
  LOGICAL            LSE, LSERES
!     .. External Subroutines ..
  EXTERNAL           LSE, LSERES, NUMXER
!     .. Intrinsic Functions ..
  EXTERNAL           SMAKE2, SMVCH, SSPR2, SSYR2
!     .. Data statements ..
  INTRINSIC          ABS, MAX
!***FIRST EXECUTABLE STATEMENT  SCHK62
  DATA               ICH/'UL'/
  FULL = SNAME( 3: 3 ) == 'Y'
!     Define the number of arguments.
  PACKED = SNAME( 3: 3 ) == 'P'
  if (  FULL  ) then
     NARGS = 9
  else if (  PACKED  ) then
     NARGS = 8
!
  end if
  NC = 0
  RESET = .TRUE.
!
  ERRMAX = ZERO
  DO 140 IN = 1, NIDIM
!        Set LDA to 1 more than minimum value if room.
     N = IDIM( IN )
     LDA = N
     if (  LDA < NMAX ) &
!        Skip tests if not enough room.
        LDA = LDA + 1
     if (  LDA > NMAX ) &
        GO TO 140
     if (  PACKED  ) then
        LAA = ( N*( N + 1 ) )/2
     else
        LAA = LDA*N
!
     end if
     DO 130 IC = 1, 2
        UPLO = ICH( IC: IC )
!
        UPPER = UPLO == 'U'
        DO 120 IX = 1, NINC
           INCX = INC( IX )
!
!              Generate the vector X.
!
           LX = ABS( INCX )*N
           TRANSL = HALF
           call SMAKE2( 'GE', ' ', ' ', 1, N, X, 1, XX, ABS( INCX ), &
                       0, N - 1, RESET, TRANSL )
           if (  N > 1  ) then
              X( N/2 ) = ZERO
              XX( 1 + ABS( INCX )*( N/2 - 1 ) ) = ZERO
!
           end if
           DO 110 IY = 1, NINC
              INCY = INC( IY )
!
!                 Generate the vector Y.
!
              LY = ABS( INCY )*N
              TRANSL = ZERO
              call SMAKE2( 'GE', ' ', ' ', 1, N, Y, 1, YY, &
                          ABS( INCY ), 0, N - 1, RESET, TRANSL )
              if (  N > 1  ) then
                 Y( N/2 ) = ZERO
                 YY( 1 + ABS( INCY )*( N/2 - 1 ) ) = ZERO
!
              end if
              DO 100 IA = 1, NALF
                 ALPHA = ALF( IA )
!
!                    Generate the matrix A.
!
                 NULL = N <= 0.OR.ALPHA == ZERO
                 TRANSL = ZERO
                 call SMAKE2( SNAME( 2: 3 ), UPLO, ' ', N, N, A, &
                             NMAX, AA, LDA, N - 1, N - 1, RESET, &
!
                             TRANSL )
!
!                    Save every datum before calling the subroutine.
!
                 NC = NC + 1
                 UPLOS = UPLO
                 NS = N
                 ALS = ALPHA
                 DO 10 I = 1, LAA
                    AS( I ) = AA( I )
   10                continue
                 LDAS = LDA
                 DO 20 I = 1, LX
                    XS( I ) = XX( I )
   20                continue
                 INCXS = INCX
                 DO 30 I = 1, LY
                    YS( I ) = YY( I )
   30                continue
!
!                    Call the subroutine.
!
                 INCYS = INCY
                 if (  FULL  ) then
                    call SSYR2( UPLO, N, ALPHA, XX, INCX, YY, INCY, &
                                AA, LDA )
                 else if (  PACKED  ) then
                    call SSPR2( UPLO, N, ALPHA, XX, INCX, YY, INCY, &
                                AA )
!
!                    Check if error-exit was taken incorrectly.
!
                 end if
                 if ( NUMXER(NERR) /= 0 ) then
                    if ( kprint >= 2 ) then
                      write ( NOUT, FMT = 9992 )
                    end if
                    FATAL = .TRUE.
!
!                    See what data changed inside subroutines.
!
                 end if
                 ISAME( 1 ) = UPLO == UPLOS
                 ISAME( 2 ) = NS == N
                 ISAME( 3 ) = ALS == ALPHA
                 ISAME( 4 ) = LSE( XS, XX, LX )
                 ISAME( 5 ) = INCXS == INCX
                 ISAME( 6 ) = LSE( YS, YY, LY )
                 ISAME( 7 ) = INCYS == INCY
                 if (  NULL  ) then
                    ISAME( 8 ) = LSE( AS, AA, LAA )
                 else
                    ISAME( 8 ) = LSERES( SNAME( 2: 3 ), UPLO, N, N, &
                                 AS, AA, LDA )
                 end if
                 if (  .NOT.PACKED  ) then
                    ISAME( 9 ) = LDAS == LDA
!
!  If data was incorrectly changed, report and return.
!
                 end if

                 DO I = 1, NARGS
                   if ( .NOT. ISAME( I ) ) then
                     FATAL = .TRUE.
                     if ( kprint >= 2 ) then
                       write ( NOUT, FMT = 9998 )I
                     end if
                   end if
                 end do

                 FTL = .FALSE.
!
!  Check the result column by column.
!
                 if (  .NOT.NULL  ) then
                    if (  INCX > 0  ) then
                       DO I = 1, N
                          Z( I, 1 ) = X( I )
                       end do
                    else
                       DO 60 I = 1, N
                          Z( I, 1 ) = X( N - I + 1 )
   60                      continue
                    end if
                    if (  INCY > 0  ) then
                       DO 70 I = 1, N
                          Z( I, 2 ) = Y( I )
   70                      continue
                    else
                       DO 80 I = 1, N
                          Z( I, 2 ) = Y( N - I + 1 )
   80                      continue
                    end if
                    JA = 1
                    DO 90 J = 1, N
                       W( 1 ) = Z( J, 2 )
                       W( 2 ) = Z( J, 1 )
                       if (  UPPER  ) then
                          JJ = 1
                          LJ = J
                       else
                          JJ = J
                          LJ = N - J + 1
                       end if
                       call SMVCH( 'N', LJ, 2, ALPHA, Z( JJ, 1 ), &
                                   NMAX, W, 1, ONE, A( JJ, J ), 1, &
                                   YT, G, AA( JA ), EPS, ERR, FTL, &
                                   NOUT, .TRUE., kprint )
                       if (  FULL  ) then
                          if (  UPPER  ) then
                             JA = JA + LDA
                          else
                             JA = JA + LDA + 1
                          end if
                       else
                          JA = JA + LJ
                       end if
                       ERRMAX = max (  ERRMAX, ERR )
                       if ( FTL ) then
                         FATAL = .TRUE.
                         if ( kprint >= 3 ) then
                           write ( NOUT, FMT = 9995 )J
                           write ( NOUT, FMT = 9996 )SNAME
                           if (  FULL  ) then
                              write ( NOUT, FMT = 9993 )NC, SNAME, &
                               UPLO, N, ALPHA, INCX, &
                               INCY, LDA
                           else if (  PACKED  ) then
                              write ( NOUT, FMT = 9994 )NC, SNAME, &
                               UPLO, N, ALPHA, INCX, INCY
                           end if
                         end if
                       end if
   90                   continue

                 end if

  100             continue

  110          continue

  120       continue

  130    continue
!
!     Report result.
!
  140 continue
  if ( .NOT. (FATAL) ) then
    if ( kprint >= 3 ) then
      if (  ERRMAX < THRESH  ) then
         write ( NOUT, FMT = 9999 )SNAME, NC
      else
         write ( NOUT, FMT = 9997 )SNAME, NC, ERRMAX
      end if
    end if
  end if
!
  return
 9999 FORMAT( ' ', A6, ' PASSED THE COMPUTATIONAL TESTS (', I6, ' CALL', &
        'S)' )
 9998 FORMAT( ' ******* FATAL ERROR - PARAMETER NUMBER ', I2, ' WAS CH', &
        'ANGED INCORRECTLY *******' )
 9997 FORMAT( ' ', A6, ' COMPLETED THE COMPUTATIONAL TESTS (', I6, ' C', &
        'ALLS)', /' ******* BUT WITH MAXIMUM TEST RATIO', F8.2, &
        ' - SUSPECT *******' )
 9996 FORMAT( ' ******* ', A6, ' FAILED ON call NUMBER:' )
 9995 FORMAT( '      THESE ARE THE RESULTS FOR COLUMN ', I3 )
 9994 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', I3, ',', F4.1, ', X,', &
        I2, ', Y,', I2, ', AP)                     .' )
 9993 FORMAT( 1X, I6, ': ', A6, '(''', A1, ''',', I3, ',', F4.1, ', X,', &
        I2, ', Y,', I2, ', A,', I3, ')                  .' )
 9992 FORMAT( ' ******* FATAL ERROR - ERROR-EXIT TAKEN ON VALID call *', &
        '******' )
end
