      write (LUN, *) ' Y(1) ', Y(1)
      write (LUN, *) ' Y(2) ', Y(2)
      write (LUN, *) ' Y(3) ', Y(3)
      write (LUN, *) &
      ' Number of steps taken is  ', NSTEP
      write (LUN, *) &
      ' Number of evaluations of the right hand side is  ', NFE
      write (LUN, *) &
      ' Number of evaluations of the Jacobian matrix is  ', NJE
      write (LUN, '(//)')
    end if
    ipass = 0
  end if
!                                            Exercise SDRIV3 for problem
!                                            with known solution.
  call xerclr
  T = 0.E0
  Y(1) = 10.E0
  Y(2) = 0.E0
  Y(3) = 10.E0
  Y(4) = ALFA
  NSTATE = 1
  TOUT = 10.E0
  MINT = 2
