              IEND = J
           end if
        else
           if (  UNIT  ) then
              IBEG = J + 1
           else
              IBEG = J
           end if
           IEND = N
        end if
        DO 60 I = 1, IBEG - 1
           AA( I + ( J - 1 )*LDA ) = ROGUE
   60       continue
        DO 70 I = IBEG, IEND
           AA( I + ( J - 1 )*LDA ) = A( I, J )
   70       continue
        DO 80 I = IEND + 1, LDA
           AA( I + ( J - 1 )*LDA ) = ROGUE
   80       continue
   90    continue
  end if
!
!     End of SMAKE3.
!
  return
end
subroutine SMMCH (TRANSA, TRANSB, M, N, KK, ALPHA, A, LDA, B, LDB, &
!! SMMCH
!***SUBSIDIARY
!***PURPOSE  Check the results of the computational tests.
!***LIBRARY   SLATEC (BLAS)
!***AUTHOR  Dongarra, J. J., (ANL)
!           Duff, I., (AERE)
!           Du Croz, J., (NAG)
!           Hammarling, S., (NAG)
!***DESCRIPTION
!
!  Checks the results of the computational tests.
!
!  Auxiliary routine for test program for Level 3 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  (NONE)
!***REVISION HISTORY  (YYMMDD)
!   890208  DATE WRITTEN
!   910620  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SMMCH
!     .. Parameters ..
     BETA, C, LDC, CT, G, CC, LDCC, EPS, ERR, FTL, NOUT, MV, KPRINT)
  REAL               ZERO, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, ONE = 1.0 )
  REAL               ALPHA, BETA, EPS, ERR
  integer            KK, KPRINT, LDA, LDB, LDC, LDCC, M, N, NOUT
  LOGICAL            MV, FTL
!     .. Array Arguments ..
  CHARACTER*1        TRANSA, TRANSB
  REAL               A( LDA, * ), B( LDB, * ), C( LDC, * ), &
!     .. Local Scalars ..
                     CC( LDCC, * ), CT( * ), G( * )
  REAL               ERRI
  integer            I, J, K
!     .. Intrinsic Functions ..
  LOGICAL            TRANA, TRANB
!***FIRST EXECUTABLE STATEMENT  SMMCH
  INTRINSIC          ABS, MAX, SQRT
  TRANA = TRANSA == 'T'.OR.TRANSA == 'C'
!
!     Compute expected result, one column at a time, in CT using data
!     in A, B and C.
!     Compute gauges in G.
!
  TRANB = TRANSB == 'T'.OR.TRANSB == 'C'
!
  DO 120 J = 1, N
     DO 10 I = 1, M
        CT( I ) = ZERO
        G( I ) = ZERO
   10    continue
     if (  .NOT.TRANA .and. .NOT.TRANB  ) then
        DO 30 K = 1, KK
           DO 20 I = 1, M
              CT( I ) = CT( I ) + A( I, K )*B( K, J )
              G( I ) = G( I ) + ABS( A( I, K ) )*ABS( B( K, J ) )
   20          continue
   30       continue
     else if (  TRANA .and. .NOT.TRANB  ) then
        DO 50 K = 1, KK
           DO 40 I = 1, M
              CT( I ) = CT( I ) + A( K, I )*B( K, J )
              G( I ) = G( I ) + ABS( A( K, I ) )*ABS( B( K, J ) )
   40          continue
   50       continue
     else if (  .NOT.TRANA .and. TRANB  ) then
        DO 70 K = 1, KK
           DO 60 I = 1, M
              CT( I ) = CT( I ) + A( I, K )*B( J, K )
              G( I ) = G( I ) + ABS( A( I, K ) )*ABS( B( J, K ) )
   60          continue
   70       continue
     else if (  TRANA .and. TRANB  ) then
        DO 90 K = 1, KK
           DO 80 I = 1, M
              CT( I ) = CT( I ) + A( K, I )*B( J, K )
              G( I ) = G( I ) + ABS( A( K, I ) )*ABS( B( J, K ) )
   80          continue
   90       continue
     end if
     DO 100 I = 1, M
        CT( I ) = ALPHA*CT( I ) + BETA*C( I, J )
        G( I ) = ABS( ALPHA )*G( I ) + ABS( BETA )*ABS( C( I, J ) )
!
!        Compute the error ratio for this result.
!
  100    continue
     ERR = ZERO
     DO 110 I = 1, M
        ERRI = ABS( CT( I ) - CC( I, J ) )/EPS
        if (  G( I ) /= ZERO ) &
           ERRI = ERRI/G( I )
        ERR = max (  ERR, ERRI )
        if (  ERR*SQRT( EPS ) >= ONE  ) then
          FTL = .TRUE.
          if ( kprint >= 2 ) then
           write ( NOUT, FMT = 9999 )
           DO 140 K = 1, M
              if (  MV  ) then
                 write ( NOUT, FMT = 9998 )K, CT( K ), CC( K, J )
              else
                 write ( NOUT, FMT = 9998 )K, CC( K, J ), CT( K )
              end if
  140          continue
          end if
        end if
  110    continue
  120 continue
!
  return
 9999 FORMAT( ' ******* FATAL ERROR - COMPUTED RESULT IS LESS THAN HAL', &
        'F ACCURATE *******', /'           EXPECTED RESULT   COMPU', &
        'TED RESULT' )
!
!     End of SMMCH.
!
 9998 FORMAT( 1X, I7, 2G18.6 )
end
subroutine SMVCH (TRANS, M, N, ALPHA, A, NMAX, X, INCX, BETA, Y, &
!! SMVCH
!***SUBSIDIARY
!***PURPOSE  Check the results of the computational tests.
!***LIBRARY   SLATEC (BLAS)
!***AUTHOR  Du Croz, J. J., (NAG)
!           Hanson, R. J., (SNLA)
!***DESCRIPTION
!
!  Checks the results of the computational tests.
!
!  Auxiliary routine for test program for Level 2 Blas.
!***REFERENCES  (NONE)
!***ROUTINES CALLED  (NONE)
!***REVISION HISTORY  (YYMMDD)
!   870810  DATE WRITTEN
!   910620  Modified to meet SLATEC code and prologue standards.  (BKS)
!***END PROLOGUE  SMVCH
!     .. Parameters ..
     INCY, YT, G, YY, EPS, ERR, FTL, NOUT, MV, KPRINT)
  REAL               ZERO, ONE
!     .. Scalar Arguments ..
  PARAMETER          ( ZERO = 0.0, ONE = 1.0 )
  REAL               ALPHA, BETA, EPS, ERR
  integer            INCX, INCY, KPRINT, M, N, NMAX, NOUT
  LOGICAL            MV, FTL
!     .. Array Arguments ..
  CHARACTER*1        TRANS
  REAL               A( NMAX, * ), G( * ), X( * ), Y( * ), YT( * ), &
!     .. Local Scalars ..
                     YY( * )
  REAL               ERRI
  integer            I, INCXL, INCYL, IY, J, JX, K, KX, KY, ML, NL
!     .. Intrinsic Functions ..
  LOGICAL            TRAN
!***FIRST EXECUTABLE STATEMENT  SMVCH
  INTRINSIC          ABS, MAX, SQRT
  TRAN = TRANS == 'T'.OR.TRANS == 'C'
  if (  TRAN  ) then
     ML = N
     NL = M
  else
     ML = M
     NL = N
  end if
  if (  INCX < 0  ) then
     KX = NL
     INCXL = -1
  else
     KX = 1
     INCXL = 1
  end if
  if (  INCY < 0  ) then
     KY = ML
     INCYL = -1
  else
     KY = 1
     INCYL = 1
!
!     Compute expected result in YT using data in A, X and Y.
!     Compute gauges in G.
!
  end if
  IY = KY
  DO 30 I = 1, ML
     YT( IY ) = ZERO
     G( IY ) = ZERO
     JX = KX
     if (  TRAN  ) then
        DO 10 J = 1, NL
           YT( IY ) = YT( IY ) + A( J, I )*X( JX )
           G( IY ) = G( IY ) + ABS( A( J, I )*X( JX ) )
           JX = JX + INCXL
   10       continue
     else
        DO 20 J = 1, NL
           YT( IY ) = YT( IY ) + A( I, J )*X( JX )
           G( IY ) = G( IY ) + ABS( A( I, J )*X( JX ) )
           JX = JX + INCXL
   20       continue
     end if
     YT( IY ) = ALPHA*YT( IY ) + BETA*Y( IY )
     G( IY ) = ABS( ALPHA )*G( IY ) + ABS( BETA*Y( IY ) )
     IY = IY + INCYL
!
!     Compute the error ratio for this result.
!
   30 continue
  ERR = ZERO
  DO 40 I = 1, ML
     ERRI = ABS( YT( I ) - YY( 1 + ( I - 1 )*ABS( INCY ) ) )/EPS
     if (  G( I ) /= ZERO ) &
        ERRI = ERRI/G( I )
     ERR = max (  ERR, ERRI )
     if (  ERR*SQRT( EPS ) >= ONE  ) then
       FTL = .TRUE.
       if ( kprint >= 2 ) then
         write ( NOUT, FMT = 9999 )
         DO 60 K = 1, ML
            if (  MV  ) then
               write ( NOUT, FMT = 9998 )K, YT( K ), &
                  YY( 1 + ( K - 1 )*ABS( INCY ) )
            else
               write ( NOUT, FMT = 9998 )K, &
                  YY( 1 + ( K - 1 )*ABS( INCY ) ), YT(K)
            end if
   60        continue
       end if
     end if
   40 continue
!
  return
 9999 FORMAT( ' ******* FATAL ERROR - COMPUTED RESULT IS LESS THAN HAL', &
        'F ACCURATE *******', /'           EXPECTED RESULT   COMPU', &
        'TED RESULT' )
!
!     End of SMVCH.
!
 9998 FORMAT( 1X, I7, 2G18.6 )
end
