         write (LUN,9010) IFLAGS,FNORMS,IFLAG,FNORM
     if ( KPRINT >= 2 .OR. (KPRINT == 1 .and. ITEST(ICNT) /= 1)) &
         call PASS (LUN,ICNT,ITEST(ICNT))
!
