subroutine bab ( n, alpha, beta, a )

!*****************************************************************************80
!
!! BAB returns the BAB matrix.
!
!  Discussion:
!
!    The name is meant to suggest the pattern "B  A  B" formed by
!    the nonzero entries in a general row of the matrix.
!
!  Example:
!
!    N = 5
!    ALPHA = 5, BETA = 2
!
!    5  2  .  .  .
!    2  5  2  .  .
!    .  2  5  2  .
!    .  .  2  5  2
!    .  .  .  2  5
!
!  Properties:
!
!    A is banded, with bandwidth 3.
!
!    A is tridiagonal.
!
!    Because A is tridiagonal, it has property A (bipartite).
!
!    A is Toeplitz: constant along diagonals.
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    05 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    CM da Fonseca, J Petronilho,
!    Explicit Inverses of Some Tridiagonal Matrices,
!    Linear Algebra and Its Applications,
!    Volume 325, 2001, pages 7-21.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the parameters.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) beta
  integer ( kind = 4 ) i

  a(1:n,1:n) = 0.0D+00

  do i = 1, n
    a(i,i) = alpha
  end do

  do i = 1, n - 1
    a(i,i+1) = beta
    a(i+1,i) = beta
  end do
 
  return
end
subroutine bab_condition ( n, alpha, beta, cond )

!*****************************************************************************80
!
!! BAB_CONDITION returns the L1 condition of the BAB matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    08 April 2012
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the parameters.
!
!    Output, real ( kind = 8 ) COND, the L1 condition number.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a_norm
  real ( kind = 8 ) alpha
  real ( kind = 8 ) b(n,n)
  real ( kind = 8 ) b_norm
  real ( kind = 8 ) beta
  real ( kind = 8 ) cond
  real ( kind = 8 ) r8mat_norm_l1

  if ( n == 1 ) then
    a_norm = abs ( alpha )
  else if ( n == 2 ) then
    a_norm = abs ( alpha ) + abs ( beta )
  else
    a_norm = abs ( alpha ) + 2.0D+00 * abs ( beta )
  end if

  call bab_inverse ( n, alpha, beta, b )
  b_norm = r8mat_norm_l1 ( n, n, b )

  cond = a_norm * b_norm

  return
end
subroutine bab_determinant ( n, alpha, beta, determ )

!*****************************************************************************80
!
!! BAB_DETERMINANT returns the determinant of the BAB matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    05 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the parameters.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) alpha
  real ( kind = 8 ) beta
  real ( kind = 8 ) determ
  real ( kind = 8 ) determ_nm1
  real ( kind = 8 ) determ_nm2
  integer ( kind = 4 ) i

  determ_nm1 = alpha

  if ( n == 1 ) then
    determ = determ_nm1
    return
  end if

  determ_nm2 = determ_nm1
  determ_nm1 = alpha * alpha - beta * beta

  if ( n == 2 ) then
    determ = determ_nm1
    return
  end if

  do i = n - 2, 1, -1

    determ = alpha * determ_nm1 - beta * beta * determ_nm2

    determ_nm2 = determ_nm1
    determ_nm1 = determ
    
  end do

  return
end
subroutine bab_eigenvalues ( n, alpha, beta, lambda )

!*****************************************************************************80
!
!! BAB_EIGENVALUES returns the eigenvalues of the BAB matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    29 May 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the parameters.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) alpha
  real ( kind = 8 ) angle
  real ( kind = 8 ) beta
  integer ( kind = 4 ) i
  real ( kind = 8 ) lambda(n)
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00

  do i = 1, n
    angle = real ( i, kind = 8 ) * pi / real ( n + 1, kind = 8 )
    lambda(i) = alpha + 2.0D+00 * beta * cos ( angle )
  end do

  return
end
subroutine bab_inverse ( n, alpha, beta, a )

!*****************************************************************************80
!
!! BAB_INVERSE returns the inverse of the BAB matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    08 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the parameters.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) beta
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8_mop
  real ( kind = 8 ) u(0:n)
  real ( kind = 8 ) x

  if ( beta == 0.0D+00 ) then

    if ( alpha == 0.0D+00 ) then
      write ( *, '(a)' ) ' '
      write ( *, '(a)' ) 'BAB_INVERSE - Fatal error!'
      write ( *, '(a)' ) '  ALPHA = BETA = 0.'
      stop
    end if

    a(1:n,1:n) = 0.0D+00

    do i = 1, n
      a(i,i) = 1.0D+00 / alpha
    end do

  else

    x = 0.5D+00 * alpha / beta

    call cheby_u_polynomial ( n, x, u )

    do i = 1, n
      do j = 1, i
        a(i,j) = r8_mop ( i + j ) * u(j-1) * u(n-i) / u(n) / beta
      end do
      do j = i + 1, n
        a(i,j) = r8_mop ( i + j ) * u(i-1) * u(n-j) / u(n) / beta
      end do
    end do

  end if

  return
end
subroutine balanc ( nm, n, a, low, igh, scale )

!*****************************************************************************80
!
!! BALANC balances a real matrix before eigenvalue calculations.
!
!  Discussion:
!
!    This subroutine balances a real matrix and isolates eigenvalues
!    whenever possible.
!
!    Suppose that the principal submatrix in rows LOW through IGH
!    has been balanced, that P(J) denotes the index interchanged
!    with J during the permutation step, and that the elements
!    of the diagonal matrix used are denoted by D(I,J).  Then
!
!      SCALE(J) = P(J),    J = 1,...,LOW-1,
!               = D(J,J),  J = LOW,...,IGH,
!               = P(J)     J = IGH+1,...,N.
!
!    The order in which the interchanges are made is N to IGH+1,
!    then 1 to LOW-1.
!
!    Note that 1 is returned for LOW if IGH is zero formally.
!
!  Modified:
!
!    20 March 2002
!
!  Reference:
!
!    James Wilkinson, Christian Reinsch,
!    Handbook for Automatic Computation,
!    Volume II, Linear Algebra, Part 2,
!    Springer, 1971,
!    ISBN: 0387054146,
!    LC: QA251.W67.
!
!    Brian Smith, James Boyle, Jack Dongarra, Burton Garbow, 
!    Yasuhiko Ikebe, Virginia Klema, Cleve Moler,
!    Matrix Eigensystem Routines, EISPACK Guide,
!    Lecture Notes in Computer Science, Volume 6,
!    Springer, 1976,
!    ISBN13: 978-3540075462,
!    LC: QA193.M37.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) NM, the leading dimension of A, which must
!    be at least N.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input/output, real ( kind = 8 ) A(NM,N), the matrix.  On output,
!    the matrix has been balanced.
!
!    Output, integer ( kind = 4 ) LOW, IGH, indicate that A(I,J) 
!    is equal to zero if
!    (1) I is greater than J and
!    (2) J=1,...,LOW-1 or I=IGH+1,...,N.
!
!    Output, real ( kind = 8 ) SCALE(N), contains information determining the
!    permutations and scaling factors used.
!
  implicit none

  integer ( kind = 4 ) nm
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(nm,n)
  real ( kind = 8 ) b2
  real ( kind = 8 ) c
  real ( kind = 8 ) f
  real ( kind = 8 ) g
  integer ( kind = 4 ) i
  integer ( kind = 4 ) iexc
  integer ( kind = 4 ) igh
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  integer ( kind = 4 ) l
  integer ( kind = 4 ) low
  integer ( kind = 4 ) m
  logical noconv
  real ( kind = 8 ) r
  real ( kind = 8 ) radix
  real ( kind = 8 ) s
  real ( kind = 8 ) scale(n)

  radix = 16.0D+00

  iexc = 0
  j = 0
  m = 0

  b2 = radix * radix
  k = 1
  l = n
  go to 100

20 continue

  scale(m) = j

  if ( j /= m ) then

    do i = 1, l
      call r8_swap ( a(i,j), a(i,m) )
    end do

    do i = k, n
      call r8_swap ( a(j,i), a(m,i) )
    end do

  end if

50 continue

  if ( iexc == 2 ) then
    go to 130
  end if
!
!  Search for rows isolating an eigenvalue and push them down.
!
80 continue

  if ( l == 1 ) then
    low = k
    igh = l
    return
  end if

  l = l - 1

100 continue

  do j = l, 1, -1

     do i = 1, l
       if ( i /= j ) then
         if ( a(j,i) /= 0.0D+00 ) then
           go to 120
         end if
       end if
     end do

     m = l
     iexc = 1
     go to 20

120  continue

  end do

  go to 140
!
!  Search for columns isolating an eigenvalue and push them left.
!
130 continue

  k = k + 1

140 continue

  do j = k, l

    do i = k, l
      if ( i /= j ) then
        if ( a(i,j) /= 0.0D+00 ) then
          go to 170
        end if
      end if
    end do

    m = k
    iexc = 2
    go to 20

170 continue

  end do
!
!  Balance the submatrix in rows K to L.
!
  scale(k:l) = 1.0D+00
!
!  Iterative loop for norm reduction.
!
  noconv = .true.

  do while ( noconv )

    noconv = .false.

    do i = k, l

      c = 0.0D+00
      r = 0.0D+00

      do j = k, l
        if ( j /= i ) then
          c = c + abs ( a(j,i) )
          r = r + abs ( a(i,j) )
        end if
      end do
!
!  Guard against zero C or R due to underflow.
!
      if ( c /= 0.0D+00 .and. r /= 0.0D+00 ) then

        g = r / radix
        f = 1.0D+00
        s = c + r

        do while ( c < g )
          f = f * radix
          c = c * b2
        end do

        g = r * radix

        do while ( g <= c )
          f = f / radix
          c = c / b2
        end do
!
!  Balance.
!
        if ( ( c + r ) / f < 0.95D+00 * s ) then

          g = 1.0D+00 / f
          scale(i) = scale(i) * f
          noconv = .true.

          a(i,k:n) = a(i,k:n) * g
          a(1:l,i) = a(1:l,i) * f

        end if

      end if

    end do

  end do

  low = k
  igh = l

  return
end
subroutine bernstein ( n, a )

!*****************************************************************************80
!
!! BERNSTEIN returns the Bernstein matrix.
!
!  Discussion:
!
!    The Bernstein matrix of order N is an NxN matrix A which can be used to
!    transform a vector of power basis coefficients C representing a polynomial 
!    P(X) to a corresponding Bernstein basis coefficient vector B:
!
!      B = A * C
!
!    The N power basis vectors are ordered as (1,X,X^2,...X^(N-1)) and the N 
!    Bernstein basis vectors as ((1-X)^(N-1), X*(1_X)^(N-2),...,X^(N-1)).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    10 July 2011
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the Bernstein matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i0
  integer ( kind = 4 ) j0
  integer ( kind = 4 ) n0
  real ( kind = 8 ) r8_choose
  real ( kind = 8 ) r8_mop

  a(1:n,1:n) = 0.0D+00

  n0 = n - 1

  do j0 = 0, n0
    do i0 = 0, j0
      a(i0+1,j0+1) = r8_mop ( j0 - i0 ) * r8_choose ( n0 - i0, j0 - i0 ) &
        * r8_choose ( n0, i0 )
    end do
  end do

  return
end
subroutine bernstein_inverse ( n, a )

!*****************************************************************************80
!
!! BERNSTEIN_INVERSE returns the inverse Bernstein matrix.
!
!  Discussion:
!
!    The inverse Bernstein matrix of order N is an NxN matrix A which can 
!    be used to transform a vector of Bernstein basis coefficients B
!    representing a polynomial P(X) to a corresponding power basis 
!    coefficient vector C:
!
!      C = A * B
!
!    The N power basis vectors are ordered as (1,X,X^2,...X^(N-1)) and the N 
!    Bernstein basis vectors as ((1-X)^(N-1), X*(1_X)^(N-2),...,X^(N-1)).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    10 July 2011
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the inverse Bernstein matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i0
  integer ( kind = 4 ) j0
  integer ( kind = 4 ) n0
  real ( kind = 8 ) r8_choose

  a(1:n,1:n) = 0.0D+00

  n0 = n - 1

  do j0 = 0, n0
    do i0 = 0, j0
      a(i0+1,j0+1) = r8_choose ( j0, i0 ) / r8_choose ( n0, i0 )
    end do
  end do

  return
end
subroutine bimarkov_random ( n, seed, a )

!*****************************************************************************80
!
!! BIMARKOV_RANDOM returns a BIMARKOV matrix.
!
!  Discussion:
!
!    A Bimarkov matrix is also known as a doubly stochastic matrix.
!
!  Example:
!
!    N = 5
!
!    1/5   1/5   1/5   1/5   1/5
!    1/2   1/2    0     0     0
!    1/6   1/6   2/3    0     0
!    1/12  1/12  1/12  3/4    0
!    1/20  1/20  1/20  1/20  4/5
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    0 <= A(I,J) <= 1.0D+00 for every I and J.
!
!    A has constant row sum 1.
!
!    Because it has a constant row sum of 1,
!    A has an eigenvalue of 1
!    and a (right) eigenvector of ( 1, 1, 1, ..., 1 ).
!
!    A has constant column sum 1.
!
!    Because it has a constant column sum of 1,
!    A has an eigenvalue of 1
!    and a (left) eigenvector of ( 1, 1, 1, ..., 1 ).
!
!    All the eigenvalues of A have modulus 1.
!
!    The eigenvalue 1 lies on the boundary of all the Gershgorin
!    row or column sum disks.
!
!    Every doubly stochastic matrix is a combination
!      A = w1 * P1 + w2 * P2 + ... + wk * Pk
!    of permutation matrices, with positive weights w that sum to 1.
!    (Birkhoff's theorem, see Horn and Johnson.)
!
!    A is a Markov matrix.
!
!    A is a transition matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 May 2006
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Roger Horn, Charles Johnson,
!    Matrix Analysis,
!    Cambridge, 1985,
!    ISBN: 0-521-38632-2,
!    LC: QA188.H66.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input/output, integer ( kind = 4 ) SEED, a seed for the random 
!    number generator.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) seed
!
!  Get a random orthogonal matrix.
!
  call orth_random ( n, seed, a )
!
!  Square each entry.
!
  a(1:n,1:n) = a(1:n,1:n)**2

  return
end
subroutine bis ( alpha, beta, m, n, a )

!*****************************************************************************80
!
!! BIS returns the BIS matrix.
!
!  Discussion:
!
!    The BIS matrix is a bidiagonal scalar matrix.
!
!  Formula:
!
!    if ( I = J )
!      A(I,J) = ALPHA
!    else if ( J = I + 1 )
!      A(I,J) = BETA
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 7, BETA = 2, M = 5, N = 4
!
!    7  2  0  0
!    0  7  2  0
!    0  0  7  2
!    0  0  0  7
!    0  0  0  0
!
!  Properties:
!
!    A is bidiagonal.
!
!    Because A is bidiagonal, it has property A (bipartite).
!
!    A is upper triangular.
!
!    A is banded with bandwidth 2.
!
!    A is Toeplitz: constant along diagonals.
!
!    A is generally not symmetric: A' /= A.
!
!    A is nonsingular if and only if ALPHA is nonzero.
!
!    det ( A ) = ALPHA^N.
!
!    LAMBDA(1:N) = ALPHA.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    The INTEGRATION matrix is a special case of the BIS matrix.
!    The JORDAN matrix is a special case of the BIS matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the scalars which define the
!    diagonal and first superdiagonal of the matrix.
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(M,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) beta
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, m

      if ( j == i ) then
        a(i,j) = alpha
      else if ( j == i + 1 ) then
        a(i,j) = beta
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine bis_determinant ( alpha, beta, n, determ )

!*****************************************************************************80
!
!! BIS_DETERMINANT returns the determinant of the BIS matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the scalars which define the
!    diagonal and first superdiagonal of the matrix.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
