!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) alpha
  real ( kind = 8 ) beta
  real ( kind = 8 ) determ

  determ = alpha**n

  return
end
subroutine bis_eigenvalues ( alpha, beta, n, lambda )

!*****************************************************************************80
!
!! BIS_EIGENVALUES returns the eigenvalues of the BIS matrix.
!
