subroutine bis_inverse ( alpha, beta, n, a )

!*****************************************************************************80
!
!! BIS_INVERSE returns the inverse of the BIS matrix.
!
!  Formula:
!
!    if ( I <= J )
!      A(I,J) = (-BETA)^(J-I) / ALPHA^(J+1-I)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 7.0, BETA = 2.0, N = 4
!
!   1/7  -1/2   1/4  -1/8
!    0    1/7  -1/2   1/4
!    0     0    1/7  -1/2
!    0     0     0    1/7
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is upper triangular
!
!    A is Toeplitz: constant along diagonals.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    det ( A ) = (1/ALPHA)^N.
!
!    LAMBDA(1:N) = 1 / ALPHA.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, BETA, the scalars which define the
!    diagonal and first superdiagonal of the matrix.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) beta
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  if ( alpha == 0.0D+00 ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'BIS_INVERSE - Fatal error!'
    write ( *, '(a)' ) '  The input parameter ALPHA was 0.'
    stop
  end if

  do j = 1, n
    do i = 1, n

      if ( i <= j ) then
        a(i,j) = ( - beta / alpha )**( j - i ) / alpha
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine bodewig ( a )

!*****************************************************************************80
!
!! BODEWIG returns the BODEWIG matrix.
!
!  Example:
!
!    2   1   3   4
!    1  -3   1   5
!    3   1   6  -2
!    4   5  -2  -1
!
!  Properties:
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    A is symmetric: A' = A.
!
!    det ( A ) = 568.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    23 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Joan Westlake,
!    A Handbook of Numerical Matrix Inversion and Solution of 
!    Linear Equations,
!    John Wiley, 1968,
!    ISBN13: 978-0471936756,
!    LC: QA263.W47.
!
!  Parameters:
!
!    Output, real ( kind = 8 ) A(4,4), the matrix.
!
  implicit none

  real ( kind = 8 ) a(4,4)
  real ( kind = 8 ), dimension ( 4, 4 ), save :: a_save = reshape ( (/ &
    2.0D+00,  1.0D+00,  3.0D+00,  4.0D+00, &
    1.0D+00, -3.0D+00,  1.0D+00,  5.0D+00, &
    3.0D+00,  1.0D+00,  6.0D+00, -2.0D+00, &
