subroutine boothroyd ( n, a )

!*****************************************************************************80
!
!! BOOTHROYD returns the BOOTHROYD matrix.
!
!  Formula:
!
!    A(I,J) = C(N+I-1,I-1) * C(N-1,N-J) * N / ( I + J - 1 )
!
!  Example:
!
!    N = 5
!
!     5    10    10     5     1
!    15    40    45    24     5
!    35   105   126    70    15
!    70   224   280   160    35
!   126   420   540   315    70
!
!  Properties:
!
!    A is not symmetric.
!
!    A is positive definite.
!
!    det ( A ) = 1.
!
!    The eigenvalues are real, and come in pairs whose product is 1.
!    When N is odd, there is one unpaired eigenvalue equal to 1.
!
!    The inverse matrix has the same entries, but with alternating sign.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 June 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    John Boothroyd,
!    Algorithm 274:
!    Generation of Hilbert Derived Test Matrix,
!    Communications of the ACM,
!    Volume 9, Number 1, January 1966, page 11.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8_choose

  do j = 1, n
    do i = 1, n

      a(i,j) = r8_choose ( n + i - 1, i - 1 ) * r8_choose ( n - 1, n - j ) &
        * real ( n, kind = 8 ) / real ( i + j - 1, kind = 8 )

    end do
  end do

  return
end
