end
subroutine boothroyd_inverse ( n, a )

!*****************************************************************************80
!
!! BOOTHROYD_INVERSE returns the inverse of the BOOTHROYD matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 June 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8_choose
  real ( kind = 8 ) r8_mop

  do j = 1, n
    do i = 1, n

      a(i,j) = r8_mop ( i + j ) * r8_choose ( n + i - 1, i - 1 ) &
        * r8_choose ( n - 1, n - j ) &
        * real ( n, kind = 8 ) / real ( i + j - 1, kind = 8 )

    end do
  end do

  return
end
subroutine borderband ( n, a )

!*****************************************************************************80
!
!! BORDERBAND returns the BORDERBAND matrix.
!
!  Formula:
!
!    If ( I = J )
!      A(I,I) = 1
!    else if ( I = N )
!      A(N,J) = 2^(1-J)
!    else if ( J = N )
!      A(I,N) = 2^(1-I)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    N = 5
!
!     1  0   0   0   1
!     0  1   0   0  1/2
!     0  0   1   0  1/4
!     0  0   0   1  1/8
!     1 1/2 1/4 1/8  1
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    A is border-banded.
!
!    A has N-2 eigenvalues of 1.
!
!    det ( A ) = 1 - sum ( 1 <= I <= N-1 ) 2^(2-2*I)
!
!    For N = 2, A is singular.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, n

      if ( i == j ) then
        a(i,j) = 1.0D+00
      else if ( j == n ) then
        a(i,j) = 2.0D+00**( 1 - i )
      else if ( i == n ) then
        a(i,j) = 2.0D+00**( 1 - j )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine borderband_determinant ( n, determ )

!*****************************************************************************80
!
!! BORDERBAND_DETERMINANT returns the determinant of the BORDERBAND matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) n

  determ = 0.0D+00
  do i = 1, n - 1
    determ = determ - 2.0D+00**( 2 - 2 * i )
  end do
  determ = determ + 1.0D+00

  return
end
subroutine borderband_inverse ( n, a )

!*****************************************************************************80
!
!! BORDERBAND_INVERSE returns the inverse of the BORDERBAND matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    17 August 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the inverse matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) l(n,n)
  real ( kind = 8 ) p(n,n)
  real ( kind = 8 ) u(n,n)

  call borderband_plu ( n, p, l, u )

  call plu_inverse ( n, p, l, u, a )

  return
end
subroutine borderband_plu ( n, p, l, u )

!*****************************************************************************80
!
!! BORDERBAND_PLU returns the PLU factors of the BORDERBAND matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    16 August 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) P(N,N), L(N,N), U(N,N), the PLU factors.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) l(n,n)
  real ( kind = 8 ) p(n,n)
  real ( kind = 8 ) u(n,n)

  do j = 1, n
    do i = 1, n
      if ( i == j ) then
        p(i,j) = 1.0D+00
      else
        p(i,j) = 0.0D+00
      end if
    end do
  end do

  do j = 1, n
    do i = 1, n
      if ( i == j ) then
        l(i,j) = 1.0D+00
      else if ( i == n ) then
        l(i,j) = 2.0D+00**( 1 - j )
      else
        l(i,j) = 0.0D+00
      end if
    end do
  end do

  do j = 1, n
    do i = 1, n
      if ( i == n .and. j == n ) then
        u(i,j) = 0.0D+00
        do k = 2, n - 1
          u(i,j) = u(i,j) - 2.0D+00**( 2 - 2 * k )
        end do
      else if ( i == j ) then
        u(i,j) = 1.0D+00
      else if ( j == n ) then
        u(i,j) = 2.0D+00**( 1 - i )
      else
        u(i,j) = 0.0D+00
      end if
    end do
  end do

  return
end
