  return
end
subroutine c8mat_is_unitary ( m, n, a, ival )

!*****************************************************************************80
!
!! C8MAT_IS_UNITARY checks whether a C8MAT is unitary.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    31 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions 
!    of the matrix.  M and N must be positive.
!
!    Input, complex ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not unitary; M /= N.
!    -2, the matrix is not unitary; row I * column I /= 1;
!    -3, the matrix is not unitary; row I * column J /= 0;
!    1, the matrix is unitary.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  complex ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  complex ( kind = 8 ) test
  real ( kind = 8 ), parameter :: tol = 0.0001D+00

  if ( m /= n ) then
    ival = -1
    return
  end if

  do i = 1, n

    test = dot_product ( a(i,1:n), a(1:n,i) ) &
      - cmplx ( 1.0D+00, 0.0D+00, kind = 8 )

    if ( tol < abs ( test ) ) then
      ival = -2
      return
    end if

    do j = i + 1, n

      test = dot_product ( a(i,1:n), a(1:n,j) )

      if ( tol < abs ( test ) ) then
        ival = -3
        return
      end if

    end do

  end do

  ival = 1

  return
end
function c8mat_norm_fro ( m, n, a )

!*****************************************************************************80
!
!! C8MAT_NORM_FRO returns the Frobenius norm of a C8MAT.
!
!  Discussion:
!
!    The Frobenius norm is defined as
!
!      C8MAT_NORM_FRO = sqrt (
!        sum ( 1 <= I <= M ) Sum ( 1 <= J <= N ) |A(I,J)| )
!
!    The matrix Frobenius-norm is not derived from a vector norm, but
!    is compatible with the vector L2 norm, so that:
!
!      c8vec_norm_l2 ( A*x ) <= c8mat_norm_fro ( A ) * c8vec_norm_l2 ( x ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, complex ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) C8MAT_NORM_FRO, the Frobenius norm of A.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  complex ( kind = 8 ) a(m,n)
  real ( kind = 8 ) c8mat_norm_fro

  c8mat_norm_fro = sqrt ( sum ( a(1:m,1:n) * conjg ( a(1:m,1:n) ) ) )

  return
end
