subroutine c8vec_unity ( n, a )

!*****************************************************************************80
!
!! C8VEC_UNITY returns the N roots of unity.
!
!  Discussion:
!
!    X(1:N) = exp ( 2 * PI * (0:N-1) / N )
!
!    X(1:N)^N = ( (1,0), (1,0), ..., (1,0) ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    12 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the number of elements of A.
!
!    Output, complex ( kind = 8 ) A(N), the N roots of unity.
!
  implicit none

  integer ( kind = 4 ) n

  complex ( kind = 8 ) a(n)
  real ( kind = 8 ) angle
  integer ( kind = 4 ) i
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00

  do i = 1, n
    angle = pi * real ( 2 * ( i - 1 ), kind = 8 ) / real ( n, kind = 8 )
    a(i) = cmplx ( cos ( angle ), sin ( angle ), kind = 8 )
  end do

  return
end
subroutine carry ( alpha, n, a )

!*****************************************************************************80
!
!! CARRY returns the CARRY matrix.
!
!  Discussion:
!
!    We assume that arithmetic is being done in base ALPHA.  We are adding
!    a column of N digits base ALPHA, as part of adding N random numbers.
!    We know the carry digit, between 0 and N-1, that is being carried into the
!    column sum (the incarry digit), and we want to know the probability of
!    the various carry digits 0 through N-1 (the outcarry digit) that could
!    be carried out of the column sum.
!
!    The carry matrix summarizes this data.  The entry A(I,J) represents
!    the probability that, given that the incarry digit is I-1, the
!    outcarry digit will be J-1.
!
!  Formula:
!
!    A(I,J) = ( 1 / ALPHA )^N * sum ( 0 <= K <= J-1 - floor ( I-1 / ALPHA ) )
!      (-1)^K * C(N+1,K) * C(N-I+(J-K)*ALPHA, N )
!
!  Example:
!
!    ALPHA = 10, N = 4
!
!    0.0715 0.5280 0.3795 0.0210
!    0.0495 0.4840 0.4335 0.0330
!    0.0330 0.4335 0.4840 0.0495
!    0.0210 0.3795 0.5280 0.0715
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is a Markov matrix.
!
!    A is centrosymmetric: A(I,J) = A(N+1-I,N+1-J).
!
!    LAMBDA(I) = 1 / ALPHA^(I-1)
!
!    det ( A ) = 1 / ALPHA^((N*(N-1))/2)
!
!    The eigenvectors do not depend on ALPHA.
!
!    A is generally not normal: A' * A /= A * A'.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 June 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    John Holte,
!    Carries, Combinatorics, and an Amazing Matrix,
!    The American Mathematical Monthly,
!    Volume 104, Number 2, February 1997, pages 138-149.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) ALPHA, the numeric base being used 
!    in the addition.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) alpha
  real ( kind = 8 ) c1
  real ( kind = 8 ) c2
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) r8_choose
  real ( kind = 8 ) r8_mop
  real ( kind = 8 ) temp

  do j = 1, n
    do i = 1, n

      temp = 0.0D+00

      do k = 0, j - 1 - ( i - 1 ) / alpha
        c1 = r8_choose ( n + 1, k )
        c2 = r8_choose ( n - i + ( j - k ) * alpha, n )
        temp = temp + r8_mop ( k ) * c1 * c2
      end do

      a(i,j) = temp / real ( alpha**n, kind = 8 )

    end do

  end do

  return
end
subroutine carry_determinant ( alpha, n, determ )

!*****************************************************************************80
!
!! CARRY_DETERMINANT returns the determinant of the CARRY matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) ALPHA, the numeric base being used 
!    in the addition.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) alpha
  real ( kind = 8 ) determ
  integer ( kind = 4 ) power

  power = ( n * ( n - 1 ) ) / 2
  determ = 1.0D+00 / real ( alpha**power, kind = 8 )

  return
end
subroutine carry_eigenvalues ( alpha, n, lambda )

!*****************************************************************************80
!
!! CARRY_EIGENVALUES returns the eigenvalues of the CARRY matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    19 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) ALPHA, the ALPHA value, which represents the
!    numeric base being used in the addition.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) alpha
  integer ( kind = 4 ) i
  real ( kind = 8 ) lambda(n)

  do i = 1, n
    lambda(i) = 1.0D+00 / real ( alpha**( i - 1 ), kind = 8 )
  end do

  return
end
subroutine carry_inverse ( alpha, n, a )

!*****************************************************************************80
!
!! CARRY_INVERSE returns the inverse of the CARRY matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    26 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) ALPHA, the numeric base being used 
!    in the addition.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) alpha
  real ( kind = 8 ) d(n)
  real ( kind = 8 ) d_inv(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8_factorial
  real ( kind = 8 ) t
  real ( kind = 8 ) u(n,n)
  real ( kind = 8 ) v(n,n)

  call carry_left ( n, v )

  call carry_eigenvalues ( alpha, n, d )

  call carry_right ( n, u )

  do j = 1, n
    do i = 1, n
      v(i,j) = v(i,j) / d(i)
    end do
  end do

  a = matmul ( u, v )

  t = r8_factorial ( n )

  a(1:n,1:n) = a(1:n,1:n) / t

  return
end
subroutine carry_left ( n, a )

!*****************************************************************************80
!
!! CARRY_LEFT returns the left eigenvectors of the CARRY matrix.
!
!  Formula:
!
!    A(I,J) = sum ( 0 <= K <= J-1 )
!      (-1)^K * C(N+1,K) * ( J - K )^(N+1-I)
!
!  Example:
!
!    N = 4
!
!    1  11  11   1
!    1   3  -3  -1
!    1  -1  -1   1
!    1  -3   3  -1
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    Column 1 is all 1's, and column N is (-1)^(I+1).
!
!    The top row is proportional to a row of Eulerian numbers, and
!    can be normalized to represent the stationary probablities
!    for the carrying process when adding N random numbers.
!
!    The bottom row is proportional to a row of Pascal's triangle,
!    with alternating signs.
!
!    The product of the left and right eigenvector matrices of
!    order N is N! times the identity.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 June 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    John Holte,
!    Carries, Combinatorics, and an Amazing Matrix,
!    The American Mathematical Monthly,
!    Volume 104, Number 2, February 1997, pages 138-149.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) r8_choose
  real ( kind = 8 ) r8_mop

  a(1:n,1:n) = 0.0D+00

  do j = 1, n
    do i = 1, n

      do k = 0, j - 1
        a(i,j) = a(i,j) + r8_mop ( k ) * r8_choose ( n + 1, k ) &
          * ( j - k )**( n + 1 - i )
      end do

    end do

  end do

  return
end
subroutine carry_right ( n, a )

!*****************************************************************************80
!
!! CARRY_RIGHT returns the right eigenvectors of the CARRY matrix.
!
!  Discussion:
!
!    A(I,J) = sum ( N+1-J) <= K <= N )
!      S1(N,K) * C(K,N+1-J) ( N - I )^(K-N+J-1)
!
!    where S1(N,K) is a signed Sterling number of the first kind.
!
!  Example:
!
!    N = 4
!
!    1   6  11   6
!    1   2  -1  -2
!    1  -2  -1   2
!    1  -6  11  -6
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    The first column is all 1's.
!
!    The last column is reciprocals of binomial coefficients with
!    alternating sign multiplied by (N-1)!.
!
!    The top and bottom rows are the unsigned and signed Stirling numbers
!    of the first kind.
!
!    The entries in the J-th column are a degree (J-1) polynomial
!    in the row index I.  (Column 1 is constant, the first difference
!    in column 2 is constant, the second difference in column 3 is
!    constant, and so on.)
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 June 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    John Holte,
!    Carries, Combinatorics, and an Amazing Matrix,
!    The American Mathematical Monthly,
!    Volume 104, Number 2, February 1997, pages 138-149.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) r8_choose
  real ( kind = 8 ) s1(n,n)

  call stirling ( n, n, s1 )

  a(1:n,1:n) = 0.0D+00

  do j = 1, n
    do i = 1, n

      do k = n + 1 - j, n
        if ( n - i == 0 .and. k - n + j - 1 == 0 ) then
          a(i,j) = a(i,j) + s1(n,k) * r8_choose ( k, n + 1 - j )
        else
          a(i,j) = a(i,j) + s1(n,k) * r8_choose ( k, n + 1 - j ) &
            * ( n - i )**( k - n + j - 1 )
        end if
      end do

    end do

  end do

  return
end
subroutine cauchy ( n, x, y, a )

!*****************************************************************************80
!
!! CAUCHY returns the CAUCHY matrix.
!
!  Formula:
!
!    A(I,J) = 1.0D+00 / ( X(I) + Y(J) )
!
!  Example:
!
!    N = 5, X = ( 1, 3, 5, 8, 7 ), Y = ( 2, 4, 6, 10, 9 )
!
!    1/3  1/5  1/7  1/11 1/10
!    1/5  1/7  1/9  1/13 1/12
!    1/7  1/9  1/11 1/15 1/14
!    1/10 1/12 1/14 1/18 1/17
!    1/9  1/11 1/13 1/17 1/16
!
!    or, in decimal form,
!
!    0.333333      0.200000      0.142857      0.0909091     0.100000
!    0.200000      0.142857      0.111111      0.0769231     0.0833333
!    0.142857      0.111111      0.0909091     0.0666667     0.0714286
!    0.100000      0.0833333     0.0714286     0.0555556     0.0588235
!    0.111111      0.0909091     0.0769231     0.0588235     0.0625000
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is totally positive if 0 < X(1) < ... < X(N) and 0 < Y1 < ... < Y(N).
!
!    A will be singular if any X(I) equals X(J), or
!    any Y(I) equals Y(J), or if any X(I)+Y(J) equals zero.
!
!    A is generally not normal: A' * A /= A * A'.
!
!    The HILBERT matrix is a special case of the CAUCHY matrix.
!
!    The PARTER matrix is a special case of the CAUCHY matrix.
!
!    The RIS or "ding-dong" matrix is a special case of the CAUCHY matrix.
!
!    det ( A ) = product ( 1 <= I < J <= N ) ( X(J) - X(I) )* ( Y(J) - Y(I) )
!           / product ( 1 <= I <= N, 1 <= J <= N ) ( X(I) + Y(J) )
!
!    The inverse of A is
!
!      INVERSE(A)(I,J) = product ( 1 <= K <= N ) [ (X(J)+Y(K)) * (X(K)+Y(I)) ] /
!            [ (X(J)+Y(I)) * product ( 1 <= K <= N, K /= J ) (X(J)-X(K))
!                          * product ( 1 <= K <= N, K /= I ) (Y(I)-Y(K)) ]
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!  Reference:
!
!    Robert Gregory, David Karney,
!    A Collection of Matrices for Testing Computational Algorithms,
!    Wiley, 1969,
!    ISBN: 0882756494,
!    LC: QA263.68
!
!    Nicholas Higham,
!    Accuracy and Stability of Numerical Algorithms,
!    SIAM, 1996.
!
!    Donald Knuth,
!    The Art of Computer Programming,
!    Volume 1, Fundamental Algorithms, Second Edition
!    Addison-Wesley, Reading, Massachusetts, 1973, page 36.
!
!    Olga Taussky, Marvin Marcus,
!    Eigenvalues of finite matrices,
!    in Survey of Numerical Analysis, 
!    Edited by John Todd,
!    McGraw-Hill, pages 279-313, 1962.
!
!    Evgeny Tyrtyshnikov,
!    Cauchy-Toeplitz matrices and some applications,
!    Linear Algebra and Applications,
!    Volume 149, 1991, pages 1-18.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), Y(N), vectors that determine A.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n)
  real ( kind = 8 ) y(n)

  do j = 1, n
    do i = 1, n

      if ( x(i) + y(j) == 0.0D+00 ) then
        write ( *, '(a)' ) ' '
        write ( *, '(a)' ) 'CAUCHY - Fatal error!'
        write ( *, '(a)' ) '  The denominator X(I)+Y(J) was zero'
        write ( *, '(a,i8)' ) '  for I = ', i
        write ( *, '(a,g14.6)' ) '  X(I)=', x(i)
        write ( *, '(a,i8)' ) '  and J = ', j
        write ( *, '(a,g14.6)' ) '  Y(J)=', y(j)
        stop
      end if

      a(i,j) = 1.0D+00 / ( x(i) + y(j) )

    end do
  end do

  return
end
subroutine cauchy_determinant ( n, x, y, determ )

!*****************************************************************************80
!
!! CAUCHY_DETERMINANT returns the determinant of the CAUCHY matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), Y(N), vectors that determine A.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) bottom
  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) top
  real ( kind = 8 ) x(n)
  real ( kind = 8 ) y(n)

  top = 1.0D+00
  do i = 1, n
    do j = i + 1, n
      top = top * ( x(j) - x(i) ) * ( y(j) - y(i) )
    end do
  end do

  bottom = 1.0D+00
  do j = 1, n
    do i = 1, n
      bottom = bottom * ( x(i) + y(j) )
    end do
  end do

  determ = top / bottom

  return
end
subroutine cauchy_inverse ( n, x, y, a )

!*****************************************************************************80
!
!! CAUCHY_INVERSE returns the inverse of the CAUCHY matrix.
!
!  Formula:
!
!    A(I,J) = product ( 1 <= K <= N ) [(X(J)+Y(K))*(X(K)+Y(I))] /
!      [ (X(J)+Y(I)) * product ( 1 <= K <= N, K /= J ) (X(J)-X(K))
!                    * product ( 1 <= K <= N, K /= I ) (Y(I)-Y(K)) ]
!
!  Example:
!
!    N = 5, X = ( 1, 3, 5, 8, 7 ), Y = ( 2, 4, 6, 10, 9 )
!
!       241.70      -2591.37       9136.23      10327.50     -17092.97
!     -2382.19      30405.38    -116727.19    -141372.00     229729.52
!      6451.76     -89667.70     362119.56     459459.00    -737048.81
!     10683.11    -161528.55     690983.38     929857.44   -1466576.75
!    -14960.00     222767.98    -942480.06   -1253376.00    1983696.00
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    The sum of the entries of A equals the sum of the entries of X and Y.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Donald Knuth,
!    The Art of Computer Programming,
!    Volume 1, Fundamental Algorithms, Second Edition,
!    Addison-Wesley, Reading, Massachusetts, 1973, page 36.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), Y(N), vectors that determine A.
!    The following conditions on X and Y must hold:
!
!      X(I)+Y(J) must not be zero for any I and J;
!      X(I) must never equal X(J);
!      Y(I) must never equal Y(J).
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) bot1
  real ( kind = 8 ) bot2
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) top
  real ( kind = 8 ) x(n)
  real ( kind = 8 ) y(n)
!
!  Check the data.
!
  do j = 1, n
    do i = 1, n

      if ( x(i) + y(j) == 0.0D+00 ) then
        write ( *, '(a)' ) ' '
        write ( *, '(a)' ) 'CAUCHY_INVERSE - Fatal error!'
        write ( *, '(a)' ) '  The denominator X(I)+Y(J) was zero'
        write ( *, '(a,i8)' ) '  for I = ', i
        write ( *, '(a,i8)' ) '  and J = ', j
        stop
      end if

      if ( i /= j .and. x(i) == x(j) ) then
        write ( *, '(a)' ) ' '
        write ( *, '(a)' ) 'CAUCHY_INVERSE - Fatal error!'
        write ( *, '(a)' ) '  X(I) equals X(J)'
        write ( *, '(a,i8)' ) '  for I = ', i
        write ( *, '(a,i8)' ) '  and J = ', j
        stop
      end if

      if ( i /= j .and. y(i) == y(j) ) then
        write ( *, '(a)' ) ' '
        write ( *, '(a)' ) 'CAUCHY_INVERSE - Fatal error!'
        write ( *, '(a)' ) '  Y(I) equals Y(J)'
        write ( *, '(a,i8)' ) '  for I = ', i
        write ( *, '(a,i8)' ) '  and J = ', j
        stop
      end if

    end do
  end do

  do j = 1, n
    do i = 1, n

      top = 1.0D+00
      bot1 = 1.0D+00
      bot2 = 1.0D+00

      do k = 1, n

        top = top * ( x(j) + y(k) ) * ( x(k) + y(i) )

        if ( k /= j ) then
          bot1 = bot1 * ( x(j) - x(k) )
        end if

        if ( k /= i ) then
          bot2 = bot2 * ( y(i) - y(k) )
        end if

      end do

      a(i,j) = top / ( ( x(j) + y(i) ) * bot1 * bot2 )

    end do
  end do

  return
end
subroutine cheby_diff1 ( n, a )

!*****************************************************************************80
!
!! CHEBY_DIFF1 returns the CHEBY_DIFF1 matrix.
!
!  Example:
!
!    N = 6
!
!    8.5000 -10.4721   2.8944  -1.5279   1.1056  -0.5000
!    2.6180  -1.1708  -2.0000   0.8944  -0.6810   0.2764
!   -0.7236   2.0000  -0.1708   1.6180   0.8944  -0.3820
!    0.3820  -0.8944   1.6180   0.1708  -2.0000   0.7236
!   -0.2764   0.6180  -0.8944   2.0000   1.1708  -2.6180
!    0.5000  -1.1056   1.5279  -2.8944  10.4721  -8.5000
!
!  Properties:
!
!    If N is odd, then det ( A ) = 0.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 June 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Lloyd Trefethen,
!    Spectral Methods in MATLAB,
!    SIAM, 2000, page 54.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) c(n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00
  real ( kind = 8 ) r8_mop
  real ( kind = 8 ) x(n)

  if ( n <= 0 ) then
    return
  end if

  if ( n == 1 ) then
    a(1,1) = 1.0D+00
    return
  end if

  c(1) = 2.0D+00
  c(2:n-1) = 1.0D+00
  c(n) = 2.0D+00
!
!  Get the Chebyshev points.
!
  do i = 1, n
    x(i) = cos ( pi * real ( i - 1, kind = 8 ) / real ( n - 1, kind = 8 ) )
  end do

  do j = 1, n
    do i = 1, n

      if ( i /= j ) then
        a(i,j) = r8_mop ( i + j ) * c(i) / ( c(j) * ( x(i) - x(j) ) )
      else if ( i == 1 ) then
        a(i,i) =  real ( 2 * ( n - 1 ) * ( n - 1 ) + 1, kind = 8 ) / 6.0D+00
      else if ( i == n ) then
        a(i,i) = - real ( 2 * ( n - 1 ) * ( n - 1 ) + 1, kind = 8 ) / 6.0D+00
      else
        a(i,i) = - 0.5D+00 * x(i) / ( 1.0D+00 - x(i) * x(i) ) 
      end if

    end do
  end do

  return
end
subroutine cheby_diff1_null ( n, x )

!*****************************************************************************80
!
!! CHEBY_DIFF1_NULL returns a null vector of the CHEBY_DIFF1 matrix.
!
!  Discussion:
!
!    The matrix only has a (nonzero) null vector when N is odd.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) X(N), the null vector.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) x(n)

  if ( mod ( n, 2 ) == 1 ) then
    x(1:n) = 1.0D+00
  else
    x(1:n) = 0.0D+00
  end if

  return
end
subroutine cheby_t ( n, a )

!*****************************************************************************80
!
!! CHEBY_T returns the CHEBY_T matrix.
!
!  Discussion
!
!    CHEBY_T is the Chebyshev T matrix, associated with the Chebyshev
!    "T" polynomials, or Chebyshev polynomials of the first kind.
!
!  Example:
!
!    N = 11
!
!    1  .   .    .    .    .    .    .     .   .   .
!    .  1   .    .    .    .    .    .     .   .   .
!   -1  .   2    .    .    .    .    .     .   .   .
!    . -3   .    4    .    .    .    .     .   .   .
!    1  .  -8    .    8    .    .    .     .   .   .
!    .  5   .  -20    .   16    .    .     .   .   .
!   -1  .  18    .  -48    .   32    .     .   .   .
!    . -7   .   56    . -112    .   64     .   .   .
!    1  . -32    .  160    . -256    .   128   .   .
!    .  9   . -120    .  432    . -576     . 256   .
!   -1  .  50    . -400    . 1120    . -1280   . 512
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    A is reducible.
!
!    A is lower triangular.
!
!    Each row of A sums to 1.
!
!    det ( A ) = 2^( (N-1) * (N-2) / 2 )
!
!    A is not normal: A' * A /= A * A'.
!
!    For I = 1:
!
!      LAMBDA(1) = 1
!
!    For 1 < I
!
!      LAMBDA(I) = 2^(I-2)
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    11 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  if ( n <= 0 ) then
    return
  end if

  a(1:n,1:n) = 0.0D+00

  a(1,1) = 1.0D+00

  if ( n == 1 ) then
    return
  end if

  a(2,2) = 1.0D+00

  if ( n == 2 ) then
    return
  end if

  do i = 3, n
    do j = 1, n
      if ( j == 1 ) then
        a(i,j) = - a(i-2,j)
      else
        a(i,j) = 2.0D+00 * a(i-1,j-1) - a(i-2,j)
      end if
    end do
  end do

  return
end
subroutine cheby_t_determinant ( n, determ )

!*****************************************************************************80
!
!! CHEBY_T_DETERMINANT returns the determinant of the CHEBY_T matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  real ( kind = 8 ) determ
  integer ( kind = 4 ) n
  integer ( kind = 4 ) power

  power = ( ( n - 1 ) * ( n - 2 ) ) / 2

  determ = real ( 2 ** power, kind = 8 )

  return
end
subroutine cheby_t_eigenvalues ( n, lambda )

!*****************************************************************************80
!
!! CHEBY_T_EIGENVALUES returns the eigenvalues of the CHEBY_T matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    19 August 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) i
  real ( kind = 8 ) lambda(n)

  lambda(1) = 1.0D+00

  do i = 2, n
    lambda(i) = 2.0D+00**( i - 2 )
  end do

  return
end
subroutine cheby_t_inverse ( n, a )

!*****************************************************************************80
!
!! CHEBY_T_INVERSE returns the inverse of the CHEBY_T matrix.
!
!  Example:
!
!    N = 11
!
!      1   .   .  .   .  .  .  .  .  .  .
!      .   1   .  .   .  .  .  .  .  .  .
!      1   .   1  .   .  .  .  .  .  .  .  /   2
!      .   3   .  1   .  .  .  .  .  .  .  /   4
!      3   .   4  .   1  .  .  .  .  .  .  /   8
!      .  10   .  5   .  1  .  .  .  .  .  /  16
!     10   .  15  .   6  .  1  .  .  .  .  /  32
!      .  35   . 21   .  7  .  1  .  .  .  /  64
!     35   .  56  .  28  .  8  .  1  .  .  / 128
!      . 126   . 84   . 36  .  9  .  1  .  / 256
!    126   . 210  . 120  . 45  . 10  .  1  / 512
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    12 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  if ( n <= 0 ) then
    return
  end if

  a(1:n,1:n) = 0.0D+00

  a(1,1) = 1.0D+00

  if ( n == 1 ) then
    return
  end if

  a(2,2) = 1.0D+00

  if ( n == 2 ) then
    return
  end if

  do i = 3, n
    do j = 1, n
      if ( j == 1 ) then
        a(i,j) =                          a(i-1,j+1)   / 2.0D+00
      else if ( j == 2 ) then
        a(i,j) = ( 2.0D+00 * a(i-1,j-1) + a(i-1,j+1) ) / 2.0D+00
      else if ( j < n ) then
        a(i,j) = (           a(i-1,j-1) + a(i-1,j+1) ) / 2.0D+00
      else
        a(i,j) =             a(i-1,j-1)                / 2.0D+00
      end if
    end do
  end do

  return
end
subroutine cheby_u ( n, a )

!*****************************************************************************80
!
!! CHEBY_U returns the CHEBY_U matrix.
!
!  Discussion
!
!    CHEBY_T is the Chebyshev T matrix, associated with the Chebyshev
!    "T" polynomials, or Chebyshev polynomials of the first kind.
!
!  Example:
!
!    N = 11
!
!    1  .   .    .    .    .    .     .     .   .    .
!    .  2   .    .    .    .    .     .     .   .    .
!   -1  .   4    .    .    .    .     .     .   .    .
!    . -4   .    8    .    .    .     .     .   .    .
!    1  . -12    .   16    .    .     .     .   .    .
!    .  6   .  -32    .   32    .     .     .   .    .
!   -1  .  24    .  -80    .   64     .     .   .    .
!    . -8   .   80    . -192    .   128     .   .    .
!    1  . -40    .  240    . -448     .   256   .    .
!    . 10   . -160    .  672    . -1024     . 512    .
!   -1  .  60    . -560    . 1792     . -2304   . 1024
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    A is generally not normal: A' * A /= A * A'.
!
!    A is lower triangular.
!
!    A is reducible.
!
!    The entries of row N sum to N.
!
!    det ( A ) = 2^((N*(N-1))/2).
!
!    LAMBDA(I) = 2^(I-1)
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    11 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  if ( n <= 0 ) then
    return
  end if

  a(1:n,1:n) = 0.0D+00

  a(1,1) = 1.0D+00

  if ( n == 1 ) then
    return
  end if

  a(2,2) = 2.0D+00

  if ( n == 2 ) then
    return
  end if

  do i = 3, n
    do j = 1, n
      if ( j == 1 ) then
        a(i,j) = - a(i-2,j)
      else
        a(i,j) = 2.0D+00 * a(i-1,j-1) - a(i-2,j)
      end if
    end do
  end do

  return
end
subroutine cheby_u_determinant ( n, determ )

!*****************************************************************************80
!
!! CHEBY_U_DETERMINANT returns the determinant of the CHEBY_U matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  real ( kind = 8 ) determ
  integer ( kind = 4 ) n
  integer ( kind = 4 ) power

  power = ( n * ( n - 1 ) ) / 2

  determ = real ( 2 ** power, kind = 8 )

  return
end
subroutine cheby_u_eigenvalues ( n, lambda )

!*****************************************************************************80
!
!! CHEBY_U_EIGENVALUES returns the eigenvalues of the CHEBY_U matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    19 August 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) i
  real ( kind = 8 ) lambda(n)

  do i = 1, n
    lambda(i) = 2.0D+00**( i - 1 )
  end do

  return
end
subroutine cheby_u_inverse ( n, a )

!*****************************************************************************80
!
!! CHEBY_U_INVERSE returns the inverse of the CHEBY_U matrix.
!
!  Example:
!
!    N = 11
!
!      1   .   .  .   .  .  .  .  .  .  .
!      .   1   .  .   .  .  .  .  .  .  .  /    2
!      1   .   1  .   .  .  .  .  .  .  .  /    4
