subroutine cycol_random ( m, n, k, seed, a )

!*****************************************************************************80
!
!! CYCOL_RANDOM returns the CYCOL_RANDOM matrix.
!
!  Discussion:
!
!    This matrix is an example of a K-cyclical column matrix.
!
!    A K-cyclical column matrix has the property that the first K columns
!    of the matrix are repeated.  Thus,
!
!      A(I,J) = arbitrary, for J <= K.
!      A(I,J+L*K) = A(I,J) for L = 2, 3, ...
!
!  Example:
!
!    K = 2, M = 4, N = 5
!
!    0.1  0.2  0.1  0.2  0.1
!    0.3  0.4  0.3  0.4  0.3
!    0.7  0.2  0.7  0.2  0.7
!    0.1  0.8  0.1  0.8  0.1
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A has rank at most K.
!
!    A can lead to underflow problems for Gauss elimination.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    NA Digest,
!    Volume 89, Issue 3, 22 January 1989.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, integer ( kind = 4 ) K, the number of distinct columns to form.
!
!    Input/output, integer ( kind = 4 ) SEED, a seed for the random 
!    number generator.
!
!    Output, real ( kind = 8 ) A(M,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) j
  integer ( kind = 4 ) jj
  integer ( kind = 4 ) k
  integer ( kind = 4 ) seed

  call r8mat_uniform_01 ( m, k, seed, a )

  do j = k + 1, n
    jj = mod ( j-1, k ) + 1
    a(1:m,j) = a(1:m,jj)
  end do

  return
end
subroutine daub2 ( n, a )

!*****************************************************************************80
!
!! DAUB2 returns the DAUB2 matrix.
!
!  Discussion:
!
!    The DAUB2 matrix is the Daubechies wavelet transformation matrix 
!    with 2 coefficients.
!
!    The DAUB2 matrix is also known as the Haar matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 July 2011
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!    N must be at least 2 and a multiple of 2.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) c0
  real ( kind = 8 ) c1
  integer ( kind = 4 ) i

  if ( n < 2 .or. mod ( n, 2 ) /= 0 ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'DAUB2 - Fatal error!'
    write ( *, '(a)' ) '  N must be at least 2 and a multiple of 2.'
    stop
  end if

  a(1:n,1:n) = 0.0D+00

  c0 = sqrt ( 2.0D+00 ) / 2.0D+00
  c1 = sqrt ( 2.0D+00 ) / 2.0D+00

  do i = 1, n - 1, 2

    a(i,i)     =   c0
    a(i,i+1)   =   c1

    a(i+1,i)   =   c1
    a(i+1,i+1) = - c0

  end do

  return
end
