  determ = + 1.0D+00

  return
end
subroutine daub2_inverse ( n, a )

!*****************************************************************************80
!
!! DAUB2_INVERSE returns the inverse of the DAUB2 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 July 2011
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
