subroutine dif2_eigenvalues ( n, lambda )

!*****************************************************************************80
!
!! DIF2_EIGENVALUES returns the eigenvalues of the DIF2 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) angle
  real ( kind = 8 ) lambda(n)
  integer ( kind = 4 ) i
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00

  do i = 1, n
    angle = real ( i, kind = 8 ) * pi / real ( 2 * ( n + 1 ), kind = 8 )
    lambda(i) = 4.0D+00 * ( sin ( angle ) )**2
  end do

  return
end
subroutine dif2_inverse ( n, a )

!*****************************************************************************80
!
!! DIF2_INVERSE returns the inverse of the DIF2 matrix.
!
!  Formula:
!
!    if ( I <= J )
!      A(I,J) = I * (N-J+1) / (N+1)
!    else
!      A(I,J) = J * (N-I+1) / (N+1)
!
!  Example:
!
!    N = 4
!
!            4 3 2 1
!    (1/5) * 3 6 4 2
!            2 4 6 3
!            1 2 3 4
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    det ( A ) = 1 / ( N + 1 ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, n
      if ( i <= j ) then
        a(i,j) = real ( i * ( n - j + 1 ), kind = 8 ) / real ( n + 1, kind = 8 )
      else
        a(i,j) = real ( j * ( n - i + 1 ), kind = 8 ) / real ( n + 1, kind = 8 )
      end if
    end do
  end do

  return
end
subroutine dif2_plu ( n, p, l, u )

!*****************************************************************************80
!
!! DIF2_PLU returns the PLU factors of the DIF2 matrix.
!
!  Discussion:
!
!    A = P * L * U
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    12 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) P(N,N), L(N,N), U(N,N), the pivot
!    matrix, the unit lower triangular matrix, and the upper
!    triangular matrix that form the PLU factoriztion of A.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) l(n,n)
  real ( kind = 8 ) p(n,n)
  real ( kind = 8 ) u(n,n)

  do j = 1, n
    do i = 1, n
      if ( i == j ) then
        p(i,j) = 1.0D+00
      else
        p(i,j) = 0.0D+00
      end if
    end do
  end do

  l(1:n,1:n) = 0.0D+00
  do i = 1, n
    l(i,i) = 1.0D+00
  end do
  do i = 1, n - 1
    l(i+1,i) = - real ( i, kind = 8 ) / real ( i + 1, kind = 8 )
  end do

  u(1:n,1:n) = 0.0D+00
  do i = 1, n
    u(i,i) = real ( i + 1, kind = 8 ) / real ( i, kind = 8 )
  end do
  do i = 1, n - 1
    u(i,i+1) = -1.0D+00
  end do

  return
end
subroutine dif2_rhs ( m, k, b )

!*****************************************************************************80
!
!! DIF2_RHS returns the DIF2 right hand side.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    27 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, the row dimension.
!
!    Input, integer ( kind = 4 ) K, the column dimension ( should be 2).
!
!    Output, real ( kind = 8 ) B(M,K), the right hand side matrix.
!
  implicit none

  integer k
  integer m

  real ( kind = 8 ) b(m,k)

  b(1    ,1) = 1.0D+00
  b(2:m-1,1) = 0.0D+00
  b(  m,  1) = 1.0D+00

  b(1:m-1,2) = 0.0D+00
  b(  m,  2) = real ( m + 1, kind = 8 )

  return
end
subroutine dif2_right ( n, a )

!*****************************************************************************80
!
!! DIF2_RIGHT returns the right eigenvectors of the DIF2 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the right eigenvector matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) angle
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00

  do j = 1, n
    do i = 1, n
      angle = real ( i * j, kind = 8 ) * pi / real ( n + 1, kind = 8 )
      a(i,j) = sqrt ( 2.0D+00 / real ( n + 1, kind = 8 ) ) * sin ( angle )
    end do
  end do

  return
end
subroutine dif2_solution ( n, k, x )

!*****************************************************************************80
!
!! DIF2_SOLUTION returns the DIF2 solution matrix.
