end
subroutine eberlein_eigenvalues ( alpha, n, lambda )

!*****************************************************************************80
!
!! EBERLEIN_EIGENVALUES returns the eigenvalues of the EBERLEIN matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    26 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the parameter.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  real ( kind = 8 ) lambda(n)

  do i = 1, n
    lambda(i) = - real ( i - 1, kind = 8 ) * ( alpha + real ( i, kind = 8 ) )
  end do

  return
end
