subroutine frank_eigenvalues ( n, lambda )

!*****************************************************************************80
!
!! FRANK_EIGENVALUES returns the eigenvalues of the FRANK matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    16 August 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none


  integer ( kind = 4 ) n

  integer ( kind = 4 ) i
  real ( kind = 8 ) lambda(n)
  real ( kind = 8 ) x
  real ( kind = 8 ) xtab(n)

  call hermite_roots ( n, xtab )

  do i = 1, n
    if ( 2 * i < n + 1 ) then
      x = xtab(i)
      x = x * x + 1.0D+00 - sqrt ( x * x * ( x * x + 2.0D+00 ) )
    else if ( 2 * i == n + 1 ) then
      x = 1.0D+00
    else
      x = xtab(i)
      x = x * x + 1.0D+00 + sqrt ( x * x * ( x * x + 2.0D+00 ) )
    end if

    lambda(i) = x

  end do

  return
end
subroutine frank_inverse ( n, a )

!*****************************************************************************80
!
!! FRANK_INVERSE returns the inverse of the FRANK matrix.
!
!  Formula:
!
!    if ( I = J-1 ) then
!      A(I,J) = -1
!    else if ( I = J ) then
!      if ( I = 1 ) then
!        A(I,J) = 1
!      else
!        A(I,J) = N + 2 - I
!    else if ( J < I ) then
!      A(I,J) = - (N+1-I) * A(I-1,J)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    N = 5
!
!     1  -1  0  0  0
!    -4   5 -1  0  0
!    12 -15  4 -1  0
!   -24  30 -8  3 -1
!    24 -30  8 -3  2
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is lower Hessenberg.
!
!    det ( A ) = 1.
!
!    A is unimodular.
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do i = 1, n
    do j = 1, n

      if ( i == j - 1 ) then
        a(i,j) = - 1.0D+00
      else if ( i == j ) then
        if ( i == 1 ) then
          a(i,j) = 1.0D+00
        else
          a(i,j) = real ( n + 2 - i, kind = 8 )
        end if
      else if ( j < i ) then
        a(i,j) = - real ( n + 1 - i, kind = 8 ) * a(i-1,j)
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine frank_rhs ( m, k, b )

!*****************************************************************************80
!
!! FRANK_RHS returns the FRANK right hand side.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    27 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, the row dimension.
!
!    Input, integer ( kind = 4 ) K, the column dimension ( should be 2).
!
!    Output, real ( kind = 8 ) B(M,K), the right hand side matrix.
!
  implicit none

  integer k
  integer m

  real ( kind = 8 ) b(m,k)
  integer ( kind = 4 ) i

  b(1:m,1) = 1.0D+00

  b(1,2) = real ( ( m * ( m + 1 ) ) / 2, kind = 8 )
  do i = 2, m
    b(i,2) = real ( ( ( m + 1 - i ) * ( m + 4 - i ) ) / 2, kind = 8 )
  end do

  return
end
subroutine frank_solution ( n, k, x )

!*****************************************************************************80
!
!! FRANK_SOLUTION returns the FRANK solution matrix.
!
