subroutine i4_factor ( n, maxfactor, nfactor, factor, power, nleft )

!*****************************************************************************80
!
!! I4_FACTOR factors an integer into prime factors.
!
!  Formula:
!
!    N = NLEFT * product ( 1 <= I <= NFACTOR ) FACTOR(I)^POWER(I).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the integer to be factored.  N may be 
!    positive, negative, or 0.
!
!    Input, integer ( kind = 4 ) MAXFACTOR, the maximum number of prime factors
!    for which storage has been allocated.
!
!    Output, integer ( kind = 4 ) NFACTOR, the number of prime factors of N 
!    discovered by the routine.
!
!    Output, integer ( kind = 4 ) FACTOR(MAXFACTOR), the prime factors of N.
!
!    Output, integer ( kind = 4 ) POWER(MAXFACTOR).  POWER(I) is the power of
!    the FACTOR(I) in the representation of N.
!
!    Output, integer ( kind = 4 ) NLEFT, the factor of N that the routine could
!    not divide out.  If NLEFT is 1, then N has been completely factored.
!    Otherwise, NLEFT represents factors of N involving large primes.
!
  implicit none

  integer ( kind = 4 ) maxfactor

  integer ( kind = 4 ) factor(maxfactor)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) maxprime
  integer ( kind = 4 ) n
  integer ( kind = 4 ) nleft
  integer ( kind = 4 ) nfactor
  integer ( kind = 4 ) p
  integer ( kind = 4 ) power(maxfactor)
  integer ( kind = 4 ) prime

  nfactor = 0

  factor(1:maxfactor) = 0
  power(1:maxfactor) = 0

  nleft = n

  if ( n == 0 ) then
    return
  end if

  if ( abs ( n ) == 1 ) then
    nfactor = 1
    factor(1) = 1
    power(1) = 1
    return
  end if
!
!  Find out how many primes we stored.
!
  maxprime = prime ( -1 )
!
!  Try dividing the remainder by each prime.
!
  do i = 1, maxprime

    p = prime ( i )

    if ( mod ( abs ( nleft ), p ) == 0 ) then

      if ( nfactor < maxfactor ) then

        nfactor = nfactor + 1
        factor(nfactor) = p

        do

          power(nfactor) = power(nfactor) + 1
          nleft = nleft / p

          if ( mod ( abs ( nleft ), p ) /= 0 ) then
            exit
          end if

        end do

        if ( abs ( nleft ) == 1 ) then
          exit
        end if

      end if

    end if

  end do

  return
end
function i4_is_prime ( n )

!*****************************************************************************80
!
!! I4_IS_PRIME reports whether an integer is prime.
!
!  Method:
!
!    A simple, unoptimized sieve of Erasthosthenes is used to
!    check whether N can be divided by any integer between 2
!    and sqrt(N).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    29 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the integer to be tested.
!
!    Output, logical I4_IS_PRIME, is TRUE if N is prime, and FALSE
!    otherwise.  Note that negative numbers and 0 are not
!    considered prime.
!
  implicit none

  integer ( kind = 4 ) i
  logical i4_is_prime
  integer ( kind = 4 ) n
  integer ( kind = 4 ) nhi

  if ( n <= 0 ) then
    i4_is_prime = .false.
    return
  end if

  if ( n <= 3 ) then
    i4_is_prime = .true.
    return
  end if

  nhi = int ( sqrt ( real ( n, kind = 8 ) ) )

  do i = 2, nhi
    if ( mod ( n, i ) == 0 ) then
      i4_is_prime = .false.
      return
    end if
  end do

  i4_is_prime = .true.

  return
end
function i4_log_10 ( x )

!*****************************************************************************80
!
!! I4_LOG_10 returns the integer part of the logarithm base 10 of abs ( X ).
!
!  Example:
!
!    if 0.01 <  X <= 0.1   I4_LOG_10(X) = -1,
!    if 0.1  <  X <= 1     I4_LOG_10(X) = 0,
!    if 1    <= X <  10    I4_LOG_10(X) = 0,
!    if 10   <= X <  100   I4_LOG_10(X) = 1,
!    if 100  <= X <  1000  I4_LOG_10(X) = 2.
!
!    To get the exact logarithm, base 10, of a positive number, use
!    the intrinsic LOG10 function, as in Y = LOG10 ( X )
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    30 June 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) X, the number for which the whole number part
!    of the logarithm base 10 of the absolute value is desired.
!
!    Output, integer ( kind = 4 ) I4_LOG_10, the whole number part of the
!    logarithm base 10 of the absolute value of X.
!
!    For positive I4_LOG_10(X), it should be true that
!
!      10^I4_LOG_10(X) <= abs ( X ) < 10^(I4_LOG_10(X)+1).
!
!    However, if X = 0, then I4_LOG_10 = 0.
!
  implicit none

  integer ( kind = 4 ) i4_log_10
  real ( kind = 8 ) temp
  integer ( kind = 4 ) x

  i4_log_10 = 0

  if ( x == 0 ) then
    return
  end if

  if ( x == 1 .or. x == -1 ) then
    return
  end if

  temp = abs ( real ( x, kind = 8 ) )

  do while ( 10.0D+00 <= temp )
    temp = temp / 10.0D+00
    i4_log_10 = i4_log_10 + 1
  end do

  do while ( 10.0D+00 * temp <= 1.0D+00 )
    temp = temp * 10.0D+00
    i4_log_10 = i4_log_10 - 1
  end do

  return
end
function i4_modp ( i, j )

!*****************************************************************************80
!
!! I4_MODP returns the nonnegative remainder of integer division.
!
!  Discussion:
!
!    The MOD function computes a result with the same sign as the
!    quantity being divided.  Thus, suppose you had an angle A,
!    and you wanted to ensure that it was between 0 and 360.
!    Then mod(A,360) would do, if A was positive, but if A
!    was negative, your result would be between -360 and 0.
!
!    On the other hand, I4_MODP(A,360) is between 0 and 360, always.
!
!  Formula:
!
!    If
!      NREM = I4_MODP ( I, J )
!      NMULT = ( I - NREM ) / J
!    then
!      I = J * NMULT + NREM
!    where NREM is always nonnegative.
!
!  Example:
!
!        I     J     MOD  I4_MODP    Factorization
!
!      107    50       7       7    107 =  2 *  50 + 7
!      107   -50       7       7    107 = -2 * -50 + 7
!     -107    50      -7      43   -107 = -3 *  50 + 43
!     -107   -50      -7      43   -107 =  3 * -50 + 43
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 March 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) I, the number to be divided.
!
!    Input, integer ( kind = 4 ) J, the number that divides I.
!
!    Output, integer ( kind = 4 ) I4_MODP, the nonnegative remainder when I is
!    divided by J.
!
  implicit none

  integer ( kind = 4 ) i
  integer ( kind = 4 ) i4_modp
  integer ( kind = 4 ) j

  if ( j == 0 ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'I4_MODP - Fatal error!'
    write ( *, '(a,i8)' ) '  I4_MODP ( I, J ) called with J = ', j
    stop
  end if

  i4_modp = mod ( i, j )

  if ( i4_modp < 0 ) then
    i4_modp = i4_modp + abs ( j )
  end if

  return
end
