subroutine ijfact1 ( n, a )

!*****************************************************************************80
!
!! IJFACT1 returns the IJFACT1 matrix.
!
!  Discussion:
!
!    This matrix is sometimes called the IJ-factorial matrix.
!
!  Formula:
!
!    A(I,J) = (I+J)!
!
!  Example:
!
!    N = 4
!
!     2   6   24   120
!     6  24  120   720
!    24 120  720  5040
!   120 720 5040 40320
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    A is a Hankel matrix: constant along anti-diagonals.
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    MJC Gover,
!    The explicit inverse of factorial Hankel matrices,
!    Department of Mathematics, University of Bradford, 1993.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) fact
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ihi
  integer ( kind = 4 ) ilo
  integer ( kind = 4 ) k

  fact = 1

  do k = 2, 2 * n

    fact = fact * k
    ilo = max ( 1, k - n )
    ihi = min ( n, k - 1 )

    do i = ilo, ihi
      a(i,k-i) = real ( fact, kind = 8 )
    end do

  end do

  return
end
subroutine ijfact1_determinant ( n, determ )

!*****************************************************************************80
!
!! IJFACT1_DETERMINANT returns the determinant of the IJFACT1 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ip1
  integer ( kind = 4 ) nmi
  integer ( kind = 4 ) np1
  real ( kind = 8 ) r8_factorial

  determ = 1.0D+00

  do i = 1, n - 1
    ip1 = i + 1
    nmi = n - i
    determ = determ * r8_factorial ( ip1 ) * r8_factorial ( nmi )
  end do

  np1 = n + 1
  determ = determ * r8_factorial ( np1 )

  return
end
subroutine ijfact2 ( n, a )

!*****************************************************************************80
!
!! IJFACT2 returns the IJFACT2 matrix.
!
!  Formula:
!
!    A(I,J) = 1 / ( (I+J)! )
!
!  Example:
!
!    N = 4
!
!   1/2   1/6   1/24   1/120
!   1/6   1/24  1/120  1/720
!   1/24  1/120 1/720  1/5040
!   1/120 1/720 1/5040 1/40320
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    A is a Hankel matrix: constant along anti-diagonals.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    MJC Gover,
!    The explicit inverse of factorial Hankel matrices,
!    Department of Mathematics, University of Bradford, 1993.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) fact
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ihi
  integer ( kind = 4 ) ilo
  integer ( kind = 4 ) ipj
  integer ( kind = 4 ) j

  fact = 1.0D+00

  do ipj = 2, 2 * n

    fact = fact * ipj
    ilo = max ( 1, ipj - n )
    ihi = min ( n, ipj - 1 )

    do i = ilo, ihi
      a(i,ipj-i) = 1.0D+00 / fact
    end do

  end do

  return
end
subroutine ijfact2_determinant ( n, determ )

!*****************************************************************************80
!
!! IJFACT2_DETERMINANT returns the determinant of the IJFACT2 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) np1pi
  real ( kind = 8 ) r8_factorial

  determ = 1.0D+00

  do i = 0, n - 1
    np1pi = n + 1 + i
    determ = determ * r8_factorial ( i ) / r8_factorial ( np1pi )
  end do

  if ( mod ( ( n * ( n - 1 ) ) / 2, 2 ) /= 0 ) then
    determ = - determ
  end if

  return
end
subroutine ill3 ( a )

!*****************************************************************************80
!
!! ILL3 returns the ILL3 matrix.
!
!  Discussion:
!
!    This is a 3x3 ill-conditioned matrix.
!
!  Formula:
!
!    -149  -50 -154
!     537  180  546
!     -27   -9  -25
!
!  Properties:
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    The eigenvalues are ( 1, 2, 3 ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    17 July 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Joan Westlake,
!    A Handbook of Numerical Matrix Inversion and Solution of 
!    Linear Equations,
!    John Wiley, 1968,
!    ISBN13: 978-0471936756,
!    LC: QA263.W47.
!
!  Parameters:
!
!    Output, real ( kind = 8 ) A(3,3), the matrix.
!
  implicit none

  real ( kind = 8 ) a(3,3)
  real ( kind = 8 ), dimension ( 3, 3 ), save :: a_save = reshape ( (/ &
  -149.0D+00,  537.0D+00, -27.0D+00, &
   -50.0D+00,  180.0D+00,  -9.0D+00, &
  -154.0D+00,  546.0D+00, -25.0D+00 /), (/ 3, 3 /) )

  call r8mat_copy ( 3, 3, a_save, a )

  return
end
