   0.909137290098421D+00, &
   0.101015254452291D+00, &
   0.316227766017190D+00, &
  -0.948683298050396D+00, &
  -0.000000000000407D+00 /), (/ 3, 3 /) )

  call r8mat_copy ( 3, 3, a_save, a )

  return
end
subroutine indicator ( m, n, a )

!*****************************************************************************80
!
!! INDICATOR returns the INDICATOR matrix.
!
!  Discussion:
!
!    The indicator matrix has entries which reveal their row and column.
!
!    That makes it useful for testing printing algorithms, or schemes
!    which select a submatrix, or extract a particular value from a
!    matrix, or in general, any situation in which it would be useful
!    to know the origin of a value that was extracted from a matrix.
!
!  Example:
!
!    M = 3, N = 4
!
!    A =
!
!    11 12 13 14
!    21 22 23 24
!    31 32 33 34
!
!  Properties:
!
!    For a particular A, no two entries are equal.
!
!    A is generally not symmetric: A' /= A.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    29 April 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix A.
!
!    Output, real ( kind = 8 ) A(M,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i4_log_10
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k

  k = 10**( i4_log_10 ( n ) + 1 )

  do j = 1, n
    do i = 1, m
      a(i,j) = real ( i * k + j, kind = 8 )
    end do
  end do

  return
end
subroutine integration ( alpha, n, a )

!*****************************************************************************80
!
!! INTEGRATION returns the INTEGRATION matrix.
!
!  Formula:
!
!    if ( I = J )
!      A(I,J) = 1
!    else if ( J = I + 1 )
!      A(I,J) = ALPHA / I
!    else
!      A(I,J) = 0
!
!  Example:
!
!    N = 5,
!    ALPHA = 2,
!
!    1  2   0   0   0
!    0  1  2/2  0   0
!    0  0   1  2/3  0
!    0  0   0   1  2/4
!    0  0   0   0   1
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is unit upper triangular.
!
!    A is bidiagonal.
!
!    Because A is bidiagonal, it has property A (bipartite).
!
!    A is nonsingular.
!
!    det ( A ) = 1.
!
!    A is unimodular.
!
!    LAMBDA(1:N) = 1.
!
!    The INTEGRATION matrix is a special case of the BIS matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 February 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the scalar which defines the first 
!    superdiagonal of the matrix.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, n

      if ( j == i ) then
        a(i,j) = 1.0D+00
      else if ( j == i + 1 ) then
        a(i,j) = alpha / real ( i, kind = 8 )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
