subroutine integration_inverse ( alpha, n, a )

!*****************************************************************************80
!
!! INTEGRATION_INVERSE returns the inverse of the INTEGRATION matrix.
!
!  Formula:
!
!    if ( I = J )
!      A(I,J) = 1
!    else if ( I < J )
!      A(I,J) = (-ALPHA)^(J-I) / (I*...*J-1)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 2, N = 5
!
!    1 -2   2  -4/3   2/3
!    0  1  -1   2/3  -1/3
!    0  0   1  -2/3   1/3
!    0  0   0    1   -1/2
!    0  0   0    0     1
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is unit upper triangular.
!
!    A is nonsingular.
!
!    det ( A ) = 1.
!
!    A is unimodular.
!
!    LAMBDA(1:N) = 1.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 February 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the scalar which defines the first
!    superdiagonal of the matrix.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i4_pochhammer
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, n

      if ( j == i ) then
        a(i,j) = 1.0D+00
      else if ( i < j ) then
        a(i,j) = ( -alpha ) ** ( j - i ) &
          / real ( i4_pochhammer ( i, j-1 ), kind = 8 )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine invol ( n, a )

!*****************************************************************************80
!
!! INVOL returns the INVOL matrix.
!
!  Discussion:
!
!    This matrix is an example of an involutional matrix.
!
!  Formula:
!
!    A(I,J) = 1 / ( I + J - 1 )
!
!    Set D = -N
!
!    Multiply column 1 of A by D.
!
!    For I = 1 to N-1
!      D = -(N+I)*(N-I)*D/(I*I)
!      Multiply row I + 1 by D.
!    End
!
!  Example:
!
!    N = 5
!
!       -5     0.5     0.33     0.25    0.2
!     -300    40.0    30.00    24.00   20.0
!     1050  -157.5  -126.00  -105.00  -90.0
!    -1400   224.0   186.66   160.00  140.0
!      630  -105.0   -90.00   -78.75  -70.0
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is involutional: A * A = I.
!
!    det ( A ) = +1 or -1.
!
!    A is unimodular.
!
!    The matrices:
!      B = 1/2 ( I - A )
!    and
!      C = 1/2 ( I + A )
!    are idempotent, that is, B * B = B, and C * C = C.
!
!    A is ill-conditioned.
!
!    A is a diagonally scaled version of the Hilbert matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    23 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Alston Householder, John Carpenter,
!    The singular values of involutory and of idempotent matrices,
!    Numerische Mathematik,
!    Volume 5, 1963, pages 234-237.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) d
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, n
      a(i,j) = 1.0D+00 / real ( i + j - 1, kind = 8 )
    end do
  end do

  a(1:n,1) = - real ( n, kind = 8 ) * a(1:n,1)

  d = - real ( n, kind = 8 )
  do i = 1, n - 1
    d = - real ( n + i, kind = 8 ) * real ( n - i, kind = 8 ) * d &
      / real ( i * i, kind = 8 )
    a(i+1,1:n) = d * a(i+1,1:n)
  end do

  return
end
subroutine invol_determinant ( n, determ )

!*****************************************************************************80
!
!! INVOL_DETERMINANT returns the determinant of the INVOL matrix.
!
!  Licensing:
!
