  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ

  if ( mod ( n, 4 ) == 0 .or. mod ( n, 4 ) == 3 ) then
    determ = 1.0D+00
  else
    determ = -1.0D+00
  end if

  return
end
