subroutine invol_random ( n, rank, seed, a )

!*****************************************************************************80
!
!! INVOL_RANDOM returns the INVOL_RANDOM matrix.
!
!  Discussion:
!
!    This matrix is a random involutional matrix.
!
!  Properties:
!
!    A is nonsingular.
!
!    A is involutional: A * A = I
!
!    det ( A ) = +1 or -1.
!
!    A is unimodular.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 April 2000
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Alston Householder, John Carpenter,
!    The singular values of involutory and of idempotent matrices,
!    Numerische Mathematik,
!    Volume 5, 1963, pages 234-237.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, integer ( kind = 4 ) RANK, the rank of the idempotent matrix
!    used to generate A.  Setting RANK = 0 or N will yield
!    a multiple of the identity.  Intermediate values will give
!    more interesting results.
!
!    Input/output, integer ( kind = 4 ) SEED, a seed for the random number
!    generator.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) rank
  integer ( kind = 4 ) seed

  call idem_random ( n, rank, seed, a )

  do j = 1, n
    do i = 1, n
      if ( i == j ) then
        a(i,j) = 1.0D+00 - 2.0D+00 * a(i,j)
      else
        a(i,j) = - 2.0D+00 * a(i,j)
      end if
    end do
  end do

  return
end
subroutine jacobi ( m, n, a )

!*****************************************************************************80
!
!! JACOBI returns the JACOBI matrix.
!
!  Formula:
!
!    if ( J = I - 1 )
!      A(I,J) = 0.5 * sqrt ( ( 4 * J^2 ) / ( 4 * J^2 - 1 ) )
!    else if ( J = I + 1 ) then
!      A(I,J) = 0.5 * sqrt ( ( 4 * (J-1)^2 ) / ( 4 * (J-1)^2 - 1 ) )
!    else
!      A(I,J) = 0
!
!  Example:
!
!    M = 4, N = 4
!
!    0            0.577350269  0            0
!    0.577350269  0            0.516397779  0
!    0            0.516397779  0            0.507092553
!    0            0            0.507092553  0
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    A has a zero diagonal.
!
!    The eigenvalues of A are the zeros of the Legendre polynomial
!    of degree N.  They lie symmetrically in [-1,1], and are also
!    the nodes of Gauss-Legendre quadrature.  For the case of N = 4,
!    these eigenvalues are
!
!      [ -0.861136312, -0.339981044, +0.339981044, +0.861136312 ].
!
!    It follows that A is singular when N is odd.
!
!    The J-th Gauss-Legendre weight is twice the square of the first
!    component of the J-th eigenvector of A.  For the case of N = 4,
!    the eigenvector matrix is:
!
!      -0.417046     -0.571028     -0.571028    0.417046
!       0.622037      0.336258     -0.336258    0.622038
!      -0.571028      0.417046      0.417046    0.571028
!       0.336258     -0.622037      0.622038    0.336258
!
!    and the corresponding weights are
!
!      [ 0.347854845, 0.652145155, 0.652145155, 0.347854845 ]
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Lloyd Trefethen, David Bau,
!    Numerical Linear Algebra,
!    SIAM, 1997, pages 287-292.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(M,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, m

      if ( j == i - 1 ) then
        a(i,j) = 0.5D+00 * sqrt ( real ( 4 * j * j, kind = 8 ) &
          / real ( 4 * j * j - 1, kind = 8 ) )
      else if ( j == i + 1 ) then
        a(i,j) = 0.5D+00 * sqrt ( real ( 4 * ( j - 1 )**2, kind = 8 ) &
          / real ( 4 * ( j - 1 )**2 - 1, kind = 8 ) )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine jacobi_determinant ( n, determ )

!*****************************************************************************80
!
!! JACOBI_DETERMINANT returns the determinant of the JACOBI matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    12 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ
  real ( kind = 8 ) lambda(n)

  if ( mod ( n, 2 ) == 1 ) then
  
    determ = 0.0D+00
    
  else
  
    call legendre_zeros ( n, lambda )
  
    determ = product ( lambda(1:n) )

  end if
  
  return
end
