subroutine jacobi_iterate ( n, a, lambda, x )

!*****************************************************************************80
!
!! JACOBI_ITERATE applies the Jacobi eigenvalue iteration to a symmetric matrix.
!
!  Discussion:
!
!    I had to modify the code, in order to avoid cases where the
!    off-diagonal element was not exactly zero, but very very close.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 June 2003
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), a symmetric matrix.
!    On output, the matrix has been overwritten by an approximately
!    diagonal matrix, with the eigenvalues on the diagonal.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the computed eigenvalues.
!
!    Output, real ( kind = 8 ) X(N,N), the computed eigenvector matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) b(n,n)
  real ( kind = 8 ) c
  real ( kind = 8 ), parameter :: eps = 0.00001D+00
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  integer ( kind = 4 ) it
  integer ( kind = 4 ), parameter :: it_max = 100
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) lambda(n)
  real ( kind = 8 ) norm_fro
  real ( kind = 8 ) r8mat_norm_fro
  real ( kind = 8 ) s
  real ( kind = 8 ) sum2
  real ( kind = 8 ) t
  real ( kind = 8 ) t1
  real ( kind = 8 ) t2
  real ( kind = 8 ) u
  real ( kind = 8 ) x(n,n)

  call r8mat_is_symmetric ( n, n, a, error_frobenius )

  if ( eps < error_frobenius ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'JACOBI_ITERATE - Fatal error!'
    write ( *, '(a)' ) '  The input matrix is not symmetric.'
    stop
  end if

  b(1:n,1:n) = a(1:n,1:n)

  norm_fro = r8mat_norm_fro ( n, n, b )

  call identity ( n, n, x )

  do it = 1, it_max

    do i = 1, n
      do j = 1, i - 1

        if ( eps * norm_fro < abs ( b(i,j) ) + abs ( b(j,i) ) ) then

          u = ( b(j,j) - b(i,i) ) / ( b(i,j) + b(j,i) )

          t = sign ( 1.0D+00, u ) / ( abs ( u ) + sqrt ( u * u + 1.0D+00 ) )
          c = 1.0D+00 / sqrt ( t * t + 1.0D+00 )
          s = t * c
!
!  A -> A * Q.
!
          do k = 1, n
            t1 = b(i,k)
            t2 = b(j,k)
            b(i,k) = t1 * c - t2 * s
            b(j,k) = t1 * s + t2 * c
          end do
!
!  A -> Q' * A
!
          do k = 1, n
            t1 = b(k,i)
            t2 = b(k,j)
            b(k,i) = c * t1 - s * t2
            b(k,j) = s * t1 + c * t2
          end do
!
!  X -> Q' * X
!
          do k = 1, n
            t1 = x(k,i)
            t2 = x(k,j)
            x(k,i) = c * t1 - s * t2
            x(k,j) = s * t1 + c * t2
          end do

        end if

      end do
    end do
!
!  Test the size of the off-diagonal elements.
!
    sum2 = 0.0D+00
    do i = 1, n
      sum2 = sum2 + sum ( abs ( b(i,1:i-1) ) )
    end do

    if ( sum2 <= eps * ( norm_fro + 1.0D+00 ) ) then
      exit
    end if

  end do

  call r8mat_diag_get_vector ( n, b, lambda )

  return
end
subroutine jacobi_symbol ( q, p, j )

!*****************************************************************************80
!
!! JACOBI_SYMBOL evaluates the Jacobi symbol (Q/P).
!
!  Discussion:
!
!    If P is prime, then
!      Jacobi Symbol (Q/P) = Legendre Symbol (Q/P)
!    Else let P have the prime factorization
!      P = product ( 1 <= I <= N ) P(I)^E(I)
!    Then
!      Jacobi Symbol (Q/P) =
!        product ( 1 <= I <= N ) Legendre Symbol (Q/P(I))^E(I)
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    30 June 2000
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Daniel Zwillinger,
!    CRC Standard Mathematical Tables and Formulae,
!    30th Edition,
!    CRC Press, 1996, pages 86-87.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) Q, an integer whose Jacobi symbol with
!    respect to P is desired.
!
!    Input, integer ( kind = 4 ) P, the number with respect to which the Jacobi
!    symbol of Q is desired.  P should be 2 or greater.
!
!    Output, integer ( kind = 4 ) L, the Jacobi symbol (Q/P).
!    Ordinarily, L will be -1, 0 or 1.
!    -2, not enough factorization space.
!    -3, an error during Legendre symbol calculation.
!
  implicit none

  integer ( kind = 4 ), parameter :: maxfactor = 20

  integer ( kind = 4 ) factor(maxfactor)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) l
  integer ( kind = 4 ) nfactor
  integer ( kind = 4 ) nleft
  integer ( kind = 4 ) p
  integer ( kind = 4 ) power(maxfactor)
  integer ( kind = 4 ) pp
  integer ( kind = 4 ) q
  integer ( kind = 4 ) qq
!
!  P must be greater than 1.
!
  if ( p <= 1 ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'JACOBI_SYMBOL - Fatal error!'
    write ( *, '(a)' ) '  P must be greater than 1.'
    l = -2
    stop
  end if
!
!  Decompose P into factors of prime powers.
!
  call i4_factor ( p, maxfactor, nfactor, factor, power, nleft )

  if ( nleft /= 1 ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'JACOBI_SYMBOL - Fatal error!'
    write ( *, '(a)' ) '  Not enough factorization space.'
    j = -2
    stop
  end if
!
!  Force Q to be nonnegative.
!
  qq = q

  do while ( qq < 0 )
    qq = qq + p
  end do
!
!  For each prime factor, compute the Legendre symbol, and
!  multiply the Jacobi symbol by the appropriate factor.
!
  j = 1
  do i = 1, nfactor
    pp = factor(i)
    call legendre_symbol ( qq, pp, l )
    if ( l < - 1 ) then
      write ( *, '(a)' ) ' '
      write ( *, '(a)' ) 'JACOBI_SYMBOL - Fatal error!'
      write ( *, '(a)' ) '  Error during Legendre symbol calculation.'
      j = -3
      stop
    end if
    j = j * l**power(i)
  end do

  return
end
subroutine jordan ( alpha, m, n, a )

!*****************************************************************************80
!
!! JORDAN returns the JORDAN matrix.
!
!  Formula:
!
!    if ( I = J )
!      A(I,J) = ALPHA
!    else if ( I = J-1 )
!      A(I,J) = 1
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 2, M = 5, N = 5
!
!    2  1  0  0  0
!    0  2  1  0  0
!    0  0  2  1  0
!    0  0  0  2  1
!    0  0  0  0  2
!
!  Properties:
!
!    A is upper triangular.
!
!    A is lower Hessenberg.
!
!    A is bidiagonal.
!
!    Because A is bidiagonal, it has property A (bipartite).
!
!    A is banded, with bandwidth 2.
!
!    A is generally not symmetric: A' /= A.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    A is nonsingular if and only if ALPHA is nonzero.
!
!    det ( A ) = ALPHA^N.
!
!    LAMBDA(I) = ALPHA.
!
!    A is defective, having only one eigenvector, namely (1,0,0,...,0).
!
!    The JORDAN matrix is a special case of the BIS matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the eigenvalue of the matrix.
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(M,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, m

      if ( i == j ) then
        a(i,j) = alpha
      else if ( j == i + 1 ) then
        a(i,j) = 1.0D+00
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine jordan_determinant ( alpha, n, determ )

!*****************************************************************************80
!
!! JORDAN_DETERMINANT returns the determinant of the JORDAN matrix.
!
!  Licensing:
