subroutine jordan_inverse ( alpha, n, a )

!*****************************************************************************80
!
!! JORDAN_INVERSE returns the inverse of the JORDAN matrix.
!
!  Formula:
!
!    if ( I <= J )
!      A(I,J) =  -1 * (-1/ALPHA)^(J+1-I)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 2, N = 4
!
!    1/2 -1/4  1/8 -1/16
!    0    1/2 -1/4  1/8
!    0    0    1/2 -1/4
!    0    0    0    1/2
!
!  Properties:
!
!    A is upper triangular.
!
!    A is Toeplitz: constant along diagonals.
!
!    A is generally not symmetric: A' /= A.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    The inverse of A is the Jordan block matrix, whose diagonal
!    entries are ALPHA, whose first superdiagonal entries are 1,
!    with all other entries zero.
!
!    det ( A ) = (1/ALPHA)^N.
!
!    LAMBDA(1:N) = 1 / ALPHA.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the eigenvalue of the matrix.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  if ( alpha == 0.0D+00 ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'JORDAN_INVERSE - Fatal error!'
    write ( *, '(a)' ) '  The input parameter ALPHA was 0.'
    stop
  end if

  do i = 1, n
    do j = 1, n

      if ( i <= j ) then
        a(i,j) =  - ( - 1.0D+00 / alpha )**( j + 1 - i )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine kahan ( alpha, m, n, a )

!*****************************************************************************80
!
!! KAHAN returns the KAHAN matrix.
!
!  Formula:
!
!    if ( I = J )
!      A(I,I) =  sin(ALPHA)^I
!    else if ( I < J )
!      A(I,J) = - sin(ALPHA)^I * cos(ALPHA)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 0.25, N = 4
!
!    S  -C*S    -C*S      -C*S
!    0     S^2  -C*S^2    -C*S^2
!    0     0       S^3    -C*S^3
!    0     0       0         S^4
!
!    where
!
!      S = sin(ALPHA), C=COS(ALPHA)
!
!  Properties:
!
!    A is upper triangular.
!
!    A = B * C, where B is a diagonal matrix and C is unit upper triangular.
!    For instance, for the case M = 3, N = 4:
!
!    A = | S 0    0    |  * | 1 -C -C  -C |
!        | 0 S^2  0    |    | 0  1 -C  -C |
!        | 0 0    S^3  |    | 0  0  1  -C |
!
!    A is generally not symmetric: A' /= A.
!
!    A has some interesting properties regarding estimation of
!    condition and rank.
!
!    det ( A ) = sin(ALPHA)^(N*(N+1)/2).
!
!    LAMBDA(I) = sin ( ALPHA )^I
!
!    A is nonsingular if and only if sin ( ALPHA ) =/= 0.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Nicholas Higham,
!    A survey of condition number estimation for triangular matrices,
!    SIAM Review,
!    Volume 9, Number 4, December 1987, pages 575-596.
!
!    W Kahan,
!    Numerical Linear Algebra,
!    Canadian Mathematical Bulletin,
!    Volume 9, 1966, pages 757-801.
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the scalar that defines A.  A typical
!    value is 1.2.  The "interesting" range of ALPHA is 0 < ALPHA < PI.
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(M,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) csi
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) si

  do i = 1, m

    si = sin ( alpha )**i
    csi = - cos ( alpha ) * si

    do j = 1, n

      if ( j < i ) then
        a(i,j) = 0.0D+00
      else if ( j == i ) then
        a(i,j) = si
      else
        a(i,j) = csi
      end if

    end do
  end do

  return
end
subroutine kahan_determinant ( alpha, n, determ )

!*****************************************************************************80
!
!! KAHAN_DETERMINANT returns the determinant of the KAHAN matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the scalar that defines A.  A typical 
!    value is 1.2.  The "interesting" range of ALPHA is 0 < ALPHA < PI.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  real ( kind = 8 ) alpha
  real ( kind = 8 ) determ
  integer ( kind = 4 ) n
  integer ( kind = 4 ) power

  power = ( n * ( n + 1 ) ) / 2
  determ = ( sin ( alpha ) )**power

  return
end
subroutine kahan_inverse ( alpha, n, a )

!*****************************************************************************80
!
!! KAHAN_INVERSE returns the inverse of the KAHAN matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    27 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the scalar that defines A.  A typical 
!    value is 1.2.  The "interesting" range of ALPHA is 0 < ALPHA < PI.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) ci
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) si

  ci = cos ( alpha )

  do i = 1, n
    do j = 1, n

      if ( i == j ) then
        a(i,j) = 1.0D+00
      else if ( i == j - 1 ) then
        a(i,j) = ci
      else if ( i < j ) then
        a(i,j) = ci * ( 1.0D+00 + ci )**( j - i - 1 )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do
!
!  Scale the columns.
!
  do j = 1, n
    si = sin ( alpha )**j
    a(1:n,j) = a(1:n,j) / si
  end do

  return
end
subroutine kershaw ( a )

!*****************************************************************************80
!
!! KERSHAW returns the KERSHAW matrix.
!
!  Discussion:
!
!    The Kershaw matrix is a simple example of a symmetric
!    positive definite matrix for which the incomplete Cholesky
!    factorization fails, because of a negative pivot.
!
!  Example:
!
!     3 -2  0  2
!    -2  3 -2  0
!     0 -2  3 -2
!     2  0 -2  3
!
!  Properties:
!
!    A is symmetric.
!
!    A is positive definite.
!
!    det ( A ) = 1.
!
!    LAMBDA(A) = [ 
!      5.828427124746192
!      5.828427124746188
!      0.171572875253810
!      0.171572875253810 ].
!
!    A does not have an incompete Cholesky factorization.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    13 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    David Kershaw,
!    The Incomplete Cholesky-Conjugate Gradient Method for the Iterative
!    Solution of Systems of Linear Equations,
!    Journal of Computational Physics,
!    Volume 26, Number 1, January 1978, pages 43-65.
!
!  Parameters:
!
!    Output, real ( kind = 8 ) A(4,4), the matrix.
!
  implicit none

  real ( kind = 8 ) a(4,4)
  real ( kind = 8 ), dimension ( 4, 4 ), save :: a_save = reshape ( (/&
     3.0D+00, -2.0D+00,  0.0D+00,  2.0D+00, &
    -2.0D+00,  3.0D+00, -2.0D+00,  0.0D+00, &
     0.0D+00, -2.0D+00,  3.0D+00, -2.0D+00, &
     2.0D+00,  0.0D+00, -2.0D+00,  3.0D+00 /), (/ 4, 4 /) )

  call r8mat_copy ( 4, 4, a_save, a )

  return
end
