subroutine moler3_inverse ( n, a )

!*****************************************************************************80
!
!! MOLER3_INVERSE returns the inverse of the MOLER3 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    20 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) l(n,n)
  real ( kind = 8 ) value

  l(1:n,1:n) = 0.0D+00

  do j = 1, n
    l(j,j) = 1.0D+00
    value = 1.0D+00
    do i = j + 1, n
      l(i,j) = value
      value = value * 2.0D+00
    end do
  end do

  a(1:n,1:n) = matmul ( transpose ( l(1:n,1:n) ), l(1:n,1:n) )

  return
end
subroutine moler3_plu ( n, p, l, u )

!*****************************************************************************80
!
!! MOLER3_PLU returns the PLU factors of the MOLER3 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    26 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) P(N,N), L(N,N), U(N,N), the PLU factors.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) l(n,n)
  real ( kind = 8 ) p(n,n)
  real ( kind = 8 ) u(n,n)

  do j = 1, n
    do i = 1, n
      if ( i == j ) then
        p(i,j) = 1.0D+00
      else
        p(i,j) = 0.0D+00
      end if
    end do
  end do

  do i = 1, n
    l(i,1:i-1  ) = -1.0D+00
    l(i,  i    ) =  1.0D+00
    l(i,  i+1:n) =  0.0D+00
  end do

  do j = 1, n
    u(1:j-1,  j) = -1.0D+00
    u(  j,    j) =  1.0D+00
    u(  j+1:n,j) =  0.0D+00
  end do

  return
end
subroutine neumann ( nrow, ncol, n, a )

!*****************************************************************************80
!
!! NEUMANN returns the NEUMANN matrix.
!
!  Formula:
!
!    I1 = 1 + ( I - 1 ) / NROW
!    I2 = I - ( I1 - 1 ) * NROW
!    J1 = 1 + ( J - 1 ) / NROW
!
!    if ( I = J )
!      A(I,J) = 4
!    else If ( I = J-1 )
!      If ( I2 = 1 )
!        A(I,J) = -2
!      else
!        A(I,J) = -1
!    else if ( I = J+1 )
!      If ( I2 = NROW )
!        A(I,J) = -2
!      else
!        A(I,J) = -1
!    else if ( I = J - NROW )
!      if ( J1 = 2 )
!        A(I,J) = -2
!      else
!        A(I,J) = -1
!    else if ( I = J + NROW )
!      if ( J1 = NCOL-1 )
!        A(I,J) = -2
!      else
!        A(I,J) = -1
!    else
!      A(I,J) = 0.0D+00
!
!  Example:
!
!    NROW = NCOL = 3
!
!     4 -2  0 | -2  0  0 |  0  0  0
!    -1  4 -1 |  0 -2  0 |  0  0  0
!     0 -2  4 |  0  0 -2 |  0  0  0
!     ----------------------------
!    -1  0  0 |  4 -1  0 | -1  0  0
!     0 -1  0 | -1  4 -1 |  0 -1  0
!     0  0 -1 |  0 -1  4 |  0  0 -1
!     ----------------------------
!     0  0  0 | -2  0  0 |  4 -2  0
!     0  0  0 |  0 -2  0 | -1  4 -1
!     0  0  0 |  0  0 -2 |  0 -2  4
!
!  Properties:
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    A is block tridiagonal.
!
!    A results from discretizing Neumann's equation with the
!    5 point operator on a mesh of NROW by NCOL points.
!
!    A is singular.
!
!    A has the null vector ( 1, 1, ..., 1 ).
!
!    det ( A ) = 0.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    25 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Gene Golub, Charles Van Loan,
!    Matrix Computations, second edition,
!    Johns Hopkins University Press, Baltimore, Maryland, 1989
!    (Section 4.5.4).
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) NROW, NCOL, the number of rows and columns
!    in the grid.
!
!    Output, integer ( kind = 4 ) N, the order of the matrix, which 
!    is NROW*NCOL.
!
!    Output, real ( kind = 8 ) A(NROW*NCOL,NROW*NCOL), the NROW*NCOL 
!    by NROW*NCOL matrix.
!
  implicit none

  integer ( kind = 4 ) ncol
  integer ( kind = 4 ) nrow

  real ( kind = 8 ) a(nrow*ncol,nrow*ncol)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i1
  integer ( kind = 4 ) j
  integer ( kind = 4 ) j1
  integer ( kind = 4 ) n

  n = nrow * ncol
  a(1:n,1:n) = 0.0D+00

  i = 0

  do i1 = 1, nrow
    do j1 = 1, ncol

      i = i + 1

      if ( 1 < i1 ) then
        j = i - nrow
      else
        j = i + nrow
      end if

      a(i,j) = a(i,j) - 1.0D+00

      if ( 1 < j1 ) then
        j = i - 1
      else
        j = i + 1
      end if

      a(i,j) = a(i,j) - 1.0D+00

      j = i
      a(i,j) = 4.0D+00

      if ( j1 < ncol ) then
        j = i + 1
      else
        j = i - 1
      end if

      a(i,j) = a(i,j) - 1.0D+00

      if ( i1 < nrow ) then
        j = i + nrow
      else
        j = i - nrow
      end if

      a(i,j) = a(i,j) - 1.0D+00

    end do
  end do

  return
end
