!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) X(N,N), the right eigenvectors.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n,n)

  x(1:n,1:n) = 0.0D+00

  do j = 1, n - 1
    x(  1,j) = +1.0D+00
    x(j+1,j) = -1.0D+00
  end do

  j = n
  x(1:n,j) = 1.0D+00

  return
end
subroutine ortega ( n, u, v, d, a )

!*****************************************************************************80
!
!! ORTEGA returns the ORTEGA matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    06 September 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    James Ortega,
!    Generation of Test Matrices by Similarity Transformations,
!    Communications of the ACM,
!    Volume 7, 1964, pages 377-378.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!    2 <= N.
!
!    Input, real ( kind = 8 ) U(N), V(N), vectors which define the matrix.
!    U'V must not equal -1.0.  If, in fact, U'V = 0, and U, V and D are
!    integers, then the matrix, inverse, eigenvalues, and eigenvectors 
!    will be integers.
!
!    Input, real ( kind = 8 ) D(N), the desired eigenvalues.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n
  
  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) beta
  real ( kind = 8 ) bik
  real ( kind = 8 ) ckj
  real ( kind = 8 ) d(n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) u(n)
  real ( kind = 8 ) v(n)
  real ( kind = 8 ) vtu

  vtu = dot_product ( v(1:n), u(1:n) )

  beta = 1.0D+00 / ( 1.0D+00 + vtu )

  do j = 1, n
    do i = 1, n

      a(i,j) = 0.0D+00

      do k = 1, n

        if ( i == k ) then
          bik = 1.0D+00 + u(i) * v(k)
        else
          bik =           u(i) * v(k)
        end if

        if ( k == j ) then
          ckj = 1.0D+00 - beta * u(k) * v(j)
        else
          ckj =         - beta * u(k) * v(j)
        end if

        a(i,j) = a(i,j) + bik * d(k) * ckj

      end do
    end do
  end do

  return
end
subroutine ortega_determinant ( n, u, v, d, determ )

!*****************************************************************************80
!
!! ORTEGA_DETERMINANT returns the determinant of the ORTEGA matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    06 September 2008
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    James Ortega,
!    Generation of Test Matrices by Similarity Transformations,
!    Communications of the ACM,
!    Volume 7, 1964, pages 377-378.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!    2 <= N.
!
!    Input, real ( kind = 8 ) U(N), V(N), vectors which define the matrix.
!    U'V must not equal -1.0.  If, in fact, U'V = 0, and U, V and D are
!    integers, then the matrix, inverse, eigenvalues, and eigenvectors 
!    will be integers.
!
!    Input, real ( kind = 8 ) D(N), the desired eigenvalues.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n
  
  real ( kind = 8 ) d(n)
  real ( kind = 8 ) determ
  real ( kind = 8 ) u(n)
  real ( kind = 8 ) v(n)

  determ = product ( d(1:n) )

  return
end
subroutine ortega_eigenvalues ( n, u, v, d, lambda )

!*****************************************************************************80
!
!! ORTEGA_EIGENVALUES returns the eigenvalues of the ORTEGA matrix.
!
