subroutine pascal2_inverse ( n, a )

!*****************************************************************************80
!
!! PASCAL2_INVERSE returns the inverse of the PASCAL2 matrix.
!
!  Formula:
!
!    A(I,J) = sum ( max(I,J) <= K <= N )
!      (-1)^(J+I) * COMB(K-1,I-1) * COMB(K-1,J-1)
!
!  Example:
!
!    N = 5
!
!      5 -10  10  -5   1
!    -10  30 -35  19  -4
!     10 -35  46 -27   6
!     -5  19 -27  17  -4
!      1  -4   6  -4   1
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    The first row sums to 1, the others to 0.
!
!    The first column sums to 1, the others to 0.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 June 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  integer ( kind = 4 ) klo
  real ( kind = 8 ) r8_choose
  real ( kind = 8 ) r8_mop

  do i = 1, n
    do j = 1, n
      a(i,j) = 0.0D+00
      klo = max ( i, j )
      do k = klo, n
        a(i,j) = a(i,j) + r8_mop ( i + j ) * r8_choose ( k - 1, i - 1 ) &
          * r8_choose ( k - 1, j - 1 )
      end do
    end do
  end do

  return
end
subroutine pascal2_plu ( n, p, l, u )

!*****************************************************************************80
!
!! PASCAL2_PLU returns the PLU factors of the PASCAL2 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    17 August 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) P(N,N), L(N,N), U(N,N), the PLU factors.
!
  implicit none

  integer ( kind = 4 ) n

  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) l(n,n)
  real ( kind = 8 ) p(n,n)
  real ( kind = 8 ) u(n,n)

  do j = 1, n
    do i = 1, n
      if ( i == j ) then
        p(i,j) = 1.0D+00
      else
        p(i,j) = 0.0D+00
      end if
    end do
  end do

  call pascal1 ( n, l )

  u(1:n,1:n) = transpose ( l(1:n,1:n) )

  return
end
subroutine pascal3 ( n, alpha, a )

!*****************************************************************************80
!
!! PASCAL3 returns the PASCAL3 matrix.
!
!  Formula:
!
!    if ( J = 1 ) then
!      A(I,J) = 1
!    else if ( I = 0 ) then
!      A(1,J) = 0
!    else
!      A(I,J) =  ALPHA * A(I-1,J) + A(I-1,J-1) )
!
!  Example:
!
!    N = 5, ALPHA = 2
!
!     1   0   0   0   0
!     2   1   0   0   0
!     4   4   1   0   0
!     8  12   6   1   0
!    16  32  24   8   1
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A[0] is the identity matrix.
!
!    A[1] is the usual (lower triangular) Pascal matrix.
!
!    A is nonsingular.
!
!    A is lower triangular.
!
!    If ALPHA is integral, then A is integral.
!    If A is integral, then det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    det ( A ) = 1.
!
!    A is unimodular.
!
!    LAMBDA(1:N) = 1.
!
!    (0, 0, ..., 0, 1) is an eigenvector.
!
!    The inverse of A[X] is A[-X].
!
!    A[ALPHA] * A[BETA] = A[ALPHA*BETA].
!
!    A[1/2] is the "square root" of A[1], and so on.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    13 October 2000
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Gregory Call, Daniel Velleman,
!    Pascal's Matrices,
!    American Mathematical Monthly,
!    Volume 100, Number 4, April 1993, pages 372-376.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, the parameter.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do i = 1, n
    do j = 1, n

      if ( i == 1 ) then

        if ( j == 1 ) then
          a(i,j) = 1.0D+00
        else
          a(i,j) = 0.0D+00
        end if

      else if ( j == 1 ) then

        a(i,j) = alpha * a(i-1,j)

      else

        a(i,j) = a(i-1,j-1) + alpha * a(i-1,j)

      end if

    end do
  end do

  return
end
