  determ = 1.0D+00

  return
end
subroutine pascal3_inverse ( n, alpha, a )

!*****************************************************************************80
!
!! PASCAL3_INVERSE returns the inverse of the PASCAL3 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    28 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, the parameter.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do i = 1, n
    do j = 1, n

      if ( i == 1 ) then

        if ( j == 1 ) then
          a(i,j) = 1.0D+00
        else
          a(i,j) = 0.0D+00
        end if

      else if ( j == 1 ) then

        a(i,j) = - alpha * a(i-1,j)

      else

        a(i,j) = a(i-1,j-1) - alpha * a(i-1,j)

      end if

    end do
  end do

  return
end
subroutine pds_random ( n, seed, a )

!*****************************************************************************80
!
!! PDS_RANDOM returns the PDS_RANDOM matrix.
!
!  Discussion:
!
!    The matrix is a "random" positive definite symmetric matrix.
!
!    The matrix returned will have eigenvalues in the range [0,1].
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    A is positive definite: 0 < x'*A*x for nonzero x.
!
!    The eigenvalues of A will be real.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    30 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input/output, integer ( kind = 4 ) SEED, a seed for the random 
!    number generator.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) lambda(n)
  real ( kind = 8 ) q(n,n)
  integer ( kind = 4 ) seed
!
!  Get a random set of eigenvalues.
!
  call r8vec_uniform_01 ( n, seed, lambda )
!
!  Get a random orthogonal matrix Q.
!
  call orth_random ( n, seed, q )
!
!  Set A = Q * Lambda * Q'.
!
  do i = 1, n
    do j = 1, n
      a(i,j) = 0.0D+00
      do k = 1, n
        a(i,j) = a(i,j) + q(i,k) * lambda(k) * q(j,k)
      end do
    end do
  end do

  return
end
subroutine pds_random_determinant ( n, seed, determ )

!*****************************************************************************80
!
!! PDS_RANDOM_DETERMINANT returns the determinant of the PDS_RANDOM matrix.
