subroutine prolate ( alpha, n, a )

!*****************************************************************************80
!
!! PROLATE returns the PROLATE matrix.
!
!  Formula:
!
!    If ( I == J ) then
!      A(I,J) = 2 * ALPHA
!    else
!      K = abs ( I - J ) + 1
!      A(I,J) = sin ( 2 * pi * ALPHA * K ) / ( pi * K )
!
!  Example:
!
!    N = 5, ALPHA = 0.25
!
!     0.5        0.0D+00       -0.106103   0.0        0.0636620
!     0.0D+00        0.5        0.0       -0.106103   0.0D+00
!    -0.106103   0.0D+00        0.5        0.0       -0.106103
!     0.0D+00       -0.106103   0.0        0.5        0.0D+00
!     0.0636620  0.0D+00       -0.106103   0.0        0.5
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    A is centrosymmetric: A(I,J) = A(N+1-I,N+1-J).
!
!    A is Toeplitz: constant along diagonals.
!
!    If 0 < ALPHA < 0.5, then
!      A is positive definite,
!      the eigenvalues of A are distinct,
!      the eigenvalues lie in (0,1) and cluster around 0 and 1.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    James Varah,
!    The Prolate Matrix,
!    Linear Algebra and Applications,
!    Volume 187, pages 269-278, 1993.
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the parameter.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) angle
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00

  do j = 1, n
    do i = 1, n

      if ( i == j ) then
        a(i,j) = 2.0D+00 * alpha
      else
        k = abs ( i - j ) + 1
        angle = 2.0D+00 * pi * alpha * real ( k, kind = 8 )
        a(i,j) = sin (  angle ) / ( pi * real ( k, kind = 8 ) )
      end if

    end do
  end do

  return
end
subroutine propa_no_random ( prob, k, n, seed, a )

!*****************************************************************************80
!
!! PROPA_NO_RANDOM returns a PROPA_NO_RANDOM matrix.
!
!  Discussion:
!
!    The matrix is a random matrix that does not have property A.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    23 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) PROB, the probability that a link between
!    two eligible nodes will be made.
!
!    Input, integer ( kind = 4 ) K, the number of illegal links between nodes 
!    to make.  The routine will TRY to make this many illegal links.  However,
!    it is obviously possible to make K too big.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) bad
  real ( kind = 8 ) chance
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i4_uniform
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) prob
  real ( kind = 8 ) r8_uniform_01
  integer ( kind = 4 ) seed
  integer ( kind = 4 ) set(n)
  integer ( kind = 4 ) tries

  a(1:n,1:n) = 0.0D+00
!
!  Assign each index randomly to one of two sets.
!  SET(I) is 0 if I is in set 0, and 1 if it is in set 1.
!
  call subset_random ( n, seed, set )

  do j = 1, n
    do i = 1, n
      if ( set(i) /= set(j) ) then
        chance = r8_uniform_01 ( seed )
        if ( chance <= prob ) then
          a(i,j) = 1.0D+00
        end if
      end if
    end do
  end do
!
!  Now repeatedly pick a pair of indices, and consider setting the
!  corresponding entry of A to 1.
!
  bad = 0
  tries = 0

  do

    tries = tries + 1

    if ( 1000 < tries ) then
      exit
    end if

    if ( k <= bad ) then
      exit
    end if

    i = i4_uniform ( 1, n, seed )
    j = i4_uniform ( 1, n, seed )

    if ( i == j ) then
      cycle
    end if

    if ( set(i) /= set(j) ) then
      cycle
    end if

    if ( a(i,j) /= 0.0D+00 .and. a(j,i) /= 0.0D+00 ) then
      cycle
    end if

    if ( a(i,j) == 0.0D+00 ) then
      a(i,j) = 1.0D+00
    else
      a(j,i) = 1.0D+00
    end if

    bad = bad + 1

  end do

  return
end
