subroutine r8mat_determinant ( n, a, determ )

!*****************************************************************************80
!
!! R8MAT_DETERMINANT computes the determinant of a square R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of R8 values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix A.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) b(n,n)
  real ( kind = 8 ) determ
  integer ( kind = 4 ) info
  integer ( kind = 4 ) pivot(n)

  b(1:n,1:n) = a(1:n,1:n)

  call r8mat_gefa ( b, n, pivot, info )

  call r8mat_gedet ( b, n, pivot, determ )

  return
end
