subroutine r8mat_eigenvalues ( n, a, lambda )

!*****************************************************************************80
!
!! R8MAT_EIGENVALUES computes the eigenvalues of a square R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    This subroutine calls the recommended sequence of EISPACK routines.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    27 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, complex ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) a_copy(n,n)
  real ( kind = 8 ) fv1(n)
  integer ( kind = 4 ) ierr
  integer ( kind = 4 ) is1
  integer ( kind = 4 ) is2
  integer ( kind = 4 ) iv1(n)
  complex ( kind = 8 ) lambda(n)
  real ( kind = 8 ) wi(n)
  real ( kind = 8 ) wr(n)

  a_copy(1:n,1:n) = a(1:n,1:n)

  call balanc ( n, n, a_copy, is1, is2, fv1 )

  call elmhes ( n, n, is1, is2, a_copy, iv1 )

  call hqr ( n, n, is1, is2, a_copy, wr, wi, ierr )

  lambda(1:n) = cmplx ( wr(1:n), wi(1:n), kind = 8 )

  return
end
subroutine r8mat_geco ( a, n, pivot, rcond, z )

!*****************************************************************************80
!
!! R8MAT_GECO factors an R8MAT and estimates its condition number.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    For the system A * X = B, relative perturbations in A and B
!    of size EPSILON may cause relative perturbations in X of size
!    EPSILON/RCOND.
!
!    If RCOND is so small that the logical expression
!      1.0D+00 + rcond == 1.0D+00
!    is true, then A may be singular to working precision.  In particular,
!    RCOND is zero if exact singularity is detected or the estimate
!    underflows.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    29 May 2004
!
!  Author:
!
!    Cleve Moler,
!    University of New Mexico / Argonne National Lab.
!
!  Reference:
!
!    Jack Dongarra, Jim Bunch, Cleve Moler, Pete Stewart,
!    LINPACK User's Guide,
!    SIAM, 1979,
!    ISBN13: 978-0-898711-72-1,
!    LC: QA214.L56.
!
!  Parameters:
!
!    Input/output, real ( kind = 8 ) A(N,N).  On input, a matrix to be factored.
!    On output, the LU factorization of the matrix.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix A.
!
!    Output, integer ( kind = 4 ) PIVOT(N), the pivot indices.
!
!    Output, real ( kind = 8 ) RCOND, an estimate of the reciprocal condition
!    number of A.
!
!    Output, real ( kind = 8 ) Z(N), a work vector whose contents are 
!    usually unimportant.  If A is close to a singular matrix, then Z is 
!    an approximate null vector in the sense that
!      norm ( A * Z ) = RCOND * norm ( A ) * norm ( Z ).
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) anorm
  real ( kind = 8 ) ek
  integer ( kind = 4 ) info
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  integer ( kind = 4 ) l
  integer ( kind = 4 ) pivot(n)
  real ( kind = 8 ) rcond
  real ( kind = 8 ) s
  real ( kind = 8 ) sm
  real ( kind = 8 ) t
  real ( kind = 8 ) wk
  real ( kind = 8 ) wkm
  real ( kind = 8 ) ynorm
  real ( kind = 8 ) z(n)
!
!  Compute the L1 norm of A.
!
  anorm = 0.0D+00
  do j = 1, n
    anorm = max ( anorm, sum ( abs ( a(1:n,j) ) ) )
  end do
!
!  Compute the LU factorization.
!
  call r8mat_gefa ( a, n, pivot, info )
!
!  RCOND = 1 / ( norm(A) * (estimate of norm(inverse(A))) )
!
!  estimate of norm(inverse(A)) = norm(Z) / norm(Y)
!
!  where
!    A * Z = Y
!  and
!    A' * Y = E
!
!  The components of E are chosen to cause maximum local growth in the
!  elements of W, where U'*W = E.  The vectors are frequently rescaled
!  to avoid overflow.
!
!  Solve U' * W = E.
!
  ek = 1.0D+00
  z(1:n) = 0.0D+00

  do k = 1, n

    if ( z(k) /= 0.0D+00 ) then
      ek = sign ( ek, -z(k) )
    end if

    if ( abs ( a(k,k) ) < abs ( ek - z(k) ) ) then
      s = abs ( a(k,k) ) / abs ( ek - z(k) )
      z(1:n) = s * z(1:n)
      ek = s * ek
    end if

    wk = ek - z(k)
    wkm = -ek - z(k)
    s = abs ( wk )
    sm = abs ( wkm )

    if ( a(k,k) /= 0.0D+00 ) then
      wk = wk / a(k,k)
      wkm = wkm / a(k,k)
    else
      wk = 1.0D+00
      wkm = 1.0D+00
    end if

    if ( k+1 <= n ) then

      do j = k+1, n
        sm = sm + abs ( z(j) + wkm * a(k,j) )
        z(j) = z(j) + wk * a(k,j)
        s = s + abs ( z(j) )
      end do

      if ( s < sm ) then
        t = wkm - wk
        wk = wkm
        z(k+1:n) = z(k+1:n) + t * a(k,k+1:n)
      end if

    end if

    z(k) = wk

  end do

  z(1:n) = z(1:n) / sum ( abs ( z(1:n) ) )
!
!  Solve L' * Y = W
!
  do k = n, 1, -1

    z(k) = z(k) + dot_product ( a(k+1:n,k), z(k+1:n) )

    if ( 1.0D+00 < abs ( z(k) ) ) then
      z(1:n) = z(1:n) / abs ( z(k) )
    end if

    l = pivot(k)

    t    = z(l)
    z(l) = z(k)
    z(k) = t

  end do

  z(1:n) = z(1:n) / sum ( abs ( z(1:n) ) )

  ynorm = 1.0D+00
!
!  Solve L * V = Y.
!
  do k = 1, n

    l = pivot(k)

    t    = z(l)
    z(l) = z(k)
    z(k) = t

    z(k+1:n) = z(k+1:n) + t * a(k+1:n,k)

    if ( 1.0D+00 < abs ( z(k) ) ) then
      ynorm = ynorm / abs ( z(k) )
      z(1:n) = z(1:n) / abs ( z(k) )
    end if

  end do

  s = sum ( abs ( z(1:n) ) )
  z(1:n) = z(1:n) / s
  ynorm = ynorm / s
!
!  Solve U * Z = V.
!
  do k = n, 1, -1

    if ( abs ( a(k,k) ) < abs ( z(k) ) ) then
      s = abs ( a(k,k) ) / abs ( z(k) )
      z(1:n) = s * z(1:n)
      ynorm = s * ynorm
    end if

    if ( a(k,k) /= 0.0D+00 ) then
      z(k) = z(k) / a(k,k)
    else
      z(k) = 1.0D+00
    end if

    z(1:k-1) = z(1:k-1) - z(k) * a(1:k-1,k)

  end do
!
!  Normalize Z in the L1 norm.
!
  s = 1.0D+00 / sum ( abs ( z(1:n) ) )
  z(1:n) = s * z(1:n)
  ynorm = s * ynorm

  if ( anorm /= 0.0D+00 ) then
    rcond = ynorm / anorm
  else
    rcond = 0.0D+00
  end if

  return
end
subroutine r8mat_gedet ( a, n, pivot, determ )

!*****************************************************************************80
!
!! R8MAT_GEDET computes the determinant of an R8MAT factored by R8MAT_GEFA.
!
!  Discussion:
!
!    An R8MAT is a matrix of R8 values.
!
!    This is a modified version of the LINPACK routine DGEDI.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    25 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Jack Dongarra, Cleve Moler, Jim Bunch, Pete Stewart,
!    LINPACK User's Guide,
!    SIAM, (Society for Industrial and Applied Mathematics),
!    3600 University City Science Center,
!    Philadelphia, PA, 19104-2688.
!    ISBN: 0-89871-172-X.
!
!  Parameters:
!
!    Input, real ( kind = 8 ) A(N,N), the LU factors computed by R8MAT_GEFA.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!    N must be positive.
!
!    Input, integer ( kind = 4 ) PIVOT(N), as computed by R8MAT_GEFA.
!
!    Output, real ( kind = 8 ) DETERM, the determinant of the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) pivot(n)

  determ = 1.0D+00

  do i = 1, n
    determ = determ * a(i,i)
  end do

  do i = 1, n
    if ( pivot(i) /= i ) then
      determ = - determ
    end if
  end do

  return
end
subroutine r8mat_gefa ( a, n, pivot, info )

!*****************************************************************************80
!
!! R8MAT_GEFA factors an R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of R8 values.
!
!    This is a simplified version of the LINPACK routine DGEFA.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    25 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Jack Dongarra, Cleve Moler, Jim Bunch, Pete Stewart,
!    LINPACK User's Guide,
!    SIAM, (Society for Industrial and Applied Mathematics),
!    3600 University City Science Center,
!    Philadelphia, PA, 19104-2688.
!    ISBN 0-89871-172-X
!
!  Parameters:
!
!    Input/output, real ( kind = 8 ) A(N,N), the matrix to be factored.
!    On output, A contains an upper triangular matrix and the multipliers
!    which were used to obtain it.  The factorization can be written
!    A = L * U, where L is a product of permutation and unit lower
!    triangular matrices and U is upper triangular.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!    N must be positive.
!
!    Output, integer ( kind = 4 ) PIVOT(N), a vector of pivot indices.
!
!    Output, integer ( kind = 4 ) INFO, singularity flag.
!    0, no singularity detected.
!    nonzero, the factorization failed on the INFO-th step.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  logical, parameter :: debug = .false.
  integer ( kind = 4 ) i
  integer ( kind = 4 ) info
  integer ( kind = 4 ) pivot(n)
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  integer ( kind = 4 ) l

  info = 0

  do k = 1, n - 1
!
!  Find L, the index of the pivot row.
!
    l = k
    do i = k + 1, n
      if ( abs ( a(l,k) ) < abs ( a(i,k) ) ) then
        l = i
      end if
    end do

    pivot(k) = l
!
!  If the pivot index is zero, the algorithm has failed.
!
    if ( a(l,k) == 0.0D+00 ) then
      info = k
      if ( debug ) then
        write ( *, '(a)' ) ' '
        write ( *, '(a)' ) 'R8MAT_GEFA - Warning!'
        write ( *, '(a,i8)' ) '  Zero pivot on step ', info
      end if
      return
    end if
!
!  Interchange rows L and K if necessary.
!
    if ( l /= k ) then
      call r8_swap ( a(l,k), a(k,k) )
    end if
!
!  Normalize the values that lie below the pivot entry A(K,K).
!
    a(k+1:n,k) = - a(k+1:n,k) / a(k,k)
!
!  Row elimination with column indexing.
!
    do j = k + 1, n

      if ( l /= k ) then
        call r8_swap ( a(l,j), a(k,j) )
      end if

      a(k+1:n,j) = a(k+1:n,j) + a(k+1:n,k) * a(k,j)

    end do

  end do

  pivot(n) = n

  if ( a(n,n) == 0.0D+00 ) then
    info = n
    if ( debug ) then
      write ( *, '(a)' ) ' '
      write ( *, '(a)' ) 'R8MAT_GEFA - Warning!'
      write ( *, '(a,i8)' ) '  Zero pivot on step ', info
    end if
  end if

  return
end
subroutine r8mat_geinverse ( a, n, pivot )

!*****************************************************************************80
!
!! R8MAT_GEINVERSE computes the inverse of an R8MAT factored by R8MAT_GEFA.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    R8MAT_GEINVERSE is a modified version of the LINPACK routine DGEDI.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    25 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Jack Dongarra, Cleve Moler, Jim Bunch, Pete Stewart,
!    LINPACK User's Guide,
!    SIAM, (Society for Industrial and Applied Mathematics),
!    3600 University City Science Center,
!    Philadelphia, PA, 19104-2688.
!    ISBN 0-89871-172-X
!
!  Parameters:
!
!    Input/output, real ( kind = 8 ) A(N,N).
!    On input, the factor information computed by R8MAT_GEFA.
!    On output, the inverse matrix.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix A.
!
!    Input, integer ( kind = 4 ) PIVOT(N), the pivot vector from R8MAT_GEFA.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) pivot(n)
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) temp
  real ( kind = 8 ) work(n)
!
!  Compute Inverse(U).
!
  do k = 1, n

    a(k,k) = 1.0D+00 / a(k,k)

    a(1:k-1,k) = - a(1:k-1,k) * a(k,k)

    do j = k + 1, n

      temp = a(k,j)
      a(k,j) = 0.0D+00

      a(1:k,j) = a(1:k,j) + temp * a(1:k,k)

    end do

  end do
!
!  Form Inverse(U) * Inverse(L).
!
  do k = n - 1, 1, -1

    work(k+1:n) = a(k+1:n,k)
    a(k+1:n,k) = 0.0D+00

    do j = k + 1, n
      a(1:n,k) = a(1:n,k) + work(j) * a(1:n,j)
    end do

    if ( pivot(k) /= k ) then

      do i = 1, n
        call r8_swap ( a(i,k), a(i,pivot(k)) )
      end do

    end if

  end do

  return
end
subroutine r8mat_geplu ( m, n, a, p, l, u )

!*****************************************************************************80
!
!! R8MAT_GEPLU produces the PLU factors of an R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The PLU factors of the M by N matrix A are:
!
!      P, an M by M permutation matrix P,
!      L, an M by M unit lower triangular matrix,
!      U, an M by N upper triangular matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    30 April 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix to be factored.
!
!    Output, real ( kind = 8 ) P(M,M), the permutation factor.
!
!    Output, real ( kind = 8 ) L(M,M), the unit lower triangular factor.
!
!    Output, real ( kind = 8 ) U(M,N), the upper triangular factor.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) l(m,m)
  real ( kind = 8 ) p(m,m)
  integer ( kind = 4 ) pivot_row
  real ( kind = 8 ) pivot_value
  real ( kind = 8 ) u(m,n)
!
!  Initialize:
!
!    P:=Identity
!    L:=Identity
!    U:=A
!
  call identity ( m, m, p )
  call identity ( m, m, l )

  u(1:m,1:n) = a(1:m,1:n)
!
!  On step J, find the pivot row and the pivot value.
!
  do j = 1, min ( m - 1, n )

    pivot_value = 0.0D+00
    pivot_row = 0

    do i = j, m

      if ( pivot_value < abs ( u(i,j) ) ) then
        pivot_value = abs ( u(i,j) )
        pivot_row = i
      end if

    end do
!
!  If the pivot row is nonzero, swap rows J and PIVOT_ROW.
!
    if ( pivot_row /= 0 ) then

      call r8row_swap ( m, n, u, j, pivot_row )

      call r8row_swap ( m, m, l, j, pivot_row )

      call r8col_swap ( m, m, l, j, pivot_row )

      call r8col_swap ( m, m, p, j, pivot_row )
!
!  Zero out the entries in column J, from row J+1 to M.
!
      do i = j + 1, m

        if ( u(i,j) /= 0.0D+00 ) then

          l(i,j) = u(i,j) / u(j,j)

          u(i,j) = 0.0D+00
          u(i,j+1:n) = u(i,j+1:n) - l(i,j) * u(j,j+1:n)

        end if

      end do

    end if

  end do

  return
end
subroutine r8mat_gesl ( a, n, pivot, b, job )

!*****************************************************************************80
!
!! R8MAT_GESL solves a system factored by R8MAT_GEFA.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    This is a simplified version of the LINPACK routine DGESL.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 March 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Jack Dongarra, Cleve Moler, Jim Bunch, Pete Stewart,
!    LINPACK User's Guide,
!    SIAM, (Society for Industrial and Applied Mathematics),
!    3600 University City Science Center,
!    Philadelphia, PA, 19104-2688.
!    ISBN 0-89871-172-X
!
!  Parameters:
!
!    Input, real ( kind = 8 ) A(N,N), the LU factors from R8MAT_GEFA.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!    N must be positive.
!
!    Input, integer ( kind = 4 ) PIVOT(N), the pivot vector from R8MAT_GEFA.
!
!    Input/output, real ( kind = 8 ) B(N).
!    On input, the right hand side vector.
!    On output, the solution vector.
!
!    Input, integer ( kind = 4 ) JOB, specifies the operation.
!    0, solve A * x = b.
!    nonzero, solve A' * x = b.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) b(n)
  integer ( kind = 4 ) job
  integer ( kind = 4 ) k
  integer ( kind = 4 ) l
  integer ( kind = 4 ) pivot(n)
  real ( kind = 8 ) t
!
!  Solve A * x = b.
!
  if ( job == 0 ) then
!
!  Solve PL * Y = B.
!
    do k = 1, n - 1

      l = pivot(k)

      if ( l /= k ) then
        t    = b(l)
        b(l) = b(k)
        b(k) = t
      end if

      b(k+1:n) = b(k+1:n) + a(k+1:n,k) * b(k)

    end do
!
!  Solve U * X = Y.
!
    do k = n, 1, -1
      b(k) = b(k) / a(k,k)
      b(1:k-1) = b(1:k-1) - a(1:k-1,k) * b(k)
    end do
!
!  Solve A' * X = B.
!
  else
!
!  Solve U' * Y = B.
!
    do k = 1, n
      b(k) = ( b(k) - dot_product ( b(1:k-1), a(1:k-1,k) ) ) / a(k,k)
    end do
!
!  Solve ( PL )' * X = Y.
!
    do k = n - 1, 1, -1

      b(k) = b(k) + dot_product ( b(k+1:n), a(k+1:n,k) )

      l = pivot(k)

      if ( l /= k ) then
        t    = b(l)
        b(l) = b(k)
        b(k) = t
      end if

    end do

  end if

  return
end
subroutine r8mat_house_axh ( n, a, v, ah )

!*****************************************************************************80
!
!! R8MAT_HOUSE_AXH computes A*H where H is a compact Householder matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The Householder matrix H(V) is defined by
!
!      H(V) = I - 2 * v * v' / ( v' * v )
!
!    This routine is not particularly efficient.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    26 March 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Input, real ( kind = 8 ) V(N), a vector defining a Householder matrix.
!
!    Output, real ( kind = 8 ) AH(N,N), the product A*H.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) ah(n,n)
  real ( kind = 8 ) ah_temp(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) v(n)
  real ( kind = 8 ) v_normsq

  v_normsq = sum ( v(1:n)**2 )
!
!  Compute A*H' = A*H
!
  do i = 1, n
    do j = 1, n
      ah_temp(i,j) = a(i,j)
      do k = 1, n
        ah_temp(i,j) = ah_temp(i,j) - 2.0D+00 * a(i,k) * v(k) * v(j) / v_normsq
      end do
    end do
  end do
!
!  Copy the temporary result into AH.
!  Doing it this way means the user can identify the input arguments A and AH.
!
  ah(1:n,1:n) = ah_temp(1:n,1:n)

  return
end
subroutine r8mat_house_form ( n, v, h )

!*****************************************************************************80
!
!! R8MAT_HOUSE_FORM constructs a Householder matrix from its compact form.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    H(v) = I - 2 * v * v' / ( v' * v )
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 March 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) V(N), the vector defining the Householder matrix.
!
!    Output, real ( kind = 8 ) H(N,N), the Householder matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) beta
  real ( kind = 8 ) h(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) v(n)
!
!  Compute the L2 norm of V.
!
  beta = sum ( v(1:n)**2 )
!
!  Form the matrix H.
!
  call r8mat_identity ( n, h )

  do i = 1, n
    do j = 1, n
      h(i,j) = h(i,j) - 2.0D+00 * v(i) * v(j) / beta
    end do
  end do

  return
end
subroutine r8mat_identity ( n, a )

!*****************************************************************************80
!
!! R8MAT_IDENTITY sets an R8MAT to the identity.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 March 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix which has been set 
!    to the identity.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i

  a(1:n,1:n) = 0.0D+00

  do i = 1, n
    a(i,i) = 1.0D+00
  end do

  return
end
subroutine r8mat_inverse ( n, a, b )

!*****************************************************************************80
!
!! R8MAT_INVERSE computes the inverse of an R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    22 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the orderof the matrix A.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, real ( kind = 8 ) B(N,N), the inverse matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) b(n,n)
  integer ( kind = 4 ) info
  integer ( kind = 4 ) pivot(n)

  b(1:n,1:n) = a(1:n,1:n)

  call r8mat_gefa ( b, n, pivot, info )

  call r8mat_geinverse ( b, n, pivot )

  return
end
subroutine r8mat_is_adjacency ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_ADJACENCY checks whether an R8MAT is an adjacency matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not an adjacency matrix.
!    1, the matrix is an adjacency matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) jval
  real ( kind = 8 ), parameter :: tol = 0.00001D+00

  if ( m /= n ) then
    ival = -1
    return
  end if

  call r8mat_is_symmetric ( m, n, a, error_frobenius )

  if ( tol < error_frobenius ) then
    ival = -1
    return
  end if

  call r8mat_is_zero_one ( m, n, a, jval )

  if ( jval /= 1 ) then
    ival = -1
    return
  end if

  return
end
subroutine r8mat_is_anticirculant ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_ANTICIRCULANT checks whether an R8MAT is an anticirculant matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not anticirculant.
!    1, the matrix is anticirculant.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k

  ival = 1

  do i = 2, m
    do j = 1, n

      k = 1 + mod ( j + i - 2, n )

      if ( a(i,j) /= a(1,k) ) then
        ival = -1
        return
      end if

    end do
  end do

  return
end
subroutine r8mat_is_antipersymm ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_ANTIPERSYMM checks an R8MAT for antipersymmetry.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A is antipersymmetric if A(I,J) = -A(N+1-J,N+1-I).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not antipersymmetric.
!    1, the matrix is antipersymmetric.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j

  ival = 1

  do i = 1, min ( m, n )
    do j = n, max ( 1, n - m + 1 ), -1
      if ( a(i,j) /= -a(n+1-j,n+1-i) ) then
        ival = -1
        return
      end if
    end do
  end do

  return
end
subroutine r8mat_is_antisymm ( m, n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_ANTISYMM checks an R8MAT for antisymmetry.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius error in
!    antisymmetry, which would be 0 if the matrix is exactly antisymmetric.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8_huge

  error_frobenius = 0.0D+00

  if ( m /= n ) then
    error_frobenius = r8_huge ( )
    return
  end if

  do i = 1, n
    do j = 1, i - 1
      error_frobenius = error_frobenius + ( a(i,j) + a(j,i) )**2
    end do
    error_frobenius = error_frobenius + a(i,i)**2
  end do

  error_frobenius = sqrt ( error_frobenius )

  return
end
subroutine r8mat_is_band ( m, n, a, ival, jval )

!*****************************************************************************80
!
!! R8MAT_IS_BAND determines whether an R8MAT is banded.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    13 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL, the lower bandwidth, measured as the
!    number of nonzero diagonals, starting with the main diagonal
!    and proceeding down, with values between 0 and M.
!
!    Output, integer ( kind = 4 ) JVAL, the upper bandwidth, measured as the
!    number of nonzero diagonals, starting with the main diagonal
!    and proceeding right, with values between 0 and N.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) jval

  ival = 0
  jval = 0

  do i = 1, m
    do j = 1, n

      if ( a(i,j) /= 0.0D+00 ) then

        if ( i <= j ) then
          jval = max ( jval, j + 1 - i )
        end if

        if ( j <= i ) then
          ival = max ( ival, i + 1 - j )
        end if

      end if

    end do
  end do

  return
end
subroutine r8mat_is_centrosymm ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_CENTROSYMM checks an R8MAT for centrosymmetry.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A is centrosymmetric: A(I,J) = A(N+1-I,N+1-J).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    23 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not centrosymmetric.
!    1, the matrix is centrosymmetric.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j

  ival = 1

  do i = 1, min ( m, n )
    do j = n, max ( 1, n - m + 1 ), -1
      if ( a(i,j) /= a(n+1-i,n+1-j) ) then
        ival = -1
        return
      end if
    end do
  end do

  return
end
subroutine r8mat_is_circulant ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_CIRCULANT checks whether an R8MAT is a circulant matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not circulant.
!    1, the matrix is circulant.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i4_modp
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k

  ival = 1

  do i = 2, m
    do j = 1, n

      k = 1 + i4_modp ( j - i, n )

      if ( a(i,j) /= a(1,k) ) then
        ival = -1
        return
      end if

    end do
  end do

  return
end
subroutine r8mat_is_column_orthogonal ( m, n, a, error_sum )

!*****************************************************************************80
!
!! R8MAT_IS_COLUMN_ORTHOGONAL checks whether an R8MAT is column orthogonal.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.  
!    M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_SUM, the sum of the errors.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_sum
  integer ( kind = 4 ) j
  integer ( kind = 4 ) j2

  error_sum = 0.0D+00

  do j = 1, n

    do j2 = j + 1, n

      error_sum = error_sum  + ( dot_product ( a(1:m,j), a(1:m,j2) ) )**2

    end do

  end do

  error_sum = sqrt ( error_sum )

  return
end
subroutine r8mat_is_cyclic_tridiagonal ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_CYCLIC_TRIDIAGONAL determines if an R8MAT is cyclic tridiagonal.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    23 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!    M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL, the report:
!    -1, the matrix is not square.
!    -2, the matrix has illegal nonzero values.
!    1, the matrix is cyclic tridiagonal.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k

  if ( m /= n ) then
    ival = -1
    return
  end if

  do i = 1, n

    j = i + 1

    do k = 1, n - 3

      j = j + 1

      if ( n < j ) then
        j = j - n
      end if

      if ( a(i,j) /= 0.0D+00 ) then
        ival = -2
        return
      end if

    end do

  end do

  ival = 1

  return
end
subroutine r8mat_is_diag2 ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_DIAG2 counts the number of nonzero diagonals in an R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL, the number of nonzero diagonals.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ihi
  integer ( kind = 4 ) ilo
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) jmi
  integer ( kind = 4 ) nonzero

  ival = 0

  do jmi = 1 - m, n - 1
    nonzero = 0
    ilo = max ( 1, 1 - jmi )
    ihi = min ( m, n - jmi )
    do i = ilo, ihi
      if ( a(i,i+jmi) /= 0 ) then
        nonzero = 1
      end if
    end do
    ival = ival + nonzero
  end do

  return
end
subroutine r8mat_is_diagdom ( m, n, a, ival, jval )

!*****************************************************************************80
!
!! R8MAT_IS_DIAGDOM checks whether an R8MAT is diagonally dominant.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not row diagonally dominant.
!    1, the matrix is row diagonally dominant.
!    2, the matrix is strictly row diagonally dominant.
!
!    Output, integer ( kind = 4 ) JVAL:
!    -1, the matrix is not column diagonally dominant.
!    1, the matrix is column diagonally dominant.
!    2, the matrix is strictly column diagonally dominant.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) jval
  real ( kind = 8 ) s
  real ( kind = 8 ) sum2

  ival = 2

  do i = 1, min ( m, n )

    s = abs ( a(i,i) )
    sum2 = 0.0D+00
    do j = 1, n
      if ( j /= i ) then
        sum2 = sum2 + abs ( a(i,j) )
      end if
    end do

    if ( s == sum2 ) then
      if ( ival == 2 ) then
        ival = 1
      end if
    else if ( s < sum2 ) then
      ival = -1
    end if

  end do

  jval = 2

  do j = 1, min ( m, n )

    s = abs ( a(j,j) )
    sum2 = 0.0D+00
    do i = 1, m
      if ( j /= i ) then
        sum2 = sum2 + abs ( a(i,j) )
      end if
    end do

    if ( s == sum2 ) then
      if ( jval == 2 ) then
        jval = 1
      end if
    else if ( s < sum2 ) then
      jval = -1
    end if

  end do

  return
end
subroutine r8mat_is_eigen_right ( n, k, a, x, lambda, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_EIGEN_RIGHT determines the error in a (right) eigensystem.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    This routine computes the Frobenius norm of
!
!      A * X - X * LAMBDA
!
!    where
!
!      A is an N by N matrix,
!      X is an N by K matrix (each of K columns is an eigenvector)
!      LAMBDA is a K by K diagonal matrix of eigenvalues.
!
!    This routine assumes that A, X and LAMBDA are all real!
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, integer ( kind = 4 ) K, the number of eigenvectors.
!    K is usually 1 or N.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Input, real ( kind = 8 ) X(N,K), the K eigenvectors.
!
!    Input, real ( kind = 8 ) LAMBDA(K), the K eigenvalues.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius norm
!    of the difference matrix A * X - X * LAMBDA, which would be exactly zero
!    if X and LAMBDA were exact eigenvectors and eigenvalues of A.
!
  implicit none

  integer ( kind = 4 ) k
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) c(n,k)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) j
  real ( kind = 8 ) lambda(k)
  real ( kind = 8 ) r8mat_norm_fro
  real ( kind = 8 ) x(n,k)

  c(1:n,1:k) = matmul ( a(1:n,1:n), x(1:n,1:k) )

  do j = 1, k
    c(1:n,j) = c(1:n,j) - lambda(j) * x(1:n,j)
  end do

  error_frobenius = r8mat_norm_fro ( n, k, c )

  return
end
subroutine r8mat_is_hankel ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_HANKEL checks whether an R8MAT is Hankel.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A Hankel matrix is one which is constant along each anti-diagonal.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not Hankel.
!    1, the matrix is Hankel.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ihi
  integer ( kind = 4 ) ilo
  integer ( kind = 4 ) ipj
  integer ( kind = 4 ) ival
  real ( kind = 8 ) s

  ival = 1

  do ipj = 2, n + m
    ilo = max ( 1, ipj - n )
    ihi = min ( m, ipj - 1 )
    s = a(ilo,ipj-ilo)
    do i = ilo + 1, ihi
      if ( a(i,ipj-i) /= s ) then
        ival = -1
        return
      end if
    end do
  end do

  return
end
subroutine r8mat_is_identity ( n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_IDENTITY determines if an R8MAT is the identity.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The routine returns the Frobenius norm of A - I.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    02 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius norm
!    of the difference matrix A - I, which would be exactly zero
!    if A were the identity matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) value

  error_frobenius = 0.0D+00

  do i = 1, n
    do j = 1, n
      if ( i == j ) then
        error_frobenius = error_frobenius + ( a(i,j) - 1.0D+00 )**2
      else
        error_frobenius = error_frobenius + a(i,j)**2
      end if
    end do 
  end do

  error_frobenius = sqrt ( error_frobenius )

  return
end
subroutine r8mat_is_int ( m, n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_INT checks whether an R8MAT has only integer entries.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius norm of
!    the difference between A and the nearest integer matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  error_frobenius = 0.0D+00

  do i = 1, m
    do j = 1, n
      error_frobenius = error_frobenius + ( a(i,j) - anint ( a(i,j) ) )**2
    end do
  end do

  return
end
subroutine r8mat_is_inverse ( n, a, b, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_INVERSE determines if one R8MAT is the inverse of another.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    This routine  returns the sum of the Frobenius norms of
!    A * B - I and B * A - I.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 July 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), B(N,N), the matrices.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the sum of the Frobenius norms
!    of the difference matrices A * B - I and B * A - I which would both
!    be exactly zero if B was the exact inverse of A and computer arithmetic 
!    were exact.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) b(n,n)
  real ( kind = 8 ) error_frobenius
  real ( kind = 8 ) error_left
  real ( kind = 8 ) error_right

  call r8mat_is_inverse_left ( n, n, a, b, error_left )

  call r8mat_is_inverse_right ( n, n, a, b, error_right )

  error_frobenius = error_left + error_right

  return
end
subroutine r8mat_is_inverse_left ( m, n, a, b, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_INVERSE_LEFT determines if one R8MAT is the left inverse of another.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    This routine returns the Frobenius norm of the NxN matrix:
!
!      B * A - I.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 January 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix A.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix to be checked.
!
!    Input, real ( kind = 8 ) B(N,M), the matrix which is to be tested
!    as a left inverse of A.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius norm
!    of the difference matrix B * A - I, which would be exactly zero
!    if B was the exact left inverse of A and computer arithmetic were exact.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) b(n,m)
  real ( kind = 8 ) c(n,n)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  real ( kind = 8 ) r8mat_norm_fro

  c(1:n,1:n) = matmul ( b(1:n,1:m), a(1:m,1:n) )

  do i = 1, n
    c(i,i) = c(i,i) - 1.0D+00
  end do

  error_frobenius = r8mat_norm_fro ( n, n, c )

  return
end
subroutine r8mat_is_inverse_right ( m, n, a, b, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_INVERSE_RIGHT: is one R8MAT the right inverse of another.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    This routine returns the Frobenius norm of the MxM matrix:
!
!      A * B - I.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 January 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix A.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix to be checked.
!
!    Input, real ( kind = 8 ) B(N,M), the matrix which is to be tested
!    as a left inverse of A.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius norm
!    of the difference matrix A * B - I, which would be exactly zero
!    if B was the exact right inverse of A and computer arithmetic were exact.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) b(n,m)
  real ( kind = 8 ) c(m,m)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  real ( kind = 8 ) r8mat_norm_fro

  c(1:m,1:m) = matmul ( a(1:m,1:n), b(1:n,1:m) )

  do i = 1, m
    c(i,i) = c(i,i) - 1.0D+00
  end do

  error_frobenius = r8mat_norm_fro ( m, m, c )

  return
end
subroutine r8mat_is_irreducible ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_IRREDUCIBLE determines if an R8MAT is irreducible.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The procedure is based on the idea that a matrix can be viewed as
!    the (weighted) adjacency matrix of a directed graph.  Ignoring the
!    sign and magnitude of the entries, we say there is a directed edge from
!    node I to node J if and only if A(I,J) is nonzero.  Then A is
!    irreducible if and only if this directed graph is strongly connected;
!    that is, if and only if it is possible to start at any node and
!    reach any other (distinct) node by a sequence of connecting edges.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    25 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL,
!    -1, the matrix is not irreducible;
!    1, the matrix is irreducible.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) base
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) level
  integer ( kind = 4 ) list(n)
  integer ( kind = 4 ) nfound
  integer ( kind = 4 ) ntotal

  if ( m /= n ) then
    ival = -1
    return
  end if

  do base = 1, n

    list(1:n) = 0
    list(base) = 1

    nfound = 1
    ntotal = 1

    level = 0

    do

      level = level + 1
      nfound = 0

      do i = 1, n

        if ( list(i) == level ) then

          do j = 1, n

            if ( a(i,j) /= 0.0D+00 ) then

              if ( list(j) == 0 ) then
                list(j) = level + 1
                nfound = nfound + 1
              end if

            end if

          end do
        end if
      end do

      ntotal = ntotal + nfound

      if ( n <= ntotal ) then
        exit
      end if

      if ( nfound == 0 ) then
        ival = -1
        return
      end if

    end do

  end do

  ival = 1

  return
end
subroutine r8mat_is_l ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_L checks whether an R8MAT is an L matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A matrix is an L matrix if it has positive diagonal and nonpositive
!    offdiagonal entries.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL: the matrix is:
!    -1, not an L matrix because a diagonal entry is nonpositive.
!    -2, not an L matrix because an offdiagonal entry is positive.
!    1, an L matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j

  do i = 1, n
    if ( a(i,i) <= 0.0D+00 ) then
      ival = -1
      return
    end if
  end do

  do i = 1, n
    do j = 1, n
      if ( i /= j ) then
        if ( 0.0D+00 < a(i,i) ) then
          ival = -2
          return
        end if
      end if
    end do
  end do

  ival = 1

  return
end
subroutine r8mat_is_m ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_M checks whether an R8MAT is an M matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL: the matrix is:
!    -1, not an M matrix because it is not square.
!    -2, not an M matrix because its offdiagonal is not nonpositive.
!    -3, not an M matrix because it is not invertible;
!    -4, not an M matrix because its inverse is not nonnegative.
!    1, an M matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) a2(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) info
  integer ( kind = 4 ) pivot(n)
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j

  ival = 1

  if ( m /= n ) then
    ival = -1
    return
  end if

  do i = 1, n
    do j = 1, n

      if ( i == j ) then

      else if ( 0.0D+00 < a(i,j) ) then
        ival = -2
        return
      end if

    end do
  end do

  a2(1:n,1:n) = a(1:n,1:n)

  call r8mat_gefa ( a2, n, pivot, info )

  if ( info /= 0 ) then
    ival = -3
    return
  end if

  call r8mat_geinverse ( a2, n, pivot )

  do i = 1, n
    do j = 1, n
      if ( a2(i,j) < 0.0D+00 ) then
        ival = -4
        return
      end if
    end do
  end do

  return
end
subroutine r8mat_is_normal ( m, n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_NORMAL determines whether an R8MAT is normal.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A real square matrix A is normal if
!
!      A * A' = A' * A.
!
!    A (real) matrix is normal if and only if it is diagonalizable,
!    that is, orthogonally similar to a diagonal matrix:
!
!      A = U' * D * U.
!
!    A matrix is automatically normal if it is symmetric, anti-symmetric,
!    orthogonal, or diagonal.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius normality
!    error, which is 0 if the matrix is exactly normal.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) bij
  real ( kind = 8 ) cij
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) r8_huge
!
!  If not square, no hope.
!
  if ( m /= n ) then
    error_frobenius = r8_huge ( )
    return
  end if
!
!  Compare A * A' to A' * A.
!
  error_frobenius = 0.0D+00

  do i = 1, m
    do j = 1, n

      bij = 0.0D+00
      cij = 0.0D+00
      do k = 1, n
        bij = bij + a(i,k) * a(j,k)
        cij = cij + a(k,i) * a(k,j)
      end do

      error_frobenius = error_frobenius + ( bij - cij )**2

    end do
  end do

  error_frobenius = sqrt ( error_frobenius )

  return
end
subroutine r8mat_is_null_vector ( m, n, a, x, error_l2 )

!*****************************************************************************80
!
!! R8MAT_IS_NULL_VECTOR determines if vector x is a null vector of an R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The nonzero N vector x is a null vector of the MxN matrix A if
!
!      A * x = 0
!
!    If A is a square matrix, then this implies that A is singular.
!
!    If A is a square matrix, this implies that 0 is an eigenvalue of A,
!    and that x is an associated eigenvector.
!
!    This routine returns 0 if x is exactly a null vector of A.
!
!    It returns a "huge" value if x is the zero vector.
!
!    Otherwise, it returns the L2 norm of A * x divided by the L2 norm of x:
!
!      ERROR_L2 = NORM_L2 ( A * x ) / NORM_L2 ( x )
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Input, real ( kind = 8 ) X(N), the vector.
!
!    Output, real ( kind = 8 ) ERROR_L2, the result.
!    0.0 indicates that X is exactly a null vector.
!    A "huge" value indicates that ||x|| = 0;
!    Otherwise, the value returned is a relative error ||A*x||/||x||.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) ax_norm
  real ( kind = 8 ) error_l2
  real ( kind = 8 ) r8_huge
  real ( kind = 8 ) x(n)
  real ( kind = 8 ) x_norm

  x_norm = sqrt ( sum ( x(1:n)**2 ) )

  if ( x_norm == 0.0D+00 ) then
    error_l2 = r8_huge ( )
    return
  end if

  ax_norm = sqrt ( sum ( ( matmul ( a(1:m,1:n), x(1:n) ) )**2 ) )

  error_l2 = ax_norm / x_norm

  return
end
subroutine r8mat_is_orthogonal ( m, n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_ORTHOGONAL checks whether an R8MAT is orthogonal.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius orthogonality
!    error, which is zero if the matrix is exactly orthogonal.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ), allocatable, dimension ( :, : ) :: b
  integer ( kind = 4 ) error_frobenius
  real ( kind = 8 ) r8_huge

  if ( m /= n ) then
    error_frobenius = r8_huge ( )
    return
  end if

  allocate ( b(1:n,1:n) )

  b(1:n,1:n) = matmul ( transpose ( a(1:n,1:n) ), a(1:n,1:n) )

  call r8mat_is_identity ( n, b, error_frobenius )

  deallocate ( b )

  return
end
subroutine r8mat_is_pds ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_PDS checks for if an R8MAT is positive definite symmetric.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    26 March 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not square.
!    -2, the matrix is not symmetric.
!    -3, the matrix is symmetric, but not positive definite.
!    0, the matrix is positive semi-definite symmetric.
!    1, the matrix is positive definite symmetric.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) c(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ierror
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j

  if ( m /= n ) then
    ival = -1
    return
  end if

  do i = 1, n
    do j = 1, i - 1
      if ( a(i,j) /= a(j,i) ) then
        ival = -2
        return
      end if
    end do
  end do

  call r8mat_cholesky_factor ( n, a, c, ierror )

  if ( ierror == 2 ) then
    ival = -3
  else if ( ierror == 1 ) then
    ival = 0
  else
    ival = 1
  end if

  return
end
subroutine r8mat_is_perm ( n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_PERM checks whether an R8MAT is a permutation matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not a zero-one matrix.
!    -2, there is a row that does not sum to 1.
!    -3, there is a column that does not sum to 1.
!    1, the matrix is a permutation matrix,
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) jval

  call r8mat_is_zero_one ( n, n, a, jval )

  if ( jval /= 1 ) then
    ival = -1
    return
  end if

  do i = 1, n
    if ( sum ( a(i,1:n) ) /= 1.0D+00 ) then
      ival = -2
      return
    end if
  end do

  do j = 1, n
    if ( sum ( a(1:n,j) ) /= 1.0D+00 ) then
      ival = -3
      return
    end if
  end do

  ival = 1

  return
end
subroutine r8mat_is_persymm ( m, n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_PERSYMM checks an R8MAT for persymmetry.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A is persymmetric if A(I,J) = A(N+1-J,N+1-I).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius error
!    in persymmetry, which will be 0 if the matrix is exactly
!    persymmetric.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  error_frobenius = 0.0D+00

  do i = 1, min ( m, n )
    do j = n, max ( 1, n - m + 1 ), -1
      error_frobenius = error_frobenius + ( a(i,j) - a(n+1-j,n+1-i) )**2
    end do
  end do

  error_frobenius = sqrt ( error_frobenius )

  return
end
subroutine r8mat_is_plu ( m, n, a, p, l, u, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_PLU measures the error in a PLU factorization.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    This routine simply returns the Frobenius norm of the M x N matrix:
!    A - P * L * U.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    16 August 2008
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Input, real ( kind = 8 ) P(M,M), L(M,M), U(M,N), the PLU factors.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius norm
!    of the difference matrix A  - P * L * U.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) d(m,n)
  real ( kind = 8 ) error_frobenius
  real ( kind = 8 ) l(m,m)
  real ( kind = 8 ) p(m,m)
  real ( kind = 8 ) r8mat_norm_fro
  real ( kind = 8 ) u(m,n)

  d(1:m,1:n) = a(1:m,1:n) - matmul ( p(1:m,1:m), &
                            matmul ( l(1:m,1:m), u(1:m,1:n) ) )

  error_frobenius = r8mat_norm_fro ( m, n, d )

  return
end
subroutine r8mat_is_positive ( m, n, a, ival, jval )

!*****************************************************************************80
!
!! R8MAT_IS_POSITIVE checks whether an R8MAT is a positive matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not positive.
!    0, the matrix is nonnegative.
!    1, the matrix is positive.
!
!    Output, integer ( kind = 4 ) JVAL:
!    -1, the matrix is not negative.
!    0, the matrix is nonpositive.
!    1, the matrix is negative.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) jval

  ival = 1
  jval = 1

  do i = 1, m
    do j = 1, n

      if ( 0.0D+00 < a(i,j) ) then
        jval = -1
      else if ( a(i,j) == 0.0D+00 ) then
        if ( ival == 1 ) then
          ival = 0
        end if
        if ( jval == 1 ) then
          jval = 0
        end if
      else if ( a(i,j) < 0.0D+00 ) then
        ival = -1
      end if

    end do
  end do

  return
end
subroutine r8mat_is_propa ( n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_PROPA checks whether an R8MAT has property A.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A matrix has property A if the indices 1 through N can be divided
!    into two sets, S1 and S2, in such a way that the only nonzero
!    offdiagonal elements occur as "connections" between sets S1 and S2.
!
!    In other words, if I /= J, then A(I,J) /= 0 implies that I is in
!    S1 and J is in S2, or vice versa.
!
!    In this case, A can be reordered to have the form:
!
!      D1  B
!      C   D2
!
!    where D1 and D2 are diagonal matrices.
!
!    A matrix has property A is roughly the same as saying that the
!    corresponding graph ( I is connected to J if and only if A(I,J)
!    or A(J,I) is nonzero) is bipartite.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    23 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix does not have property A.
!    1, the matrix has property A.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) nset
  integer ( kind = 4 ) nset_old
  integer ( kind = 4 ) nstack1
  integer ( kind = 4 ) nstack2
  integer ( kind = 4 ) set(n)
  integer ( kind = 4 ) stack1(n)
  integer ( kind = 4 ) stack2(n)

  set(1:n) = 0
  nset = 0

  nstack1 = 0
  nstack2 = 0

  nstack1 = nstack1 + 1
  stack1(nstack1) = 1
  set(1) = 1
  nset = nset + 1

  do
!
!  Check all connections from the new candidates for set 1...
!  ...to old nodes, for consistency, and
!  ...to new nodes, to find candidates for the next sweep.
!
    nset_old = nset

    do while ( 0 < nstack1 )

      i = stack1(nstack1)

      do j = 1, n

        if ( j == i ) then
          cycle
        end if

        if ( a(i,j) == 0.0D+00 .and. a(j,i) == 0.0D+00 ) then
          cycle
        end if

        if ( set(j) == 2 ) then
          cycle
        else if ( set(j) == 1 ) then
          ival = -1
          return
        end if

        nstack2 = nstack2 + 1
        stack2(nstack2) = j
        set(j) = 2
        nset = nset + 1

      end do

      nstack1 = nstack1 - 1

    end do
!
!  If we didn't add any new neighbors...
!
    if ( nset == nset_old ) then
!
!  ...and the current candidates make up the rest of the set,
!  then we've looked at everything and we're done.
!
      if ( nset == n ) then
        ival = 1
        exit
      end if
!
!  ...otherwise, grab an unused index as a new candidate, and continue.
!
      do i = 1, n
        if ( set(i) == 0 ) then
          nstack2 = nstack2 + 1
          stack2(nstack2) = i
          set(i) = 2
          nset = nset + 1
          exit
        end if
      end do

    end if
!
!  Check all connections from the new candidates for set 2...
!  ...to old nodes, for consistency, and
!  ...to new nodes, to find candidates for the next sweep.
!
    nset_old = nset

    do while ( 0 < nstack2 )

      i = stack2(nstack2)

      do j = 1, n

        if ( j == i ) then
          cycle
        end if

        if ( a(i,j) == 0.0D+00 .and. a(j,i) == 0.0D+00 ) then
          cycle
        end if

        if ( set(j) == 1 ) then
          cycle
        else if ( set(j) == 2 ) then
          ival = -1
          return
        end if

        nstack1 = nstack1 + 1
        stack1(nstack1) = j
        set(j) = 1
        nset = nset + 1

      end do

      nstack2 = nstack2 - 1

    end do
!
!  If we didn't add any new neighbors...
!
    if ( nset == nset_old ) then
!
!  ...and the current candidates make up the rest of the set,
!  then we've looked at everything and we're done.
!
      if ( nset == n ) then
        ival = 1
        exit
      end if
!
!  ...otherwise, grab an unused index as a new candidate, and continue.
!
      do i = 1, n
        if ( set(i) == 0 ) then
          nstack1 = nstack1 + 1
          stack1(nstack1) = i
          set(i) = 1
          nset = nset + 1
          exit
        end if
      end do
    end if

  end do

  return
end
subroutine r8mat_is_ref ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_REF determines whether an R8MAT is in row echelon form.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the ow and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not in row echelon form.
!    1, the matrix is in row echelon form.
!    2, the matrix is in reduced row echelon form.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i2
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) last_one
  integer ( kind = 4 ) this_one

  ival = 2
  last_one = 0

  do i = 1, m

    this_one = n + 1

    do j = n, 1, -1
      if ( a(i,j) /= 0.0D+00 ) then
        this_one = j
      end if
    end do

    if ( this_one <= n ) then

      if ( a(i,this_one) /= 1.0D+00 ) then
        ival = -1
        return
      end if

      if ( this_one <= last_one ) then
        ival = -1
        return
      end if

      do i2 = 1, m
        if ( i2 /= i ) then
          if ( a(i2,this_one) /= 0.0D+00 ) then
            ival = 1
          end if
        end if
      end do

    end if

    last_one = this_one

  end do

  return
end
subroutine r8mat_is_row_orthogonal ( m, n, a, error_sum )

!*****************************************************************************80
!
!! R8MAT_IS_ROW_ORTHOGONAL checks whether an R8MAT is row orthogonal.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_SUM, the sum of the errors.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_sum
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i2

  error_sum = 0.0D+00

  do i = 1, m

    do i2 = i + 1, m

      error_sum = error_sum + ( dot_product ( a(i,1:n), a(i2,1:n) ) )**2

    end do

  end do

  error_sum = sqrt ( error_sum )

  return
end
subroutine r8mat_is_scalar ( m, n, a, ival, jval, kval, lval )

!*****************************************************************************80
!
!! R8MAT_IS_SCALAR checks whether an R8MAT is a scalar along rows, columns, etc.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not row scalar.
!    1, the matrix is row scalar.
!
!    Output, integer ( kind = 4 ) JVAL:
!    -1, the matrix is not column scalar.
!    1, the matrix is column scalar.
!
!    Output, integer ( kind = 4 ) KVAL:
!    -1, the matrix is not diagonal scalar.
!    1, the matrix is diagonal scalar.
!
!    Output, integer ( kind = 4 ) LVAL:
!    -1, the matrix is not antidiagonal scalar.
!    1, the matrix is antidiagonal scalar.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ihi
  integer ( kind = 4 ) ilo
  integer ( kind = 4 ) ipj
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) jmi
  integer ( kind = 4 ) jval
  integer ( kind = 4 ) kval
  integer ( kind = 4 ) lval
  real ( kind = 8 ) s

  ival = 1

  do i = 1, m
    s = a(i,1)
    do j = 2, n
      if ( a(i,j) /= s ) then
        ival = -1
      end if
    end do
  end do

  jval = 1

  do j = 1, n
    s = a(1,j)
    do i = 2, m
      if ( a(i,j) /= s ) then
        jval = -1
      end if
    end do
  end do

  kval = 1

  do jmi = 1 - m, n - 1
    ilo = max ( 1, 1 - jmi )
    ihi = min ( m, n - jmi )
    s = a(ilo,ilo+jmi)
    do i = ilo, ihi
      if ( a(i,i+jmi) /= s ) then
        kval = -1
      end if
    end do
  end do

  lval = 1

  do ipj = 2, n + m
    ilo = max ( 1, ipj - n )
    ihi = min ( m, ipj - 1 )
    s = a(ilo,ipj-ilo)
    do i = ilo + 1, ihi
      if ( a(i,ipj-i) /= s ) then
        lval = -1
      end if
    end do
  end do

  return
end
subroutine r8mat_is_solution ( m, n, k, a, x, b, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_SOLUTION measures the error in a linear system solution.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The system matrix A is an M x N matrix.
!    It is not required that A be invertible.
!
!    The solution vector X is actually allowed to be an N x K matrix.
!
!    The right hand side "vector" B is actually allowed to be an M x K matrix.
!
!    This routine simply returns the Frobenius norm of the M x K matrix:
!    A * X - B.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    28 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, K, the order of the matrices.
!
!    Input, real ( kind = 8 ) A(M,N), X(N,K), B(M,K), the matrices.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius norm
!    of the difference matrix A * X - B, which would be exactly zero
!    if X was the "solution" of the linear system.
!
  implicit none

  integer ( kind = 4 ) k
  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) b(m,k)
  real ( kind = 8 ) c(m,k)
  real ( kind = 8 ) error_frobenius
  real ( kind = 8 ) r8mat_norm_fro
  real ( kind = 8 ) x(n,k)

  c(1:m,1:k) = matmul ( a(1:m,1:n), x(1:n,1:k) ) - b(1:m,1:k)

  error_frobenius = r8mat_norm_fro ( m, k, c )

  return
end
subroutine r8mat_is_sparse ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_SPARSE counts the number of zero entries in an R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL, the number of zero entries.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j

  ival = 0

  do i = 1, m
    do j = 1, n
      if ( a(i,j) == 0.0D+00 ) then
        ival = ival + 1
      end if
    end do
  end do

  return
end
subroutine r8mat_is_sum ( m, n, a, ival, jval, kval )

!*****************************************************************************80
!
!! R8MAT_IS_SUM checks whether an R8MAT has constant sums on rows or columns.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not row sum constant.
!    1, the matrix is row sum constant.
!    2, the matrix is row sum constant with constant = 1 (Markov/stochastic).
!
!    Output, integer ( kind = 4 ) JVAL:
!    -1, the matrix is not column sum constant.
!    1, the matrix is column sum constant.
!    2, the matrix is column sum constant with constant = 1 (transition).
!
!    Output, integer ( kind = 4 ) KVAL:
!    -1, row sum constant not equal to column sum constant.
!    1, row sum constant = column sum constant (weak magic).
!    2, row sum constant = column sum constant = 1 (biMarkov/double stochastic).
!    3, row sum constant = column sum constant
!       = main diagonals sum constant (magic)
!    4, row sum constant = column sum constant
!       = main diagonals sum constant = 1 (magic biMarkov)
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) a_norm
  real ( kind = 8 ) cave
  real ( kind = 8 ) col_sum(n)
  real ( kind = 8 ) cmax
  real ( kind = 8 ) cmin
  real ( kind = 8 ) ctol
  real ( kind = 8 ) dave
  real ( kind = 8 ) dsum1
  real ( kind = 8 ) dsum2
  real ( kind = 8 ) dtol
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) jval
  integer ( kind = 4 ) kval
  real ( kind = 8 ) rave
  real ( kind = 8 ) rmax
  real ( kind = 8 ) rmin
  real ( kind = 8 ) row_sum(m)
  real ( kind = 8 ) rtol
  real ( kind = 8 ), parameter :: tol = 0.0001D+00

  a_norm = sum ( abs ( a(1:m,1:n) ) )
!
!  Look at row sums.
!
  ival = -1

  row_sum(1:m) = sum ( a(1:m,1:n), dim = 2 )

  rmax = maxval ( row_sum(1:m) )
  rmin = minval ( row_sum(1:m) )
  rave = 0.5D+00 * ( rmax + rmin )
  rtol = tol * ( 1.0D+00 + a_norm / real ( n, kind = 8 ) )

  if ( abs ( rmax - rmin ) <= rtol ) then
    ival = 1
    if ( abs ( rave - 1.0D+00 ) <= rtol ) then
      ival = 2
    end if
  end if
!
!  Look at column sums.
!
  jval = -1

  col_sum(1:n) = sum ( a(1:m,1:n), dim = 1 )

  cmax = maxval ( col_sum(1:n) )
  cmin = minval ( col_sum(1:n) )
  cave = 0.5D+00 * ( cmax + cmin )
  ctol = tol * ( 1.0D+00 + a_norm / real ( m, kind = 8 ) )

  if ( abs ( cmax - cmin ) <= ctol ) then
    jval = 1
    if ( abs ( cave - 1.0D+00 ) <= ctol ) then
      jval = 2
    end if
  end if
!
!  Compute diagonal sums, compare row and column situations,
!  and see if we can throw in the diagonal as well.
!
  kval = -1

  dsum1 = 0.0D+00
  do i = 1, min ( m, n )
    dsum1 = dsum1 + a(i,i)
  end do

  dsum2 = 0.0D+00
  do i = 1, min ( m, n )
    dsum2 = dsum2 + a(i,n+1-i)
  end do

  dave = 0.5D+00 * ( dsum1 + dsum2 )

  dtol = tol * ( 1.0D+00 + a_norm / real ( min ( m, n ), kind = 8 ) )

  if ( 1 <= ival .and. 1 <= jval ) then

    if ( abs ( rave - cave ) <= dtol ) then

      kval = 1

      if ( ival == 2 .and. jval == 2 ) then
        kval = 2
      end if

      if ( abs ( dsum1 - dsum2 ) <= dtol ) then
        if ( abs ( dave - rave ) <= dtol ) then
          kval = kval + 2
        end if
      end if

    end if

  end if

  return
end
subroutine r8mat_is_symmetric ( m, n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_SYMMETRIC checks an R8MAT for symmetry.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    16 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, measures the 
!    Frobenius norm of ( A - A' ), which would be zero if the matrix
!    were exactly symmetric.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_frobenius
  real ( kind = 8 ) r8_huge

  if ( m /= n ) then
    error_frobenius = r8_huge ( )
    return
  end if

  error_frobenius = sqrt ( &
                      sum ( &
                        ( &
                          abs ( a(1:m,1:n) - transpose ( a(1:m,1:n) ) ) &
                         )**2 &
                       ) &
                     )

  return
end
subroutine r8mat_is_toeplitz ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_TOEPLITZ checks if an R8MAT is a Toeplitz matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A Toeplitz matrix is constant along each diagonal.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not Toeplitz.
!    1, the matrix is Toeplitz.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  integer ( kind = 4 ) khi
  real ( kind = 8 ) test

  ival = 1

  do i = 1, m
    test = a(i,1)
    khi = min ( m - i, n - 1 )
    do k = 1, khi
      if ( a(i+k,1+k) /= test ) then
        ival = -1
        return
      end if
    end do
  end do

  do j = 2, n
    test = a(1,j)
    khi = min ( m - 1, n - j )
    do k = 1, khi
      if ( a(1+k,j+k) /= test ) then
        ival = -1
        return
      end if
    end do
  end do

  return
end
subroutine r8mat_is_tournament ( n, a, error_frobenius )

!*****************************************************************************80
!
!! R8MAT_IS_TOURNAMENT checks if an R8MAT is a tournament matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    A matrix is a tournament matrix if:
!    * it is square, and
!    * it is antisymmetric: A' = -A, and
!    * the offdiagonal elements are either 1 or -1.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_FROBENIUS, the Frobenius tournament error.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) error_frobenius
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  call r8mat_is_antisymm ( n, n, a, error_frobenius )

  error_frobenius = error_frobenius**2

  do i = 1, n - 1
    do j = i + 1, n
      error_frobenius = error_frobenius + ( abs ( a(i,j) ) - 1.0D+00 )**2
    end do
  end do

  error_frobenius = sqrt ( error_frobenius )

  return
end
subroutine r8mat_is_triangular ( m, n, a, ival, jval )

!*****************************************************************************80
!
!! R8MAT_IS_TRIANGULAR determines whether an R8MAT is triangular.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 November 1998
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not upper triangular
!    1, the matrix is upper triangular.
!    2, the matrix is unit upper triangular.
!
!    Output, integer ( kind = 4 ) JVAL:
!    -1, the matrix is not lower triangular.
!    1, the matrix is lower triangular.
!    2, the matrix is unit lower triangular.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j
  integer ( kind = 4 ) jval

  ival = 2
  jval = 2

  do i = 1, m
    do j = 1, n

      if ( i == j ) then
        if ( a(i,j) /= 1.0D+00 ) then
          if ( ival == 2 ) then
            ival = 1
          end if
          if ( jval == 2 ) then
            jval = 1
          end if
        end if
      else if ( i < j ) then
        if ( a(i,j) /= 0.0D+00 ) then
          jval = -1
        end if
      else if ( j < i ) then
        if ( a(i,j) /= 0.0D+00 ) then
          ival = -1
        end if
      end if

      if ( ival == -1 .and. jval == -1 ) then
        return
      end if

    end do
  end do

  return
end
subroutine r8mat_is_unit_column ( m, n, a, error_sum )

!*****************************************************************************80
!
!! R8MAT_IS_UNIT_COLUMN checks if an R8MAT has columns of unit Euclidean norm.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) ERROR_SUM, the error sum.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_sum
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8vec_norm_l2

  error_sum = 0.0D+00

  do j = 1, n

    error_sum = error_sum + ( 1.0D+00 - r8vec_norm_l2 ( m, a(1:m,j) ) )**2

  end do

  error_sum = sqrt ( error_sum )

  return
end
subroutine r8mat_is_unit_row ( m, n, a, error_sum )

!*****************************************************************************80
!
!! R8MAT_IS_UNIT_ROW checks whether an R8MAT has rows of unit Euclidean norm.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) ERROR_SUM, the sum of the errors.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) error_sum
  integer ( kind = 4 ) i
  real ( kind = 8 ) r8vec_norm_l2

  error_sum = 0.0D+00

  do i = 1, m

    error_sum = error_sum + ( 1.0D+00 - r8vec_norm_l2 ( n, a(i,1:n) ) )**2

  end do

  error_sum = sqrt ( error_sum )

  return
end
subroutine r8mat_is_zero_one ( m, n, a, ival )

!*****************************************************************************80
!
!! R8MAT_IS_ZERO_ONE checks whether an R8MAT is a zero/one matrix.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    07 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the row and column dimensions of 
!    the matrix.  M and N must be positive.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, integer ( kind = 4 ) IVAL:
!    -1, the matrix is not a zero/one matrix.
!    1, the matrix is a zero/one matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) ival
  integer ( kind = 4 ) j

  do i = 1, m
    do j = 1, n

      if ( a(i,j) /= 0.0D+00 .and. a(i,j) /= 1.0D+00 ) then
        ival = -1
        return
      end if

    end do
  end do

  ival = 1

  return
end
function r8mat_norm_eis ( m, n, a )

!*****************************************************************************80
!
!! R8MAT_NORM_EIS returns the EISPACK norm of an M by N R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The EISPACK norm is defined as:
!
!      R8MAT_NORM_EIS =  sum ( 1 <= I <= M ) sum ( 1 <= J <= N )
!        ( abs ( A(I,J) ) ) )
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) R8MAT_NORM_EIS, the EISPACK norm of A.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) r8mat_norm_eis

  r8mat_norm_eis = sum ( abs ( a(1:m,1:n) ) )

  return
end
function r8mat_norm_fro ( m, n, a )

!*****************************************************************************80
!
!! R8MAT_NORM_FRO returns the Frobenius norm of an M by N R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The Frobenius norm is defined as
!
!      R8MAT_NORM_FRO = sqrt (
!        sum ( 1 <= I <= M ) Sum ( 1 <= J <= N ) A(I,J)^2 )
!
!    The matrix Frobenius-norm is not derived from a vector norm, but
!    is compatible with the vector L2 norm, so that:
!
!      r8vec_norm_l2 ( A*x ) <= r8mat_norm_fro ( A ) * r8vec_norm_l2 ( x ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) R8MAT_NORM_FRO, the Frobenius norm of A.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) r8mat_norm_fro

  r8mat_norm_fro = sqrt ( sum ( a(1:m,1:n)**2 ) )

  return
end
function r8mat_norm_l1 ( m, n, a )

!*****************************************************************************80
!
!! R8MAT_NORM_L1 returns the matrix L1 norm of an M by N R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The matrix L1 norm is defined as:
!
!      R8MAT_NORM_L1 =  max ( 1 <= J <= N )
!        sum ( 1 <= I <= M ) abs ( A(I,J) ).
!
!    The matrix L1 norm is derived from the vector L1 norm, and
!    satisifies:
!
!      r8vec_norm_l1 ( A*x ) <= r8mat_norm_l1 ( A ) * r8vec_norm_l1 ( x ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) R8MAT_NORM_L1, the L1 norm of A.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8mat_norm_l1

  r8mat_norm_l1 = 0.0D+00

  do j = 1, n
    r8mat_norm_l1 = max ( r8mat_norm_l1, sum ( abs ( a(1:m,j) ) ) )
  end do

  return
end
function r8mat_norm_l2 ( m, n, a )

!*****************************************************************************80
!
!! R8MAT_NORM_L2 returns the matrix L2 norm of an M by N R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The matrix L2 norm is defined as:
!
!      || A || =  sqrt ( max ( 1 <= I <= M ) LAMBDA(I) )
!
!    where LAMBDA contains the eigenvalues of A * A'.
!
!    The matrix L2 norm is derived from the vector L2 norm, and satisifies:
!
!      r8vec_norm_l2 ( A*x ) <= r8mat_norm_l2 ( A ) * r8vec_norm_l2 ( x ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    19 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) R8MAT_NORM_L2, the L2 norm of A.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  real ( kind = 8 ) b(m,m)
  real ( kind = 8 ) lambda(m)
  real ( kind = 8 ) r8mat_norm_l2
  real ( kind = 8 ) x(m,m)
!
!  Compute the M by M matrix B = A * A'.
!
  b(1:m,1:m) = matmul ( a(1:m,1:n), transpose ( a(1:m,1:n) ) )
!
!  Diagonalize B.
!
  call jacobi_iterate ( m, b, lambda, x )
!
!  Find the maximum eigenvalue, and take its square root.
!
  r8mat_norm_l2 = sqrt ( maxval ( lambda(1:m) ) )

  return
end
function r8mat_norm_li ( m, n, a )

!*****************************************************************************80
!
!! R8MAT_NORM_LI returns the matrix L-infinity norm of an M by N R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The matrix L-infinity norm is defined as:
!
!      R8MAT_NORM_LI =  Max ( 1 <= I <= M )
!        sum ( 1 <= J <= N ) abs ( A(I,J) ).
!
!    The matrix L-infinity norm is derived from the vector L-infinity norm,
!    and satisifies:
!
!      r8vec_norm_li ( A*x ) <= r8mat_norm_li ( A ) * r8vec_norm_li ( x ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(M,N), the matrix.
!
!    Output, real ( kind = 8 ) R8MAT_NORMI_LI, the L-infinity norm of A.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  real ( kind = 8 ) r8mat_norm_li

  r8mat_norm_li = 0.0D+00

  do i = 1, m
    r8mat_norm_li = max ( r8mat_norm_li, sum ( abs ( a(i,1:n) ) ) )
  end do

  return
end
function r8mat_norm_spec ( n, a )

!*****************************************************************************80
!
!! R8MAT_NORM_SPEC returns the spectral radius norm of an N by N R8MAT.
!
!  Discussion:
!
!    An R8MAT is a matrix of real ( kind = 8 ) values.
!
!    The spectral radius norm is defined as:
!
!      R8MAT_NORM_SPEC =  max ( 1 <= I <= N ) abs ( LAMBDA ( I ) )
!
!    where LAMBDA is the vector of eigenvalues of A.
!
!    A simple-minded power method approach is used.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    13 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, real ( kind = 8 ) R8MAT_NORM_SPEC, the estimated spectral 
!    radius norm of A.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) eps
  integer ( kind = 4 ) it
  real ( kind = 8 ), parameter :: it_eps = 0.0001D+00
  integer ( kind = 4 ), parameter :: it_max = 100
  integer ( kind = 4 ), parameter :: it_min = 10
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8mat_norm_spec
  real ( kind = 8 ) r8vec_norm_l2
  real ( kind = 8 ) s
  real ( kind = 8 ) s2
  real ( kind = 8 ) s_old
  real ( kind = 8 ) x(n)
  real ( kind = 8 ) y(n)

  eps = sqrt ( epsilon ( 1.0D+00 ) )
  x(1:n) = 1.0D+00

  s = r8vec_norm_l2 ( n, x )

  x(1:n) = x(1:n) / s

  do it = 1, it_max

    y(1:n) = matmul ( a(1:n,1:n), x(1:n) )

    s_old = s
    s = r8vec_norm_l2 ( n, y )

    if ( it_min < it ) then
      if ( abs ( s - s_old ) <= it_eps * ( 1.0D+00 + abs ( s ) ) ) then
        exit
      end if
    end if

    x(1:n) = y(1:n)

    if ( s /= 0.0D+00 ) then
      x(1:n) = x(1:n) / s
    end if
!
!  Perturb X a bit, to avoid cases where the initial guess is exactly
!  the eigenvector of a smaller eigenvalue.
!
    if ( it < it_max / 2 ) then
      j = 1 + mod ( it - 1, n )
      x(j) = x(j) + eps * ( 1.0D+00 + abs ( x(j) ) )
      s2 = r8vec_norm_l2 ( n, x )
      x(1:n) = x(1:n) / s2
    end if

  end do

  r8mat_norm_spec = s

  return
end
subroutine r8mat_plot ( m, n, a, title )

!*****************************************************************************80
!
!! R8MAT_PLOT "plots" an R8MAT with an optional title.
!
!  Discussion:
!
