subroutine routh ( n, x, a )

!*****************************************************************************80
!
!! ROUTH returns the ROUTH matrix.
!
!  Formula:
!
!    A is tridiagonal.
!    A(1,1)   =          X(1).
!    A(I-1,I) =   sqrt ( X(I) ), for I = 2 to N.
!    A(I,I-1) = - sqrt ( X(I) ), for I = 2 to N.
!
!  Example:
!
!    N = 5, X = ( 1, 4, 9, 16, 25 )
!
!    1 -2  0  0  0
!    2  0 -3  0  0
!    0  3  0 -4  0
!    0  0  4  0 -5
!    0  0  0  5  0
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is tridiagonal.
!
!    Because A is tridiagonal, it has property A (bipartite).
!
!    A is banded, with bandwidth 3.
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    det ( A ) = product ( X(N) * X(N-2) * X(N-4) * ... * X(N+1-2*(N/2)) )
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    04 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the data that defines the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n)

  if ( any ( x(2:n) < 0.0D+00 ) ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'ROUTH - Fatal error!'
    write ( *, '(a)' ) '  At least one of X(2:N) was negative.'
    stop
  end if

  do i = 1, n
    do j = 1, n

      if ( i == 1 .and. j == 1 ) then
        a(i,j) = x(1)
      else if ( i == j + 1 ) then
        a(i,j) = sqrt ( x(i) )
      else if ( i == j - 1 ) then
        a(i,j) = - sqrt ( x(i+1) )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
