subroutine rutis4 ( n, a )

!*****************************************************************************80
!
!! RUTIS4 returns the RUTIS4 matrix.
!
!  Example:
!
!    N = 6
!
!    14 14  6  1  0  0
!    14 20 15  6  1  0
!     6 15 20 15  6  1
!     1  6 15 20 15  6
!     0  1  6 15 20 14
!     0  0  1  6 14 14
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    A is integral, therefore det ( A ) is integral, and 
!    det ( A ) * inverse ( A ) is integral.
!
!    A is banded with a bandwidth of 7.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    A is the cube of the scalar tridiagonal matrix whose diagonals
!    are ( 1, 2, 1 ).
!
!    LAMBDA(I) = 64 * ( cos ( i * pi / ( 2 * ( n + 1 ) ) ) )^6
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    23 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Joan Westlake,
!    A Handbook of Numerical Matrix Inversion and Solution of 
!    Linear Equations,
!    John Wiley, 1968,
!    ISBN13: 978-0471936756,
!    LC: QA263.W47.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i

  a(1:n,1:n) = 0.0D+00

  do i = 1, n

    if ( 1 <= i - 3 ) then
      a(i,i-3) = 1.0D+00
    end if

    if ( 1 <= i - 2 ) then
      a(i,i-2) = 6.0D+00
    end if

    if ( 1 <= i - 1 ) then
      a(i,i-1) = 15.0D+00
    end if

    a(i,i) = 20.0D+00

    if ( i + 1 <= n ) then
      a(i,i+1) = 15.0D+00
    end if

    if ( i + 2 <= n ) then
      a(i,i+2) = 6.0D+00
    end if

    if ( i + 3 <= n ) then
      a(i,i+3) = 1.0D+00
    end if


  end do

  a(1,1) = 14.0D+00
  a(1,2) = 14.0D+00
  a(2,1) = 14.0D+00

  a(n,n) = 14.0D+00
  a(n-1,n) = 14.0D+00
  a(n,n-1) = 14.0D+00

  return
end
subroutine rutis4_determinant ( n, determ )

!*****************************************************************************80
!
!! RUTIS4_DETERMINANT returns the determinant of the RUTIS4 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) angle
  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00

  determ = 1.0D+00

  do i = 1, n
    angle = real ( i, kind = 8 ) * pi / real ( 2 * ( n + 1 ), kind = 8 )
    determ = determ * 64.0D+00 * ( cos ( angle ) )**6
  end do

  return
end
subroutine rutis4_eigenvalues ( n, lambda )

!*****************************************************************************80
!
!! RUTIS4_EIGENVALUES returns the eigenvalues of the RUTIS4 matrix.
!
!  Licensing:
