!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) angle
  integer ( kind = 4 ) i
  real ( kind = 8 ) lambda(n)
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00

  do i = 1, n
    angle = real ( i, kind = 8 ) * pi / real ( 2 * ( n + 1 ), kind = 8 )
    lambda(i) = 64.0D+00 * ( cos ( angle ) )**6
  end do

  return
end
subroutine rutis4_inverse ( n, a )

!*****************************************************************************80
!
!! RUTIS4_INVERSE returns the inverse of the RUTIS4 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    05 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)

  call oto_inverse ( n, a )

  a(1:n,1:n) = matmul ( a(1:n,1:n), matmul ( a(1:n,1:n), a(1:n,1:n) ) )

  return
end
