subroutine sylvester ( n, nx, x, ny, y, a )

!*****************************************************************************80
!
!! SYLVESTER returns the SYLVESTER matrix.
!
!  Formula:
!
!    For rows 1 through NY,
!
!      A(I,J) = X(NX+I-J)
!
!    For rows NY+1 through NY+NX:
!
!      A(I,J) = Y(I-J)
!
!  Example:
!
!    N = 5,
!    NX = 3, X = ( 1, 2, 3, 4 ),
!    NY = 2, Y = ( 5, 6, 7 )
!
!    4 3 2 1 0
!    0 4 3 2 1
!    7 6 5 0 0
!    0 7 6 5 0
!    0 0 7 6 5
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    Given two polynomials, P1(X) and P2(X) of orders N1 and N2 respectively,
!    if P1 has the the roots X1 through XN1, and leading coefficient
!    A, then the resultant R(P1,P2) is
!
!      R1(P1,P2) = A**N2 * P2(X1) * P2(X2) * ... * P2(XN1).
!
!    The resultant is zero if and only if P1 and P2 have a common root.
!
!
!    The determinant of the Sylvester matrix is the resultant of the
!    polynomials whose coefficient vectors are X and Y.  Thus, the
!    polynomials have a common zero if and only if the resultant is zero.
!    This fact allows the resultant to be calculated without determining
!    the roots of the polynomial.
!
!
!    The coefficient vector C(0:N) represents the polynomial
!
!      C(N) * X^N + C(N-1) * X^(N-1) + ... + C(1) * X + C(0).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 March 2001
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Jacqueline Burm, Paul Fishback,
!    Period-3 Orbits Via Sylvester's Theorem and Resultants,
!    Mathematics Magazine,
!    Volume 74, Number 1, February 2001, pages 47-51.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, integer ( kind = 4 ) NX, the dimension of X.
!
!    Input, real ( kind = 8 ) X(0:NX), the first polynomial coefficient vector.
!
!    Input, integer ( kind = 4 ) NY, the dimension of Y.
!
!    Input, real ( kind = 8 ) Y(0:NY), the second polynomial coefficient vector.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n
  integer ( kind = 4 ) nx
  integer ( kind = 4 ) ny

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(0:nx)
  real ( kind = 8 ) y(0:ny)

  if ( nx + ny /= n ) then
    write ( *, '(a)' ) ' '
    write ( *, '(a)' ) 'SYLVESTER - Fatal error!'
    write ( *, '(a)' ) '  NX + NY = N is required.'
    write ( *, '(a,i8)' ) '  NX = ', nx
    write ( *, '(a,i8)' ) '  NY = ', ny
    write ( *, '(a,i8)' ) '  N =  ', n
    stop
  end if

  a(1:n,1:n) = 0.0D+00

  do i = 1, nx + ny

    if ( i <= ny ) then

      do j = i, i + nx
        a(i,j) = x(nx+i-j)
      end do

    else

      do j = i - ny, i
        a(i,j) = y(i-j)
      end do

    end if

  end do

  return
end
subroutine symm_random ( n, x, seed, a )

!*****************************************************************************80
!
!! SYMM_RANDOM returns the SYMM_RANDOM matrix.
!
!  Discussion:
!
!    The user is able to specify the eigenvalues.
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    The eigenvalues of A will be real.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 April 2000
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the desired eigenvalues for the matrix.
!
!    Input/output, integer ( kind = 4 ) SEED, a seed for the random 
!    number generator.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) q(n,n)
  integer ( kind = 4 ) seed
  real ( kind = 8 ) x(n)
!
!  Get a random orthogonal matrix Q.
!
  call orth_random ( n, seed, q )
!
!  Set A = Q * Lambda * Q'.
!
  do j = 1, n
    do i = 1, n
      a(i,j) = 0.0D+00
      do k = 1, n
        a(i,j) = a(i,j) + q(i,k) * x(k) * q(j,k)
      end do
    end do
  end do

  return
end
subroutine symm_random_determinant ( n, x, determ )

!*****************************************************************************80
!
!! SYMM_RANDOM_DETERMINANT returns the determinant of the SYMM_RANDOM matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    25 October 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the desired eigenvalues for the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ
  real ( kind = 8 ) x(n)

  determ = product ( x(1:n) )

  return
end
subroutine symm_random_eigenvalues ( n, x, lambda )

!*****************************************************************************80
!
!! SYMM_RANDOM_EIGENVALUES returns the eigenvalues of the SYMM_RANDOM matrix.
!
