subroutine tri_upper_inverse ( alpha, n, a )

!*****************************************************************************80
!
!! TRI_UPPER_INVERSE returns the inverse of the TRI_UPPER matrix.
!
!  Formula:
!
!    if ( I = J ) then
!      A(I,J) = 1
!    else if ( I = J - 1 ) then
!      A(I,J) = -ALPHA
!    else if ( I < J ) then
!      A(I,J) = - ALPHA * ( 1-ALPHA)^(J-I-1)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 3, N = 5
!
!    1 -3  6 -12  24
!    0  1 -3   6 -12
!    0  0  1  -3   6
!    0  0  0   1  -3
!    0  0  0   0   1
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is nonsingular.
!
!    A is upper triangular.
!
!    A is Toeplitz: constant along diagonals.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    det ( A ) = 1.
!
!    A is unimodular.
!
!    LAMBDA(1:N) = 1.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, value used on the superdiagonals.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j

  do j = 1, n
    do i = 1, n

      if ( i == j ) then
        a(i,j) = 1.0D+00
      else if ( i == j - 1 ) then
        a(i,j) = - alpha
      else if ( i < j ) then
        a(i,j) = - alpha * ( 1.0D+00 - alpha )**( j - i - 1 )
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine tridiagonal_determinant ( n, a, determ )

!*****************************************************************************80
!
!! TRIDIAGONAL_DETERMINANT computes the determinant of a tridiagonal matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    06 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) A(N,N), the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) determ
  real ( kind = 8 ) determ_nm1
  real ( kind = 8 ) determ_nm2
  integer ( kind = 4 ) i

  determ_nm1 = a(n,n)

  if ( n == 1 ) then
    determ = determ_nm1
    return
  end if

  determ_nm2 = determ_nm1
  determ_nm1 = a(n-1,n-1) * a(n,n) - a(n-1,n) * a(n,n-1)

  if ( n == 2 ) then
    determ = determ_nm1
    return
  end if

  do i = n - 2, 1, -1

    determ = a(i,i) * determ_nm1 - a(i,i+1) * a(i+1,i) * determ_nm2

    determ_nm2 = determ_nm1
    determ_nm1 = determ
    
  end do

  return
end
subroutine tris ( m, n, x, y, z, a )

!*****************************************************************************80
!
!! TRIS returns the TRIS matrix.
!
!  Discussion:
!
!    The matrix is a tridiagonal matrix defined by three scalars.
!
!    See page 155 of the Todd reference.
!
!  Formula:
!
!    if ( J = I-1 )
!      A(I,J) = X
!    else if ( J = I )
!      A(I,J) = Y
!    else if ( J = I + 1 )
!      A(I,J) = Z
!    else
!      A(I,J) = 0
!
!  Example:
!
!    M = 5, N = 5, X = 1, Y = 2, Z = 3
!
!    2 3 0 0 0
!    1 2 3 0 0
!    0 1 2 3 0
!    0 0 1 2 3
!    0 0 0 1 2
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is tridiagonal.
!
!    Because A is tridiagonal, it has property A (bipartite).
!
!    A is banded, with bandwidth 3.
!
!    A is Toeplitz: constant along diagonals.
!
!    If Y is not zero, then for A to be singular, it must be the case that
!
!      0.5 * Y / sqrt ( X * Z ) < 1
!
!    and
!
!      cos (K*PI/(N+1)) = - 0.5 * Y / sqrt ( X * Z ) for some 1 <= K <= N.
!
!    If Y is zero, then A is singular when N is odd, or if X or Z is zero.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    A has eigenvalues
!
!      LAMBDA(I) = Y + 2 * sqrt(X*Z) * COS(I*PI/(N+1))
!
!    The eigenvalues will be complex if X * Z < 0.
!
!    If X = Z, the matrix is symmetric.
!
!    As long as X and Z are nonzero, the matrix is irreducible.
!
!    If X = Z = -1, and Y = 2, the matrix is a symmetric, positive
!    definite M matrix, the negative of the second difference matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    01 July 2000
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    John Todd,
!    Basic Numerical Mathematics,
!    Volume 2: Numerical Algebra,
!    Birkhauser, 1980,
!    ISBN: 0817608117,
!    LC: QA297.T58.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) M, N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X, Y, Z, the scalars that define A.
!
!    Output, real ( kind = 8 ) A(M,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) m
  integer ( kind = 4 ) n

  real ( kind = 8 ) a(m,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x
  real ( kind = 8 ) y
  real ( kind = 8 ) z

  do j = 1, n
    do i = 1, m

      if ( j == i - 1 ) then
        a(i,j) = x
      else if ( j == i ) then
        a(i,j) = y
      else if ( j == i + 1 ) then
        a(i,j) = z
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine tris_determinant ( n, x, y, z, determ )

!*****************************************************************************80
!
!! TRIS_DETERMINANT returns the determinant of the TRIS matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    03 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X, Y, Z, the scalars that define the matrix.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  real ( kind = 8 ) angle
  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) i_hi
  integer ( kind = 4 ) n
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00
  real ( kind = 8 ) x
  real ( kind = 8 ) y
  real ( kind = 8 ) z

  determ = 1.0D+00

  if ( 0.0D+00 <= x * z ) then

    do i = 1, n
      angle = real ( i, kind = 8 ) * pi / real ( n + 1, kind = 8 )
      determ = determ * ( y + 2.0D+00 * sqrt ( x * z ) * cos ( angle ) )
    end do

  else

    i_hi = n / 2

    do i = 1, i_hi
      angle = real ( i, kind = 8 ) * pi / real ( n + 1, kind = 8 )
      determ = determ * ( y * y - 4.0D+00 * x * z * cos ( angle )**2 )
    end do

    if ( mod ( n, 2 ) == 1 ) then
      determ = determ * y
    end if

  end if

  return
end
subroutine tris_eigenvalues ( n, x, y, z, lambda )

!*****************************************************************************80
!
!! TRIS_EIGENVALUES returns the eigenvalues of the TRIS matrix.
!
!  Discussion:
!
!    The eigenvalues will be complex if X * Z < 0.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    09 June 2003
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X, Y, Z, the scalars that define A.
!
!    Output, complex ( kind = 8 ) LAMBDA(N), the eigenvalues.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) angle
  complex ( kind = 8 ) arg
  integer ( kind = 4 ) i
  complex ( kind = 8 ) lambda(n)
  real ( kind = 8 ), parameter :: pi = 3.141592653589793D+00
  real ( kind = 8 ) x
  real ( kind = 8 ) y
  real ( kind = 8 ) z

  do i = 1, n
    angle = real ( i, kind = 8 ) * pi / real ( n + 1, kind = 8 )
    arg = cmplx ( x * z, 0.0D+00, kind = 8 )
    lambda(i) = y + 2.0D+00 * sqrt ( arg ) * cos ( angle )
  end do

  return
end
subroutine tris_inverse ( n, alpha, beta, gamma, a )

!*****************************************************************************80
!
!! TRIS_INVERSE returns the inverse of the TRIS matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    19 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    CM daFonseca, J Petronilho,
!    Explicit Inverses of Some Tridiagonal Matrices,
!    Linear Algebra and Its Applications,
!    Volume 325, 2001, pages 7-21.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) ALPHA, BETA, GAMMA, the constant values
!    associated with the subdiagonal, diagonal and superdiagonal of 
!    the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the inverse of the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  real ( kind = 8 ) beta
  real ( kind = 8 ) d(n)
  real ( kind = 8 ) gamma
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8_mop

  d(n) = beta
  do i = n - 1, 1, -1
    d(i) = beta - alpha * gamma / d(i+1)
  end do

  do i = 1, n
    do j = 1, i
      a(i,j) = r8_mop ( i + j ) * alpha**( i - j ) &
        * product ( d(i+1:n) ) / product ( d(1:n+1-j) )
    end do
    do j = i + 1, n
      a(i,j) = r8_mop ( i + j ) * gamma**( j - i ) &
        * product ( d(j+1:n) ) / product ( d(1:n+1-i) )
    end do
  end do

  return
end
subroutine triv ( n, x, y, z, a )

!*****************************************************************************80
!
!! TRIV returns the TRIV matrix.
!
!  Discussion:
!
!    The three vectors define the subdiagonal, main diagonal, and
!    superdiagonal.
!
!  Formula:
!
!    if ( J = I - 1 )
!      A(I,J) = X(J)
!    else if ( J = I )
!      A(I,J) = Y(I)
!    else if ( J = I + 1 )
!      A(I,J) = Z(I)
!    else
!      A(I,J) = 0
!
!  Example:
!
!    N = 5, X = ( 1, 2, 3, 4 ), Y = ( 5, 6, 7, 8, 9 ), Z = ( 10, 11, 12, 13 )
!
!     5 10  0  0  0
!     1  6 11  0  0
!     0  2  7 12  0
!     0  0  3  8 13
!     0  0  0  4  9
!
!  Properties:
!
!    A is tridiagonal.
!
!    A is banded, with bandwidth 3.
!
!    A is generally not symmetric: A' /= A.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N-1), Y(N), Z(N-1), the vectors that define
!    the subdiagonal, diagonal, and superdiagonal of A.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n-1)
  real ( kind = 8 ) y(n)
  real ( kind = 8 ) z(n-1)

  do j = 1, n
    do i = 1, n

      if ( j == i - 1 ) then
        a(i,j) = x(j)
      else if ( j == i ) then
        a(i,j) = y(i)
      else if ( j == i + 1 ) then
        a(i,j) = z(i)
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine triv_determinant ( n, x, y, z, determ )

!*****************************************************************************80
!
!! TRIV_DETERMINANT computes the determinant of the TRIV matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    06 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N-1), Y(N), Z(N-1), the vectors that define
!    the subdiagonal, diagonal, and superdiagonal of A.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ
  real ( kind = 8 ) determ_nm1
  real ( kind = 8 ) determ_nm2
  integer ( kind = 4 ) i
  real ( kind = 8 ) x(n-1)
  real ( kind = 8 ) y(n)
  real ( kind = 8 ) z(n-1)
  
  determ_nm1 = y(n)

  if ( n == 1 ) then
    determ = determ_nm1
    return
  end if

  determ_nm2 = determ_nm1
  determ_nm1 = y(n-1) * y(n) - z(n-1) * x(n-1)

  if ( n == 2 ) then
    determ = determ_nm1
    return
  end if

  do i = n - 2, 1, -1

    determ = y(i) * determ_nm1 - z(i) * x(i) * determ_nm2

    determ_nm2 = determ_nm1
    determ_nm1 = determ
    
  end do

  return
end
subroutine triv_inverse ( n, x, y, z, a )

!*****************************************************************************80
!
!! TRIV_INVERSE returns the inverse of the TRIV matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 November 2007
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    CM daFonseca, J Petronilho,
!    Explicit Inverses of Some Tridiagonal Matrices,
!    Linear Algebra and Its Applications,
!    Volume 325, 2001, pages 7-21.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N-1), Y(N), Z(N-1), the vectors that define
!    the subdiagonal, diagonal, and superdiagonal of A.
!
!    Output, real ( kind = 8 ) A(N,N), the inverse of the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) d(n)
  real ( kind = 8 ) e(n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) r8_mop
  real ( kind = 8 ) x(n-1)
  real ( kind = 8 ) y(n)
  real ( kind = 8 ) z(n-1)

  d(n) = y(n)
  do i = n - 1, 1, -1
    d(i) = y(i) - x(i) * z(i) / d(i+1)
  end do

  e(1) = y(1)
  do i = 2, n
    e(i) = y(i) - x(i-1) * z(i-1) / e(i-1)
  end do

  do i = 1, n
    do j = 1, i
      a(i,j) = r8_mop ( i + j ) * product ( x(j:i-1) ) &
        * product ( d(i+1:n) ) / product ( e(j:n) )
    end do
    do j = i + 1, n
      a(i,j) = r8_mop ( i + j ) * product ( z(i:j-1) ) &
        * product ( d(j+1:n) ) / product ( e(i:n) )
    end do
  end do

  return
end
subroutine triw ( alpha, k, n, a )

!*****************************************************************************80
!
!! TRIW returns the TRIW matrix.
!
!  Discussion:
!
!    The matrix is the Wilkinson banded upper triangular matrix.
!
!  Formula:
!
!    if ( I = J )
!      A(I,J) = 1
!    else if ( I < J and J <= K + I )
!      A(I,J) = ALPHA
!    else
!      A(I,J) = 0
!
!  Example:
!
!    ALPHA = 3, K = 2, N = 5
!
!    1 3 3 0 0
!    0 1 3 3 0
!    0 0 1 3 3
!    0 0 0 1 3
!    0 0 0 0 1
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is nonsingular.
!
!    A is upper triangular.
!
!    det ( A ) = 1.
!
!    A is unimodular.
!
!    LAMBDA(1:N) = 1.
!
!    A is Toeplitz: constant along diagonals.
!
!    A is persymmetric: A(I,J) = A(N+1-J,N+1-I).
!
!    Adding -2^(2-N) to the (N,1) element makes the matrix singular.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Gene Golub, James Wilkinson,
!    Ill-conditioned eigensystems and the computation of the Jordan
!    canonical form,
!    SIAM Review,
!    Volume 18, Number 4, 1976, pages 578-619.
!
!    W Kahan,
!    Numerical linear algebra,
!    Canadian Mathematical Bulletin,
!    Volume 9, 1966, pages 757-801.
!
!    AM Ostrowski,
!    On the spectrum of a one-parametric family of matrices,
!    Journal fuer Reine und Angewandte Mathematik,
!    Volume 193, Number (3/4), 1954, pages 143-160.
!
!    James Wilkinson,
!    Singular-value decomposition - basic aspects,
!    in Numerical Software - Needs and Availability,
!    edited by DAH Jacobs,
!    Academic Press, London, 1978, pages 109-135.
!
!  Parameters:
!
!    Input, real ( kind = 8 ) ALPHA, the superdiagonal value. 
!    A typical value is -1.
!
!    Input, integer ( kind = 4 ) K, the number of nonzero superdiagonals.  
!    A typical value is N-1.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ) alpha
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k

  do j = 1, n
    do i = 1, n

      if ( i == j ) then
        a(i,j) = 1.0D+00
      else if ( i < j .and. j - i <= k ) then
        a(i,j) = alpha
      else
        a(i,j) = 0.0D+00
      end if

    end do
  end do

  return
end
subroutine triw_determinant ( alpha, k, n, determ )

!*****************************************************************************80
!
!! TRIW_DETERMINANT returns the determinant of the TRIW matrix.
!
!  Licensing:
