subroutine vand1 ( n, x, a )

!*****************************************************************************80
!
!! VAND1 returns the VAND1 matrix.
!
!  Formula:
!
!    A(I,J) = X(J)^(I-1)
!
!  Example:
!
!    N = 5, 
!    X = ( 2, 3, 4, 5, 6 )
!
!    1  1   1   1   1
!    2  3   4   5   6
!    4  9  16  25  36
!    8 27  64 125  216
!   16 81 256 625 1296
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is nonsingular if, and only if, the X values are distinct.
!
!    det ( A ) = product ( 1 <= I <= N ) ( 1 <= J < I ) ( X(I) - X(J) ).
!             = product ( 1 <= J <= N ) X(J)
!             * product ( 1 <= I < J ) ( X(J) - X(I) ).
!
!    A is generally ill-conditioned.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Robert Gregory, David Karney,
!    A Collection of Matrices for Testing Computational Algorithms,
!    Wiley, 1969,
!    ISBN: 0882756494,
!    LC: QA263.68
!
!    Nicholas Higham,
!    Stability analysis of algorithms for solving confluent
!    Vandermonde-like systems,
!    SIAM Journal on Matrix Analysis and Applications,
!    Volume 11, 1990, pages 23-41.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the values that define A.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n)

  do j = 1, n
    do i = 1, n

      if ( i == 1 .and. x(j) == 0.0D+00 ) then
        a(i,j) = 1.0D+00
      else
        a(i,j) = x(j)**( i - 1 )
      end if

    end do
  end do

  return
end
subroutine vand1_determinant ( n, x, determ )

!*****************************************************************************80
!
!! VAND1_DETERMINANT returns the determinant of the VAND1 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the values that define A.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n)

  determ = 1.0D+00

  do i = 1, n
    do j = 1, i - 1
      determ = determ * ( x(i) - x(j) )
    end do
  end do

  return
end
subroutine vand1_inverse ( n, x, a )

!*****************************************************************************80
!
!! VAND1_INVERSE returns the inverse of the VAND1 matrix.
!
!  Formula:
!
!    A(I,J) = coefficient of X^(J-1) in I-th Lagrange basis polynomial.
!
!  Example:
!
!    N = 5, 
!    X = ( 2, 3, 4, 5, 6 )
!
!     15.00  -14.25    4.96  -0.75   0.04
!    -40.00   44.67  -17.33   2.83  -0.17
!     45.00  -54.00   22.75  -4.00   0.25
!    -24.00   30.00  -13.33   2.50  -0.17
!      5.00   -6.42    2.96  -0.58   0.04
!
!  Properties:
!
!    The sum of the entries of A is
!
!      1 - product ( 1 <= I <= N ) ( 1 - 1 / X(I) ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    10 January 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the values that define A.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) index
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) x(n)

  do j = 1, n
    do i = 1, n
      if ( j == 1 ) then
        a(i,j) = 1.0D+00
      else
        a(i,j) = 0.0D+00
      end if
    end do
  end do

  do i = 1, n

    index = 0

    do k = 1, n

      if ( k /= i ) then

        index = index + 1

        do j = index + 1, 1, -1

          a(i,j) = - x(k) * a(i,j) / ( x(i) - x(k) )

          if ( 1 < j ) then
            a(i,j) = a(i,j) + a(i,j-1) / ( x(i) - x(k) )
          end if

        end do

      end if

    end do

  end do

  return
end
subroutine vand2 ( n, x, a )

!*****************************************************************************80
!
!! VAND2 returns the VAND2 matrix.
!
!  Discussion:
!
!    For this version of the Vandermonde matrix, the 1's occur in the
!    first column.
!
!  Formula:
!
!    A(I,J) = X(I)^(J-1)
!
!  Example:
!
!    N = 5,
!    X = (2, 3, 4, 5, 6)
!
!    1 2  4   8   16
!    1 3  9  27   81
!    1 4 16  64  256
!    1 5 25 125  625
!    1 6 36 216 1296
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is nonsingular if, and only if, the X values are distinct.
!
!    det ( A ) = product ( 1 <= I <= N ) (
!      product ( 1 <= J < I ) ( ( X(I) - X(J) ) ) ).
!
!    det ( A ) = product ( 1 <= I <= N ) (
!        X(I) * product ( 1 <= J <= I - 1 ) ( ( X(I) - X(J) ) ).
!
!    A is generally ill-conditioned.
!
!    The sum of the entries of A is
!
!      1 - product ( 1 <= I <= N ) ( 1 - 1 / X(I) ).
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Nicholas Higham,
!    Stability analysis of algorithms for solving confluent
!    Vandermonde-like systems,
!    SIAM Journal on Matrix Analysis and Applications,
!    Volume 11, 1990, pages 23-41.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the values that define A.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n)

  do j = 1, n
    do i = 1, n

      if ( j == 1 .and. x(i) == 0.0D+00 ) then
        a(i,j) = 1.0D+00
      else
        a(i,j) = x(i)**( j - 1 )
      end if

    end do
  end do

  return
end
subroutine vand2_determinant ( n, x, determ )

!*****************************************************************************80
!
!! VAND2_DETERMINANT returns the determinant of the VAND2 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the values that define A.
!
!    Output, real ( kind = 8 ) DETERM, the determinant.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) determ
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x(n)

  determ = 1.0D+00

  do j = 1, n
    do i = 1, j - 1
      determ = determ * ( x(i) - x(j) )
    end do
  end do

  return
end
subroutine vand2_inverse ( n, x, a )

!*****************************************************************************80
!
!! VAND2_INVERSE returns the inverse of the VAND2 matrix.
!
!  Formula:
!
!    A(I,J) = coefficient of X^(I-1) in J-th Lagrange basis polynomial.
!
!  Example:
!
!    N = 5, X = ( 2, 3, 4, 5, 6 )
!
!     15.00  -40.00   45.00  -24.00   5.00
!    -14.25   44.67  -54.00   30.00  -6.42
!      4.96  -17.33   22.75  -13.33   2.96
!     -0.75    2.83   -4.00    2.50  -0.58
!      0.04   -0.17    0.25   -0.17   0.04
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    10 January 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X(N), the values that define the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) index
  integer ( kind = 4 ) j
  integer ( kind = 4 ) k
  real ( kind = 8 ) x(n)

  do j = 1, n
    do i = 1, n
      if ( i == 1 ) then
        a(i,j) = 1.0D+00
      else
        a(i,j) = 0.0D+00
      end if
    end do
  end do

  do i = 1, n

    index = 0

    do k = 1, n

      if ( k /= i ) then

        index = index + 1

        do j = index + 1, 1, -1

          a(j,i) = - x(k) * a(j,i) / ( x(i) - x(k) )

          if ( 1 < j ) then
            a(j,i) = a(j,i) + a(j-1,i) / ( x(i) - x(k) )
          end if

        end do

      end if

    end do

  end do

  return
end
subroutine wathen ( nx, ny, n, a )

!*****************************************************************************80
!
!! WATHEN returns the WATHEN matrix.
!
!  Discussion:
!
!    The Wathen matrix is a finite element matrix which is sparse.
!
!    The entries of the matrix depend in part on a physical quantity
!    related to density.  That density is here assigned random values between
!    0 and 100.
!
!    The matrix order N is determined by the input quantities NX and NY,
!    which would usually be the number of elements in the X and Y directions.
!    The value of N is
!
!      N = 3*NX*NY + 2*NX + 2*NY + 1,
!
!    and sufficient storage in A must have been set aside to hold
!    the matrix.
!
!    A is the consistent mass matrix for a regular NX by NY grid
!    of 8 node serendipity elements.  
!
!    Here is an illustration for NX = 3, NX = 2:
!
!     23-24-25-26-27-28-29
!      |     |     |     |
!     19    20    21    22
!      |     |     |     |
!     12-13-14-15-16-17-18
!      |     |     |     |
!      8     9    10    11
!      |     |     |     |
!      1--2--3--4--5--6--7
!
!    For this example, the total number of nodes is, as expected,
!
!      N = 3 * 3 * 2 + 2 * 2 + 2 * 3 + 1 = 29
!
!  Properties:
!
!    A is symmetric positive definite for any positive values of the
!    density RHO(NX,NY), which is here given the value 1.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    24 March 2006
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Nicholas Higham,
!    Algorithm 694: A Collection of Test Matrices in MATLAB,
!    ACM Transactions on Mathematical Software,
!    Volume 17, Number 3, September 1991, pages 289-305.
!
!    Andrew Wathen,
!    Realistic eigenvalue bounds for the Galerkin mass matrix,
!    IMA Journal of Numerical Analysis,
!    Volume 7, 1987, pages 449-457.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) NX, NY, values which determine the size of A.
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  real ( kind = 8 ), dimension ( 8, 8 ), save :: em =  reshape ( (/ &
     6.0, -6.0,  2.0, -8.0,  3.0, -8.0,  2.0, -6.0, &
    -6.0, 32.0, -6.0, 20.0, -8.0, 16.0, -8.0, 20.0, &
     2.0, -6.0,  6.0, -6.0,  2.0, -8.0,  3.0, -8.0, &
    -8.0, 20.0, -6.0, 32.0, -6.0, 20.0, -8.0, 16.0, &
     3.0, -8.0,  2.0, -6.0,  6.0, -6.0,  2.0, -8.0, &
    -8.0, 16.0, -8.0, 20.0, -6.0, 32.0, -6.0, 20.0, &
     2.0, -8.0,  3.0, -8.0,  2.0, -6.0,  6.0, -6.0, &
    -6.0, 20.0, -8.0, 16.0, -8.0, 20.0, -6.0, 32.0 /), &
    (/ 8, 8 /) )
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  integer ( kind = 4 ) kcol
  integer ( kind = 4 ) krow
  integer ( kind = 4 ) nx
  integer ( kind = 4 ) ny
  integer ( kind = 4 ) node(8)
  real ( kind = 8 ) r8_uniform_01
  real ( kind = 8 ) rho
  integer ( kind = 4 ) seed

  a(1:n,1:n) = 0.0D+00

  do j = 1, ny

    do i = 1, nx
!
!  For the element (I,J), determine the indices of the 8 nodes.
!
      node(1) = 3 * j * nx + 2 * j + 2 * i + 1
      node(2) = node(1) - 1
      node(3) = node(1) - 2

      node(4) = ( 3 * j - 1 ) * nx + 2 * j + i - 1
      node(8) = node(4) + 1

      node(5) = ( 3 * j - 3 ) * nx + 2 * j + 2 * i - 3
      node(6) = node(5) + 1
      node(7) = node(5) + 2
!
!  The density RHO can also be set to a random positive value.
!
      do krow = 1, 8
        do kcol = 1, 8

          if ( node(krow) < 1 .or. n < node(krow) ) then
            write ( *, '(a)' ) ' '
            write ( *, '(a)' ) 'WATHEN - Fatal error!'
            write ( *, '(a)' ) '  Index NODE(KROW) out of bounds.'
            write ( *, '(a,i8)' ) '  I = ', i
            write ( *, '(a,i8)' ) '  J = ', j
            write ( *, '(a,i8)' ) '  KROW = ', krow
            write ( *, '(a,i8)' ) '  NODE(KROW) = ', node(krow)
            stop
          else if ( node(kcol) < 1 .or. n < node(kcol) ) then
            write ( *, '(a)' ) ' '
            write ( *, '(a)' ) 'WATHEN - Fatal error!'
            write ( *, '(a)' ) '  Index NODE(KCOL) out of bounds.'
            write ( *, '(a,i8)' ) '  I = ', i
            write ( *, '(a,i8)' ) '  J = ', j
            write ( *, '(a,i8)' ) '  KCOL = ', kcol
            write ( *, '(a,i8)' ) '  NODE(KCOL) = ', node(kcol)
            stop
          end if

          rho = 1.0D+00
!         rho = 100.0D+00 * r8_uniform_01 ( seed )

          a(node(krow),node(kcol)) = a(node(krow),node(kcol)) &
            + 20.0D+00 * rho * em(krow,kcol) / 9.0D+00

        end do
      end do

    end do
  end do

  return
end
subroutine wathen_order ( nx, ny, n )

!*****************************************************************************80
!
!! WATHEN_ORDER returns the order of the WATHEN matrix.
!
!  Discussion:
!
!    N = 3 * 3 * 2 + 2 * 2 + 2 * 3 + 1 = 29
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    17 June 2011
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Nicholas Higham,
!    Algorithm 694: A Collection of Test Matrices in MATLAB,
!    ACM Transactions on Mathematical Software,
!    Volume 17, Number 3, September 1991, pages 289-305.
!
!    Andrew Wathen,
!    Realistic eigenvalue bounds for the Galerkin mass matrix,
!    IMA Journal of Numerical Analysis,
!    Volume 7, 1987, pages 449-457.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) NX, NY, values which determine the size of A.
!
!    Output, integer ( kind = 4 ) N, the order of the matrix, 
!    as determined by NX and NY.
!
  implicit none

  integer ( kind = 4 ) n
  integer ( kind = 4 ) nx
  integer ( kind = 4 ) ny

  n = 3 * nx * ny + 2 * nx + 2 * ny + 1

  return
end
subroutine wilk03 ( a )

!*****************************************************************************80
!
!! WILK03 returns the WILK03 matrix.
!
!  Formula:
!
!    1.0D-10  0.9  -0.4
!    0        0.9  -0.4
!    0        0     1.0D-10
!
!  Discussion:
!
!    The linear equation under study is
!      A * X = B,
!    where A is the 3 by 3 Wilkinson matrix, and
!      B = ( 0, 0, 1 )'
!    and the correct solution is
!      X = ( 0, 4.0D+10 / 9.0D+00, 1.0D+10 )
!
!    Since the matrix is already in upper triangular form, errors can
!    occur only in the backsubstitution.
!
!  Properties:
!
!    A is generally not symmetric: A' /= A.
!
!    A is upper triangular.
!
!    det ( A ) = 0.9D-20
!
!    LAMBDA = ( 1.0D-10, 0.9, 1.0D-10 )
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    James Wilkinson,
!    Error Analysis of Direct Methods of Matrix Inversion,
!    Journal of the Association for Computing Machinery,
!    Volume 8, 1961, pages 281-330.
!
!  Parameters:
!
!    Output, real ( kind = 8 ) A(3,3), the matrix.
!
  implicit none

  real ( kind = 8 ) a(3,3)
  real ( kind = 8 ), dimension ( 3, 3 ), save :: a_save = reshape ( (/ &
    1.0D-10,  0.0D+00,  0.0D+00, &
    0.9D+00,  0.9D+00,  0.0D+00, &
   -0.4D+00, -0.4D+00,  1.0D-10 /), (/ 3, 3 /) )

  call r8mat_copy ( 3, 3, a_save, a )

  return
end
subroutine wilk03_condition ( cond )

!*****************************************************************************80
!
!! WILK03_CONDITION returns the L1 condition of the WILK03 matrix.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    08 April 2012
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    James Wilkinson,
!    Error Analysis of Direct Methods of Matrix Inversion,
!    Journal of the Association for Computing Machinery,
!    Volume 8, 1961, pages 281-330.
!
!  Parameters:
!
!    Output, real ( kind = 8 ) COND, the L1 condition.
!
  implicit none

  real ( kind = 8 ) cond

  cond = 1.8D+00 * ( 13.0D+00 * 10.0D+10 / 9.0D+00 )

  return
end
