subroutine wilk03_inverse ( a )

!*****************************************************************************80
!
!! WILK03_INVERSE returns the inverse of the WILK03 matrix.
!
!  Formula:
!
!    1.0D+10  -1.0D+10  0
!    0         10/9     4/9 * 1.0D+10
!    0         0        1.0D+10
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Output, real ( kind = 8 ) A(3,3), the matrix.
!
  implicit none

  real ( kind = 8 ) a(3,3)

  a(1,1) =   1.0D+10
  a(2,1) =   0.0D+00
  a(3,1) =   0.0D+00

  a(1,2) = - 1.0D+10
  a(2,2) =   10.0D+00 / 9.0D+00
  a(3,2) =   0.0D+00

  a(1,3) =   0.0D+00
  a(2,3) =   4.0D+10 / 9.0D+00
  a(3,3) =   1.0D+10

  return
end
subroutine wilk03_rhs ( b )

!*****************************************************************************80
!
!! WILK03_RHS returns the right hand side of the WILK03 linear system.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    21 May 2002
!
!  Author:
!
!    John Burkardt
!
!  Parameters:
!
!    Output, real ( kind = 8 ) B(3), the right hand side of the system.
!
  implicit none

  real ( kind = 8 ) b(3)
  real ( kind = 8 ), dimension(3), save :: b_save = (/ &
