subroutine zielke ( n, x, y, z, a )

!*****************************************************************************80
!
!! ZIELKE returns the ZIELKE matrix.
!
!  Formula:
!
!    if ( I == J ) then
!      if ( I + J <= N )
!        A(I,J) = X+Y+Z
!      else if ( I + J ) < 2*N )
!        A(I,J) = X  +Z
!      else
!        A(I,J) = X-Y+Z
!    else
!      if ( I + J <= N )
!        A(I,J) = X+Y
!      else
!        A(I,J) = X
!
!  Example:
!
!   N = 5, X = 1, Y = 2, Z = 5
!
!    8 3 3 3 1
!    3 8 3 1 1
!    3 3 6 1 1
!    3 1 1 6 1
!    1 1 1 1 4
!
!  Properties:
!
!    A is symmetric: A' = A.
!
!    Because A is symmetric, it is normal.
!
!    Because A is normal, it is diagonalizable.
!
!    There are clusters of eigenvalues.
!
!  Licensing:
!
!    This code is distributed under the GNU LGPL license.
!
!  Modified:
!
!    15 April 1999
!
!  Author:
!
!    John Burkardt
!
!  Reference:
!
!    Gerhard Zielke,
!    Testmatrizen mit maximaler Konditionszahl,
!    (Test matrices with maximal condition number),
!    Computing,
!    Volume 13, Number 1, March 1974, pages 33-54.
!
!  Parameters:
!
!    Input, integer ( kind = 4 ) N, the order of the matrix.
!
!    Input, real ( kind = 8 ) X, Y, Z, parameters that define the matrix.
!
!    Output, real ( kind = 8 ) A(N,N), the matrix.
!
  implicit none

  integer ( kind = 4 ) n

  real ( kind = 8 ) a(n,n)
  integer ( kind = 4 ) i
  integer ( kind = 4 ) j
  real ( kind = 8 ) x
  real ( kind = 8 ) y
  real ( kind = 8 ) z

  do i = 1, n
    do j = 1, n

      if ( i == j ) then
        if ( i + j <= n ) then
          a(i,j) = x + y + z
        else if ( i + j < 2 * n ) then
          a(i,j) = x + z
        else
          a(i,j) = x - y + z
        end if
      else
        if ( i + j <= n ) then
          a(i,j) = x + y
        else
          a(i,j) = x
        end if
      end if

    end do
  end do

  return
end
